INSERT INTO `v1_24be58b05b58cd3e1fbd03ee5771bc21aff6a720855ab324ffd359059c7d84c7` VALUES (1, "ペコリーヌの\n満腹ツインおにぎり", 51001, 52001, 0, 2, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 500, 1, 10, 1, "ペコリーヌの満腹ツインおにぎり", "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, 1, 0, "500マイルを獲得する。");
