INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("よい香りですね\n魔女教の悪臭とは\n大違いです", 1, 3147, 4, 0, 1, 109701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 1, 3148, 5, 0, 1, 109701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 1, 3150, 7, 0, 1, 109701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("絵がいっぱい…\n異世界の本は\nとても面白いですね", 1, 3151, 8, 0, 1, 109701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 3153, 1, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 3154, 2, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 3155, 3, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 3156, 4, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 3157, 5, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 4, 3158, 101, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 8, 3159, 102, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 3160, 1, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 1, 3161, 2, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 1, 3162, 3, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 1, 3163, 4, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 4, 3164, 101, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 8, 3165, 102, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 3166, 1, 0, 1, 109801, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 1, 3167, 2, 0, 1, 109801, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 1, 3168, 3, 0, 1, 109801, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…ここから動かない\nという、誓いを\n立てたくなるわね", 1, 3170, 2, 0, 1, 109801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これは…かわいいわね\nもちろん、\nラムほどではないけど", 1, 3171, 3, 0, 1, 109801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 1, 3172, 4, 0, 1, 109801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 1, 3173, 5, 0, 1, 109801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 1, 3175, 7, 0, 1, 109801, 0, 3);
