INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 1, 3200, 7, 0, 1, 109901, 0, 3);
