INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 12, 8032, 103, 0, 1, 108701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 8033, 1, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 8034, 2, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 8035, 3, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 8036, 4, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 8037, 5, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 4, 8038, 101, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 8, 8039, 102, 0, 1, 129401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 8040, 1, 0, 1, 129401, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 8041, 2, 0, 1, 129401, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 8042, 3, 0, 1, 129401, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 8043, 4, 0, 1, 129401, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 4, 8044, 101, 0, 1, 129401, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 8, 8045, 102, 0, 1, 129401, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 8046, 1, 0, 1, 129401, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 8047, 2, 0, 1, 129401, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 8048, 3, 0, 1, 129401, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 8050, 2, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 8051, 3, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 8052, 4, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 8053, 5, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 8055, 7, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 8056, 8, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 8058, 10, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 8059, 11, 0, 1, 129401, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("さてと…\n明日の訓練メニュー\n考えねえとな", 1, 8060, 1, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 8061, 2, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 8062, 3, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 8063, 4, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 8064, 5, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 4, 8065, 101, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 8, 8066, 102, 0, 1, 129801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 8067, 1, 0, 1, 129801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 8068, 2, 0, 1, 129801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 8069, 3, 0, 1, 129801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 8070, 4, 0, 1, 129801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 4, 8071, 101, 0, 1, 129801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 8, 8072, 102, 0, 1, 129801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 8073, 1, 0, 1, 129801, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 8074, 2, 0, 1, 129801, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 8075, 3, 0, 1, 129801, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 8077, 2, 0, 1, 129801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 8078, 3, 0, 1, 129801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 8079, 4, 0, 1, 129801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 8080, 5, 0, 1, 129801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 8082, 7, 0, 1, 129801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 8083, 8, 0, 1, 129801, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 8085, 1, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 8086, 2, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 8087, 3, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 8088, 4, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 8089, 5, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 4, 8090, 101, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 8, 8091, 102, 0, 1, 129901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 8092, 1, 0, 1, 129901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 8093, 2, 0, 1, 129901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 8094, 3, 0, 1, 129901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 8095, 4, 0, 1, 129901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 4, 8096, 101, 0, 1, 129901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 8, 8097, 102, 0, 1, 129901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 8098, 1, 0, 1, 129901, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 8099, 2, 0, 1, 129901, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 8100, 3, 0, 1, 129901, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 8102, 2, 0, 1, 129901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 8103, 3, 0, 1, 129901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 8104, 4, 0, 1, 129901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 8105, 5, 0, 1, 129901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 8107, 7, 0, 1, 129901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 8108, 8, 0, 1, 129901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("……（そわそわ）", 1, 8110, 1, 0, 1, 129601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ここに寄らずの花陣…\n作ったらダメですよね", 1, 8111, 2, 0, 1, 129601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 8112, 3, 0, 1, 129601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("身を隠せる場所が\nあると…\n助かるんですけど", 1, 8113, 4, 0, 1, 129601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 8114, 5, 0, 1, 129601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("出入りするひと\nみんな、笑顔…\n…変わった家ですね", 4, 8115, 101, 0, 1, 129601, 0, 1);
