INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 8462, 3, 0, 1, 131901, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 8464, 2, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("細かいところまで\nすごーく丁寧に\n作られてる…", 1, 8465, 3, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 8466, 4, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 8467, 5, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 8469, 7, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 8470, 8, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 8472, 10, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 8473, 11, 0, 1, 131901, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 8474, 1, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 8475, 2, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 8476, 3, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 8477, 4, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 8478, 5, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 4, 8479, 101, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 8, 8480, 102, 0, 1, 132101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 8481, 1, 0, 1, 132101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 8482, 2, 0, 1, 132101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 8483, 3, 0, 1, 132101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 8484, 4, 0, 1, 132101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 4, 8485, 101, 0, 1, 132101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 8, 8486, 102, 0, 1, 132101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 8487, 1, 0, 1, 132101, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 8488, 2, 0, 1, 132101, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 8489, 3, 0, 1, 132101, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 8491, 2, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 8492, 3, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 8493, 4, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 8494, 5, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 8496, 7, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 8497, 8, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 8499, 10, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 8500, 11, 0, 1, 132101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これがこの世界の\n居住環境か…\n面白い構造だね", 1, 8501, 1, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 8502, 2, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 8503, 3, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 8504, 4, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 8505, 5, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 4, 8506, 101, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("普段と違う格好で\n歩き回るのも\n悪くない経験だ", 8, 8507, 102, 0, 1, 132001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 8508, 1, 0, 1, 132001, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("気軽に魔女に\n話しかけるなんて\n君は物好きだね", 1, 8509, 2, 0, 1, 132001, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この世界においては\nボクは異物だからね\n大人しくしているさ", 1, 8510, 3, 0, 1, 132001, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 1, 8511, 4, 0, 1, 132001, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 4, 8512, 101, 0, 1, 132001, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 8, 8513, 102, 0, 1, 132001, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 8514, 1, 0, 1, 132001, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 1, 8515, 2, 0, 1, 132001, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 1, 8516, 3, 0, 1, 132001, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 1, 8518, 2, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 1, 8519, 3, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("いい香りだ\nこれにはどんな\n効能があるんだろう？", 1, 8520, 4, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("火の点け方に\nボクのいた世界との\n違いはないのかな？", 1, 8521, 5, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 1, 8523, 7, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("素晴らしいね\nここには叡智が\n詰まっている", 1, 8524, 8, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 1, 8526, 10, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 1, 8527, 11, 0, 1, 132001, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 12, 8528, 103, 0, 1, 109701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 12, 8529, 103, 0, 1, 109701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 12, 8530, 103, 0, 1, 109801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 12, 8531, 103, 0, 1, 109801, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 12, 8532, 103, 0, 1, 109901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 12, 8533, 103, 0, 1, 109901, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 8534, 1, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 8535, 2, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 8536, 3, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 8537, 4, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 8538, 5, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 4, 8539, 101, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 8, 8540, 102, 0, 1, 132201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 8541, 1, 0, 1, 132201, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 1, 8542, 2, 0, 1, 132201, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 1, 8543, 3, 0, 1, 132201, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 1, 8544, 4, 0, 1, 132201, 0, 2);
