INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 1, 8800, 4, 0, 1, 133201, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 4, 8801, 101, 0, 1, 133201, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 8, 8802, 102, 0, 1, 133201, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 1, 8803, 1, 0, 1, 133201, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 1, 8804, 2, 0, 1, 133201, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 1, 8805, 3, 0, 1, 133201, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これに座って飲む\n甘酒は格別だな", 1, 8807, 2, 0, 1, 133201, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 1, 8808, 3, 0, 1, 133201, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("これは梅の香り…？\nふふ、なかなか\n風流ではないか", 1, 8809, 4, 0, 1, 133201, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 1, 8810, 5, 0, 1, 133201, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 1, 8812, 7, 0, 1, 133201, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 1, 8813, 8, 0, 1, 133201, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 1, 8815, 1, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 1, 8816, 2, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 1, 8817, 3, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 1, 8818, 4, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 1, 8819, 5, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 4, 8820, 101, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 8, 8821, 102, 0, 1, 133501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 1, 8822, 1, 0, 1, 133501, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 1, 8823, 2, 0, 1, 133501, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 1, 8824, 3, 0, 1, 133501, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 1, 8825, 4, 0, 1, 133501, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 4, 8826, 101, 0, 1, 133501, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 8, 8827, 102, 0, 1, 133501, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 1, 8828, 1, 0, 1, 133501, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 1, 8829, 2, 0, 1, 133501, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 1, 8830, 3, 0, 1, 133501, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 1, 8832, 2, 0, 1, 133501, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 1, 8833, 3, 0, 1, 133501, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 1, 8834, 4, 0, 1, 133501, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 1, 8835, 5, 0, 1, 133501, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 1, 8837, 7, 0, 1, 133501, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 1, 8838, 8, 0, 1, 133501, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 1, 8840, 1, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お姉ちゃん…\n早く会いたいなぁ…", 1, 8841, 2, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 1, 8842, 3, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 1, 8843, 4, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 1, 8844, 5, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 4, 8845, 101, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 8, 8846, 102, 0, 1, 133601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 1, 8847, 1, 0, 1, 133601, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 1, 8848, 2, 0, 1, 133601, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 1, 8849, 3, 0, 1, 133601, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 1, 8850, 4, 0, 1, 133601, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 4, 8851, 101, 0, 1, 133601, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 8, 8852, 102, 0, 1, 133601, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 1, 8853, 1, 0, 1, 133601, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 1, 8854, 2, 0, 1, 133601, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 1, 8855, 3, 0, 1, 133601, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 1, 8857, 2, 0, 1, 133601, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 1, 8858, 3, 0, 1, 133601, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 1, 8859, 4, 0, 1, 133601, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 1, 8860, 5, 0, 1, 133601, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 1, 8862, 7, 0, 1, 133601, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 1, 8863, 8, 0, 1, 133601, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 1, 8865, 1, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 1, 8866, 2, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 1, 8867, 3, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("自分の原点を\n振り返ってみるのも\nたまには必要だね", 1, 8868, 4, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 1, 8869, 5, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 4, 8870, 101, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 8, 8871, 102, 0, 1, 133701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 1, 8872, 1, 0, 1, 133701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 1, 8873, 2, 0, 1, 133701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 1, 8874, 3, 0, 1, 133701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 1, 8875, 4, 0, 1, 133701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 4, 8876, 101, 0, 1, 133701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 8, 8877, 102, 0, 1, 133701, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 1, 8878, 1, 0, 1, 133701, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 1, 8879, 2, 0, 1, 133701, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 1, 8880, 3, 0, 1, 133701, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 1, 8882, 2, 0, 1, 133701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 1, 8883, 3, 0, 1, 133701, 0, 3);
