INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふわぁぁ～\nおはようごじゃいます\n…んむむ", 1, 9055, 1, 0, 1, 134701, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 1, 9056, 2, 0, 1, 134701, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 1, 9057, 3, 0, 1, 134701, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 1, 9059, 2, 0, 1, 134701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふむ…\nそばに置いてあげても\nいいでしょう", 1, 9060, 3, 0, 1, 134701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ほわぁ～\nいいかおり～", 1, 9061, 4, 0, 1, 134701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あたたかい…\nなんだか元気が\nでてきますねっ", 1, 9062, 5, 0, 1, 134701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 1, 9064, 7, 0, 1, 134701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ふむふむ…\n人の子にしては\nよく書けていますね♪", 1, 9065, 8, 0, 1, 134701, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 1, 9067, 1, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 1, 9068, 2, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 1, 9069, 3, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 1, 9070, 4, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 1, 9071, 5, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 4, 9072, 101, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 8, 9073, 102, 0, 1, 133101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 1, 9074, 1, 0, 1, 133101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 1, 9075, 2, 0, 1, 133101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 1, 9076, 3, 0, 1, 133101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 1, 9077, 4, 0, 1, 133101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 4, 9078, 101, 0, 1, 133101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 8, 9079, 102, 0, 1, 133101, 0, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 1, 9080, 1, 0, 1, 133101, 1, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 1, 9081, 2, 0, 1, 133101, 2, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 1, 9082, 3, 0, 1, 133101, 3, 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 1, 9084, 2, 0, 1, 133101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 1, 9085, 3, 0, 1, 133101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 1, 9086, 4, 0, 1, 133101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 1, 9087, 5, 0, 1, 133101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 1, 9089, 7, 0, 1, 133101, 0, 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES ("『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 1, 9090, 8, 0, 1, 133101, 0, 3);
