INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, "ここに困ってる人は\nいないよね…？\nよかった♪", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 2, 0, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 3, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 4, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 5, 0, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 6, 101, 0, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 7, 102, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1, 0, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 9, 2, 0, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 10, 3, 0, "えっとね…しっぽは\n触っちゃダメだよ？", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 11, 4, 0, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 12, 101, 0, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 13, 102, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 14, 1, 1, "おはよー！\n今日も元気に\nいっちゃうよー！", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 15, 2, 2, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 16, 3, 3, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 2, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 17, 1, 0, "ちゃんとケアして\n傷が残らないように\nしないと…", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 18, 2, 0, "一回休むと\nこのままずるずる\nいきそう…", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 19, 3, 0, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 20, 4, 0, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 21, 5, 0, "あったかい所に\nいると体が丸く\nなっちゃう…", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 22, 6, 0, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 23, 7, 0, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 24, 8, 0, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 3, 0, 1, 100101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 26, 1, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 27, 2, 0, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 1, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 28, 3, 0, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 1, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 29, 4, 0, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 1, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 30, 5, 0, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 1, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 31, 101, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 32, 102, 0, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 1, 0, 1, 100102);
