INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 34, 2, 0, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 2, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 35, 3, 0, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 2, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 36, 4, 0, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 2, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 37, 101, 0, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 2, 0, 1, 100102);
