INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 49, 8, 0, "色んなことを\n知っておくのは\n大事だよね！", 3, 0, 1, 100102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 51, 1, 0, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 52, 2, 0, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 53, 3, 0, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 54, 4, 0, "戦いは\n得意じゃないけど\nお料理なら…！", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 55, 5, 0, "こういう平和な\n時間が、もっと\n続くといいな", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 56, 101, 0, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 57, 102, 0, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 1, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 58, 1, 0, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 59, 2, 0, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 60, 3, 0, "困ったことが\nあったら\nいつでも話してね", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 61, 4, 0, "え！　む、虫っ！？\n取って取って！\nはやく～！", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 62, 101, 0, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 63, 102, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 64, 1, 1, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 65, 2, 2, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 66, 3, 3, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 2, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 67, 1, 0, "よし…\nこれなら\n褒めてもらえるかな？", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 68, 2, 0, "がんばることも\n必要だけど、\nお休みも大切だよね", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 69, 3, 0, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 70, 4, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 71, 5, 0, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 72, 6, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 73, 7, 0, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 74, 8, 0, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 3, 0, 1, 100201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 76, 1, 0, "洗濯と掃除\nどっちから\nはじめよう？", 1, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 77, 2, 0, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 1, 0, 1, 100202);
