INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 81, 101, 0, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！", 1, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 82, 102, 0, "騎士クンのそばに\nいられるだけで\n幸せだよ", 1, 0, 1, 100202);
