INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 87, 101, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 2, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 88, 102, 0, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 2, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 89, 1, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 2, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 90, 2, 2, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 2, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 91, 3, 3, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 2, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 92, 1, 0, "よし…\nこれなら\n褒めてもらえるかな？", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 93, 2, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 94, 3, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 95, 4, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 96, 5, 0, "あの…！\nよ、良かったら\n一緒に…！", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 97, 6, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 98, 7, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 99, 8, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 3, 0, 1, 100202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1, 0, "装備の手入れでも\nしようかな…", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 2, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 103, 3, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 104, 4, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 105, 5, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 106, 101, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 107, 102, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 108, 1, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 109, 2, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 110, 3, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 111, 4, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 112, 101, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 113, 102, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 114, 1, 1, "おはよう\n今日も充実した\n一日にしていこう", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 115, 2, 2, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 116, 3, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 117, 1, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 118, 2, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 119, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 120, 4, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 121, 5, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 122, 6, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 123, 7, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 124, 8, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3, 0, 1, 100301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 126, 1, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 127, 2, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 128, 3, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 129, 4, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 130, 5, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 131, 101, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 132, 102, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 133, 1, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 134, 2, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 135, 3, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 136, 4, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 137, 101, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 138, 102, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 139, 1, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 140, 2, 2, "休息も時には\n必要だと、仲間から\n教わったよ", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 141, 3, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 142, 1, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 143, 2, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 144, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 145, 4, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 146, 5, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 147, 6, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 148, 7, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 149, 8, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3, 0, 1, 100302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 151, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 152, 2, 0, "落とし穴…どこに\n作ったんだっけ？", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 153, 3, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 154, 4, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 155, 5, 0, "かくれんぼする人\nこの指、と～まれ！", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 156, 101, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 157, 102, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 158, 1, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 159, 2, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 160, 3, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 161, 4, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 162, 101, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 163, 102, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 164, 1, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 165, 2, 2, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 166, 3, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 167, 1, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 168, 2, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 169, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 170, 4, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 171, 5, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 172, 6, 0, "探検家は\n体力勝負だよー！", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 173, 7, 0, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 174, 8, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3, 0, 1, 100401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 176, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 177, 2, 0, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 178, 3, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 179, 4, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 180, 5, 0, "かくれんぼする人\nこの指、と～まれ！", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 181, 101, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 182, 102, 0, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 183, 1, 0, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 184, 2, 0, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 185, 3, 0, "将来の夢は探検家！\n探したい物はないけど", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 186, 4, 0, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 187, 101, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 188, 102, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 189, 1, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 190, 2, 2, "にいちゃんが昼寝したら\nイタズラし放題♪", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 191, 3, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 192, 1, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 193, 2, 0, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 194, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 195, 4, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 196, 5, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 197, 6, 0, "探検家は\n体力勝負だよー！", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 198, 7, 0, "探検のおやつに\nぴったりかも～！", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 199, 8, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3, 0, 1, 100402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 201, 1, 0, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 202, 2, 0, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 203, 3, 0, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 204, 4, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 205, 5, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 206, 101, 0, "お兄ちゃんと私は\n相性抜群です♪", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 207, 102, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 208, 1, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 209, 2, 0, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 210, 3, 0, "そんなところ\n触っちゃダメ♪", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 211, 4, 0, "お兄ちゃん、イジワル\nしないでください…", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 212, 101, 0, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 213, 102, 0, "コーチ、\n今日も特訓\nお願いします！", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 214, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 215, 2, 2, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 216, 3, 3, "夜はこれからですよ\nお兄ちゃん♪", 2, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 217, 1, 0, "世界一、かわいく\nなるんだから♪", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 218, 2, 0, "気持ちいい♪\nもう立てないよ～", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 219, 3, 0, "かわいい♪\nアカリのライバル\nだね", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 220, 4, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 221, 5, 0, "はぁ…アカリ、\nとろけちゃう…", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 222, 6, 0, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 223, 7, 0, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 224, 8, 0, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 3, 0, 1, 100601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 226, 1, 0, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 227, 2, 0, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 228, 3, 0, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 229, 4, 0, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 230, 5, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 231, 101, 0, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 232, 102, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 233, 1, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 234, 2, 0, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 235, 3, 0, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 236, 4, 0, "お兄ちゃん、イジワル\nしないでください…", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 237, 101, 0, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 238, 102, 0, "コーチ、\n今日も特訓\nお願いします！", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 239, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 240, 2, 2, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 241, 3, 3, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 2, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 242, 1, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 243, 2, 0, "気持ちいい♪\nもう立てないよ～", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 244, 3, 0, "かわいい♪\nアカリのライバル\nだね", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 245, 4, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 246, 5, 0, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 247, 6, 0, "汗をかくと\n気持ちいいですよね", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 248, 7, 0, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 249, 8, 0, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 3, 0, 1, 100602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 251, 1, 0, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 252, 2, 0, "う～ら～め～し～や～\nなの～♪", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 253, 3, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 254, 4, 0, "幽霊の正体見たり、\nミヤコなの～", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 255, 5, 0, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 256, 101, 0, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 257, 102, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 258, 1, 0, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 259, 2, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 260, 3, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 261, 4, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 262, 101, 0, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 263, 102, 0, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 264, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 265, 2, 2, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 266, 3, 3, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 2, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 267, 1, 0, "体が透けちゃうから\nうまく使えないの～", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 268, 2, 0, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 269, 3, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 270, 4, 0, "は～　いい気分なの…\n成仏しそうなの…", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 271, 5, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 272, 6, 0, "幽霊には、体力も\n筋力も関係ないの～！", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 273, 7, 0, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 274, 8, 0, "プリンの写真集が\nあればいいのに\nなの～", 3, 0, 1, 100701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 276, 1, 0, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 277, 2, 0, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 278, 3, 0, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 279, 4, 0, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 280, 5, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 281, 101, 0, "アイツがいて\nプリンもある\nきっとここが天国なの", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 282, 102, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 283, 1, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 284, 2, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 285, 3, 0, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 286, 4, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 287, 101, 0, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 288, 102, 0, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 289, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの… ", 2, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 290, 2, 2, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 2, 0, 1, 100702);
