INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 296, 5, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 3, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 297, 6, 0, "幽霊には、体力も\n筋力も関係ないの～！　", 3, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 298, 7, 0, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 3, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 299, 8, 0, "プリンの写真集が\nあればいいのに\nなの～ ", 3, 0, 1, 100702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 301, 1, 0, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 302, 2, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 303, 3, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 304, 4, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 305, 5, 0, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 306, 101, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 307, 102, 0, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 1, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 308, 1, 0, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 309, 2, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 310, 3, 0, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 311, 4, 0, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 312, 101, 0, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 313, 102, 0, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 314, 1, 1, "美しいボクの\n美しい一日が\n始まるよ！", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 315, 2, 2, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 316, 3, 3, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 2, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 317, 1, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 318, 2, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 319, 3, 0, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 320, 4, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 321, 5, 0, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 322, 6, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 323, 7, 0, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 324, 8, 0, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 3, 0, 1, 100801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 326, 1, 0, "ボクの美しさは\nみんなのために\nあるんだよ！", 1, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 327, 2, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 328, 3, 0, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 329, 4, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 330, 5, 0, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 331, 101, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 332, 102, 0, "あの人も今\n可愛いボクのこと\n考えてるかな…", 1, 0, 1, 100802);
