INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 334, 2, 0, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 2, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 335, 3, 0, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 2, 0, 1, 100802);
