INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 337, 101, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 338, 102, 0, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 2, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 339, 1, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 2, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 340, 2, 2, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 2, 0, 1, 100802);
