INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 342, 1, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 343, 2, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 344, 3, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 345, 4, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 346, 5, 0, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 347, 6, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 348, 7, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 349, 8, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 3, 0, 1, 100802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 351, 1, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 352, 2, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 353, 3, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 354, 4, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 355, 5, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 356, 101, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 357, 102, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 358, 1, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 359, 2, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 360, 3, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 361, 4, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 362, 101, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 363, 102, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 364, 1, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 365, 2, 2, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 366, 3, 3, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 2, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 367, 1, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 368, 2, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 369, 3, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 370, 4, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 371, 5, 0, "これが我らの求めた\n安寧か…\n悪くない…", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 372, 6, 0, "この程度\n前世での激闘に\n比べれば…！", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 373, 7, 0, "最近金欠でな…\nいやぁありがたい…", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 374, 8, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 3, 0, 1, 100901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 376, 1, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 377, 2, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 378, 3, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 379, 4, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 380, 5, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 381, 101, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 382, 102, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 383, 1, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 384, 2, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 385, 3, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 386, 4, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 387, 101, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 388, 102, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 389, 1, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 390, 2, 2, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 391, 3, 3, "夜になると、力が\n増した気分になるな\n…なりませんか？", 2, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 392, 1, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 393, 2, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 394, 3, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 395, 4, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 396, 5, 0, "これが我らの求めた\n安寧か…\n悪くない…", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 397, 6, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 398, 7, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 399, 8, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 3, 0, 1, 100902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 401, 1, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 402, 2, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 403, 3, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 404, 4, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 405, 5, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 406, 101, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 407, 102, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 408, 1, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 409, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 410, 3, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 411, 4, 0, "うちの話し方\nのんびりしてます\nやろか？", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 412, 101, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 413, 102, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 414, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 415, 2, 2, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 416, 3, 3, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 2, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 417, 1, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 418, 2, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 419, 3, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 420, 4, 0, "心地ええどす。まるで\nお花畑にいるようやね", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 421, 5, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 422, 6, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 423, 7, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 424, 8, 0, "絵本、絵本～\n幸せやわぁ♪", 3, 0, 1, 101001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 426, 1, 0, "王子はんにとって\n自慢の姫になるえ～", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 427, 2, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 428, 3, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 429, 4, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 430, 5, 0, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 431, 101, 0, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 432, 102, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 433, 1, 0, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 434, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 435, 3, 0, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 436, 4, 0, "絵本読むんも\nええなぁ…", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 437, 101, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 438, 102, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 439, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 440, 2, 2, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 441, 3, 3, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 2, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 442, 1, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 443, 2, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 444, 3, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 445, 4, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 446, 5, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 447, 6, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 448, 7, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 449, 8, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 3, 0, 1, 101002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 451, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 452, 2, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 453, 3, 0, "い、いけない！\n縫い針落としちゃった", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 454, 4, 0, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 455, 5, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 456, 101, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 457, 102, 0, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 1, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 458, 1, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 459, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 460, 3, 0, "勉強、見てくれるん\nですか？　やった～♪", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 461, 4, 0, "服がほつれてますよ？\n直してあげます！", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 462, 101, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 463, 102, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 464, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 465, 2, 2, "お昼休憩に\n甘いものが\n食べたいです～", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 466, 3, 3, "ねむいです～\nでももうちょっと\n勉強しないと～…", 2, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 467, 1, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 468, 2, 0, "勉強で疲れたから\n休憩しよ～っと", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 469, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 470, 4, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 471, 5, 0, "のんびりしたら、\nまた勉強しよっと！", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 472, 6, 0, "運動すると勉強も\nはかどるよね！", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 473, 7, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 474, 8, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 3, 0, 1, 101101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 476, 1, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 477, 2, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 478, 3, 0, "い、いけない！\n縫い針落としちゃった", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 479, 4, 0, "笑う時には服着ます！", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 480, 5, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 481, 101, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 482, 102, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 1, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 483, 1, 0, "勉強、見てくれるん\nですか？　やった～♪", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 484, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 485, 3, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 486, 4, 0, "勉強は…\n明日にしようっと", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 487, 101, 0, "妹だけど、大人な\nレディですからね！", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 488, 102, 0, "な、ナデナデとか\n恥ずかしいな～", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 489, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 490, 2, 2, "お昼休憩に\n甘いものが\n食べたいです～", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 491, 3, 3, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 2, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 492, 1, 0, "服装や髪形が変わると\n気分も変わるよね", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 493, 2, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 494, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 495, 4, 0, "いまならどんな試験も\n合格できそうです！", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 496, 5, 0, "のんびりしたら\nまた勉強しよっと！", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 497, 6, 0, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 498, 7, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 499, 8, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 3, 0, 1, 101102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 501, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 502, 2, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 503, 3, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 504, 4, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 505, 5, 0, "もし、超能力のことが\nバレたら…ブルブル", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 506, 101, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 507, 102, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 508, 1, 0, "ん～、なになに？\n一緒にお昼寝する？", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 509, 2, 0, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 510, 3, 0, "おおっ、相談ごと？\nなにかな\nなにかな～？", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 511, 4, 0, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 512, 101, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 513, 102, 0, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 514, 1, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 515, 2, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 516, 3, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 2, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 517, 1, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 518, 2, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 519, 3, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 520, 4, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 521, 5, 0, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 522, 6, 0, "眠くならないように、\n運動しよっと！", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 523, 7, 0, "お腹いっぱいになると\n眠くなるよね～", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 524, 8, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 3, 0, 1, 101201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 526, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 527, 2, 0, "もっといっぱい\n超能力使えたら\nいいのにな～", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 528, 3, 0, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 529, 4, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 530, 5, 0, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 531, 101, 0, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 532, 102, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 533, 1, 0, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 534, 2, 0, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 535, 3, 0, "一緒に\nお出かけしたいなー\nデートしよっ！", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 536, 4, 0, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 537, 101, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 538, 102, 0, "う～だんだん眠く…\nねぇ一緒に寝よ？", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 539, 1, 1, "キミって早起きだね！\n私は寝ようかな～", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 540, 2, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 541, 3, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 2, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 542, 1, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 543, 2, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 544, 3, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 545, 4, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 546, 5, 0, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 547, 6, 0, "よーし\n困ってる人のために\nパワーアップ☆", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 548, 7, 0, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 549, 8, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 3, 0, 1, 101202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 551, 1, 0, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 552, 2, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 553, 3, 0, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 554, 4, 0, "ここで撮影会、\nしようかな～♪", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 555, 5, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 556, 101, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 557, 102, 0, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 1, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 558, 1, 0, "うちの写真撮るの？\nどんなポーズ？", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 559, 2, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 560, 3, 0, "宿題見てくんない？\nひとりじゃムリ～", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 561, 4, 0, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 562, 101, 0, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 563, 102, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 564, 1, 1, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 565, 2, 2, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 566, 3, 3, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 2, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 567, 1, 0, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 568, 2, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 569, 3, 0, "きゃわたーん☆\n君もおしゃれしよっか", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 570, 4, 0, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 571, 5, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 572, 6, 0, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 573, 7, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 574, 8, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 3, 0, 1, 101601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 576, 1, 0, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 577, 2, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 578, 3, 0, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 579, 4, 0, "体形維持とか\n気にしてるんだよ～", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 580, 5, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 581, 101, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…　", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 582, 102, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 1, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 583, 1, 0, "うちの写真撮るの？\nどんなポーズ？", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 584, 2, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 585, 3, 0, "この雑誌の服\nガチめにほしぃ～", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 586, 4, 0, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 587, 101, 0, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 588, 102, 0, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 589, 1, 1, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？", 2, 0, 1, 101602);
