INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 591, 3, 3, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 2, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 592, 1, 0, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 593, 2, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 594, 3, 0, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 595, 4, 0, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 596, 5, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 597, 6, 0, "食べた分は\n消費しないとね～", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 598, 7, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 599, 8, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 3, 0, 1, 101602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 601, 1, 0, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 602, 2, 0, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 603, 3, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 604, 4, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 605, 5, 0, "さぁ、みんなで歌って\n踊るさ～♪", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 606, 101, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 607, 102, 0, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 1, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 608, 1, 0, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 609, 2, 0, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 610, 3, 0, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 611, 4, 0, "空手の型の\n練習するさー\nキミもどう？", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 612, 101, 0, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 613, 102, 0, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 614, 1, 1, "ハイターイ！\n今日も一日、\nがんばるさー！", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 615, 2, 2, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 616, 3, 3, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 2, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 617, 1, 0, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 618, 2, 0, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 619, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 620, 4, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 621, 5, 0, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 622, 6, 0, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 623, 7, 0, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 624, 8, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 3, 0, 1, 101701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 626, 1, 0, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 1, 0, 1, 101702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 627, 2, 0, "さぁ、みんなで歌って\n踊るさ～♪", 1, 0, 1, 101702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 628, 3, 0, "元気がないときは\n私のところに来るさー", 1, 0, 1, 101702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 629, 4, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 0, 1, 101702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 630, 5, 0, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 1, 0, 1, 101702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 631, 101, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 0, 1, 101702);
