INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 653, 3, 0, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 654, 4, 0, "キンコンカンコ～ン♪\n授業をはじめま～す", 1, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 655, 5, 0, "はー…\nちょっと肩が\nこっちゃった…", 1, 0, 1, 101801);
