INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 657, 102, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 1, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 658, 1, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 659, 2, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 660, 3, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 661, 4, 0, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 662, 101, 0, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 663, 102, 0, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 664, 1, 1, "学校に\n遅刻しないように\n早く準備しないとね", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 665, 2, 2, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 666, 3, 3, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 667, 1, 0, "もしかして…\n少し露出多いかしら？", 3, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 668, 2, 0, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 3, 0, 1, 101801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 669, 3, 0, "かわいい♪　あなたも\n私の生徒になる？", 3, 0, 1, 101801);
