INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 692, 1, 0, "もしかして…\n少し露出多いかしら？", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 693, 2, 0, "私の膝枕と、どっちが\nフカフカかしら？", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 694, 3, 0, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 695, 4, 0, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 696, 5, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 697, 6, 0, "運動部の顧問も\nできるかしら？　", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 698, 7, 0, "あん！\nお胸にこぼしちゃった", 3, 0, 1, 101802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 699, 8, 0, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 3, 0, 1, 101802);
