INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 703, 3, 0, "あのね、ミミと\nあそんでほしいな～", 1, 0, 1, 102001);
