INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 756, 101, 0, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 1, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 757, 102, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 758, 1, 0, "こ、この本、とっても\nおもしろいんですよ♪", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 759, 2, 0, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 760, 3, 0, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 761, 4, 0, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 762, 101, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 763, 102, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 764, 1, 1, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 765, 2, 2, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 766, 3, 3, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 2, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 767, 1, 0, "可愛い女の子に変身！\n出来ますように…♪", 3, 0, 1, 102101);
