INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 771, 5, 0, "えへへ。\nぽっかぽかです…", 3, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 772, 6, 0, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 3, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 773, 7, 0, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 3, 0, 1, 102101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 774, 8, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 3, 0, 1, 102101);
