INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 777, 2, 0, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 1, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 778, 3, 0, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 1, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 779, 4, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 780, 5, 0, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 1, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 781, 101, 0, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 1, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 782, 102, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 783, 1, 0, "こ、この本、とっても\nおもしろいんですよ♪", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 784, 2, 0, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 785, 3, 0, "何か私にできる事\n…そうだ！\nお掃除しますね！", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 786, 4, 0, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 787, 101, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 788, 102, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 789, 1, 1, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 790, 2, 2, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 791, 3, 3, "寝る前に\n台本を読むと、\n覚えやすいんです", 2, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 792, 1, 0, "可愛い女の子に変身！\n出来ますように…♪", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 793, 2, 0, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 794, 3, 0, "この子を相手に\n演技の練習～", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 795, 4, 0, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 796, 5, 0, "えへへ\nぽっかぽかです…", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 797, 6, 0, "え、演劇には\nた、体力も重要\nだから～", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 798, 7, 0, "残さず\n食べますね…！", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 799, 8, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 3, 0, 1, 102102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 801, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 802, 2, 0, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 803, 3, 0, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 804, 4, 0, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 805, 5, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 806, 101, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 807, 102, 0, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 1, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 808, 1, 0, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 809, 2, 0, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 810, 3, 0, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 811, 4, 0, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 812, 101, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 813, 102, 0, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 814, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 815, 2, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 816, 3, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 2, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 817, 1, 0, "アカリみたいに\n可愛くなるのは\n難しいかな", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 818, 2, 0, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 819, 3, 0, "可愛い…けど、\n私には似合わないかな", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 820, 4, 0, "はあ、リラックスして\nゲームができるわ", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 821, 5, 0, "ん…暖かくて、\n寝落ちしちゃいそう", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 822, 6, 0, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 823, 7, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 824, 8, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 3, 0, 1, 102201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 826, 1, 0, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 827, 2, 0, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 828, 3, 0, "レアカードをゲット！\nすごいでしょ！？", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 829, 4, 0, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 830, 5, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 831, 101, 0, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 832, 102, 0, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 1, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 833, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 834, 2, 0, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 835, 3, 0, "ねえ、見て！\n新しいゲームを\n買って来たの！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 836, 4, 0, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 837, 101, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 838, 102, 0, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 839, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 840, 2, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 841, 3, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 2, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 842, 1, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 843, 2, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 844, 3, 0, "可愛い…けど、\n私には似合わないかな", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 845, 4, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 846, 5, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 847, 6, 0, "最近ゲームばかりだし\nたまにはいいかな？", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 848, 7, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 849, 8, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 3, 0, 1, 102202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 851, 1, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 852, 2, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 853, 3, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 854, 4, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 855, 5, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 856, 101, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 857, 102, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 1, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 858, 1, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 859, 2, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 860, 3, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 861, 4, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 862, 101, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 863, 102, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 864, 1, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 865, 2, 2, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 866, 3, 3, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 2, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 868, 2, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 3, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 869, 3, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 3, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 870, 4, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 3, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 871, 5, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 3, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 873, 7, 0, "おいしい～\nぷうきちも\n食べてみる？", 3, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 874, 8, 0, "ぷうきち！\nこの本、読んで～！", 3, 0, 1, 102301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 901, 1, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 902, 2, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 903, 3, 0, "どうしたら転ばなく\nなるんでしょうか？", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 904, 4, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 905, 5, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 906, 101, 0, "おいしい料理で\nお出迎えしなくちゃ！", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 907, 102, 0, "一生懸命\nご奉仕しますよ～♪", 1, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 908, 1, 0, "耳かきしましょうか？\nえ？　こわい？", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 909, 2, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 910, 3, 0, "おかえりなさいませ\nご主人さま♪", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 911, 4, 0, "すみません\nすみません\nすみません～～", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 912, 101, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 913, 102, 0, "私のこと、\n専属メイドと思って\n頼ってくださいね", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 914, 1, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 915, 2, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 916, 3, 3, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 2, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 917, 1, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 918, 2, 0, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 919, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 920, 4, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 921, 5, 0, "ここでお洗濯ものを\n乾かしましょうか", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 922, 6, 0, "あわわわっ…\nこ、転びそうです～！", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 923, 7, 0, "これくらい\nおいしいものを\n作ってみたいです～", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 924, 8, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 3, 0, 1, 102501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 926, 1, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 927, 2, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 928, 3, 0, "どうやったら、立派な\nメイドになれるのかな", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 929, 4, 0, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 930, 5, 0, "ご用があれば\nなんでも言って\nくださいね", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 931, 101, 0, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 932, 102, 0, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 1, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 933, 1, 0, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 934, 2, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 935, 3, 0, "私、またなにか\nやっちゃいましたか？", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 936, 4, 0, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 937, 101, 0, "転んだふりであの人に\n…って！\nなんでもないですよ！", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 938, 102, 0, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 939, 1, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 940, 2, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 941, 3, 3, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 2, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 942, 1, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 943, 2, 0, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 944, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 945, 4, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 946, 5, 0, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 947, 6, 0, "運動しようとすると\nいつも怪我するん\nですよね…", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 948, 7, 0, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 949, 8, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 3, 0, 1, 102502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 951, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 952, 2, 0, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 953, 3, 0, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 954, 4, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 955, 5, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 956, 101, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 957, 102, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 958, 1, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 959, 2, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 960, 3, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 961, 4, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 962, 101, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 963, 102, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 964, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 965, 2, 2, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 966, 3, 3, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 2, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 967, 1, 0, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 968, 2, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 969, 3, 0, "あら、キメラの素体に\nぴったりですわ", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 970, 4, 0, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 971, 5, 0, "あたたかい…\n加熱調合にも\n使えそうですわね", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 972, 6, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 973, 7, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 974, 8, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 3, 0, 1, 102701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 976, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 977, 2, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 978, 3, 0, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 979, 4, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 980, 5, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 981, 101, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 982, 102, 0, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 1, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 983, 1, 0, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 984, 2, 0, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 985, 3, 0, "お願いがあるのです\n新しい研究の\n実験台に…", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 986, 4, 0, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 987, 101, 0, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 988, 102, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 989, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 990, 2, 2, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 991, 3, 3, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 2, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 992, 1, 0, "私ももう少し\n外見に気を配る\nべきでしょうか…", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 993, 2, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 994, 3, 0, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 995, 4, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 996, 5, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 997, 6, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 998, 7, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 999, 8, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 3, 0, 1, 102702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1001, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1002, 2, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1003, 3, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1004, 4, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1005, 5, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1006, 101, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1007, 102, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1008, 1, 0, "ねぇあんた、どこか\n安いお店知らない？", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1009, 2, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1010, 3, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1011, 4, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1012, 101, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1013, 102, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1014, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1015, 2, 2, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1016, 3, 3, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 2, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1017, 1, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1018, 2, 0, "これで仕事は一段落ね\nちょっと休憩…", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1019, 3, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1020, 4, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1021, 5, 0, "風邪を引かないように\n身体を温めなくちゃ", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1022, 6, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1023, 7, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1024, 8, 0, "知識から過去を学んで\n未来に活かすわよ", 3, 0, 1, 102801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1026, 1, 0, "安売りのお店の情報\n整理しないと…", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1027, 2, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1028, 3, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1029, 4, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1030, 5, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1031, 101, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1032, 102, 0, "あいつと一緒に…\n色々なところに\nいきたいな", 1, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1033, 1, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1034, 2, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1035, 3, 0, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1036, 4, 0, "あんたとこうして\nのんびりするのも\nいいものね", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1037, 101, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1038, 102, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1039, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1040, 2, 2, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1041, 3, 3, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 2, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1042, 1, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1043, 2, 0, "これで仕事は一段落ね\nちょっと休憩…", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1044, 3, 0, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1045, 4, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1046, 5, 0, "風邪を引かないように\n身体を温めなくちゃ", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1047, 6, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1048, 7, 0, "しっかり食べて\n栄養補給、栄養補給！", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1049, 8, 0, "知識から過去を学んで\n未来に活かすわよ", 3, 0, 1, 102802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1051, 1, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1052, 2, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1053, 3, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1054, 4, 0, "彼とデュエット\nしてみたいな～♪", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1055, 5, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1056, 101, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1057, 102, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 1, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1058, 1, 0, "握手会の練習、\n付き合ってくれない？", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1059, 2, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1060, 3, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1061, 4, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1062, 101, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1063, 102, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1064, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1065, 2, 2, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1066, 3, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 2, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1067, 1, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1068, 2, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1069, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1070, 4, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1071, 5, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1072, 6, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1073, 7, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1074, 8, 0, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 3, 0, 1, 102901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1076, 1, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1077, 2, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1078, 3, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1079, 4, 0, "彼とデュエット\nしてみたいな～♪", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1080, 5, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1081, 101, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1082, 102, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 1, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1083, 1, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1084, 2, 0, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1085, 3, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1086, 4, 0, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1087, 101, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1088, 102, 0, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1089, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1090, 2, 2, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1091, 3, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 2, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1092, 1, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 3, 0, 1, 102902);
