INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1094, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 3, 0, 1, 102902);
