INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1098, 7, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 3, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1099, 8, 0, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 3, 0, 1, 102902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1101, 1, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1102, 2, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1103, 3, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1104, 4, 0, "むむ、アクダイカンの\n気配がするデース", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1105, 5, 0, "クニトリ合戦、\n楽しみデース！", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1106, 101, 0, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1107, 102, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1108, 1, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1109, 2, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1110, 3, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1111, 4, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1112, 101, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1113, 102, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1114, 1, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1115, 2, 2, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1116, 3, 3, "明日もきっと\nいい日デスよ、\nショーグン！", 2, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1117, 1, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1118, 2, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1119, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1120, 4, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1121, 5, 0, "身体を暖めて\nイクサに向かうデス！", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1122, 6, 0, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1123, 7, 0, "うーん、\n美味しいデス…\n幸せデス～", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1124, 8, 0, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 3, 0, 1, 103001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1126, 1, 0, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 0, 1, 103002);
