INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1131, 101, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1132, 102, 0, "ショーグンの身は\n何があってもワタシが\n守るデス！", 1, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1133, 1, 0, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 2, 0, 1, 103002);
