INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1135, 3, 0, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 2, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1136, 4, 0, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 2, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1137, 101, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 2, 0, 1, 103002);
