INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1142, 1, 0, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1143, 2, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1144, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1145, 4, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1146, 5, 0, "身体を暖めてイクサに\n向かうデス！", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1147, 6, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1148, 7, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1149, 8, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 3, 0, 1, 103002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1151, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1152, 2, 0, "父は、いつも男性に\nきびしいですね", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1153, 3, 0, "占ってほしい人は、\nいつでも言って\nください", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1154, 4, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1155, 5, 0, "この部屋、風水的にも\n運気が上がりそうです", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1156, 101, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1157, 102, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1158, 1, 0, "女難の相が\n出ていますね…\n気をつけてください", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1159, 2, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1160, 3, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1161, 4, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1162, 101, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1163, 102, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1164, 1, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1165, 2, 2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1166, 3, 3, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 2, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1167, 1, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1168, 2, 0, "一休みしましょう。\n憑かれて疲れました", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1169, 3, 0, "かわいい…\n付喪神が宿らないかな", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1170, 4, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1171, 5, 0, "気持ちいいです…\n極楽浄土みたい…", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1172, 6, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1173, 7, 0, "お父さんも\n食べたいの？\n死んでるのに？", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1174, 8, 0, "この本…\nもしかして予言書？", 3, 0, 1, 103101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1176, 1, 0, "ここにいる幽霊は\nみんな幸せそうです", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1177, 2, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1178, 3, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1179, 4, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1180, 5, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1181, 101, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1182, 102, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 1, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1183, 1, 0, "この部屋、風水的にも\n運気が上がりそうです", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1184, 2, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1185, 3, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1186, 4, 0, "ラップ音？\nいえ、父のいびきです", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1187, 101, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1188, 102, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1189, 1, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1190, 2, 2, "明るい時間は\nあまり得意では\nないんです…", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1191, 3, 3, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 2, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1192, 1, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1193, 2, 0, "うとうと…\nあれ…\n走馬灯が見える…", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1194, 3, 0, "かわいい…\nお父さんみたい…", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1195, 4, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1196, 5, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1197, 6, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1198, 7, 0, "お父さんの分も、\n私が食べるね", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1199, 8, 0, "この本の占い\n私の占いと違う…", 3, 0, 1, 103102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1201, 1, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1202, 2, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1203, 3, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1204, 4, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1205, 5, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1206, 101, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1207, 102, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1208, 1, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1209, 2, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1210, 3, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1211, 4, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1212, 101, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1213, 102, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1214, 1, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1215, 2, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1216, 3, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 2, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1217, 1, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1218, 2, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1219, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1220, 4, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1221, 5, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1222, 6, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1223, 7, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1224, 8, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 3, 0, 1, 103201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1226, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1227, 2, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1228, 3, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1229, 4, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1230, 5, 0, "この狭さも\n慣れれば\n快適ですわ♪", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1231, 101, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1232, 102, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1233, 1, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1234, 2, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1235, 3, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1236, 4, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1237, 101, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1238, 102, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1239, 1, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1240, 2, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1241, 3, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 2, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1242, 1, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1243, 2, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1244, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1245, 4, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1246, 5, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1247, 6, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1248, 7, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1249, 8, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 3, 0, 1, 103202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1251, 1, 0, "家の中のものを使って\n物ボケ、考えるべ！", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1252, 2, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1253, 3, 0, "うちに塀ができたら、\nへー、って言いたい！", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1254, 4, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1255, 5, 0, "エリザベスも\n連れてきてぇ～", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1256, 101, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1257, 102, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 1, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1258, 1, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1259, 2, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1260, 3, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1261, 4, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1262, 101, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1263, 102, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1264, 1, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1265, 2, 2, "お昼だ～腹が減っては\n笑いはできぬ～！", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1266, 3, 3, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 2, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1267, 1, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1268, 2, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1269, 3, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1270, 4, 0, "必要以上に\n癒されちまうべや～", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1271, 5, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1272, 6, 0, "お笑い芸人は、\n体が資本だべさ～！", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1273, 7, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1274, 8, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 3, 0, 1, 103301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1276, 1, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1277, 2, 0, "この部屋でも、商品\n売っていいんだべか？", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1278, 3, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1279, 4, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1280, 5, 0, "エリザベスも\n連れてきてぇ～", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1281, 101, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1282, 102, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 1, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1283, 1, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1284, 2, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1285, 3, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1286, 4, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1287, 101, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1288, 102, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1289, 1, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1290, 2, 2, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1291, 3, 3, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 2, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1292, 1, 0, "うーん…衣装、\nずれてねぇべか？", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1293, 2, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1294, 3, 0, "はう～\nなまらめんこいべや～", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1295, 4, 0, "必要以上に\n癒されちまうべや～", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1296, 5, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1297, 6, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1298, 7, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1299, 8, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 3, 0, 1, 103302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1301, 1, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1302, 2, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1303, 3, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1304, 4, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1305, 5, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1306, 101, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1307, 102, 0, "もしかして運命の\n相手だったり…\nするのかな", 1, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1308, 1, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1309, 2, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1310, 3, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1311, 4, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1312, 101, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1313, 102, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1314, 1, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1315, 2, 2, "おっひるー♪\nなに食べよっかな～♪", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1316, 3, 3, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 2, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1317, 1, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1318, 2, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1319, 3, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1320, 4, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1321, 5, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1322, 6, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1323, 7, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1324, 8, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 3, 0, 1, 103401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1326, 1, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1327, 2, 0, "今日は\nのんびり\n過ごそうかな～", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1328, 3, 0, "あ…\nお皿、洗って\nなかった…", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1329, 4, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1330, 5, 0, "素敵な彼氏…\n欲しいなぁ…", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1331, 101, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1332, 102, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 1, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1333, 1, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1334, 2, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1335, 3, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1336, 4, 0, "お姉さんが～\n可愛がって\nあげようか～？", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1337, 101, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1338, 102, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1339, 1, 1, "さーて、今日も\nいっちょやるぞー！", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1340, 2, 2, "後でのんびり\nストレッチでも\nしようっと♪", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1341, 3, 3, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 2, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1342, 1, 0, "仕事の前に、髪とか\n整えないと…", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1343, 2, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1344, 3, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1345, 4, 0, "うわあ\nこれ、私の好きな\n香りだー！", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1346, 5, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1347, 6, 0, "地道な努力が\nリア充への\n第一歩！", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1348, 7, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1349, 8, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 3, 0, 1, 103402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1351, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1352, 2, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1353, 3, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1354, 4, 0, "ちょっと、休憩…\n本でも読もうかな", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1355, 5, 0, "もっとたくさんの本に\n囲まれて生活したいな", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1356, 101, 0, "むかしむかしある所に\nシオリと\nあの人が…", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1357, 102, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 1, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1358, 1, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1359, 2, 0, "あ…お姉ちゃんと\n約束があったんだ…", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1360, 3, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1361, 4, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1362, 101, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1363, 102, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1364, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1365, 2, 2, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1366, 3, 3, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 2, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1367, 1, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1368, 2, 0, "少し休もう\n…やっぱり\nたくさん休もう", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1369, 3, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1370, 4, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1371, 5, 0, "こうして温まって\nいると、病気も\n治る気がします", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1372, 6, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1373, 7, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1374, 8, 0, "続きが気になって\n読むのを止める\nタイミングが…", 3, 0, 1, 103801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1376, 1, 0, "しっかり休まないと\n早く回復するように", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1377, 2, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1378, 3, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1379, 4, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1380, 5, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1381, 101, 0, "むかしむかしある所に\nシオリと\nあの人が…", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1382, 102, 0, "あの人の\nためにも早く\n病気を治さないと…", 1, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1383, 1, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1384, 2, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1385, 3, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1386, 4, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1387, 101, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1388, 102, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1389, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1390, 2, 2, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1391, 3, 3, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 2, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1392, 1, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1393, 2, 0, "少し休もう\n…やっぱり\nたくさん休もう", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1394, 3, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1395, 4, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1396, 5, 0, "こうして温まって\nいると、病気も\n治る気がします", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1397, 6, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1398, 7, 0, "いっぱい食べて\n健康な体にならないと", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1399, 8, 0, "続きが気になって\n読むのを止める\nタイミングが…", 3, 0, 1, 103802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1401, 1, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1402, 2, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1403, 3, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1404, 4, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1405, 5, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1406, 101, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1407, 102, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1408, 1, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1409, 2, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1410, 3, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1411, 4, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1412, 101, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1413, 102, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1414, 1, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1415, 2, 2, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1416, 3, 3, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 2, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1417, 1, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1418, 2, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1419, 3, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1420, 4, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1421, 5, 0, "灯りを見てると、\n心がなごみます～", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1422, 6, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1423, 7, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1424, 8, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 3, 0, 1, 104001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1426, 1, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1427, 2, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1428, 3, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1429, 4, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1430, 5, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1431, 101, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1432, 102, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1433, 1, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1434, 2, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1435, 3, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1436, 4, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1437, 101, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1438, 102, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1439, 1, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1440, 2, 2, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1441, 3, 3, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 2, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1442, 1, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1443, 2, 0, "ほわあ～…\nふかふかって\nいいものですね～", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1444, 3, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1445, 4, 0, "灯りを見てると\n心がなごみます～", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1446, 5, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1447, 6, 0, "身体を鍛えて\nみなさんを\n守ります！", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1448, 7, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1449, 8, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 3, 0, 1, 104002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1451, 1, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1452, 2, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1453, 3, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1454, 4, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1455, 5, 0, "あとで曲を復習して\nおかないと…", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1456, 101, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1457, 102, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1458, 1, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1459, 2, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1460, 3, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1461, 4, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1462, 101, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1463, 102, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1464, 1, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1465, 2, 2, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1466, 3, 3, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 2, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1467, 1, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1468, 2, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1469, 3, 0, "この子たちを\n相手に練習して\nみようかな…♪", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1470, 4, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1471, 5, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1472, 6, 0, "歌うためには\n体力づくりは\n欠かせません", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1473, 7, 0, "歌った後は\nお腹が空いて\nしまうんです", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1474, 8, 0, "音楽について\n理論も勉強\nしないと…", 3, 0, 1, 104201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1476, 1, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1477, 2, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1478, 3, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1479, 4, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1480, 5, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1481, 101, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1482, 102, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 1, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1483, 1, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1484, 2, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1485, 3, 0, "魔物退治ですか！？\n…違います？\n良かった…", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1486, 4, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1487, 101, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1488, 102, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1489, 1, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1490, 2, 2, "少し休んだら\n午後の練習に\n行ってきますね", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1491, 3, 3, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 2, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1492, 1, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1493, 2, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1494, 3, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1495, 4, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1496, 5, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1497, 6, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1498, 7, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1499, 8, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 3, 0, 1, 104202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1501, 1, 0, "店の新メニューでも\n考えるかな", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1502, 2, 0, "あたしの店から、\n出前を届けようか？", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1503, 3, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1504, 4, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1505, 5, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1506, 101, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1507, 102, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1508, 1, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1509, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1510, 3, 0, "腹減ったか？　なんか\n作ってやろうか？", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1511, 4, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1512, 101, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1513, 102, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1514, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1515, 2, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1516, 3, 3, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 2, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1517, 1, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1518, 2, 0, "ふわぁ、\n一休みすっかなぁ～", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1519, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1520, 4, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1521, 5, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1522, 6, 0, "オラオラオラーーー！\n燃えるぜーーー！", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1523, 7, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1524, 8, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 3, 0, 1, 104301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1526, 1, 0, "パトロール終了～\n本日も異常なしっと！", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1527, 2, 0, "あたしの店から\n出前を届けようか？", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1528, 3, 0, "あいつ、早く\n帰ってこないかな", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1529, 4, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1530, 5, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1531, 101, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1532, 102, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1533, 1, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1534, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1535, 3, 0, "腹減ったか？　なんか\n作ってやろうか？", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1536, 4, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1537, 101, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1538, 102, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1539, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1540, 2, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1541, 3, 3, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 2, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1542, 1, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1543, 2, 0, "ふぅ～♪\n疲れがとれるな～", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1544, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1545, 4, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1546, 5, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1547, 6, 0, "オラオラオラーーー！\n燃えるぜーーー！", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1548, 7, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1549, 8, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 3, 0, 1, 104302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1551, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1552, 2, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1553, 3, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1554, 4, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1555, 5, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1556, 101, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1557, 102, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1558, 1, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1559, 2, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1560, 3, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1561, 4, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1562, 101, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1563, 102, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1564, 1, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1565, 2, 2, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1566, 3, 3, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 2, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1567, 1, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1568, 2, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1569, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1570, 4, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1571, 5, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1572, 6, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1573, 7, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1574, 8, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 3, 0, 1, 104501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1576, 1, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1577, 2, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1578, 3, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1579, 4, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1580, 5, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1581, 101, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1582, 102, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 1, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1583, 1, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1584, 2, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1585, 3, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1586, 4, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1587, 101, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1588, 102, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1589, 1, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1590, 2, 2, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1591, 3, 3, "今夜は一晩中…\nなんですね？\nぐふふ…", 2, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1592, 1, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1593, 2, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1594, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1595, 4, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1596, 5, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1597, 6, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1598, 7, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1599, 8, 0, "どんな本も\nよく見ると\nいやらしいです…！", 3, 0, 1, 104502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1601, 1, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1602, 2, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1603, 3, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1604, 4, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1605, 5, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1606, 101, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1607, 102, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 1, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1608, 1, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1609, 2, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1610, 3, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1611, 4, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1612, 101, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1613, 102, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1614, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1615, 2, 2, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1616, 3, 3, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 2, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1617, 1, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1618, 2, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1619, 3, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1620, 4, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1621, 5, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1622, 6, 0, "本当は外で\n運動したいん\nだけどにゃ", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1623, 7, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1624, 8, 0, "ふむふむ…猫の\nしつけ方は、と…", 3, 0, 1, 104601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1626, 1, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1627, 2, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1628, 3, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1629, 4, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1630, 5, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1631, 101, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1632, 102, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 1, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1633, 1, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1634, 2, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1635, 3, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1636, 4, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1637, 101, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1638, 102, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1639, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1640, 2, 2, "そろそろおっひる～\nにゃーんか\n食べたい…", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1641, 3, 3, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 2, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1642, 1, 0, "にゃにゃ…\n髪が乱れてるにゃ", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1643, 2, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1644, 3, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1645, 4, 0, "猫でもこわくない\n優しい灯りにゃ！", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1646, 5, 0, "ぽっかぽか\nしあわせな気分にゃ～", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1647, 6, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1648, 7, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1649, 8, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 3, 0, 1, 104602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1651, 1, 0, "内職手伝える人\nいないかしら？", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1652, 2, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1653, 3, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1654, 4, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1655, 5, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1656, 101, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1657, 102, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1658, 1, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1659, 2, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1660, 3, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1661, 4, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1662, 101, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1663, 102, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1664, 1, 1, "おはよう！\n今日も朝から\nバイトよ！", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1665, 2, 2, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1666, 3, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 2, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1667, 1, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1668, 2, 0, "はぁ～バイトで\n疲れたから癒される～", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1669, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1670, 4, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1671, 5, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1672, 6, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1673, 7, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1674, 8, 0, "日々絶えず学んで、\n生活に活かすのよ！", 3, 0, 1, 104801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1676, 1, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1677, 2, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1678, 3, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1679, 4, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1680, 5, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1681, 101, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1682, 102, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 1, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1683, 1, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1684, 2, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1685, 3, 0, "今から\n休憩するところなの\n一緒にどう？", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1686, 4, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1687, 101, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1688, 102, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1689, 1, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1690, 2, 2, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1691, 3, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 2, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1692, 1, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1693, 2, 0, "ここで少し\n休んだら\nまたバイトよ！", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1694, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1695, 4, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1696, 5, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1697, 6, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1698, 7, 0, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1699, 8, 0, "日々絶えず学んで\n生活に活かすのよ！", 3, 0, 1, 104802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1701, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1702, 2, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1703, 3, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1704, 4, 0, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1705, 5, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1706, 101, 0, "この部屋を、\n私と弟くんの\n愛の巣に…", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1707, 102, 0, "はぁ～…弟くん\n禁断症状だよ～", 1, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1708, 1, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1709, 2, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1710, 3, 0, "姉と弟の外出は\nデートって\nいうのかな？", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1711, 4, 0, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1712, 101, 0, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1713, 102, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1714, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1715, 2, 2, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1716, 3, 3, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 2, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1717, 1, 0, "きれいなお姉ちゃん\n好きだよね♪", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1718, 2, 0, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1719, 3, 0, "かわいい♪\nお姉ちゃんチョップ！", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1720, 4, 0, "この香り、\n弟くん好きかな？", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1721, 5, 0, "これ、弟くんみたいに\nあったかいね～", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1722, 6, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1723, 7, 0, "あ、これ\n弟くんが好きな\n味付けだね！", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1724, 8, 0, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 3, 0, 1, 104901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1726, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 0, 1, 104902);
