INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1728, 3, 0, "弟くんのお友達は\nみんな私の妹だよ♪", 1, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1729, 4, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1730, 5, 0, "はっ！\n弟くんが困ってる\n気がする！", 1, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1731, 101, 0, "はぁ～…弟くん、\n禁断症状だよ～", 1, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1732, 102, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1733, 1, 0, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 2, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1734, 2, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 2, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1735, 3, 0, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 2, 0, 1, 104902);
