INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1738, 102, 0, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 2, 0, 1, 104902);
