INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1740, 2, 2, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 2, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1741, 3, 3, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 2, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1742, 1, 0, "弟くんを\n誘惑しちゃうぞ～♪", 3, 0, 1, 104902);
