INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1746, 5, 0, "これ、弟くんみたいに\nあったかいね～", 3, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1747, 6, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 3, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1748, 7, 0, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 3, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1749, 8, 0, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 3, 0, 1, 104902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1751, 1, 0, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1752, 2, 0, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1753, 3, 0, "アタシの大人コーデで\nあいつも夢中ね♪", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1754, 4, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1755, 5, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1756, 101, 0, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1757, 102, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 1, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1758, 1, 0, "わーい、アメだー♪\n食べる食べるー♪", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1759, 2, 0, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1760, 3, 0, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1761, 4, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1762, 101, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1763, 102, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1764, 1, 1, "朝のスキンケアは\n大事なの！", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1765, 2, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1766, 3, 3, "エイジングケアを\nしないとねー", 2, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1767, 1, 0, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1768, 2, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1769, 3, 0, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1770, 4, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1771, 5, 0, "あったかーい♪\nここで宿題\nしようかしら？", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1772, 6, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1773, 7, 0, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1774, 8, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 3, 0, 1, 105001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1776, 1, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1777, 2, 0, "恋人にするなら\nイケメンか\nただイケよね～", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1778, 3, 0, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1779, 4, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1780, 5, 0, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1781, 101, 0, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1782, 102, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 1, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1783, 1, 0, "なーに？\nアタシに甘えたいの？", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1784, 2, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1785, 3, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1786, 4, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1787, 101, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1788, 102, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1789, 1, 1, "レディの朝は\n忙しいのよ", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1790, 2, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1791, 3, 3, "エイジングケアを\nしないとねー", 2, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1792, 1, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1793, 2, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1794, 3, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1795, 4, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1796, 5, 0, "あったかーい♪\nここで宿題\nしようかしら？", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1797, 6, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1798, 7, 0, "レディは、食べ物にも\n気を使うのよ", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1799, 8, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 3, 0, 1, 105002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1801, 1, 0, "魔法と科学\nどっちも極めて\nみせるわ", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1802, 2, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1803, 3, 0, "たまった治療代の\n請求書を\nまとめないと…", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1804, 4, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1805, 5, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1806, 101, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1807, 102, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1808, 1, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1809, 2, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1810, 3, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1811, 4, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1812, 101, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1813, 102, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1814, 1, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1815, 2, 2, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1816, 3, 3, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 2, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1817, 1, 0, "医者たるもの\nいつだって\n清潔に…♪", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1818, 2, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1819, 3, 0, "ぬいぐるみなら\n動物も…", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1820, 4, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1821, 5, 0, "ここにいると\nついウトウト\nしちゃうわ…", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1822, 6, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1823, 7, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1824, 8, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 3, 0, 1, 105101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1826, 1, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1827, 2, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1828, 3, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1829, 4, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1830, 5, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1831, 101, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1832, 102, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1833, 1, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1834, 2, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1835, 3, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1836, 4, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1837, 101, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1838, 102, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1839, 1, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1840, 2, 2, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1841, 3, 3, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 2, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1842, 1, 0, "身なりについて\n研究するのも\n楽しそうね", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1843, 2, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1844, 3, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1845, 4, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1846, 5, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1847, 6, 0, "運動しないと\n体型の維持が…", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1848, 7, 0, "適度な食事も\n大切よね", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1849, 8, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 3, 0, 1, 105102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1851, 1, 0, "ぺっぺっ！\n…い、いけないっ！", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1852, 2, 0, "いっけな～い！\nひづめで床に傷が！", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1853, 3, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1854, 4, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1855, 5, 0, "みんな、体重\nどれくらいなのかな？", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1856, 101, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1857, 102, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 1, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1858, 1, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1859, 2, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1860, 3, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1861, 4, 0, "力仕事ならまかせて！", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1862, 101, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1863, 102, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1864, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1865, 2, 2, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1866, 3, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 2, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1867, 1, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1868, 2, 0, "ミシッっていった！\n私、重すぎるかも！？", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1869, 3, 0, "あ！　あなたも\n美人さんね～♪", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1870, 4, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1871, 5, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1872, 6, 0, "な、なんだか野生に\n帰っちゃいそう！", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1873, 7, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1874, 8, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 3, 0, 1, 105201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1876, 1, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1877, 2, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1878, 3, 0, "動物苑…って\n嫌なギルド名ね…", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1879, 4, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1880, 5, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1881, 101, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1882, 102, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 1, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1883, 1, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1884, 2, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1885, 3, 0, "ぺっぺっ！\n…い、いけないっ！", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1886, 4, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1887, 101, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1888, 102, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1889, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1890, 2, 2, "ダイエット中だから\nお昼は抜いてるの～", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1891, 3, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 2, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1892, 1, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1893, 2, 0, "ミシッっていった！\n私、重すぎるかも！？", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1894, 3, 0, "あ！　あなたも\n美人さんね～♪", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1895, 4, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1896, 5, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1897, 6, 0, "な、なんだか野生に\n帰っちゃいそう！", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1898, 7, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1899, 8, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 3, 0, 1, 105202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1901, 1, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1902, 2, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1903, 3, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1904, 4, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1905, 5, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1906, 101, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1907, 102, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 1, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1908, 1, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1909, 2, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1910, 3, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1911, 4, 0, "私ともっと高く、\n共に飛ぼう！", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1912, 101, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1913, 102, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1914, 1, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1915, 2, 2, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1916, 3, 3, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 2, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1917, 1, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1918, 2, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1919, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1920, 4, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1921, 5, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1922, 6, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1923, 7, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1924, 8, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 3, 0, 1, 105301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1926, 1, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1927, 2, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1928, 3, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1929, 4, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1930, 5, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1931, 101, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1932, 102, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 1, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1933, 1, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1934, 2, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1935, 3, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1936, 4, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1937, 101, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1938, 102, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1939, 1, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1940, 2, 2, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1941, 3, 3, "寝る前に…あと少し\nだけ、鍛錬を…", 2, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1942, 1, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1943, 2, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1944, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1945, 4, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1946, 5, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1947, 6, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1948, 7, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1949, 8, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 3, 0, 1, 105302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1951, 1, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1952, 2, 0, "団員募集の\nポスター\n作ろうっと♪", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1953, 3, 0, "父さん…\n今頃どこで\n何してるんだろ？", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1954, 4, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1955, 5, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1956, 101, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1957, 102, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 1, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1958, 1, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1959, 2, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1960, 3, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1961, 4, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 1962, 101, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1963, 102, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1964, 1, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1965, 2, 2, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1966, 3, 3, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 2, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1967, 1, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1968, 2, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1969, 3, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1970, 4, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1971, 5, 0, "これって\n騎空艇に\n置けるかな…？", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1972, 6, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1973, 7, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1974, 8, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 3, 0, 1, 105701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1976, 1, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1977, 2, 0, "暇だよ…\nどこかに強い人は\nいないか？", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1978, 3, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1979, 4, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1980, 5, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1981, 101, 0, "私の力の源は\n仲間との\n絆だよ！", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1982, 102, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 1, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1983, 1, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1984, 2, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1985, 3, 0, "どうした？\nもしかして手合せ\nしたいか？", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1986, 4, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1987, 101, 0, "背中はあなたに\n預けたよ！\nよろしくね！", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (8, 1988, 102, 0, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1989, 1, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1990, 2, 2, "腹減ったね…\nなにか食べに\n行くよ！", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1991, 3, 3, "修行のしすぎは\n良くないよ？\n気をつけるね", 2, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1992, 1, 0, "美しい技は\n美しい体が\nあってこそ！", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1993, 2, 0, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1994, 3, 0, "…練習に\n使うのはさすがに\n可哀そうね", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1995, 4, 0, "これは…\n瞑想の時にも\n使ってみたいね", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1996, 5, 0, "出来ればもっと\n熱いほうが\n嬉しいよ", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1997, 6, 0, "ここでも\n修行出来るなんて\n最高だよ！", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1998, 7, 0, "気力の源は体力！\n体力の源は\n健康とご飯！", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1999, 8, 0, "んー…\n難しいことは\nわかんないよ！", 3, 0, 1, 105702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2001, 1, 0, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 0, 1, 105801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2002, 2, 0, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 0, 1, 105801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2003, 3, 0, "ん？\n食べ物のにおい？", 1, 0, 1, 105801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2004, 4, 0, "みんなと仲良く食卓を\n囲みたいですね～", 1, 0, 1, 105801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2005, 5, 0, "この部屋が、わたしの\n帰る場所です♪", 1, 0, 1, 105801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (4, 2006, 101, 0, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 1, 0, 1, 105801);
