INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100101, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 0, 1, 1, 1, 4, 4);
