INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0, 1, 2, 8, 101, 87);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 0, 1, 2, 8, 102, 88);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 0, 1, 2, 1, 1, 89);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 2, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 0, 1, 2, 1, 2, 90);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 3, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 0, 1, 2, 1, 3, 91);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "よし…\nこれなら\n褒めてもらえるかな？", 0, 1, 3, 1, 1, 92);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 0, 1, 3, 1, 2, 93);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 0, 1, 3, 1, 3, 94);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0, 1, 3, 1, 4, 95);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "あの…！\nよ、良かったら\n一緒に…！", 0, 1, 3, 1, 5, 96);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0, 1, 3, 1, 6, 97);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 0, 1, 3, 1, 7, 98);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100202, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 0, 1, 3, 1, 8, 99);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "装備の手入れでも\nしようかな…", 0, 1, 1, 1, 1, 101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 0, 1, 1, 1, 2, 102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0, 1, 1, 1, 3, 103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 0, 1, 1, 1, 4, 104);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 0, 1, 1, 1, 5, 105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 0, 1, 1, 4, 101, 106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0, 1, 1, 8, 102, 107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 0, 1, 2, 1, 1, 108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 0, 1, 2, 1, 2, 109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 0, 1, 2, 1, 3, 110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 0, 1, 2, 1, 4, 111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 0, 1, 2, 4, 101, 112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0, 1, 2, 8, 102, 113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 1, "おはよう\n今日も充実した\n一日にしていこう", 0, 1, 2, 1, 1, 114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 2, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 0, 1, 2, 1, 2, 115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 0, 1, 2, 1, 3, 116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 0, 1, 3, 1, 1, 117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 0, 1, 3, 1, 2, 118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0, 1, 3, 1, 3, 119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 0, 1, 3, 1, 4, 120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 0, 1, 3, 1, 5, 121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0, 1, 3, 1, 6, 122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 0, 1, 3, 1, 7, 123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0, 1, 3, 1, 8, 124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 0, 1, 1, 1, 1, 126);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0, 1, 1, 1, 2, 127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 0, 1, 1, 1, 3, 128);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 0, 1, 1, 1, 4, 129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 0, 1, 1, 1, 5, 130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 0, 1, 1, 8, 101, 131);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0, 1, 1, 8, 102, 132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 0, 1, 2, 1, 1, 133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 0, 1, 2, 1, 2, 134);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 0, 1, 2, 1, 3, 135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 0, 1, 2, 1, 4, 136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 0, 1, 2, 8, 101, 137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0, 1, 2, 8, 102, 138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 0, 1, 2, 1, 1, 139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 2, "休息も時には\n必要だと、仲間から\n教わったよ", 0, 1, 2, 1, 2, 140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 0, 1, 2, 1, 3, 141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 0, 1, 3, 1, 1, 142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 0, 1, 3, 1, 2, 143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0, 1, 3, 1, 3, 144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 0, 1, 3, 1, 4, 145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 0, 1, 3, 1, 5, 146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0, 1, 3, 1, 6, 147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 0, 1, 3, 1, 7, 148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100302, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0, 1, 3, 1, 8, 149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0, 1, 1, 1, 1, 151);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "落とし穴…どこに\n作ったんだっけ？", 0, 1, 1, 1, 2, 152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0, 1, 1, 1, 3, 153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0, 1, 1, 1, 4, 154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "かくれんぼする人\nこの指、と～まれ！", 0, 1, 1, 1, 5, 155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 0, 1, 1, 4, 101, 156);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 0, 1, 1, 8, 102, 157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 0, 1, 2, 1, 1, 158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 0, 1, 2, 1, 2, 159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 0, 1, 2, 1, 3, 160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0, 1, 2, 1, 4, 161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 0, 1, 2, 4, 101, 162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0, 1, 2, 8, 102, 163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 0, 1, 2, 1, 1, 164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 2, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 0, 1, 2, 1, 2, 165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 0, 1, 2, 1, 3, 166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 0, 1, 3, 1, 1, 167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 0, 1, 3, 1, 2, 168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0, 1, 3, 1, 3, 169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0, 1, 3, 1, 4, 170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0, 1, 3, 1, 5, 171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "探検家は\n体力勝負だよー！", 0, 1, 3, 1, 6, 172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 0, 1, 3, 1, 7, 173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0, 1, 3, 1, 8, 174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0, 1, 1, 1, 1, 176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 0, 1, 1, 1, 2, 177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0, 1, 1, 1, 3, 178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0, 1, 1, 1, 4, 179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "かくれんぼする人\nこの指、と～まれ！", 0, 1, 1, 1, 5, 180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0, 1, 1, 8, 101, 181);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 0, 1, 1, 8, 102, 182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 0, 1, 2, 1, 1, 183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 0, 1, 2, 1, 2, 184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "将来の夢は探検家！\n探したい物はないけど", 0, 1, 2, 1, 3, 185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 0, 1, 2, 1, 4, 186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0, 1, 2, 8, 101, 187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 0, 1, 2, 8, 102, 188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 0, 1, 2, 1, 1, 189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 2, "にいちゃんが昼寝したら\nイタズラし放題♪", 0, 1, 2, 1, 2, 190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 0, 1, 2, 1, 3, 191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 0, 1, 3, 1, 1, 192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 0, 1, 3, 1, 2, 193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0, 1, 3, 1, 3, 194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0, 1, 3, 1, 4, 195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0, 1, 3, 1, 5, 196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "探検家は\n体力勝負だよー！", 0, 1, 3, 1, 6, 197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "探検のおやつに\nぴったりかも～！", 0, 1, 3, 1, 7, 198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100402, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0, 1, 3, 1, 8, 199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 0, 1, 1, 1, 1, 201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 0, 1, 1, 1, 2, 202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 0, 1, 1, 1, 3, 203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 0, 1, 1, 1, 4, 204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 0, 1, 1, 1, 5, 205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お兄ちゃんと私は\n相性抜群です♪", 0, 1, 1, 4, 101, 206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 0, 1, 1, 8, 102, 207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 0, 1, 2, 1, 1, 208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 0, 1, 2, 1, 2, 209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "そんなところ\n触っちゃダメ♪", 0, 1, 2, 1, 3, 210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お兄ちゃん、イジワル\nしないでください…", 0, 1, 2, 1, 4, 211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 0, 1, 2, 4, 101, 212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "コーチ、\n今日も特訓\nお願いします！", 0, 1, 2, 8, 102, 213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 0, 1, 2, 1, 1, 214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 2, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 0, 1, 2, 1, 2, 215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 3, "夜はこれからですよ\nお兄ちゃん♪", 0, 1, 2, 1, 3, 216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "世界一、かわいく\nなるんだから♪", 0, 1, 3, 1, 1, 217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "気持ちいい♪\nもう立てないよ～", 0, 1, 3, 1, 2, 218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "かわいい♪\nアカリのライバル\nだね", 0, 1, 3, 1, 3, 219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 0, 1, 3, 1, 4, 220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "はぁ…アカリ、\nとろけちゃう…", 0, 1, 3, 1, 5, 221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 0, 1, 3, 1, 6, 222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 0, 1, 3, 1, 7, 223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 0, 1, 3, 1, 8, 224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 0, 1, 1, 1, 1, 226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 0, 1, 1, 1, 2, 227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 0, 1, 1, 1, 3, 228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 0, 1, 1, 1, 4, 229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 0, 1, 1, 1, 5, 230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 0, 1, 1, 8, 101, 231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 0, 1, 1, 8, 102, 232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 0, 1, 2, 1, 1, 233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 0, 1, 2, 1, 2, 234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 0, 1, 2, 1, 3, 235);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "お兄ちゃん、イジワル\nしないでください…", 0, 1, 2, 1, 4, 236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 0, 1, 2, 8, 101, 237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "コーチ、\n今日も特訓\nお願いします！", 0, 1, 2, 8, 102, 238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 0, 1, 2, 1, 1, 239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 2, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 0, 1, 2, 1, 2, 240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 3, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 0, 1, 2, 1, 3, 241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 0, 1, 3, 1, 1, 242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "気持ちいい♪\nもう立てないよ～", 0, 1, 3, 1, 2, 243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "かわいい♪\nアカリのライバル\nだね", 0, 1, 3, 1, 3, 244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 0, 1, 3, 1, 4, 245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 0, 1, 3, 1, 5, 246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "汗をかくと\n気持ちいいですよね", 0, 1, 3, 1, 6, 247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 0, 1, 3, 1, 7, 248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100602, 0, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 0, 1, 3, 1, 8, 249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 0, 1, 1, 1, 1, 251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "う～ら～め～し～や～\nなの～♪", 0, 1, 1, 1, 2, 252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 0, 1, 1, 1, 3, 253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "幽霊の正体見たり、\nミヤコなの～", 0, 1, 1, 1, 4, 254);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 0, 1, 1, 1, 5, 255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 0, 1, 1, 4, 101, 256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 0, 1, 1, 8, 102, 257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 0, 1, 2, 1, 1, 258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 0, 1, 2, 1, 2, 259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 0, 1, 2, 1, 3, 260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 0, 1, 2, 1, 4, 261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 0, 1, 2, 4, 101, 262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 0, 1, 2, 8, 102, 263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 0, 1, 2, 1, 1, 264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 2, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 0, 1, 2, 1, 2, 265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 3, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 0, 1, 2, 1, 3, 266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "体が透けちゃうから\nうまく使えないの～", 0, 1, 3, 1, 1, 267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 0, 1, 3, 1, 2, 268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～", 0, 1, 3, 1, 3, 269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "は～　いい気分なの…\n成仏しそうなの…", 0, 1, 3, 1, 4, 270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 0, 1, 3, 1, 5, 271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "幽霊には、体力も\n筋力も関係ないの～！", 0, 1, 3, 1, 6, 272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 0, 1, 3, 1, 7, 273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "プリンの写真集が\nあればいいのに\nなの～", 0, 1, 3, 1, 8, 274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "うらめしや～…\n裏の飯より\nプリンがいいの～", 0, 1, 1, 1, 1, 276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "プリン♪　プリン♪\nプリン♪　なの～♪　", 0, 1, 1, 1, 2, 277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 0, 1, 1, 1, 3, 278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 0, 1, 1, 1, 4, 279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 0, 1, 1, 1, 5, 280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "アイツがいて\nプリンもある\nきっとここが天国なの", 0, 1, 1, 8, 101, 281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 0, 1, 1, 8, 102, 282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 0, 1, 2, 1, 1, 283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 0, 1, 2, 1, 2, 284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 0, 1, 2, 1, 3, 285);
