INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "体が透けちゃうから\nうまく使えないの～ ", 0, 1, 3, 1, 1, 292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 0, 1, 3, 1, 2, 293);
