INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 0, 1, 3, 1, 7, 298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100702, 0, "プリンの写真集が\nあればいいのに\nなの～ ", 0, 1, 3, 1, 8, 299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 0, 1, 1, 1, 1, 301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0, 1, 1, 1, 2, 302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 0, 1, 1, 1, 3, 303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0, 1, 1, 1, 4, 304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 0, 1, 1, 1, 5, 305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0, 1, 1, 4, 101, 306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 0, 1, 1, 8, 102, 307);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 0, 1, 2, 1, 1, 308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0, 1, 2, 1, 2, 309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 0, 1, 2, 1, 3, 310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 0, 1, 2, 1, 4, 311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 0, 1, 2, 4, 101, 312);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 0, 1, 2, 8, 102, 313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 1, "美しいボクの\n美しい一日が\n始まるよ！", 0, 1, 2, 1, 1, 314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 2, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 0, 1, 2, 1, 2, 315);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 3, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 0, 1, 2, 1, 3, 316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0, 1, 3, 1, 1, 317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0, 1, 3, 1, 2, 318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 0, 1, 3, 1, 3, 319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0, 1, 3, 1, 4, 320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 0, 1, 3, 1, 5, 321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0, 1, 3, 1, 6, 322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 0, 1, 3, 1, 7, 323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 0, 1, 3, 1, 8, 324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "ボクの美しさは\nみんなのために\nあるんだよ！", 0, 1, 1, 1, 1, 326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0, 1, 1, 1, 2, 327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 0, 1, 1, 1, 3, 328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0, 1, 1, 1, 4, 329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 0, 1, 1, 1, 5, 330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0, 1, 1, 8, 101, 331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "あの人も今\n可愛いボクのこと\n考えてるかな…", 0, 1, 1, 8, 102, 332);
