INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0, 1, 2, 8, 101, 337);
