INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0, 1, 3, 1, 2, 343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 0, 1, 3, 1, 3, 344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0, 1, 3, 1, 4, 345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 0, 1, 3, 1, 5, 346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0, 1, 3, 1, 6, 347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 0, 1, 3, 1, 7, 348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100802, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 0, 1, 3, 1, 8, 349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 0, 1, 1, 1, 1, 351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 0, 1, 1, 1, 2, 352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 0, 1, 1, 1, 3, 353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0, 1, 1, 1, 4, 354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 0, 1, 1, 1, 5, 355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 0, 1, 1, 4, 101, 356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 0, 1, 1, 8, 102, 357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 0, 1, 2, 1, 1, 358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 0, 1, 2, 1, 2, 359);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 0, 1, 2, 1, 3, 360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 0, 1, 2, 1, 4, 361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0, 1, 2, 4, 101, 362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0, 1, 2, 8, 102, 363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 0, 1, 2, 1, 1, 364);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 2, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 0, 1, 2, 1, 2, 365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 3, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 0, 1, 2, 1, 3, 366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0, 1, 3, 1, 1, 367);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 0, 1, 3, 1, 2, 368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 0, 1, 3, 1, 3, 369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0, 1, 3, 1, 4, 370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "これが我らの求めた\n安寧か…\n悪くない…", 0, 1, 3, 1, 5, 371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "この程度\n前世での激闘に\n比べれば…！", 0, 1, 3, 1, 6, 372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "最近金欠でな…\nいやぁありがたい…", 0, 1, 3, 1, 7, 373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 0, 1, 3, 1, 8, 374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 0, 1, 1, 1, 1, 376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 0, 1, 1, 1, 2, 377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 0, 1, 1, 1, 3, 378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0, 1, 1, 1, 4, 379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 0, 1, 1, 1, 5, 380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 0, 1, 1, 1, 101, 381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 0, 1, 1, 8, 102, 382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 0, 1, 2, 8, 1, 383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 0, 1, 2, 1, 2, 384);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 0, 1, 2, 1, 3, 385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 0, 1, 2, 1, 4, 386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0, 1, 2, 1, 101, 387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0, 1, 2, 8, 102, 388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 0, 1, 2, 8, 1, 389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 2, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 0, 1, 2, 1, 2, 390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 3, "夜になると、力が\n増した気分になるな\n…なりませんか？", 0, 1, 2, 1, 3, 391);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0, 1, 3, 1, 1, 392);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 0, 1, 3, 1, 2, 393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 0, 1, 3, 1, 3, 394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0, 1, 3, 1, 4, 395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "これが我らの求めた\n安寧か…\n悪くない…", 0, 1, 3, 1, 5, 396);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 0, 1, 3, 1, 6, 397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 0, 1, 3, 1, 7, 398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100902, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 0, 1, 3, 1, 8, 399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 0, 1, 1, 1, 1, 401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 0, 1, 1, 1, 2, 402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0, 1, 1, 1, 3, 403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0, 1, 1, 1, 4, 404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 0, 1, 1, 1, 5, 405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 0, 1, 1, 4, 101, 406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0, 1, 1, 8, 102, 407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0, 1, 2, 1, 1, 408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0, 1, 2, 1, 2, 409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 0, 1, 2, 1, 3, 410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "うちの話し方\nのんびりしてます\nやろか？", 0, 1, 2, 1, 4, 411);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 0, 1, 2, 4, 101, 412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0, 1, 2, 8, 102, 413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 0, 1, 2, 1, 1, 414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 2, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 0, 1, 2, 1, 2, 415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 3, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 0, 1, 2, 1, 3, 416);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 0, 1, 3, 1, 1, 417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 0, 1, 3, 1, 2, 418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 0, 1, 3, 1, 3, 419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "心地ええどす。まるで\nお花畑にいるようやね", 0, 1, 3, 1, 4, 420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 0, 1, 3, 1, 5, 421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 0, 1, 3, 1, 6, 422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 0, 1, 3, 1, 7, 423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "絵本、絵本～\n幸せやわぁ♪", 0, 1, 3, 1, 8, 424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "王子はんにとって\n自慢の姫になるえ～", 0, 1, 1, 1, 1, 426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 0, 1, 1, 1, 2, 427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0, 1, 1, 1, 3, 428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0, 1, 1, 1, 4, 429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 0, 1, 1, 1, 5, 430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 0, 1, 1, 8, 101, 431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0, 1, 1, 8, 102, 432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 0, 1, 2, 1, 1, 433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0, 1, 2, 1, 2, 434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 0, 1, 2, 1, 3, 435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "絵本読むんも\nええなぁ…", 0, 1, 2, 1, 4, 436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0, 1, 2, 8, 101, 437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0, 1, 2, 8, 102, 438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 0, 1, 2, 1, 1, 439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 2, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 0, 1, 2, 1, 2, 440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 3, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 0, 1, 2, 1, 3, 441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 0, 1, 3, 1, 1, 442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 0, 1, 3, 1, 2, 443);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 0, 1, 3, 1, 3, 444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 0, 1, 3, 1, 4, 445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 0, 1, 3, 1, 5, 446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 0, 1, 3, 1, 6, 447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 0, 1, 3, 1, 7, 448);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101002, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 0, 1, 3, 1, 8, 449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0, 1, 1, 1, 1, 451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 0, 1, 1, 1, 2, 452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "い、いけない！\n縫い針落としちゃった", 0, 1, 1, 1, 3, 453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 0, 1, 1, 1, 4, 454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0, 1, 1, 1, 5, 455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0, 1, 1, 4, 101, 456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 0, 1, 1, 8, 102, 457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 0, 1, 2, 1, 1, 458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0, 1, 2, 1, 2, 459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "勉強、見てくれるん\nですか？　やった～♪", 0, 1, 2, 1, 3, 460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "服がほつれてますよ？\n直してあげます！", 0, 1, 2, 1, 4, 461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 0, 1, 2, 4, 101, 462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 0, 1, 2, 8, 102, 463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 0, 1, 2, 1, 1, 464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 2, "お昼休憩に\n甘いものが\n食べたいです～", 0, 1, 2, 1, 2, 465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 3, "ねむいです～\nでももうちょっと\n勉強しないと～…", 0, 1, 2, 1, 3, 466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 0, 1, 3, 1, 1, 467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "勉強で疲れたから\n休憩しよ～っと", 0, 1, 3, 1, 2, 468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "かわいい♪\n私が服を作って\nあげよっか？", 0, 1, 3, 1, 3, 469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 0, 1, 3, 1, 4, 470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "のんびりしたら、\nまた勉強しよっと！", 0, 1, 3, 1, 5, 471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "運動すると勉強も\nはかどるよね！", 0, 1, 3, 1, 6, 472);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0, 1, 3, 1, 7, 473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 0, 1, 3, 1, 8, 474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 0, 1, 1, 1, 1, 476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0, 1, 1, 1, 2, 477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "い、いけない！\n縫い針落としちゃった", 0, 1, 1, 1, 3, 478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "笑う時には服着ます！", 0, 1, 1, 1, 4, 479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0, 1, 1, 1, 5, 480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0, 1, 1, 8, 101, 481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 0, 1, 1, 8, 102, 482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "勉強、見てくれるん\nですか？　やった～♪", 0, 1, 2, 1, 1, 483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0, 1, 2, 1, 2, 484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 0, 1, 2, 1, 3, 485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "勉強は…\n明日にしようっと", 0, 1, 2, 1, 4, 486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "妹だけど、大人な\nレディですからね！", 0, 1, 2, 8, 101, 487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "な、ナデナデとか\n恥ずかしいな～", 0, 1, 2, 8, 102, 488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 0, 1, 2, 1, 1, 489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 2, "お昼休憩に\n甘いものが\n食べたいです～", 0, 1, 2, 1, 2, 490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 3, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 0, 1, 2, 1, 3, 491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "服装や髪形が変わると\n気分も変わるよね", 0, 1, 3, 1, 1, 492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 0, 1, 3, 1, 2, 493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "かわいい♪\n私が服を作って\nあげよっか？", 0, 1, 3, 1, 3, 494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "いまならどんな試験も\n合格できそうです！", 0, 1, 3, 1, 4, 495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "のんびりしたら\nまた勉強しよっと！", 0, 1, 3, 1, 5, 496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 0, 1, 3, 1, 6, 497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0, 1, 3, 1, 7, 498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101102, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 0, 1, 3, 1, 8, 499);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0, 1, 1, 1, 1, 501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 0, 1, 1, 1, 2, 502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 0, 1, 1, 1, 3, 503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0, 1, 1, 1, 4, 504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "もし、超能力のことが\nバレたら…ブルブル", 0, 1, 1, 1, 5, 505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 0, 1, 1, 4, 101, 506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 0, 1, 1, 8, 102, 507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "ん～、なになに？\n一緒にお昼寝する？", 0, 1, 2, 1, 1, 508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 0, 1, 2, 1, 2, 509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "おおっ、相談ごと？\nなにかな\nなにかな～？", 0, 1, 2, 1, 3, 510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 0, 1, 2, 1, 4, 511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 0, 1, 2, 4, 101, 512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 0, 1, 2, 8, 102, 513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 0, 1, 2, 1, 1, 514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 0, 1, 2, 1, 2, 515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 0, 1, 2, 1, 3, 516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 0, 1, 3, 1, 1, 517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 0, 1, 3, 1, 2, 518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 0, 1, 3, 1, 3, 519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 0, 1, 3, 1, 4, 520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 0, 1, 3, 1, 5, 521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "眠くならないように、\n運動しよっと！", 0, 1, 3, 1, 6, 522);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "お腹いっぱいになると\n眠くなるよね～", 0, 1, 3, 1, 7, 523);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 0, 1, 3, 1, 8, 524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0, 1, 1, 1, 1, 526);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "もっといっぱい\n超能力使えたら\nいいのにな～", 0, 1, 1, 1, 2, 527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 0, 1, 1, 1, 3, 528);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0, 1, 1, 1, 4, 529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 0, 1, 1, 1, 5, 530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 0, 1, 1, 8, 101, 531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 0, 1, 1, 8, 102, 532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 0, 1, 2, 1, 1, 533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 0, 1, 2, 1, 2, 534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "一緒に\nお出かけしたいなー\nデートしよっ！", 0, 1, 2, 1, 3, 535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 0, 1, 2, 1, 4, 536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 0, 1, 2, 8, 101, 537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "う～だんだん眠く…\nねぇ一緒に寝よ？", 0, 1, 2, 8, 102, 538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 1, "キミって早起きだね！\n私は寝ようかな～", 0, 1, 2, 1, 1, 539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 0, 1, 2, 1, 2, 540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 0, 1, 2, 1, 3, 541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 0, 1, 3, 1, 1, 542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 0, 1, 3, 1, 2, 543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 0, 1, 3, 1, 3, 544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 0, 1, 3, 1, 4, 545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 0, 1, 3, 1, 5, 546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "よーし\n困ってる人のために\nパワーアップ☆", 0, 1, 3, 1, 6, 547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 0, 1, 3, 1, 7, 548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101202, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 0, 1, 3, 1, 8, 549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 0, 1, 1, 1, 1, 551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 0, 1, 1, 1, 2, 552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 0, 1, 1, 1, 3, 553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "ここで撮影会、\nしようかな～♪", 0, 1, 1, 1, 4, 554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 0, 1, 1, 1, 5, 555);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…", 0, 1, 1, 4, 101, 556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 0, 1, 1, 8, 102, 557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "うちの写真撮るの？\nどんなポーズ？", 0, 1, 2, 1, 1, 558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 0, 1, 2, 1, 2, 559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "宿題見てくんない？\nひとりじゃムリ～", 0, 1, 2, 1, 3, 560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 0, 1, 2, 1, 4, 561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 0, 1, 2, 4, 101, 562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 0, 1, 2, 8, 102, 563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 1, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 0, 1, 2, 1, 1, 564);
