INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 0, 1, 3, 1, 1, 567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 0, 1, 3, 1, 2, 568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "きゃわたーん☆\n君もおしゃれしよっか", 0, 1, 3, 1, 3, 569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 0, 1, 3, 1, 4, 570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 0, 1, 3, 1, 5, 571);
