INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101602, 0, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 0, 1, 1, 1, 1, 576);
