INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 0, 1, 1, 1, 3, 778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 0, 1, 1, 1, 4, 779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 0, 1, 1, 1, 5, 780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 0, 1, 1, 8, 101, 781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 0, 1, 1, 8, 102, 782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "こ、この本、とっても\nおもしろいんですよ♪", 0, 1, 2, 1, 1, 783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 0, 1, 2, 1, 2, 784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "何か私にできる事\n…そうだ！\nお掃除しますね！", 0, 1, 2, 1, 3, 785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 0, 1, 2, 1, 4, 786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 0, 1, 2, 8, 101, 787);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 0, 1, 2, 8, 102, 788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 1, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 0, 1, 2, 1, 1, 789);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 2, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 0, 1, 2, 1, 2, 790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 3, "寝る前に\n台本を読むと、\n覚えやすいんです", 0, 1, 2, 1, 3, 791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "可愛い女の子に変身！\n出来ますように…♪", 0, 1, 3, 1, 1, 792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 0, 1, 3, 1, 2, 793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "この子を相手に\n演技の練習～", 0, 1, 3, 1, 3, 794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 0, 1, 3, 1, 4, 795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "えへへ\nぽっかぽかです…", 0, 1, 3, 1, 5, 796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "え、演劇には\nた、体力も重要\nだから～", 0, 1, 3, 1, 6, 797);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "残さず\n食べますね…！", 0, 1, 3, 1, 7, 798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102102, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 0, 1, 3, 1, 8, 799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 0, 1, 1, 1, 1, 801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 0, 1, 1, 1, 2, 802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 0, 1, 1, 1, 3, 803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 0, 1, 1, 1, 4, 804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 0, 1, 1, 1, 5, 805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 0, 1, 1, 4, 101, 806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 0, 1, 1, 8, 102, 807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 0, 1, 2, 1, 1, 808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 0, 1, 2, 1, 2, 809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 0, 1, 2, 1, 3, 810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 0, 1, 2, 1, 4, 811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 0, 1, 2, 4, 101, 812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 0, 1, 2, 8, 102, 813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 0, 1, 2, 1, 1, 814);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 0, 1, 2, 1, 2, 815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 0, 1, 2, 1, 3, 816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アカリみたいに\n可愛くなるのは\n難しいかな", 0, 1, 3, 1, 1, 817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 0, 1, 3, 1, 2, 818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "可愛い…けど、\n私には似合わないかな", 0, 1, 3, 1, 3, 819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "はあ、リラックスして\nゲームができるわ", 0, 1, 3, 1, 4, 820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ん…暖かくて、\n寝落ちしちゃいそう", 0, 1, 3, 1, 5, 821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 0, 1, 3, 1, 6, 822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 0, 1, 3, 1, 7, 823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 0, 1, 3, 1, 8, 824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 0, 1, 1, 1, 1, 826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 0, 1, 1, 1, 2, 827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "レアカードをゲット！\nすごいでしょ！？", 0, 1, 1, 1, 3, 828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 0, 1, 1, 1, 4, 829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 0, 1, 1, 1, 5, 830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 0, 1, 1, 8, 101, 831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 0, 1, 1, 8, 102, 832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 0, 1, 2, 1, 1, 833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 0, 1, 2, 1, 2, 834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ねえ、見て！\n新しいゲームを\n買って来たの！", 0, 1, 2, 1, 3, 835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 0, 1, 2, 1, 4, 836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 0, 1, 2, 8, 101, 837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 0, 1, 2, 8, 102, 838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 0, 1, 2, 1, 1, 839);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 0, 1, 2, 1, 2, 840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 0, 1, 2, 1, 3, 841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 0, 1, 3, 1, 1, 842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 0, 1, 3, 1, 2, 843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "可愛い…けど、\n私には似合わないかな", 0, 1, 3, 1, 3, 844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 0, 1, 3, 1, 4, 845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 0, 1, 3, 1, 5, 846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "最近ゲームばかりだし\nたまにはいいかな？", 0, 1, 3, 1, 6, 847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 0, 1, 3, 1, 7, 848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102202, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 0, 1, 3, 1, 8, 849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 0, 1, 1, 1, 1, 851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 0, 1, 1, 1, 2, 852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 0, 1, 1, 1, 3, 853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 0, 1, 1, 1, 4, 854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 0, 1, 1, 1, 5, 855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 0, 1, 1, 4, 101, 856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 0, 1, 1, 8, 102, 857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 0, 1, 2, 1, 1, 858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 0, 1, 2, 1, 2, 859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 0, 1, 2, 1, 3, 860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 0, 1, 2, 1, 4, 861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 0, 1, 2, 4, 101, 862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 0, 1, 2, 8, 102, 863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 0, 1, 2, 1, 1, 864);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 2, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 0, 1, 2, 1, 2, 865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 3, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 0, 1, 2, 1, 3, 866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 0, 1, 3, 1, 2, 868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 0, 1, 3, 1, 3, 869);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 0, 1, 3, 1, 4, 870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 0, 1, 3, 1, 5, 871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "おいしい～\nぷうきちも\n食べてみる？", 0, 1, 3, 1, 7, 873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "ぷうきち！\nこの本、読んで～！", 0, 1, 3, 1, 8, 874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0, 1, 1, 1, 1, 901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0, 1, 1, 1, 2, 902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "どうしたら転ばなく\nなるんでしょうか？", 0, 1, 1, 1, 3, 903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 0, 1, 1, 1, 4, 904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 0, 1, 1, 1, 5, 905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "おいしい料理で\nお出迎えしなくちゃ！", 0, 1, 1, 4, 101, 906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "一生懸命\nご奉仕しますよ～♪", 0, 1, 1, 8, 102, 907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "耳かきしましょうか？\nえ？　こわい？", 0, 1, 2, 1, 1, 908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 0, 1, 2, 1, 2, 909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "おかえりなさいませ\nご主人さま♪", 0, 1, 2, 1, 3, 910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "すみません\nすみません\nすみません～～", 0, 1, 2, 1, 4, 911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 0, 1, 2, 4, 101, 912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "私のこと、\n専属メイドと思って\n頼ってくださいね", 0, 1, 2, 8, 102, 913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 0, 1, 2, 1, 1, 914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 0, 1, 2, 1, 2, 915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 3, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 0, 1, 2, 1, 3, 916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0, 1, 3, 1, 1, 917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 0, 1, 3, 1, 2, 918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 0, 1, 3, 1, 3, 919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0, 1, 3, 1, 4, 920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "ここでお洗濯ものを\n乾かしましょうか", 0, 1, 3, 1, 5, 921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "あわわわっ…\nこ、転びそうです～！", 0, 1, 3, 1, 6, 922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "これくらい\nおいしいものを\n作ってみたいです～", 0, 1, 3, 1, 7, 923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0, 1, 3, 1, 8, 924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 0, 1, 1, 1, 1, 926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0, 1, 1, 1, 2, 927);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "どうやったら、立派な\nメイドになれるのかな", 0, 1, 1, 1, 3, 928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 0, 1, 1, 1, 4, 929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "ご用があれば\nなんでも言って\nくださいね", 0, 1, 1, 1, 5, 930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 0, 1, 1, 8, 101, 931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 0, 1, 1, 8, 102, 932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 0, 1, 2, 1, 1, 933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0, 1, 2, 1, 2, 934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "私、またなにか\nやっちゃいましたか？", 0, 1, 2, 1, 3, 935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 0, 1, 2, 1, 4, 936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "転んだふりであの人に\n…って！\nなんでもないですよ！", 0, 1, 2, 8, 101, 937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 0, 1, 2, 8, 102, 938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 0, 1, 2, 1, 1, 939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 0, 1, 2, 1, 2, 940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 3, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 0, 1, 2, 1, 3, 941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0, 1, 3, 1, 1, 942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 0, 1, 3, 1, 2, 943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 0, 1, 3, 1, 3, 944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0, 1, 3, 1, 4, 945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 0, 1, 3, 1, 5, 946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "運動しようとすると\nいつも怪我するん\nですよね…", 0, 1, 3, 1, 6, 947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 0, 1, 3, 1, 7, 948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102502, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0, 1, 3, 1, 8, 949);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あの方と私だけの\n部屋にしたいですわ", 0, 1, 1, 1, 1, 951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 0, 1, 1, 1, 2, 952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 0, 1, 1, 1, 3, 953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0, 1, 1, 1, 4, 954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 0, 1, 1, 1, 5, 955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0, 1, 1, 4, 101, 956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0, 1, 1, 8, 102, 957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 0, 1, 2, 1, 1, 958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 0, 1, 2, 1, 2, 959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 0, 1, 2, 1, 3, 960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0, 1, 2, 1, 4, 961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0, 1, 2, 4, 101, 962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 0, 1, 2, 8, 102, 963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 0, 1, 2, 1, 1, 964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 2, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 0, 1, 2, 1, 2, 965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 3, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 0, 1, 2, 1, 3, 966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 0, 1, 3, 1, 1, 967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 0, 1, 3, 1, 2, 968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あら、キメラの素体に\nぴったりですわ", 0, 1, 3, 1, 3, 969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 0, 1, 3, 1, 4, 970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あたたかい…\n加熱調合にも\n使えそうですわね", 0, 1, 3, 1, 5, 971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 0, 1, 3, 1, 6, 972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 0, 1, 3, 1, 7, 973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 0, 1, 3, 1, 8, 974);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "あの方と私だけの\n部屋にしたいですわ", 0, 1, 1, 1, 1, 976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0, 1, 1, 1, 2, 977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "新しい薬\nどうやってあの方に\n飲ませようかしら", 0, 1, 1, 1, 3, 978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0, 1, 1, 1, 4, 979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0, 1, 1, 1, 5, 980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0, 1, 1, 8, 101, 981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 0, 1, 1, 8, 102, 982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 0, 1, 2, 1, 1, 983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 0, 1, 2, 1, 2, 984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "お願いがあるのです\n新しい研究の\n実験台に…", 0, 1, 2, 1, 3, 985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 0, 1, 2, 1, 4, 986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 0, 1, 2, 8, 101, 987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0, 1, 2, 8, 102, 988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 0, 1, 2, 1, 1, 989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 2, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 0, 1, 2, 1, 2, 990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 3, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 0, 1, 2, 1, 3, 991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "私ももう少し\n外見に気を配る\nべきでしょうか…", 0, 1, 3, 1, 1, 992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 0, 1, 3, 1, 2, 993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 0, 1, 3, 1, 3, 994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 0, 1, 3, 1, 4, 995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 0, 1, 3, 1, 5, 996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 0, 1, 3, 1, 6, 997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 0, 1, 3, 1, 7, 998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102702, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 0, 1, 3, 1, 8, 999);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 0, 1, 1, 1, 1, 1001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 0, 1, 1, 1, 2, 1002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 0, 1, 1, 1, 3, 1003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 0, 1, 1, 1, 4, 1004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 0, 1, 1, 1, 5, 1005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 0, 1, 1, 4, 101, 1006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 0, 1, 1, 8, 102, 1007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "ねぇあんた、どこか\n安いお店知らない？", 0, 1, 2, 1, 1, 1008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 0, 1, 2, 1, 2, 1009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 0, 1, 2, 1, 3, 1010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 0, 1, 2, 1, 4, 1011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 0, 1, 2, 4, 101, 1012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 0, 1, 2, 8, 102, 1013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 0, 1, 2, 1, 1, 1014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 2, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 0, 1, 2, 1, 2, 1015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 3, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 0, 1, 2, 1, 3, 1016);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 0, 1, 3, 1, 1, 1017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "これで仕事は一段落ね\nちょっと休憩…", 0, 1, 3, 1, 2, 1018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 0, 1, 3, 1, 3, 1019);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0, 1, 3, 1, 4, 1020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "風邪を引かないように\n身体を温めなくちゃ", 0, 1, 3, 1, 5, 1021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0, 1, 3, 1, 6, 1022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 0, 1, 3, 1, 7, 1023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "知識から過去を学んで\n未来に活かすわよ", 0, 1, 3, 1, 8, 1024);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "安売りのお店の情報\n整理しないと…", 0, 1, 1, 1, 1, 1026);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 0, 1, 1, 1, 2, 1027);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 0, 1, 1, 1, 3, 1028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 0, 1, 1, 1, 4, 1029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 0, 1, 1, 1, 5, 1030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 0, 1, 1, 8, 101, 1031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "あいつと一緒に…\n色々なところに\nいきたいな", 0, 1, 1, 8, 102, 1032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 0, 1, 2, 1, 1, 1033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 0, 1, 2, 1, 2, 1034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 0, 1, 2, 1, 3, 1035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "あんたとこうして\nのんびりするのも\nいいものね", 0, 1, 2, 1, 4, 1036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 0, 1, 2, 8, 101, 1037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 0, 1, 2, 8, 102, 1038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 0, 1, 2, 1, 1, 1039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 2, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 0, 1, 2, 1, 2, 1040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 3, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 0, 1, 2, 1, 3, 1041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 0, 1, 3, 1, 1, 1042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "これで仕事は一段落ね\nちょっと休憩…", 0, 1, 3, 1, 2, 1043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 0, 1, 3, 1, 3, 1044);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0, 1, 3, 1, 4, 1045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "風邪を引かないように\n身体を温めなくちゃ", 0, 1, 3, 1, 5, 1046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0, 1, 3, 1, 6, 1047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "しっかり食べて\n栄養補給、栄養補給！", 0, 1, 3, 1, 7, 1048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102802, 0, "知識から過去を学んで\n未来に活かすわよ", 0, 1, 3, 1, 8, 1049);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 0, 1, 1, 1, 1, 1051);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 0, 1, 1, 1, 2, 1052);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0, 1, 1, 1, 3, 1053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "彼とデュエット\nしてみたいな～♪", 0, 1, 1, 1, 4, 1054);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 0, 1, 1, 1, 5, 1055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0, 1, 1, 4, 101, 1056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 0, 1, 1, 8, 102, 1057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "握手会の練習、\n付き合ってくれない？", 0, 1, 2, 1, 1, 1058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 0, 1, 2, 1, 2, 1059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 0, 1, 2, 1, 3, 1060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 0, 1, 2, 1, 4, 1061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 0, 1, 2, 4, 101, 1062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 0, 1, 2, 8, 102, 1063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 0, 1, 2, 1, 1, 1064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 2, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 0, 1, 2, 1, 2, 1065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 0, 1, 2, 1, 3, 1066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 0, 1, 3, 1, 1, 1067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 0, 1, 3, 1, 2, 1068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 0, 1, 3, 1, 3, 1069);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 0, 1, 3, 1, 4, 1070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 0, 1, 3, 1, 5, 1071);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 0, 1, 3, 1, 6, 1072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 0, 1, 3, 1, 7, 1073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 0, 1, 3, 1, 8, 1074);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 0, 1, 1, 1, 1, 1076);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 0, 1, 1, 1, 2, 1077);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 0, 1, 1, 1, 3, 1078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "彼とデュエット\nしてみたいな～♪", 0, 1, 1, 1, 4, 1079);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 0, 1, 1, 1, 5, 1080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0, 1, 1, 8, 101, 1081);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 0, 1, 1, 8, 102, 1082);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0, 1, 2, 1, 1, 1083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 0, 1, 2, 1, 2, 1084);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 0, 1, 2, 1, 3, 1085);
