INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 0, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 0, 1, 2, 8, 102, 1088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102902, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 0, 1, 2, 1, 1, 1089);
