INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 0, 1, 1, 1, 1, 1101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 0, 1, 1, 1, 2, 1102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 0, 1, 1, 1, 3, 1103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "むむ、アクダイカンの\n気配がするデース", 0, 1, 1, 1, 4, 1104);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "クニトリ合戦、\n楽しみデース！", 0, 1, 1, 1, 5, 1105);
