INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0, 1, 1, 8, 102, 1107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 0, 1, 2, 1, 1, 1108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 0, 1, 2, 1, 2, 1109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 0, 1, 2, 1, 3, 1110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 0, 1, 2, 1, 4, 1111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0, 1, 2, 4, 101, 1112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0, 1, 2, 8, 102, 1113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 0, 1, 2, 1, 1, 1114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 2, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 0, 1, 2, 1, 2, 1115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 3, "明日もきっと\nいい日デスよ、\nショーグン！", 0, 1, 2, 1, 3, 1116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 0, 1, 3, 1, 1, 1117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 0, 1, 3, 1, 2, 1118);
