INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0, 1, 3, 1, 4, 1120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "身体を暖めて\nイクサに向かうデス！", 0, 1, 3, 1, 5, 1121);
