INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 0, 1, 3, 1, 2, 1143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0, 1, 3, 1, 3, 1144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0, 1, 3, 1, 4, 1145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "身体を暖めてイクサに\n向かうデス！", 0, 1, 3, 1, 5, 1146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 0, 1, 3, 1, 6, 1147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 0, 1, 3, 1, 7, 1148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103002, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 0, 1, 3, 1, 8, 1149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0, 1, 1, 1, 1, 1151);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "父は、いつも男性に\nきびしいですね", 0, 1, 1, 1, 2, 1152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "占ってほしい人は、\nいつでも言って\nください", 0, 1, 1, 1, 3, 1153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0, 1, 1, 1, 4, 1154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "この部屋、風水的にも\n運気が上がりそうです", 0, 1, 1, 1, 5, 1155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 0, 1, 1, 4, 101, 1156);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0, 1, 1, 8, 102, 1157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "女難の相が\n出ていますね…\n気をつけてください", 0, 1, 2, 1, 1, 1158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 0, 1, 2, 1, 2, 1159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 0, 1, 2, 1, 3, 1160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 0, 1, 2, 1, 4, 1161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 0, 1, 2, 4, 101, 1162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 0, 1, 2, 8, 102, 1163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 0, 1, 2, 1, 1, 1164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 0, 1, 2, 1, 2, 1165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 3, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 0, 1, 2, 1, 3, 1166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 0, 1, 3, 1, 1, 1167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "一休みしましょう。\n憑かれて疲れました", 0, 1, 3, 1, 2, 1168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "かわいい…\n付喪神が宿らないかな", 0, 1, 3, 1, 3, 1169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 0, 1, 3, 1, 4, 1170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "気持ちいいです…\n極楽浄土みたい…", 0, 1, 3, 1, 5, 1171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0, 1, 3, 1, 6, 1172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "お父さんも\n食べたいの？\n死んでるのに？", 0, 1, 3, 1, 7, 1173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "この本…\nもしかして予言書？", 0, 1, 3, 1, 8, 1174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "ここにいる幽霊は\nみんな幸せそうです", 0, 1, 1, 1, 1, 1176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 0, 1, 1, 1, 2, 1177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 0, 1, 1, 1, 3, 1178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 0, 1, 1, 1, 4, 1179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0, 1, 1, 1, 5, 1180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 0, 1, 1, 8, 101, 1181);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 0, 1, 1, 8, 102, 1182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "この部屋、風水的にも\n運気が上がりそうです", 0, 1, 2, 1, 1, 1183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 0, 1, 2, 1, 2, 1184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0, 1, 2, 1, 3, 1185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "ラップ音？\nいえ、父のいびきです", 0, 1, 2, 1, 4, 1186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0, 1, 2, 8, 101, 1187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 0, 1, 2, 8, 102, 1188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 0, 1, 2, 1, 1, 1189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 2, "明るい時間は\nあまり得意では\nないんです…", 0, 1, 2, 1, 2, 1190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 3, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 0, 1, 2, 1, 3, 1191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 0, 1, 3, 1, 1, 1192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "うとうと…\nあれ…\n走馬灯が見える…", 0, 1, 3, 1, 2, 1193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "かわいい…\nお父さんみたい…", 0, 1, 3, 1, 3, 1194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 0, 1, 3, 1, 4, 1195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 0, 1, 3, 1, 5, 1196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0, 1, 3, 1, 6, 1197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "お父さんの分も、\n私が食べるね", 0, 1, 3, 1, 7, 1198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103102, 0, "この本の占い\n私の占いと違う…", 0, 1, 3, 1, 8, 1199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 0, 1, 1, 1, 1, 1201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0, 1, 1, 1, 2, 1202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 0, 1, 1, 1, 3, 1203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 0, 1, 1, 1, 4, 1204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 0, 1, 1, 1, 5, 1205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0, 1, 1, 4, 101, 1206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0, 1, 1, 8, 102, 1207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 0, 1, 2, 1, 1, 1208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 0, 1, 2, 1, 2, 1209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 0, 1, 2, 1, 3, 1210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 0, 1, 2, 1, 4, 1211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 0, 1, 2, 4, 101, 1212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 0, 1, 2, 8, 102, 1213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 0, 1, 2, 1, 1, 1214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 0, 1, 2, 1, 2, 1215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 0, 1, 2, 1, 3, 1216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 0, 1, 3, 1, 1, 1217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 0, 1, 3, 1, 2, 1218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0, 1, 3, 1, 3, 1219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 0, 1, 3, 1, 4, 1220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0, 1, 3, 1, 5, 1221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 0, 1, 3, 1, 6, 1222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 0, 1, 3, 1, 7, 1223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 0, 1, 3, 1, 8, 1224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0, 1, 1, 1, 1, 1226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 0, 1, 1, 1, 2, 1227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 0, 1, 1, 1, 3, 1228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 0, 1, 1, 1, 4, 1229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "この狭さも\n慣れれば\n快適ですわ♪", 0, 1, 1, 1, 5, 1230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0, 1, 1, 8, 101, 1231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0, 1, 1, 8, 102, 1232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 0, 1, 2, 1, 1, 1233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 0, 1, 2, 1, 2, 1234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 0, 1, 2, 1, 3, 1235);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 0, 1, 2, 1, 4, 1236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 0, 1, 2, 8, 101, 1237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 0, 1, 2, 8, 102, 1238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 0, 1, 2, 1, 1, 1239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 0, 1, 2, 1, 2, 1240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 0, 1, 2, 1, 3, 1241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 0, 1, 3, 1, 1, 1242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 0, 1, 3, 1, 2, 1243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0, 1, 3, 1, 3, 1244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 0, 1, 3, 1, 4, 1245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0, 1, 3, 1, 5, 1246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 0, 1, 3, 1, 6, 1247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 0, 1, 3, 1, 7, 1248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103202, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 0, 1, 3, 1, 8, 1249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "家の中のものを使って\n物ボケ、考えるべ！", 0, 1, 1, 1, 1, 1251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 0, 1, 1, 1, 2, 1252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "うちに塀ができたら、\nへー、って言いたい！", 0, 1, 1, 1, 3, 1253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 0, 1, 1, 1, 4, 1254);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "エリザベスも\n連れてきてぇ～", 0, 1, 1, 1, 5, 1255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 0, 1, 1, 4, 101, 1256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 0, 1, 1, 8, 102, 1257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 0, 1, 2, 1, 1, 1258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 0, 1, 2, 1, 2, 1259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0, 1, 2, 1, 3, 1260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 0, 1, 2, 1, 4, 1261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0, 1, 2, 4, 101, 1262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 0, 1, 2, 8, 102, 1263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 0, 1, 2, 1, 1, 1264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 2, "お昼だ～腹が減っては\n笑いはできぬ～！", 0, 1, 2, 1, 2, 1265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 3, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 0, 1, 2, 1, 3, 1266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 0, 1, 3, 1, 1, 1267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 0, 1, 3, 1, 2, 1268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 0, 1, 3, 1, 3, 1269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "必要以上に\n癒されちまうべや～", 0, 1, 3, 1, 4, 1270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 0, 1, 3, 1, 5, 1271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "お笑い芸人は、\n体が資本だべさ～！", 0, 1, 3, 1, 6, 1272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0, 1, 3, 1, 7, 1273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 0, 1, 3, 1, 8, 1274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 0, 1, 1, 1, 1, 1276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "この部屋でも、商品\n売っていいんだべか？", 0, 1, 1, 1, 2, 1277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 0, 1, 1, 1, 3, 1278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 0, 1, 1, 1, 4, 1279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "エリザベスも\n連れてきてぇ～", 0, 1, 1, 1, 5, 1280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0, 1, 1, 8, 101, 1281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 0, 1, 1, 8, 102, 1282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 0, 1, 2, 1, 1, 1283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 0, 1, 2, 1, 2, 1284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0, 1, 2, 1, 3, 1285);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 0, 1, 2, 1, 4, 1286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 0, 1, 2, 8, 101, 1287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 0, 1, 2, 8, 102, 1288);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 0, 1, 2, 1, 1, 1289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 2, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 0, 1, 2, 1, 2, 1290);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 3, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 0, 1, 2, 1, 3, 1291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "うーん…衣装、\nずれてねぇべか？", 0, 1, 3, 1, 1, 1292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 0, 1, 3, 1, 2, 1293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "はう～\nなまらめんこいべや～", 0, 1, 3, 1, 3, 1294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "必要以上に\n癒されちまうべや～", 0, 1, 3, 1, 4, 1295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 0, 1, 3, 1, 5, 1296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 0, 1, 3, 1, 6, 1297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0, 1, 3, 1, 7, 1298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103302, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 0, 1, 3, 1, 8, 1299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 0, 1, 1, 1, 1, 1301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 0, 1, 1, 1, 2, 1302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 0, 1, 1, 1, 3, 1303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 0, 1, 1, 1, 4, 1304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 0, 1, 1, 1, 5, 1305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 0, 1, 1, 4, 101, 1306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "もしかして運命の\n相手だったり…\nするのかな", 0, 1, 1, 8, 102, 1307);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 0, 1, 2, 1, 1, 1308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 0, 1, 2, 1, 2, 1309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 0, 1, 2, 1, 3, 1310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 0, 1, 2, 1, 4, 1311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0, 1, 2, 4, 101, 1312);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 0, 1, 2, 8, 102, 1313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 0, 1, 2, 1, 1, 1314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 2, "おっひるー♪\nなに食べよっかな～♪", 0, 1, 2, 1, 2, 1315);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 3, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 0, 1, 2, 1, 3, 1316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 0, 1, 3, 1, 1, 1317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0, 1, 3, 1, 2, 1318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 0, 1, 3, 1, 3, 1319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 0, 1, 3, 1, 4, 1320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 0, 1, 3, 1, 5, 1321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 0, 1, 3, 1, 6, 1322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 0, 1, 3, 1, 7, 1323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0, 1, 3, 1, 8, 1324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 0, 1, 1, 1, 1, 1326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "今日は\nのんびり\n過ごそうかな～", 0, 1, 1, 1, 2, 1327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "あ…\nお皿、洗って\nなかった…", 0, 1, 1, 1, 3, 1328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 0, 1, 1, 1, 4, 1329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "素敵な彼氏…\n欲しいなぁ…", 0, 1, 1, 1, 5, 1330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 0, 1, 1, 8, 101, 1331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 0, 1, 1, 8, 102, 1332);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 0, 1, 2, 1, 1, 1333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 0, 1, 2, 1, 2, 1334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 0, 1, 2, 1, 3, 1335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "お姉さんが～\n可愛がって\nあげようか～？", 0, 1, 2, 1, 4, 1336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0, 1, 2, 8, 101, 1337);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 0, 1, 2, 8, 102, 1338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 1, "さーて、今日も\nいっちょやるぞー！", 0, 1, 2, 1, 1, 1339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 2, "後でのんびり\nストレッチでも\nしようっと♪", 0, 1, 2, 1, 2, 1340);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 3, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 0, 1, 2, 1, 3, 1341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "仕事の前に、髪とか\n整えないと…", 0, 1, 3, 1, 1, 1342);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0, 1, 3, 1, 2, 1343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 0, 1, 3, 1, 3, 1344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "うわあ\nこれ、私の好きな\n香りだー！", 0, 1, 3, 1, 4, 1345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 0, 1, 3, 1, 5, 1346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "地道な努力が\nリア充への\n第一歩！", 0, 1, 3, 1, 6, 1347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 0, 1, 3, 1, 7, 1348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103402, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0, 1, 3, 1, 8, 1349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0, 1, 1, 1, 1, 1351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 0, 1, 1, 1, 2, 1352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0, 1, 1, 1, 3, 1353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "ちょっと、休憩…\n本でも読もうかな", 0, 1, 1, 1, 4, 1354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "もっとたくさんの本に\n囲まれて生活したいな", 0, 1, 1, 1, 5, 1355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "むかしむかしある所に\nシオリと\nあの人が…", 0, 1, 1, 4, 101, 1356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 0, 1, 1, 8, 102, 1357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0, 1, 2, 1, 1, 1358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "あ…お姉ちゃんと\n約束があったんだ…", 0, 1, 2, 1, 2, 1359);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0, 1, 2, 1, 3, 1360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0, 1, 2, 1, 4, 1361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 0, 1, 2, 4, 101, 1362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0, 1, 2, 8, 102, 1363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 0, 1, 2, 1, 1, 1364);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 2, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 0, 1, 2, 1, 2, 1365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 3, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 0, 1, 2, 1, 3, 1366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 0, 1, 3, 1, 1, 1367);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "少し休もう\n…やっぱり\nたくさん休もう", 0, 1, 3, 1, 2, 1368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 0, 1, 3, 1, 3, 1369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 0, 1, 3, 1, 4, 1370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "こうして温まって\nいると、病気も\n治る気がします", 0, 1, 3, 1, 5, 1371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 0, 1, 3, 1, 6, 1372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 0, 1, 3, 1, 7, 1373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "続きが気になって\n読むのを止める\nタイミングが…", 0, 1, 3, 1, 8, 1374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "しっかり休まないと\n早く回復するように", 0, 1, 1, 1, 1, 1376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 0, 1, 1, 1, 2, 1377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 0, 1, 1, 1, 3, 1378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0, 1, 1, 1, 4, 1379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0, 1, 1, 1, 5, 1380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "むかしむかしある所に\nシオリと\nあの人が…", 0, 1, 1, 8, 101, 1381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "あの人の\nためにも早く\n病気を治さないと…", 0, 1, 1, 8, 102, 1382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0, 1, 2, 1, 1, 1383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 0, 1, 2, 1, 2, 1384);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 0, 1, 2, 1, 3, 1385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0, 1, 2, 1, 4, 1386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0, 1, 2, 8, 101, 1387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0, 1, 2, 8, 102, 1388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 0, 1, 2, 1, 1, 1389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 2, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 0, 1, 2, 1, 2, 1390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 3, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 0, 1, 2, 1, 3, 1391);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 0, 1, 3, 1, 1, 1392);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "少し休もう\n…やっぱり\nたくさん休もう", 0, 1, 3, 1, 2, 1393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 0, 1, 3, 1, 3, 1394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 0, 1, 3, 1, 4, 1395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "こうして温まって\nいると、病気も\n治る気がします", 0, 1, 3, 1, 5, 1396);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 0, 1, 3, 1, 6, 1397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "いっぱい食べて\n健康な体にならないと", 0, 1, 3, 1, 7, 1398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103802, 0, "続きが気になって\n読むのを止める\nタイミングが…", 0, 1, 3, 1, 8, 1399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 0, 1, 1, 1, 1, 1401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 0, 1, 1, 1, 2, 1402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 0, 1, 1, 1, 3, 1403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 0, 1, 1, 1, 4, 1404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 0, 1, 1, 1, 5, 1405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 0, 1, 1, 4, 101, 1406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0, 1, 1, 8, 102, 1407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 0, 1, 2, 1, 1, 1408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 0, 1, 2, 1, 2, 1409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 0, 1, 2, 1, 3, 1410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 0, 1, 2, 1, 4, 1411);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 0, 1, 2, 4, 101, 1412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0, 1, 2, 8, 102, 1413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 0, 1, 2, 1, 1, 1414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 2, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 0, 1, 2, 1, 2, 1415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 3, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 0, 1, 2, 1, 3, 1416);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 0, 1, 3, 1, 1, 1417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 0, 1, 3, 1, 2, 1418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 0, 1, 3, 1, 3, 1419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 0, 1, 3, 1, 4, 1420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "灯りを見てると、\n心がなごみます～", 0, 1, 3, 1, 5, 1421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0, 1, 3, 1, 6, 1422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 0, 1, 3, 1, 7, 1423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0, 1, 3, 1, 8, 1424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 0, 1, 1, 1, 1, 1426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 0, 1, 1, 1, 2, 1427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 0, 1, 1, 1, 3, 1428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 0, 1, 1, 1, 4, 1429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 0, 1, 1, 1, 5, 1430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 0, 1, 1, 8, 101, 1431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0, 1, 1, 8, 102, 1432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 0, 1, 2, 1, 1, 1433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 0, 1, 2, 1, 2, 1434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 0, 1, 2, 1, 3, 1435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 0, 1, 2, 1, 4, 1436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 0, 1, 2, 8, 101, 1437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0, 1, 2, 8, 102, 1438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 0, 1, 2, 1, 1, 1439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 2, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 0, 1, 2, 1, 2, 1440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 3, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 0, 1, 2, 1, 3, 1441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 0, 1, 3, 1, 1, 1442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "ほわあ～…\nふかふかって\nいいものですね～", 0, 1, 3, 1, 2, 1443);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 0, 1, 3, 1, 3, 1444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "灯りを見てると\n心がなごみます～", 0, 1, 3, 1, 4, 1445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 0, 1, 3, 1, 5, 1446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "身体を鍛えて\nみなさんを\n守ります！", 0, 1, 3, 1, 6, 1447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0, 1, 3, 1, 7, 1448);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104002, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0, 1, 3, 1, 8, 1449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 0, 1, 1, 1, 1, 1451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 0, 1, 1, 1, 2, 1452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0, 1, 1, 1, 3, 1453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 0, 1, 1, 1, 4, 1454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "あとで曲を復習して\nおかないと…", 0, 1, 1, 1, 5, 1455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 0, 1, 1, 4, 101, 1456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0, 1, 1, 8, 102, 1457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0, 1, 2, 1, 1, 1458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 0, 1, 2, 1, 2, 1459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0, 1, 2, 1, 3, 1460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0, 1, 2, 1, 4, 1461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0, 1, 2, 4, 101, 1462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0, 1, 2, 8, 102, 1463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 0, 1, 2, 1, 1, 1464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 2, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 0, 1, 2, 1, 2, 1465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 3, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 0, 1, 2, 1, 3, 1466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0, 1, 3, 1, 1, 1467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 0, 1, 3, 1, 2, 1468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "この子たちを\n相手に練習して\nみようかな…♪", 0, 1, 3, 1, 3, 1469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0, 1, 3, 1, 4, 1470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0, 1, 3, 1, 5, 1471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "歌うためには\n体力づくりは\n欠かせません", 0, 1, 3, 1, 6, 1472);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "歌った後は\nお腹が空いて\nしまうんです", 0, 1, 3, 1, 7, 1473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "音楽について\n理論も勉強\nしないと…", 0, 1, 3, 1, 8, 1474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 0, 1, 1, 1, 1, 1476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0, 1, 1, 1, 2, 1477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 0, 1, 1, 1, 3, 1478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 0, 1, 1, 1, 4, 1479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 0, 1, 1, 1, 5, 1480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0, 1, 1, 8, 101, 1481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 0, 1, 1, 8, 102, 1482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0, 1, 2, 1, 1, 1483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0, 1, 2, 1, 2, 1484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "魔物退治ですか！？\n…違います？\n良かった…", 0, 1, 2, 1, 3, 1485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0, 1, 2, 1, 4, 1486);
