INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0, 1, 2, 8, 102, 1488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 0, 1, 2, 1, 1, 1489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 2, "少し休んだら\n午後の練習に\n行ってきますね", 0, 1, 2, 1, 2, 1490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 3, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 0, 1, 2, 1, 3, 1491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0, 1, 3, 1, 1, 1492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 0, 1, 3, 1, 2, 1493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 0, 1, 3, 1, 3, 1494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0, 1, 3, 1, 4, 1495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0, 1, 3, 1, 5, 1496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 0, 1, 3, 1, 6, 1497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 0, 1, 3, 1, 7, 1498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104202, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 0, 1, 3, 1, 8, 1499);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "店の新メニューでも\n考えるかな", 0, 1, 1, 1, 1, 1501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "あたしの店から、\n出前を届けようか？", 0, 1, 1, 1, 2, 1502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 0, 1, 1, 1, 3, 1503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0, 1, 1, 1, 4, 1504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 0, 1, 1, 1, 5, 1505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 0, 1, 1, 4, 101, 1506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0, 1, 1, 8, 102, 1507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 0, 1, 2, 1, 1, 1508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 0, 1, 2, 1, 2, 1509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "腹減ったか？　なんか\n作ってやろうか？", 0, 1, 2, 1, 3, 1510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 0, 1, 2, 1, 4, 1511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 0, 1, 2, 4, 101, 1512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0, 1, 2, 8, 102, 1513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 0, 1, 2, 1, 1, 1514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 0, 1, 2, 1, 2, 1515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 3, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 0, 1, 2, 1, 3, 1516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 0, 1, 3, 1, 1, 1517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "ふわぁ、\n一休みすっかなぁ～", 0, 1, 3, 1, 2, 1518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "うっ！　か、かわいい\nじゃねぇか", 0, 1, 3, 1, 3, 1519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0, 1, 3, 1, 4, 1520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 0, 1, 3, 1, 5, 1521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "オラオラオラーーー！\n燃えるぜーーー！", 0, 1, 3, 1, 6, 1522);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0, 1, 3, 1, 7, 1523);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 0, 1, 3, 1, 8, 1524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "パトロール終了～\n本日も異常なしっと！", 0, 1, 1, 1, 1, 1526);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "あたしの店から\n出前を届けようか？", 0, 1, 1, 1, 2, 1527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "あいつ、早く\n帰ってこないかな", 0, 1, 1, 1, 3, 1528);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 0, 1, 1, 1, 4, 1529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 0, 1, 1, 1, 5, 1530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 0, 1, 1, 8, 101, 1531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0, 1, 1, 8, 102, 1532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 0, 1, 2, 1, 1, 1533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 0, 1, 2, 1, 2, 1534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "腹減ったか？　なんか\n作ってやろうか？", 0, 1, 2, 1, 3, 1535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0, 1, 2, 1, 4, 1536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 0, 1, 2, 8, 101, 1537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0, 1, 2, 8, 102, 1538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 0, 1, 2, 1, 1, 1539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 0, 1, 2, 1, 2, 1540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 3, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 0, 1, 2, 1, 3, 1541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 0, 1, 3, 1, 1, 1542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "ふぅ～♪\n疲れがとれるな～", 0, 1, 3, 1, 2, 1543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "うっ！　か、かわいい\nじゃねぇか", 0, 1, 3, 1, 3, 1544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0, 1, 3, 1, 4, 1545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 0, 1, 3, 1, 5, 1546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "オラオラオラーーー！\n燃えるぜーーー！", 0, 1, 3, 1, 6, 1547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0, 1, 3, 1, 7, 1548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104302, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 0, 1, 3, 1, 8, 1549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 0, 1, 1, 1, 1, 1551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 0, 1, 1, 1, 2, 1552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 0, 1, 1, 1, 3, 1553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 0, 1, 1, 1, 4, 1554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0, 1, 1, 1, 5, 1555);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 0, 1, 1, 4, 101, 1556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0, 1, 1, 8, 102, 1557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 0, 1, 2, 1, 1, 1558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 0, 1, 2, 1, 2, 1559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 0, 1, 2, 1, 3, 1560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 0, 1, 2, 1, 4, 1561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 0, 1, 2, 4, 101, 1562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 0, 1, 2, 8, 102, 1563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 0, 1, 2, 1, 1, 1564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 2, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 0, 1, 2, 1, 2, 1565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 3, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 0, 1, 2, 1, 3, 1566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 0, 1, 3, 1, 1, 1567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 0, 1, 3, 1, 2, 1568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0, 1, 3, 1, 3, 1569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 0, 1, 3, 1, 4, 1570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 0, 1, 3, 1, 5, 1571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 0, 1, 3, 1, 6, 1572);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 0, 1, 3, 1, 7, 1573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 0, 1, 3, 1, 8, 1574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 0, 1, 1, 1, 1, 1576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 0, 1, 1, 1, 2, 1577);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 0, 1, 1, 1, 3, 1578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0, 1, 1, 1, 4, 1579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 0, 1, 1, 1, 5, 1580);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 0, 1, 1, 8, 101, 1581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 0, 1, 1, 8, 102, 1582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 0, 1, 2, 1, 1, 1583);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0, 1, 2, 1, 2, 1584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 0, 1, 2, 1, 3, 1585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 0, 1, 2, 1, 4, 1586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 0, 1, 2, 8, 101, 1587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 0, 1, 2, 8, 102, 1588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 0, 1, 2, 1, 1, 1589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 2, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 0, 1, 2, 1, 2, 1590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 3, "今夜は一晩中…\nなんですね？\nぐふふ…", 0, 1, 2, 1, 3, 1591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 0, 1, 3, 1, 1, 1592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 0, 1, 3, 1, 2, 1593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0, 1, 3, 1, 3, 1594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 0, 1, 3, 1, 4, 1595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 0, 1, 3, 1, 5, 1596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 0, 1, 3, 1, 6, 1597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 0, 1, 3, 1, 7, 1598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104502, 0, "どんな本も\nよく見ると\nいやらしいです…！", 0, 1, 3, 1, 8, 1599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 0, 1, 1, 1, 1, 1601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 0, 1, 1, 1, 2, 1602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 0, 1, 1, 1, 3, 1603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 0, 1, 1, 1, 4, 1604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 0, 1, 1, 1, 5, 1605);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 0, 1, 1, 4, 101, 1606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 0, 1, 1, 8, 102, 1607);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 0, 1, 2, 1, 1, 1608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 0, 1, 2, 1, 2, 1609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 0, 1, 2, 1, 3, 1610);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 0, 1, 2, 1, 4, 1611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 0, 1, 2, 4, 101, 1612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 0, 1, 2, 8, 102, 1613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 0, 1, 2, 1, 1, 1614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 2, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 0, 1, 2, 1, 2, 1615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 3, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 0, 1, 2, 1, 3, 1616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 0, 1, 3, 1, 1, 1617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 0, 1, 3, 1, 2, 1618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 0, 1, 3, 1, 3, 1619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 0, 1, 3, 1, 4, 1620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 0, 1, 3, 1, 5, 1621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "本当は外で\n運動したいん\nだけどにゃ", 0, 1, 3, 1, 6, 1622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 0, 1, 3, 1, 7, 1623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "ふむふむ…猫の\nしつけ方は、と…", 0, 1, 3, 1, 8, 1624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 0, 1, 1, 1, 1, 1626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 0, 1, 1, 1, 2, 1627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 0, 1, 1, 1, 3, 1628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 0, 1, 1, 1, 4, 1629);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 0, 1, 1, 1, 5, 1630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 0, 1, 1, 8, 101, 1631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 0, 1, 1, 8, 102, 1632);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 0, 1, 2, 1, 1, 1633);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 0, 1, 2, 1, 2, 1634);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 0, 1, 2, 1, 3, 1635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 0, 1, 2, 1, 4, 1636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 0, 1, 2, 8, 101, 1637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 0, 1, 2, 8, 102, 1638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 0, 1, 2, 1, 1, 1639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 2, "そろそろおっひる～\nにゃーんか\n食べたい…", 0, 1, 2, 1, 2, 1640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 3, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 0, 1, 2, 1, 3, 1641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "にゃにゃ…\n髪が乱れてるにゃ", 0, 1, 3, 1, 1, 1642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 0, 1, 3, 1, 2, 1643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 0, 1, 3, 1, 3, 1644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "猫でもこわくない\n優しい灯りにゃ！", 0, 1, 3, 1, 4, 1645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "ぽっかぽか\nしあわせな気分にゃ～", 0, 1, 3, 1, 5, 1646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 0, 1, 3, 1, 6, 1647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 0, 1, 3, 1, 7, 1648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104602, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 0, 1, 3, 1, 8, 1649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "内職手伝える人\nいないかしら？", 0, 1, 1, 1, 1, 1651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 0, 1, 1, 1, 2, 1652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 0, 1, 1, 1, 3, 1653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 0, 1, 1, 1, 4, 1654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 0, 1, 1, 1, 5, 1655);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 0, 1, 1, 4, 101, 1656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0, 1, 1, 8, 102, 1657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 0, 1, 2, 1, 1, 1658);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 0, 1, 2, 1, 2, 1659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 0, 1, 2, 1, 3, 1660);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 0, 1, 2, 1, 4, 1661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0, 1, 2, 4, 101, 1662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 0, 1, 2, 8, 102, 1663);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 1, "おはよう！\n今日も朝から\nバイトよ！", 0, 1, 2, 1, 1, 1664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 2, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 0, 1, 2, 1, 2, 1665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 0, 1, 2, 1, 3, 1666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 0, 1, 3, 1, 1, 1667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "はぁ～バイトで\n疲れたから癒される～", 0, 1, 3, 1, 2, 1668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "これを内職で\n作ったら\n儲かるかしら？", 0, 1, 3, 1, 3, 1669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 0, 1, 3, 1, 4, 1670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 0, 1, 3, 1, 5, 1671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 0, 1, 3, 1, 6, 1672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 0, 1, 3, 1, 7, 1673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "日々絶えず学んで、\n生活に活かすのよ！", 0, 1, 3, 1, 8, 1674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 0, 1, 1, 1, 1, 1676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 0, 1, 1, 1, 2, 1677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 0, 1, 1, 1, 3, 1678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 0, 1, 1, 1, 4, 1679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 0, 1, 1, 1, 5, 1680);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0, 1, 1, 8, 101, 1681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 0, 1, 1, 8, 102, 1682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 0, 1, 2, 1, 1, 1683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 0, 1, 2, 1, 2, 1684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "今から\n休憩するところなの\n一緒にどう？", 0, 1, 2, 1, 3, 1685);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 0, 1, 2, 1, 4, 1686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 0, 1, 2, 8, 101, 1687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0, 1, 2, 8, 102, 1688);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 0, 1, 2, 1, 1, 1689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 2, "お昼時ね\n少し休憩したら\nまた働くわよ！", 0, 1, 2, 1, 2, 1690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 0, 1, 2, 1, 3, 1691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 0, 1, 3, 1, 1, 1692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "ここで少し\n休んだら\nまたバイトよ！", 0, 1, 3, 1, 2, 1693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "これを内職で\n作ったら\n儲かるかしら？", 0, 1, 3, 1, 3, 1694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 0, 1, 3, 1, 4, 1695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 0, 1, 3, 1, 5, 1696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 0, 1, 3, 1, 6, 1697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 0, 1, 3, 1, 7, 1698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104802, 0, "日々絶えず学んで\n生活に活かすのよ！", 0, 1, 3, 1, 8, 1699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 0, 1, 1, 1, 1, 1701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 0, 1, 1, 1, 2, 1702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 0, 1, 1, 1, 3, 1703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "くんくん、\n弟くんのにおい…\n近くにいるね？", 0, 1, 1, 1, 4, 1704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 0, 1, 1, 1, 5, 1705);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "この部屋を、\n私と弟くんの\n愛の巣に…", 0, 1, 1, 4, 101, 1706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "はぁ～…弟くん\n禁断症状だよ～", 0, 1, 1, 8, 102, 1707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 0, 1, 2, 1, 1, 1708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 0, 1, 2, 1, 2, 1709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "姉と弟の外出は\nデートって\nいうのかな？", 0, 1, 2, 1, 3, 1710);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 0, 1, 2, 1, 4, 1711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 0, 1, 2, 4, 101, 1712);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 0, 1, 2, 8, 102, 1713);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 0, 1, 2, 1, 1, 1714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 2, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 0, 1, 2, 1, 2, 1715);
