INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104902, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 0, 1, 3, 1, 6, 1747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104902, 0, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 0, 1, 3, 1, 7, 1748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104902, 0, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 0, 1, 3, 1, 8, 1749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 0, 1, 1, 1, 1, 1751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 0, 1, 1, 1, 2, 1752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "アタシの大人コーデで\nあいつも夢中ね♪", 0, 1, 1, 1, 3, 1753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 0, 1, 1, 1, 4, 1754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 0, 1, 1, 1, 5, 1755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 0, 1, 1, 4, 101, 1756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 0, 1, 1, 8, 102, 1757);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "わーい、アメだー♪\n食べる食べるー♪", 0, 1, 2, 1, 1, 1758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 0, 1, 2, 1, 2, 1759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 0, 1, 2, 1, 3, 1760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 0, 1, 2, 1, 4, 1761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 0, 1, 2, 4, 101, 1762);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 0, 1, 2, 8, 102, 1763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 1, "朝のスキンケアは\n大事なの！", 0, 1, 2, 1, 1, 1764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 0, 1, 2, 1, 2, 1765);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 3, "エイジングケアを\nしないとねー", 0, 1, 2, 1, 3, 1766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 0, 1, 3, 1, 1, 1767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 0, 1, 3, 1, 2, 1768);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 0, 1, 3, 1, 3, 1769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 0, 1, 3, 1, 4, 1770);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "あったかーい♪\nここで宿題\nしようかしら？", 0, 1, 3, 1, 5, 1771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 0, 1, 3, 1, 6, 1772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 0, 1, 3, 1, 7, 1773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 0, 1, 3, 1, 8, 1774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 0, 1, 1, 1, 1, 1776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "恋人にするなら\nイケメンか\nただイケよね～", 0, 1, 1, 1, 2, 1777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 0, 1, 1, 1, 3, 1778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 0, 1, 1, 1, 4, 1779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 0, 1, 1, 1, 5, 1780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 0, 1, 1, 8, 101, 1781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 0, 1, 1, 8, 102, 1782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "なーに？\nアタシに甘えたいの？", 0, 1, 2, 1, 1, 1783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 0, 1, 2, 1, 2, 1784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 0, 1, 2, 1, 3, 1785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 0, 1, 2, 1, 4, 1786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 0, 1, 2, 8, 101, 1787);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 0, 1, 2, 8, 102, 1788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 1, "レディの朝は\n忙しいのよ", 0, 1, 2, 1, 1, 1789);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 0, 1, 2, 1, 2, 1790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 3, "エイジングケアを\nしないとねー", 0, 1, 2, 1, 3, 1791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 0, 1, 3, 1, 1, 1792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 0, 1, 3, 1, 2, 1793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 0, 1, 3, 1, 3, 1794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 0, 1, 3, 1, 4, 1795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "あったかーい♪\nここで宿題\nしようかしら？", 0, 1, 3, 1, 5, 1796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 0, 1, 3, 1, 6, 1797);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "レディは、食べ物にも\n気を使うのよ", 0, 1, 3, 1, 7, 1798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105002, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 0, 1, 3, 1, 8, 1799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "魔法と科学\nどっちも極めて\nみせるわ", 0, 1, 1, 1, 1, 1801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 0, 1, 1, 1, 2, 1802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "たまった治療代の\n請求書を\nまとめないと…", 0, 1, 1, 1, 3, 1803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 0, 1, 1, 1, 4, 1804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 0, 1, 1, 1, 5, 1805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 0, 1, 1, 4, 101, 1806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0, 1, 1, 8, 102, 1807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 0, 1, 2, 1, 1, 1808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 0, 1, 2, 1, 2, 1809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 0, 1, 2, 1, 3, 1810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 0, 1, 2, 1, 4, 1811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0, 1, 2, 4, 101, 1812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0, 1, 2, 8, 102, 1813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 0, 1, 2, 1, 1, 1814);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 2, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 0, 1, 2, 1, 2, 1815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 3, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 0, 1, 2, 1, 3, 1816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "医者たるもの\nいつだって\n清潔に…♪", 0, 1, 3, 1, 1, 1817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 0, 1, 3, 1, 2, 1818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "ぬいぐるみなら\n動物も…", 0, 1, 3, 1, 3, 1819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 0, 1, 3, 1, 4, 1820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "ここにいると\nついウトウト\nしちゃうわ…", 0, 1, 3, 1, 5, 1821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 0, 1, 3, 1, 6, 1822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 0, 1, 3, 1, 7, 1823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 0, 1, 3, 1, 8, 1824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 0, 1, 1, 1, 1, 1826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 0, 1, 1, 1, 2, 1827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 0, 1, 1, 1, 3, 1828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 0, 1, 1, 1, 4, 1829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 0, 1, 1, 1, 5, 1830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 0, 1, 1, 8, 101, 1831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0, 1, 1, 8, 102, 1832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 0, 1, 2, 1, 1, 1833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 0, 1, 2, 1, 2, 1834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 0, 1, 2, 1, 3, 1835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 0, 1, 2, 1, 4, 1836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0, 1, 2, 8, 101, 1837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0, 1, 2, 8, 102, 1838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 0, 1, 2, 1, 1, 1839);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 2, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 0, 1, 2, 1, 2, 1840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 3, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 0, 1, 2, 1, 3, 1841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "身なりについて\n研究するのも\n楽しそうね", 0, 1, 3, 1, 1, 1842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 0, 1, 3, 1, 2, 1843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 0, 1, 3, 1, 3, 1844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 0, 1, 3, 1, 4, 1845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 0, 1, 3, 1, 5, 1846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "運動しないと\n体型の維持が…", 0, 1, 3, 1, 6, 1847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "適度な食事も\n大切よね", 0, 1, 3, 1, 7, 1848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105102, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 0, 1, 3, 1, 8, 1849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "ぺっぺっ！\n…い、いけないっ！", 0, 1, 1, 1, 1, 1851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "いっけな～い！\nひづめで床に傷が！", 0, 1, 1, 1, 2, 1852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 0, 1, 1, 1, 3, 1853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 0, 1, 1, 1, 4, 1854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "みんな、体重\nどれくらいなのかな？", 0, 1, 1, 1, 5, 1855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0, 1, 1, 4, 101, 1856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 0, 1, 1, 8, 102, 1857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 0, 1, 2, 1, 1, 1858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 0, 1, 2, 1, 2, 1859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 0, 1, 2, 1, 3, 1860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "力仕事ならまかせて！", 0, 1, 2, 1, 4, 1861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0, 1, 2, 4, 101, 1862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0, 1, 2, 8, 102, 1863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 0, 1, 2, 1, 1, 1864);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 2, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 0, 1, 2, 1, 2, 1865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 0, 1, 2, 1, 3, 1866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 0, 1, 3, 1, 1, 1867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "ミシッっていった！\n私、重すぎるかも！？", 0, 1, 3, 1, 2, 1868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "あ！　あなたも\n美人さんね～♪", 0, 1, 3, 1, 3, 1869);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 0, 1, 3, 1, 4, 1870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 0, 1, 3, 1, 5, 1871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "な、なんだか野生に\n帰っちゃいそう！", 0, 1, 3, 1, 6, 1872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 0, 1, 3, 1, 7, 1873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0, 1, 3, 1, 8, 1874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 0, 1, 1, 1, 1, 1876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 0, 1, 1, 1, 2, 1877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "動物苑…って\n嫌なギルド名ね…", 0, 1, 1, 1, 3, 1878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 0, 1, 1, 1, 4, 1879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 0, 1, 1, 1, 5, 1880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0, 1, 1, 8, 101, 1881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 0, 1, 1, 8, 102, 1882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 0, 1, 2, 1, 1, 1883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 0, 1, 2, 1, 2, 1884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "ぺっぺっ！\n…い、いけないっ！", 0, 1, 2, 1, 3, 1885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 0, 1, 2, 1, 4, 1886);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0, 1, 2, 8, 101, 1887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0, 1, 2, 8, 102, 1888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 0, 1, 2, 1, 1, 1889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 2, "ダイエット中だから\nお昼は抜いてるの～", 0, 1, 2, 1, 2, 1890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 0, 1, 2, 1, 3, 1891);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 0, 1, 3, 1, 1, 1892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "ミシッっていった！\n私、重すぎるかも！？", 0, 1, 3, 1, 2, 1893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "あ！　あなたも\n美人さんね～♪", 0, 1, 3, 1, 3, 1894);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 0, 1, 3, 1, 4, 1895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 0, 1, 3, 1, 5, 1896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "な、なんだか野生に\n帰っちゃいそう！", 0, 1, 3, 1, 6, 1897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 0, 1, 3, 1, 7, 1898);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105202, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0, 1, 3, 1, 8, 1899);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 0, 1, 1, 1, 1, 1901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 0, 1, 1, 1, 2, 1902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 0, 1, 1, 1, 3, 1903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 0, 1, 1, 1, 4, 1904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0, 1, 1, 1, 5, 1905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 0, 1, 1, 4, 101, 1906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 0, 1, 1, 8, 102, 1907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 0, 1, 2, 1, 1, 1908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0, 1, 2, 1, 2, 1909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 0, 1, 2, 1, 3, 1910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "私ともっと高く、\n共に飛ぼう！", 0, 1, 2, 1, 4, 1911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 0, 1, 2, 4, 101, 1912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 0, 1, 2, 8, 102, 1913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 0, 1, 2, 1, 1, 1914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 2, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 0, 1, 2, 1, 2, 1915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 3, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 0, 1, 2, 1, 3, 1916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 0, 1, 3, 1, 1, 1917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0, 1, 3, 1, 2, 1918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0, 1, 3, 1, 3, 1919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 0, 1, 3, 1, 4, 1920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0, 1, 3, 1, 5, 1921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 0, 1, 3, 1, 6, 1922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 0, 1, 3, 1, 7, 1923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 0, 1, 3, 1, 8, 1924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 0, 1, 1, 1, 1, 1926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 0, 1, 1, 1, 2, 1927);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0, 1, 1, 1, 3, 1928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0, 1, 1, 1, 4, 1929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 0, 1, 1, 1, 5, 1930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 0, 1, 1, 8, 101, 1931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 0, 1, 1, 8, 102, 1932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 0, 1, 2, 1, 1, 1933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 0, 1, 2, 1, 2, 1934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 0, 1, 2, 1, 3, 1935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 0, 1, 2, 1, 4, 1936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 0, 1, 2, 8, 101, 1937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 0, 1, 2, 8, 102, 1938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 0, 1, 2, 1, 1, 1939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 2, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 0, 1, 2, 1, 2, 1940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 3, "寝る前に…あと少し\nだけ、鍛錬を…", 0, 1, 2, 1, 3, 1941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 0, 1, 3, 1, 1, 1942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0, 1, 3, 1, 2, 1943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0, 1, 3, 1, 3, 1944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 0, 1, 3, 1, 4, 1945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0, 1, 3, 1, 5, 1946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 0, 1, 3, 1, 6, 1947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 0, 1, 3, 1, 7, 1948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105302, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 0, 1, 3, 1, 8, 1949);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 0, 1, 1, 1, 1, 1951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "団員募集の\nポスター\n作ろうっと♪", 0, 1, 1, 1, 2, 1952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "父さん…\n今頃どこで\n何してるんだろ？", 0, 1, 1, 1, 3, 1953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 0, 1, 1, 1, 4, 1954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 0, 1, 1, 1, 5, 1955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 0, 1, 1, 4, 101, 1956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 0, 1, 1, 8, 102, 1957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 0, 1, 2, 1, 1, 1958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 0, 1, 2, 1, 2, 1959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 0, 1, 2, 1, 3, 1960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 0, 1, 2, 1, 4, 1961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 0, 1, 2, 4, 101, 1962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 0, 1, 2, 8, 102, 1963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 0, 1, 2, 1, 1, 1964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 2, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 0, 1, 2, 1, 2, 1965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 3, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 0, 1, 2, 1, 3, 1966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 0, 1, 3, 1, 1, 1967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 0, 1, 3, 1, 2, 1968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 0, 1, 3, 1, 3, 1969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 0, 1, 3, 1, 4, 1970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "これって\n騎空艇に\n置けるかな…？", 0, 1, 3, 1, 5, 1971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 0, 1, 3, 1, 6, 1972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 0, 1, 3, 1, 7, 1973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 0, 1, 3, 1, 8, 1974);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 0, 1, 1, 1, 1, 1976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "暇だよ…\nどこかに強い人は\nいないか？", 0, 1, 1, 1, 2, 1977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 0, 1, 1, 1, 3, 1978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 0, 1, 1, 1, 4, 1979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 0, 1, 1, 1, 5, 1980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "私の力の源は\n仲間との\n絆だよ！", 0, 1, 1, 8, 101, 1981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 0, 1, 1, 8, 102, 1982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 0, 1, 2, 1, 1, 1983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 0, 1, 2, 1, 2, 1984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "どうした？\nもしかして手合せ\nしたいか？", 0, 1, 2, 1, 3, 1985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 0, 1, 2, 1, 4, 1986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "背中はあなたに\n預けたよ！\nよろしくね！", 0, 1, 2, 8, 101, 1987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 0, 1, 2, 8, 102, 1988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 0, 1, 2, 1, 1, 1989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 2, "腹減ったね…\nなにか食べに\n行くよ！", 0, 1, 2, 1, 2, 1990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 3, "修行のしすぎは\n良くないよ？\n気をつけるね", 0, 1, 2, 1, 3, 1991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "美しい技は\n美しい体が\nあってこそ！", 0, 1, 3, 1, 1, 1992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 0, 1, 3, 1, 2, 1993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "…練習に\n使うのはさすがに\n可哀そうね", 0, 1, 3, 1, 3, 1994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "これは…\n瞑想の時にも\n使ってみたいね", 0, 1, 3, 1, 4, 1995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "出来ればもっと\n熱いほうが\n嬉しいよ", 0, 1, 3, 1, 5, 1996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "ここでも\n修行出来るなんて\n最高だよ！", 0, 1, 3, 1, 6, 1997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "気力の源は体力！\n体力の源は\n健康とご飯！", 0, 1, 3, 1, 7, 1998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105702, 0, "んー…\n難しいことは\nわかんないよ！", 0, 1, 3, 1, 8, 1999);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "わたしたちの\nギルドハウスへ\nようこそ～！", 0, 1, 1, 1, 1, 2001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 0, 1, 1, 1, 2, 2002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "ん？\n食べ物のにおい？", 0, 1, 1, 1, 3, 2003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "みんなと仲良く食卓を\n囲みたいですね～", 0, 1, 1, 1, 4, 2004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "この部屋が、わたしの\n帰る場所です♪", 0, 1, 1, 1, 5, 2005);
