INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106002, 0, "あたし、みんなや\nあいつと同じ香りを\n身に着けてるんだ…", 0, 1, 3, 1, 4, 2095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106002, 0, "うっ、のんびり\nしたくなる…\n特訓しなきゃ\nいけないのに～", 0, 1, 3, 1, 5, 2096);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106002, 0, "特訓特訓！\nあたしは\nやればできる！", 0, 1, 3, 1, 6, 2097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106002, 0, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 0, 1, 3, 1, 7, 2098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106002, 0, "魔法を使うにも\n知識が必要だものね", 0, 1, 3, 1, 8, 2099);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "この空間は主さまの\n香りがします\n…ふふふ♪", 0, 1, 1, 1, 1, 2101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 0, 1, 1, 1, 2, 2102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "アメスさまへの、\nお祈りができそうな\n場所は…", 0, 1, 1, 1, 3, 2103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "さすが都会…\nおいしいものが\nたくさんあるのですね", 0, 1, 1, 1, 4, 2104);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "主さまが不自由なく\n使える部屋に\nしなくては…", 0, 1, 1, 1, 5, 2105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "できるなら…\n主さまと二人だけの\n部屋の方が…", 0, 1, 1, 4, 101, 2106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 0, 1, 1, 8, 102, 2107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 0, 1, 2, 1, 1, 2108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 0, 1, 2, 1, 2, 2109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 0, 1, 2, 1, 3, 2110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 0, 1, 2, 1, 4, 2111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "主さまのおてて\n温かいですね…♪", 0, 1, 2, 4, 101, 2112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 0, 1, 2, 8, 102, 2113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 1, "おはようございます。\n洗濯をしなければ…", 0, 1, 2, 1, 1, 2114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 2, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 0, 1, 2, 1, 2, 2115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 3, "おやすみになる時は、\nお声をかけてください\nお供致します", 0, 1, 2, 1, 3, 2116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "もう少し…\nおっきくなりたい\nです", 0, 1, 3, 1, 1, 2117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "このふかふかの優しさ\n主さまのようです", 0, 1, 3, 1, 2, 2118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "かわいい♪\n主さまにそっくり♪", 0, 1, 3, 1, 3, 2119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 0, 1, 3, 1, 4, 2120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "ここで洗濯物を\n乾かせるでしょうか", 0, 1, 3, 1, 5, 2121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "都会の人々は\nこんなふうに\n運動を…！？", 0, 1, 3, 1, 6, 2122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 0, 1, 3, 1, 7, 2123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "よく学んで、\n主さまのお役に\n立たなければ", 0, 1, 3, 1, 8, 2124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 0, 1, 1, 1, 1, 2126);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 0, 1, 1, 1, 2, 2127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "アメスさまへの\nお祈りができそうな\n場所は…", 0, 1, 1, 1, 3, 2128);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "瞑想をすると\n心が安らぎます…", 0, 1, 1, 1, 4, 2129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 0, 1, 1, 1, 5, 2130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 0, 1, 1, 8, 101, 2131);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 0, 1, 1, 8, 102, 2132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 0, 1, 2, 1, 1, 2133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 0, 1, 2, 1, 2, 2134);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "お出かけですか？\nわたくしもお供\nいたします", 0, 1, 2, 1, 3, 2135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "ひゃん！\n主さま…\nいじわるです", 0, 1, 2, 1, 4, 2136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 0, 1, 2, 8, 101, 2137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "主さまの優しさに\nわたくしはいつも\n癒されています…", 0, 1, 2, 8, 102, 2138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 1, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 0, 1, 2, 1, 1, 2139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 2, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 0, 1, 2, 1, 2, 2140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 3, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 0, 1, 2, 1, 3, 2141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 0, 1, 3, 1, 1, 2142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "このふかふかの優しさ\n主さまのようです", 0, 1, 3, 1, 2, 2143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "かわいい♪\n主さまにそっくり♪", 0, 1, 3, 1, 3, 2144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "この心地よさ…\n主さまのようです♪", 0, 1, 3, 1, 4, 2145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "ここで洗濯物を\n乾かせるでしょうか　", 0, 1, 3, 1, 5, 2146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 0, 1, 3, 1, 6, 2147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 0, 1, 3, 1, 7, 2148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105902, 0, "都会のことを\n学ばないと…\nふむふむ", 0, 1, 3, 1, 8, 2149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 0, "ふぁ～…あっ\n見られちゃい\nましたか？", 0, 1, 2, 1, 1, 2151);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 0, "ギルドハウスの\n使い心地は\nどうですか？", 0, 1, 2, 1, 2, 2152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 0, "ご用があれば\nなんでも言って\nくださいね～♪", 0, 1, 2, 1, 3, 2153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 0, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 0, 1, 2, 1, 4, 2154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 0, 1, 2, 1, 1, 2155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 2, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 0, 1, 2, 1, 2, 2156);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190801, 3, "ここは夜でも\nにぎやかですね", 0, 1, 2, 1, 3, 2157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "…\n少しうとうと\nしてしまった", 0, 1, 1, 1, 1, 2158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 0, 1, 1, 1, 2, 2159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 0, 1, 1, 1, 3, 2160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 0, 1, 1, 1, 4, 2161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 0, 1, 1, 1, 5, 2162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "少年のことを考えると\n顔がにやけてしまう", 0, 1, 1, 4, 101, 2163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "少年は今頃\n平和に過ごせて\nいるかな…", 0, 1, 1, 8, 102, 2164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "ん？　悪いが\n兜は脱がないぞ", 0, 1, 2, 1, 1, 2165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 0, 1, 2, 1, 2, 2166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 0, 1, 2, 1, 3, 2167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 0, 1, 2, 1, 4, 2168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "君といると、兜の中が\nいつもより暑いんだ", 0, 1, 2, 4, 101, 2169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 0, 1, 2, 8, 102, 2170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 0, 1, 2, 1, 1, 2171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 2, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 0, 1, 2, 1, 2, 2172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 3, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 0, 1, 2, 1, 3, 2173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 0, 1, 3, 1, 2, 2175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 0, 1, 3, 1, 3, 2176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 0, 1, 3, 1, 4, 2177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 0, 1, 3, 1, 5, 2178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 0, 1, 3, 1, 7, 2180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 0, 1, 3, 1, 8, 2181);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 0, 1, 1, 1, 1, 2183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "ドングリの木\n近くに生えてない？", 0, 1, 1, 1, 2, 2184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "快適な寝床があれば\nそれだけで幸せだねぇ", 0, 1, 1, 1, 3, 2185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 0, 1, 1, 1, 4, 2186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 0, 1, 1, 1, 5, 2187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "あんパン買ったし\nあいつにも\n分けてあげよう", 0, 1, 1, 4, 101, 2188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 0, 1, 1, 8, 102, 2189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 0, 1, 2, 1, 1, 2190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 0, 1, 2, 1, 2, 2191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "ドングリあげるから\nあんパン買ってきて～", 0, 1, 2, 1, 3, 2192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "う～\n疲れちゃったよ～\nおんぶして運んで～", 0, 1, 2, 1, 4, 2193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "あんたといると\nリア充って\n感じがするよ", 0, 1, 2, 4, 101, 2194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 0, 1, 2, 8, 102, 2195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 0, 1, 2, 1, 1, 2196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 2, "そろそろお昼寝でも\nしたい気分だねぇ", 0, 1, 2, 1, 2, 2197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 3, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 0, 1, 2, 1, 3, 2198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 0, 1, 3, 1, 2, 2200);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 0, 1, 3, 1, 3, 2201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 0, 1, 3, 1, 4, 2202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 0, 1, 3, 1, 5, 2203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 0, 1, 3, 1, 7, 2205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 0, 1, 3, 1, 8, 2206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "ランドソル…\nおっきな街だなぁ…", 0, 1, 1, 1, 1, 2208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "ロザリア…\nきっと、いるよね？", 0, 1, 1, 1, 2, 2209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "最近、弓の練習\nサボっちゃってるな…", 0, 1, 1, 1, 3, 2210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "今日の私は\n森番じゃなくて\n家番だよ！", 0, 1, 1, 1, 4, 2211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "ちょっとだけ\n慣れてきたかも？", 0, 1, 1, 1, 5, 2212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "いつか、\nあの人と一緒に…", 0, 1, 1, 4, 101, 2213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "…なんか最近、\nあの人のこと\nばっかり…", 0, 1, 1, 8, 102, 2214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "もしよかったら\n街に出かけない？", 0, 1, 2, 1, 1, 2215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "この部屋、\n妖精さんも\n気に入るかも！", 0, 1, 2, 1, 2, 2216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "今日は\nなににしよっか？", 0, 1, 2, 1, 3, 2217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "今日はどこ行く？", 0, 1, 2, 1, 4, 2218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "たまには二人で…\nな、なんちゃって！", 0, 1, 2, 4, 101, 2219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 0, 1, 2, 8, 102, 2220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 0, 1, 2, 1, 1, 2221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 2, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 0, 1, 2, 1, 2, 2222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 3, "ランドソルは\n夜でも賑やかだね！", 0, 1, 2, 1, 3, 2223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "わぁっ！\nすっごく\nふわふわだよ！", 0, 1, 3, 1, 2, 2225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 0, 1, 3, 1, 3, 2226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "くんくん\nなんか…いい匂い？", 0, 1, 3, 1, 4, 2227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "すっごいなー\nあったかいなー！", 0, 1, 3, 1, 5, 2228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 0, 1, 3, 1, 7, 2230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 0, 1, 3, 1, 8, 2231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 0, 1, 1, 1, 1, 2233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "カップケーキが\n食べたいな…", 0, 1, 1, 1, 2, 2234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "予習が終わりました！\nさて、次は復習を\nしないと…", 0, 1, 1, 1, 3, 2235);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 0, 1, 1, 1, 4, 2236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "男の人は、みんな\nへんたいだと\n聞きました…", 0, 1, 1, 1, 5, 2237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 0, 1, 1, 4, 101, 2238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "早く大人になりたいな\n…あの人と同じくらい", 0, 1, 1, 8, 102, 2239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "ぼ、防犯魔石を\n鳴らしますよ！？", 0, 1, 2, 1, 1, 2240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 0, 1, 2, 1, 2, 2241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "これ宿題です\nやっておいて\nくださいね", 0, 1, 2, 1, 3, 2242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "あ、遅刻ですよ\n待っていたんですから", 0, 1, 2, 1, 4, 2243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 0, 1, 2, 4, 101, 2244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "あの…何歳まで\n子供だと思いますか？", 0, 1, 2, 8, 102, 2245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 0, 1, 2, 1, 1, 2246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 2, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 0, 1, 2, 1, 2, 2247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 3, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 0, 1, 2, 1, 3, 2248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 0, 1, 3, 1, 2, 2250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "つ、つぶらな瞳には\n弱いんです", 0, 1, 3, 1, 3, 2251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "リラックスできますね\n集中力も\n上がりそうです", 0, 1, 3, 1, 4, 2252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 0, 1, 3, 1, 5, 2253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "ピーマンもこれくらい\nおいしければ\nいいのに…", 0, 1, 3, 1, 7, 2255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 0, 1, 3, 1, 8, 2256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "今日はみんなに\nなにを読んで\nあげようかしら", 0, 1, 1, 1, 1, 2258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "子どもたちと同じ\n目線で考えるのが\n大事よね", 0, 1, 1, 1, 2, 2259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 0, 1, 1, 1, 3, 2260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 0, 1, 1, 1, 4, 2261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "お遊戯会の配役、\n考え出すと\n大変だわ～", 0, 1, 1, 1, 5, 2262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "彼は優しいから、\nきっと良い\nお父さんになるわね", 0, 1, 1, 4, 101, 2263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 0, 1, 1, 8, 102, 2264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 0, 1, 2, 1, 1, 2265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 0, 1, 2, 1, 2, 2266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "よかったら一緒に\n絵本を作らない？", 0, 1, 2, 1, 3, 2267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "あなたはいつも元気ね\n先生も見習わないと♪", 0, 1, 2, 1, 4, 2268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 0, 1, 2, 4, 101, 2269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 0, 1, 2, 8, 102, 2270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 0, 1, 2, 1, 1, 2271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 2, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 0, 1, 2, 1, 2, 2272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 3, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 0, 1, 2, 1, 3, 2273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 0, 1, 3, 1, 2, 2275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 0, 1, 3, 1, 3, 2276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "いい香りね～\n保育園にも\n置こうかしら", 0, 1, 3, 1, 4, 2277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 0, 1, 3, 1, 5, 2278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 0, 1, 3, 1, 7, 2280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 0, 1, 3, 1, 8, 2281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "世界の前に、\nこの部屋を征服\nしてやろう♪", 0, 1, 1, 1, 1, 2283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "拠点とはやはり\n斯くあるべきじゃの", 0, 1, 1, 1, 2, 2284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 0, 1, 1, 1, 3, 2285);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 0, 1, 1, 1, 4, 2286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "うるさいのう～\nまったく、近頃の\n若いもんは…", 0, 1, 1, 1, 5, 2287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 0, 1, 1, 4, 101, 2288);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 0, 1, 1, 8, 102, 2289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 0, 1, 2, 1, 1, 2290);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 0, 1, 2, 1, 2, 2291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 0, 1, 2, 1, 3, 2292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 0, 1, 2, 1, 4, 2293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 0, 1, 2, 4, 101, 2294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 0, 1, 2, 8, 102, 2295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 0, 1, 2, 1, 1, 2296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 2, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 0, 1, 2, 1, 2, 2297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 3, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 0, 1, 2, 1, 3, 2298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "よい座り心地じゃ\nわらわの玉座としよう", 0, 1, 3, 1, 2, 2300);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 0, 1, 3, 1, 3, 2301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 0, 1, 3, 1, 4, 2302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "あたたかい…\n身も心も\n浄化されるようじゃ", 0, 1, 3, 1, 5, 2303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 0, 1, 3, 1, 7, 2305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 0, 1, 3, 1, 8, 2306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 0, 1, 1, 1, 1, 2308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 0, 1, 1, 1, 2, 2309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 0, 1, 1, 1, 3, 2310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 0, 1, 1, 1, 4, 2311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "ノゾミさんの\n衣装はああして\nチカさんのは…", 0, 1, 1, 1, 5, 2312);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 0, 1, 1, 4, 101, 2313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "まさか私が\nアイドルになるなんて\n思ってなかったです", 0, 1, 1, 8, 102, 2314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 0, 1, 2, 1, 1, 2315);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 0, 1, 2, 1, 2, 2316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 0, 1, 2, 1, 3, 2317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 0, 1, 2, 1, 4, 2318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "どうしました？\n新しい服が\n欲しくなりましたか？", 0, 1, 2, 4, 101, 2319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 0, 1, 2, 8, 102, 2320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 0, 1, 2, 1, 1, 2321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 2, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 0, 1, 2, 1, 2, 2322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 3, "夜の方が\n作業が捗るのは\n私だけですかね？", 0, 1, 2, 1, 3, 2323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "このふかふか具合\nいい素材を\n使ってますね", 0, 1, 3, 1, 2, 2325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 0, 1, 3, 1, 3, 2326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 0, 1, 3, 1, 4, 2327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 0, 1, 3, 1, 5, 2328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "何をするにも\n食事は大切！\n体力が資本です！", 0, 1, 3, 1, 7, 2330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "私もいつか\nデザインを\nまとめて本に…", 0, 1, 3, 1, 8, 2331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "またお魚を\n捕りにいこうかな♪", 0, 1, 1, 1, 1, 2333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 0, 1, 1, 1, 2, 2334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "ここなら、\n日焼けの心配は\nありませんね！", 0, 1, 1, 1, 3, 2335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 0, 1, 1, 1, 4, 2336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "泳ぐのも食べるのも\n大得意です！", 0, 1, 1, 1, 5, 2337);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 0, 1, 1, 4, 101, 2338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 0, 1, 1, 8, 102, 2339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 0, 1, 2, 1, 1, 2340);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 0, 1, 2, 1, 2, 2341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 0, 1, 2, 1, 3, 2342);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "あなたの水着、\n私が選んであげます！", 0, 1, 2, 1, 4, 2343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 0, 1, 2, 4, 101, 2344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 0, 1, 2, 8, 102, 2345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 0, 1, 2, 1, 1, 2346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 2, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 0, 1, 2, 1, 2, 2347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 3, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 0, 1, 2, 1, 3, 2348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "泳ぎ疲れたので\nちょっと休憩です～", 0, 1, 3, 1, 2, 2350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "あなたにも、\n可愛い水着を\n着せてあげたいです", 0, 1, 3, 1, 3, 2351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "なんだか、\nリゾート気分です…", 0, 1, 3, 1, 4, 2352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 0, 1, 3, 1, 5, 2353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 0, 1, 3, 1, 7, 2355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "南国のフルーツ図鑑\n…じゅるり…", 0, 1, 3, 1, 8, 2356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "わ～♪　おいしそ…\nか、かわいいですね～", 0, 1, 3, 1, 10, 2358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 0, 1, 3, 1, 11, 2359);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 0, 1, 1, 1, 1, 2360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 0, 1, 1, 1, 2, 2361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 0, 1, 1, 1, 3, 2362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 0, 1, 1, 1, 4, 2363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "クロールの練習なら、\n部屋でもできそうです", 0, 1, 1, 1, 5, 2364);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 0, 1, 1, 4, 101, 2365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 0, 1, 1, 8, 102, 2366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 0, 1, 2, 1, 1, 2367);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 0, 1, 2, 1, 2, 2368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "海の美しさは、\n森とはちがう\n魅力がありました", 0, 1, 2, 1, 3, 2369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "この髪飾りですか？\n造花ですから\n水の中も平気です", 0, 1, 2, 1, 4, 2370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 0, 1, 2, 4, 101, 2371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "はじめての海が、\n主さまと一緒で\n幸せです♪", 0, 1, 2, 8, 102, 2372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 0, 1, 2, 1, 1, 2373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 2, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 0, 1, 2, 1, 2, 2374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 3, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 0, 1, 2, 1, 3, 2375);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 0, 1, 3, 1, 2, 2377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 0, 1, 3, 1, 3, 2378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "この香り…\nきっと主さまも\n気に入ります", 0, 1, 3, 1, 4, 2379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 0, 1, 3, 1, 5, 2380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "泳いだ後の食事は\n一段と美味しく\n感じます", 0, 1, 3, 1, 7, 2382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 0, 1, 3, 1, 8, 2383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 0, 1, 3, 1, 10, 2385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "ふかふかですね\n主さまのようです♪", 0, 1, 3, 1, 11, 2386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 0, 1, 1, 1, 1, 2387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 0, 1, 1, 1, 2, 2388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "今日はドジを\nしませんように！", 0, 1, 1, 1, 3, 2389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "接客業を極めれば\nメイドとして\n成長できるかも？", 0, 1, 1, 1, 4, 2390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "水着って\n動きやすいんですけど\n人の目が気になります", 0, 1, 1, 1, 5, 2391);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 0, 1, 1, 4, 101, 2392);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "水着でご奉仕したら、\nあの人は喜ぶかな？", 0, 1, 1, 8, 102, 2393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 0, 1, 2, 1, 1, 2394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 0, 1, 2, 1, 2, 2395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 0, 1, 2, 1, 3, 2396);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 0, 1, 2, 1, 4, 2397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 0, 1, 2, 4, 101, 2398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 0, 1, 2, 8, 102, 2399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 0, 1, 2, 1, 1, 2400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 2, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 0, 1, 2, 1, 2, 2401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 3, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 0, 1, 2, 1, 3, 2402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 0, 1, 3, 1, 2, 2404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "きれいにすれば\nかわいさも倍増\nですよ～♪", 0, 1, 3, 1, 3, 2405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 0, 1, 3, 1, 4, 2406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 0, 1, 3, 1, 5, 2407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 0, 1, 3, 1, 7, 2409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 0, 1, 3, 1, 8, 2410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "えへへ♪\n南の島まで\n乗せてください！", 0, 1, 3, 1, 10, 2412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107701, 0, "なんだか、\n人魚姫に\nなった気分です♪", 0, 1, 3, 1, 11, 2413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 0, 1, 1, 1, 1, 2414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 0, 1, 1, 1, 2, 2415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "ビーチにいると\n日焼けが心配だわ", 0, 1, 1, 1, 3, 2416);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 0, 1, 1, 1, 4, 2417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "お、泳ぎの練習なんて\nしないからね！", 0, 1, 1, 1, 5, 2418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 0, 1, 1, 4, 101, 2419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 0, 1, 1, 8, 102, 2420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 0, 1, 2, 1, 1, 2421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 0, 1, 2, 1, 2, 2422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 0, 1, 2, 1, 3, 2423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 0, 1, 2, 1, 4, 2424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 0, 1, 2, 4, 101, 2425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 0, 1, 2, 8, 102, 2426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 0, 1, 2, 1, 1, 2427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 2, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 0, 1, 2, 1, 2, 2428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 3, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 0, 1, 2, 1, 3, 2429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 0, 1, 3, 1, 2, 2431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "こんな魔物がいたら\n操るのも楽しいかも", 0, 1, 3, 1, 3, 2432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "にゃう～♪\nまるで南国にいる\n気分ね♪", 0, 1, 3, 1, 4, 2433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 0, 1, 3, 1, 5, 2434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 0, 1, 3, 1, 7, 2436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 0, 1, 3, 1, 8, 2437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 0, 1, 3, 1, 10, 2439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "リゾートリゾートっ\nのんびりしましょ♪", 0, 1, 3, 1, 11, 2440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "ここなら思う存分\nデスクワークに\n励めるわね！", 0, 1, 1, 1, 1, 2441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 0, 1, 1, 1, 2, 2442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 0, 1, 1, 1, 3, 2443);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "少し休んだら\n次のバイトに\n行ってくるわね！", 0, 1, 1, 1, 4, 2444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "この格好でここに\nいるのって\n気恥ずかしいわね…", 0, 1, 1, 1, 5, 2445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 0, 1, 1, 4, 101, 2446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "キミだけね…\n私のことをここまで\n理解してくれるのは", 0, 1, 1, 8, 102, 2447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "普段と違う格好\nだからって\nそんなに見ないでよ…", 0, 1, 2, 1, 1, 2448);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 0, 1, 2, 1, 2, 2449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 0, 1, 2, 1, 3, 2450);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 0, 1, 2, 1, 4, 2451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 0, 1, 2, 4, 101, 2452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 0, 1, 2, 8, 102, 2453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 0, 1, 2, 1, 1, 2454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 2, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 0, 1, 2, 1, 2, 2455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 3, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 0, 1, 2, 1, 3, 2456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 0, 1, 3, 1, 2, 2458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 0, 1, 3, 1, 3, 2459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "いい香りね…\n働く意欲が\n湧いてくるわ", 0, 1, 3, 1, 4, 2460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 0, 1, 3, 1, 5, 2461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 0, 1, 3, 1, 7, 2463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "座学も大切ね\nもっと知識を\n身につけないと…", 0, 1, 3, 1, 8, 2464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 0, 1, 3, 1, 10, 2466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108001, 0, "落ち着くわ…\n海とひとつになった\n気分ね！", 0, 1, 3, 1, 11, 2467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 0, 1, 1, 1, 1, 2468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "猫グッズで\n海の家を飾りたいにゃ", 0, 1, 1, 1, 2, 2469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "海の幸は美味しいけど\n水はイヤだにゃ…", 0, 1, 1, 1, 3, 2470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 0, 1, 1, 1, 4, 2471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "海と言えば水着にゃ\n似合ってるかにゃ？", 0, 1, 1, 1, 5, 2472);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 0, 1, 1, 4, 101, 2473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "あたしの宝は\nここにあるにゃ～", 0, 1, 1, 8, 102, 2474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 0, 1, 2, 1, 1, 2475);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "今からキミを\nあたしの相棒に\n任命するにゃ！", 0, 1, 2, 1, 2, 2476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 0, 1, 2, 1, 3, 2477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 0, 1, 2, 1, 4, 2478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 0, 1, 2, 4, 101, 2479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 0, 1, 2, 8, 102, 2480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 0, 1, 2, 1, 1, 2481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 2, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 0, 1, 2, 1, 2, 2482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 3, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 0, 1, 2, 1, 3, 2483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 0, 1, 3, 1, 2, 2485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "にゃにゃ～\nなんてかわいさにゃ～", 0, 1, 3, 1, 3, 2486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 0, 1, 3, 1, 4, 2487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 0, 1, 3, 1, 5, 2488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 0, 1, 3, 1, 7, 2490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 0, 1, 3, 1, 8, 2491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 0, 1, 3, 1, 10, 2493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107901, 0, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 0, 1, 3, 1, 11, 2494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "人の家って、道場とは\n違った香りがするね", 0, 1, 1, 1, 1, 2495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 0, 1, 1, 1, 2, 2496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 0, 1, 1, 1, 3, 2497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 0, 1, 1, 1, 4, 2498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "もっと\n上手く立ち回って\n出世したいな", 0, 1, 1, 1, 5, 2499);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 0, 1, 1, 4, 101, 2500);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 0, 1, 1, 8, 102, 2501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 0, 1, 2, 1, 1, 2502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 0, 1, 2, 1, 2, 2503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 0, 1, 2, 1, 3, 2504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 0, 1, 2, 1, 4, 2505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 0, 1, 2, 4, 101, 2506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 0, 1, 2, 8, 102, 2507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 0, 1, 2, 1, 1, 2508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 2, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 0, 1, 2, 1, 2, 2509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 3, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 0, 1, 2, 1, 3, 2510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "いざという時のため\nにも、休憩は大事だね", 0, 1, 3, 1, 2, 2512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 0, 1, 3, 1, 3, 2513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 0, 1, 3, 1, 4, 2514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "あったかいなぁ。\nたまにはのんびり\nしようか", 0, 1, 3, 1, 5, 2515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 0, 1, 3, 1, 7, 2517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 0, 1, 3, 1, 8, 2518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "正義の刃で\n悪を挫く！\n…キマったッス♪", 0, 1, 1, 1, 1, 2520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 0, 1, 1, 1, 2, 2521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 0, 1, 1, 1, 3, 2522);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "一日一善！\nヒーローだったら\n当たり前ッス♪", 0, 1, 1, 1, 4, 2523);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "どうッスか\nこのポーズ！\nイイ感じッスか？", 0, 1, 1, 1, 5, 2524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 0, 1, 1, 4, 101, 2525);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 0, 1, 1, 8, 102, 2526);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 0, 1, 2, 1, 1, 2527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 0, 1, 2, 1, 2, 2528);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 0, 1, 2, 1, 3, 2529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 0, 1, 2, 1, 4, 2530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 0, 1, 2, 4, 101, 2531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 0, 1, 2, 8, 102, 2532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 0, 1, 2, 1, 1, 2533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 2, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 0, 1, 2, 1, 2, 2534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 3, "夜のパトロールで\n一緒に平和を\n守るッス！", 0, 1, 2, 1, 3, 2535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 0, 1, 3, 1, 2, 2537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 0, 1, 3, 1, 3, 2538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 0, 1, 3, 1, 4, 2539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 0, 1, 3, 1, 5, 2540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 0, 1, 3, 1, 7, 2542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 0, 1, 3, 1, 8, 2543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 0, 1, 1, 1, 1, 2545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 0, 1, 1, 1, 2, 2546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 0, 1, 1, 1, 3, 2547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 0, 1, 1, 1, 4, 2548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 0, 1, 1, 1, 5, 2549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 0, 1, 1, 4, 101, 2550);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 0, 1, 1, 8, 102, 2551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 0, 1, 2, 1, 1, 2552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 0, 1, 2, 1, 2, 2553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 0, 1, 2, 1, 3, 2554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 0, 1, 2, 1, 4, 2555);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 0, 1, 2, 4, 101, 2556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 0, 1, 2, 8, 102, 2557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 0, 1, 2, 1, 1, 2558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 2, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 0, 1, 2, 1, 2, 2559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 3, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 0, 1, 2, 1, 3, 2560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 0, 1, 3, 1, 2, 2562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "つん、つん。ふふっ♪\n癒やされます…", 0, 1, 3, 1, 3, 2563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 0, 1, 3, 1, 4, 2564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "あったかいね\nお父さん…\nえ？　分からない？", 0, 1, 3, 1, 5, 2565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "霊のみなさんは\n食べられないので…\n私がいただきます", 0, 1, 3, 1, 7, 2567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 0, 1, 3, 1, 8, 2568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108101, 0, "わぁ、お菓子が沢山…", 0, 1, 3, 1, 12, 2570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 0, 1, 1, 1, 1, 2572);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 0, 1, 1, 1, 2, 2573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 0, 1, 1, 1, 3, 2574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 0, 1, 1, 1, 4, 2575);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 0, 1, 1, 1, 5, 2576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 0, 1, 1, 4, 101, 2577);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 0, 1, 1, 8, 102, 2578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 0, 1, 2, 1, 1, 2579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 0, 1, 2, 1, 2, 2580);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 0, 1, 2, 1, 3, 2581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "プリンの可能性は\n無限大なの～", 0, 1, 2, 1, 4, 2582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 0, 1, 2, 4, 101, 2583);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 0, 1, 2, 8, 102, 2584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 0, 1, 2, 1, 1, 2585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 2, "ランチは豪華に\nプリンアラモードなの", 0, 1, 2, 1, 2, 2586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 3, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 0, 1, 2, 1, 3, 2587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 0, 1, 3, 1, 2, 2589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 0, 1, 3, 1, 3, 2590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "癒されるの～\nまるで天国なの～", 0, 1, 3, 1, 4, 2591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "ここでなら、\n焼きプリンが\n作れるの！", 0, 1, 3, 1, 5, 2592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "腹が減っては\nハロウィンは\nできぬなの！", 0, 1, 3, 1, 7, 2594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 0, 1, 3, 1, 8, 2595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108201, 0, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 0, 1, 3, 1, 12, 2597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 0, 1, 1, 1, 1, 2599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 0, 1, 1, 1, 2, 2600);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 0, 1, 1, 1, 3, 2601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "この格好で学院に\n行ったら、\nみんな驚くかしら", 0, 1, 1, 1, 4, 2602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 0, 1, 1, 1, 5, 2603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 0, 1, 1, 4, 101, 2604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "アイツを魅了する\n魔法とかないかしら？", 0, 1, 1, 8, 102, 2605);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 0, 1, 2, 1, 1, 2606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 0, 1, 2, 1, 2, 2607);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 0, 1, 2, 1, 3, 2608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 0, 1, 2, 1, 4, 2609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 0, 1, 2, 4, 101, 2610);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 0, 1, 2, 8, 102, 2611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 0, 1, 2, 1, 1, 2612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 2, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 0, 1, 2, 1, 2, 2613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 3, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 0, 1, 2, 1, 3, 2614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "あう～、お菓子の\n食べすぎで\n動けな～い", 0, 1, 3, 1, 2, 2616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 0, 1, 3, 1, 3, 2617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "大人な香りに\n包まれてる～♪", 0, 1, 3, 1, 4, 2618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 0, 1, 3, 1, 5, 2619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 0, 1, 3, 1, 7, 2621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 0, 1, 3, 1, 8, 2622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108301, 0, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 0, 1, 3, 1, 12, 2624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "次はどんな策で団長を\nからかってみようか…", 0, 1, 1, 1, 1, 2626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "む？\n血の臭いがするな\n祭りの時間か☆", 0, 1, 1, 1, 2, 2627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 0, 1, 1, 1, 3, 2628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 0, 1, 1, 1, 4, 2629);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 0, 1, 1, 1, 5, 2630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 0, 1, 1, 4, 101, 2631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 0, 1, 1, 8, 102, 2632);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 0, 1, 2, 1, 1, 2633);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 0, 1, 2, 1, 2, 2634);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 0, 1, 2, 1, 3, 2635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 0, 1, 2, 1, 4, 2636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 0, 1, 2, 4, 101, 2637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 0, 1, 2, 8, 102, 2638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 0, 1, 2, 1, 1, 2639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 2, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 0, 1, 2, 1, 2, 2640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 3, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 0, 1, 2, 1, 3, 2641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 0, 1, 3, 1, 2, 2643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 0, 1, 3, 1, 3, 2644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 0, 1, 3, 1, 4, 2645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 0, 1, 3, 1, 5, 2646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 0, 1, 3, 1, 7, 2648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 0, 1, 3, 1, 8, 2649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 0, 1, 1, 1, 1, 2651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 0, 1, 1, 1, 2, 2652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 0, 1, 1, 1, 3, 2653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 0, 1, 1, 1, 4, 2654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "ふあぁ～…\n昼寝でもするか", 0, 1, 1, 1, 5, 2655);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "ふふっ…\nあの子、見た目より\n根性あるんだね", 0, 1, 1, 4, 101, 2656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 0, 1, 1, 8, 102, 2657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 0, 1, 2, 1, 1, 2658);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 0, 1, 2, 1, 2, 2659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 0, 1, 2, 1, 3, 2660);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 0, 1, 2, 1, 4, 2661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 0, 1, 2, 4, 101, 2662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 0, 1, 2, 8, 102, 2663);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 0, 1, 2, 1, 1, 2664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 2, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 0, 1, 2, 1, 2, 2665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 3, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 0, 1, 2, 1, 3, 2666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 0, 1, 3, 1, 2, 2668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 0, 1, 3, 1, 3, 2669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 0, 1, 3, 1, 4, 2670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 0, 1, 3, 1, 5, 2671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 0, 1, 3, 1, 7, 2673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 0, 1, 3, 1, 8, 2674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 0, 1, 1, 1, 1, 2676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 0, 1, 1, 1, 2, 2677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 0, 1, 1, 1, 3, 2678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 0, 1, 1, 1, 4, 2679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 0, 1, 1, 1, 5, 2680);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "次はどの漫画を\n貸そうかなー♪", 0, 1, 1, 4, 101, 2681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 0, 1, 1, 8, 102, 2682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 0, 1, 2, 1, 1, 2683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 0, 1, 2, 1, 2, 2684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 0, 1, 2, 1, 3, 2685);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "新魔法思いついた！\n私って天才！？", 0, 1, 2, 1, 4, 2686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 0, 1, 2, 4, 101, 2687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 0, 1, 2, 8, 102, 2688);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 0, 1, 2, 1, 1, 2689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 2, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 0, 1, 2, 1, 2, 2690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 3, "夜からが、\nオタ活本番でしょ！", 0, 1, 2, 1, 3, 2691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "ふいー！\nくつろいで本を読む！\n最高だねー！", 0, 1, 3, 1, 2, 2693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 0, 1, 3, 1, 3, 2694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 0, 1, 3, 1, 4, 2695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 0, 1, 3, 1, 5, 2696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "ウマ―！\nこれの詳細きぼんぬ！", 0, 1, 3, 1, 7, 2698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "こ、古代語の本！？\n現代語でおk…", 0, 1, 3, 1, 8, 2699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 0, 1, 1, 1, 1, 2701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 0, 1, 1, 1, 2, 2702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 0, 1, 1, 1, 3, 2703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 0, 1, 1, 1, 4, 2704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 0, 1, 1, 1, 5, 2705);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 0, 1, 2, 1, 1, 2706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 0, 1, 2, 1, 2, 2707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 0, 1, 2, 1, 3, 2708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 0, 1, 2, 1, 4, 2709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 0, 1, 2, 1, 1, 2710);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 2, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 0, 1, 2, 1, 2, 2711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 3, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 0, 1, 2, 1, 3, 2712);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 0, 1, 3, 1, 2, 2714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 0, 1, 3, 1, 3, 2715);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 0, 1, 3, 1, 4, 2716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 0, 1, 3, 1, 5, 2717);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 0, 1, 3, 1, 7, 2719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (190901, 0, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 0, 1, 3, 1, 8, 2720);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "メリークリスマス！\n歌で幸せを\n届けます…！", 0, 1, 1, 1, 1, 2722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 0, 1, 1, 1, 2, 2723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 0, 1, 1, 1, 3, 2724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 0, 1, 1, 1, 4, 2725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 0, 1, 1, 1, 5, 2726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 0, 1, 1, 4, 101, 2727);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 0, 1, 1, 8, 102, 2728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 0, 1, 2, 1, 1, 2729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "歌を届ける\nサンタさんに\nなりたいな…", 0, 1, 2, 1, 2, 2730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 0, 1, 2, 1, 3, 2731);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 0, 1, 2, 1, 4, 2732);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 0, 1, 2, 4, 101, 2733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 0, 1, 2, 8, 102, 2734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 0, 1, 2, 1, 1, 2735);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 2, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 0, 1, 2, 1, 2, 2736);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 3, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 0, 1, 2, 1, 3, 2737);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 0, 1, 3, 1, 2, 2739);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 0, 1, 3, 1, 3, 2740);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 0, 1, 3, 1, 4, 2741);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "この光みたいに\n灯火になるような\n歌を歌いたい…", 0, 1, 3, 1, 5, 2742);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 0, 1, 3, 1, 7, 2744);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "物語には\n曲作りのヒントが\n詰まっているんです", 0, 1, 3, 1, 8, 2745);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 0, 1, 3, 1, 14, 2747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108401, 0, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 0, 1, 3, 1, 15, 2748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 0, 1, 1, 1, 1, 2749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 0, 1, 1, 1, 2, 2750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 0, 1, 1, 1, 3, 2751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 0, 1, 1, 1, 4, 2752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 0, 1, 1, 1, 5, 2753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 0, 1, 1, 4, 101, 2754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 0, 1, 1, 8, 102, 2755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "メリー\nクリスマスです、\nお兄ちゃん♪", 0, 1, 2, 1, 1, 2756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 0, 1, 2, 1, 2, 2757);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 0, 1, 2, 1, 3, 2758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 0, 1, 2, 1, 4, 2759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 0, 1, 2, 4, 101, 2760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 0, 1, 2, 8, 102, 2761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 0, 1, 2, 1, 1, 2762);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 2, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 0, 1, 2, 1, 2, 2763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 3, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 0, 1, 2, 1, 3, 2764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 0, 1, 3, 1, 2, 2766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "この子と一緒に\nおままごとしたいな♪", 0, 1, 3, 1, 3, 2767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 0, 1, 3, 1, 4, 2768);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 0, 1, 3, 1, 5, 2769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 0, 1, 3, 1, 7, 2771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 0, 1, 3, 1, 8, 2772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 0, 1, 3, 1, 14, 2774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108501, 0, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 0, 1, 3, 1, 15, 2775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 0, 1, 1, 1, 1, 2776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "毎日が\nクリスマスだったら\nいいのにな～♪", 0, 1, 1, 1, 2, 2777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 0, 1, 1, 1, 3, 2778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 0, 1, 1, 1, 4, 2779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 0, 1, 1, 1, 5, 2780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 0, 1, 1, 4, 101, 2781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 0, 1, 1, 8, 102, 2782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 0, 1, 2, 1, 1, 2783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 0, 1, 2, 1, 2, 2784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 0, 1, 2, 1, 3, 2785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 0, 1, 2, 1, 4, 2786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 0, 1, 2, 4, 101, 2787);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 0, 1, 2, 8, 102, 2788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 0, 1, 2, 1, 1, 2789);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 2, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 0, 1, 2, 1, 2, 2790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 3, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 0, 1, 2, 1, 3, 2791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 0, 1, 3, 1, 2, 2793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 0, 1, 3, 1, 3, 2794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 0, 1, 3, 1, 4, 2795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 0, 1, 3, 1, 5, 2796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 0, 1, 3, 1, 7, 2798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 0, 1, 3, 1, 8, 2799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 0, 1, 3, 1, 14, 2801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108601, 0, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 0, 1, 3, 1, 15, 2802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "着物も慣れると\n動きやすいものさ", 0, 1, 1, 1, 1, 2803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 0, 1, 1, 1, 2, 2804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 0, 1, 1, 1, 3, 2805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 0, 1, 1, 1, 4, 2806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 0, 1, 1, 1, 5, 2807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "これからもずっと\n仲間と、彼と\n一緒に…", 0, 1, 1, 4, 101, 2808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 0, 1, 1, 8, 102, 2809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 0, 1, 2, 1, 1, 2810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 0, 1, 2, 1, 2, 2811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 0, 1, 2, 1, 3, 2812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 0, 1, 2, 1, 4, 2813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "キミといると\n気持ちが段々と\n安らいでいくよ", 0, 1, 2, 4, 101, 2814);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 0, 1, 2, 8, 102, 2815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 0, 1, 2, 1, 1, 2816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 2, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 0, 1, 2, 1, 2, 2817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 3, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 0, 1, 2, 1, 3, 2818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "…疲れていたのかな\nもう少しで眠る\nところだった…", 0, 1, 3, 1, 2, 2820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 0, 1, 3, 1, 3, 2821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 0, 1, 3, 1, 4, 2822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 0, 1, 3, 1, 5, 2823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 0, 1, 3, 1, 7, 2825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "外を出歩くのも\nいいけど、中で\n読書もありだな", 0, 1, 3, 1, 8, 2826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 0, 1, 1, 1, 1, 2828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 0, 1, 1, 1, 2, 2829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "今年は\nどんな福袋を\n買おうかな…♪", 0, 1, 1, 1, 3, 2830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 0, 1, 1, 1, 4, 2831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 0, 1, 1, 1, 5, 2832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 0, 1, 1, 4, 101, 2833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 0, 1, 1, 8, 102, 2834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 0, 1, 2, 1, 1, 2835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 0, 1, 2, 1, 2, 2836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 0, 1, 2, 1, 3, 2837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 0, 1, 2, 1, 4, 2838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 0, 1, 2, 4, 101, 2839);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 0, 1, 2, 8, 102, 2840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 0, 1, 2, 1, 1, 2841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 2, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 0, 1, 2, 1, 2, 2842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 3, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 0, 1, 2, 1, 3, 2843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "ふぅ…\n帯も少し\nゆるめちゃおう…", 0, 1, 3, 1, 2, 2845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "お正月用に\n着物を縫って\nあげたいな", 0, 1, 3, 1, 3, 2846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "これでお肌が\n保湿出来たら\nいいのになぁ", 0, 1, 3, 1, 4, 2847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "ん～…\nぬくぬくしてて\n動きたくないよ～", 0, 1, 3, 1, 5, 2848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 0, 1, 3, 1, 7, 2850);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "年末年始は\n告白のチャンス…\nむ、無理無理！", 0, 1, 3, 1, 8, 2851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 0, 1, 1, 1, 1, 2853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 0, 1, 1, 1, 2, 2854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 0, 1, 1, 1, 3, 2855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "初詣楽しみー！\n出店はどこから\n行こうかな？", 0, 1, 1, 1, 4, 2856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 0, 1, 1, 1, 5, 2857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "うーん…\n騎士くんはお正月\n何してるんだろ…", 0, 1, 1, 4, 101, 2858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 0, 1, 1, 8, 102, 2859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 0, 1, 2, 1, 1, 2860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 0, 1, 2, 1, 2, 2861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 0, 1, 2, 1, 3, 2862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 0, 1, 2, 1, 4, 2863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 0, 1, 2, 4, 101, 2864);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 0, 1, 2, 8, 102, 2865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 0, 1, 2, 1, 1, 2866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 2, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 0, 1, 2, 1, 2, 2867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 3, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 0, 1, 2, 1, 3, 2868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 0, 1, 3, 1, 2, 2870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 0, 1, 3, 1, 3, 2871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "はぁー♪\nいい香りー…\n気分スッキリ♪", 0, 1, 3, 1, 4, 2872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 0, 1, 3, 1, 5, 2873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "ど、どうしよう…！\n手が、止まんないよ！", 0, 1, 3, 1, 7, 2875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 0, 1, 3, 1, 8, 2876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 0, 1, 1, 1, 1, 2878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "ここを私のチョコで\n埋め尽くしますわ", 0, 1, 1, 1, 2, 2879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 0, 1, 1, 1, 3, 2880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 0, 1, 1, 1, 4, 2881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 0, 1, 1, 1, 5, 2882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 0, 1, 1, 4, 101, 2883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 0, 1, 1, 8, 102, 2884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "あなた様。私に\n隠しているものなど\nありませんよね…？", 0, 1, 2, 1, 1, 2885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 0, 1, 2, 1, 2, 2886);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 0, 1, 2, 1, 3, 2887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "解毒薬はどこに\nいれたかしら…？", 0, 1, 2, 1, 4, 2888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 0, 1, 2, 4, 101, 2889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 0, 1, 2, 8, 102, 2890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 0, 1, 2, 1, 1, 2891);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 2, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 0, 1, 2, 1, 2, 2892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 3, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 0, 1, 2, 1, 3, 2893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 0, 1, 3, 1, 2, 2895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "この中、何か\n入りそうですわね\n…チョコレート、とか", 0, 1, 3, 1, 3, 2896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 0, 1, 3, 1, 4, 2897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 0, 1, 3, 1, 5, 2898);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 0, 1, 3, 1, 7, 2900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109001, 0, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 0, 1, 3, 1, 8, 2901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 0, 1, 1, 1, 1, 2903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 0, 1, 1, 1, 2, 2904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 0, 1, 1, 1, 3, 2905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 0, 1, 1, 1, 4, 2906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 0, 1, 1, 1, 5, 2907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "お姉ちゃんのチョコ\n食べさせてあげたい…", 0, 1, 1, 4, 101, 2908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 0, 1, 1, 8, 102, 2909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 0, 1, 2, 1, 1, 2910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 0, 1, 2, 1, 2, 2911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "ナデナデするのは\nお姉ちゃんの仕事！", 0, 1, 2, 1, 3, 2912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 0, 1, 2, 1, 4, 2913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 0, 1, 2, 4, 101, 2914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 0, 1, 2, 8, 102, 2915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 0, 1, 2, 1, 1, 2916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 2, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 0, 1, 2, 1, 2, 2917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 3, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 0, 1, 2, 1, 3, 2918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 0, 1, 3, 1, 2, 2920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "このかわいさは\nお姉ちゃんも大満足♪", 0, 1, 3, 1, 3, 2921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "この香り、チョコに\n使えるかな…？", 0, 1, 3, 1, 4, 2922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "弟くんと一緒に\nぬくぬくしたいな…♪", 0, 1, 3, 1, 5, 2923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 0, 1, 3, 1, 7, 2925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109101, 0, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 0, 1, 3, 1, 8, 2926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "アタシって…\nここにいても\nいいのか…？", 0, 1, 1, 1, 1, 2928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 0, 1, 1, 1, 2, 2929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 0, 1, 1, 1, 3, 2930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 0, 1, 1, 1, 4, 2931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 0, 1, 1, 1, 5, 2932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "みんなが早く\nアタシのこと\n思い出さないかな", 0, 1, 1, 4, 101, 2933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 0, 1, 1, 8, 102, 2934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 0, 1, 2, 1, 1, 2935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "どうした？\n戦うのか！？\n…なんだ、違うのか", 0, 1, 2, 1, 2, 2936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "身長のことは\nあんまり言うな！\n気にしてるからな！", 0, 1, 2, 1, 3, 2937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 0, 1, 2, 1, 4, 2938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 0, 1, 2, 4, 101, 2939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 0, 1, 2, 8, 102, 2940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 0, 1, 2, 1, 1, 2941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 2, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 0, 1, 2, 1, 2, 2942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 3, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 0, 1, 2, 1, 3, 2943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "ふぃ～…\n極楽極楽…", 0, 1, 3, 1, 2, 2945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 0, 1, 3, 1, 3, 2946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "くんくん…\nたまにはこういうのも\n悪くないかもな", 0, 1, 3, 1, 4, 2947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "あー…\nもっと前にコイツと\n出会いたかった…", 0, 1, 3, 1, 5, 2948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 0, 1, 3, 1, 7, 2950);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 0, 1, 3, 1, 8, 2951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 0, 1, 1, 1, 1, 2953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "事件のオチは\n往々にして\n取るに足らないものさ", 0, 1, 1, 1, 2, 2954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "読書で得る知識が\n探偵の\n血肉になるんだよ", 0, 1, 1, 1, 3, 2955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "この前の\n捜査資料…\nどこにあったかな？", 0, 1, 1, 1, 4, 2956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 0, 1, 1, 1, 5, 2957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 0, 1, 1, 4, 101, 2958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 0, 1, 1, 8, 102, 2959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "珈琲、淹れたてだよ\n一杯どうだい？", 0, 1, 2, 1, 1, 2960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 0, 1, 2, 1, 2, 2961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 0, 1, 2, 1, 3, 2962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 0, 1, 2, 1, 4, 2963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 0, 1, 2, 4, 101, 2964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "助手くんは\n私にとって\n最高の相棒だよ☆", 0, 1, 2, 8, 102, 2965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 0, 1, 2, 1, 1, 2966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 2, "一休みしたら\n推理の答え合わせを\nしてみようか？", 0, 1, 2, 1, 2, 2967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 3, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 0, 1, 2, 1, 3, 2968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 0, 1, 3, 1, 2, 2970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "この愛らしさ…\n要捜査対象だね！", 0, 1, 3, 1, 3, 2971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "思考が活性化される\n素敵な香りだね…", 0, 1, 3, 1, 4, 2972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 0, 1, 3, 1, 5, 2973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 0, 1, 3, 1, 7, 2975);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "探偵だからね\nミステリには\n一家言あるんだ", 0, 1, 3, 1, 8, 2976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 0, 1, 1, 1, 1, 2978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "せ、先輩がいた部屋！\n私には刺激が強い…", 0, 1, 1, 1, 2, 2979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "先輩を観察しやすい\n場所はあるかな…", 0, 1, 1, 1, 3, 2980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 0, 1, 1, 1, 4, 2981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 0, 1, 1, 1, 5, 2982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 0, 1, 1, 4, 101, 2983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "せ、先輩になら\n観察されてもいいなぁ", 0, 1, 1, 8, 102, 2984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 0, 1, 2, 1, 1, 2985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 0, 1, 2, 1, 2, 2986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 0, 1, 2, 1, 3, 2987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "ま、また…\n先輩と一緒に\nバイトしたい…です", 0, 1, 2, 1, 4, 2988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 0, 1, 2, 4, 101, 2989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 0, 1, 2, 8, 102, 2990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 0, 1, 2, 1, 1, 2991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 2, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 0, 1, 2, 1, 2, 2992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 3, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 0, 1, 2, 1, 3, 2993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 0, 1, 3, 1, 2, 2995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 0, 1, 3, 1, 3, 2996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "ここから先輩の\n香りがすればなあ", 0, 1, 3, 1, 4, 2997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 0, 1, 3, 1, 5, 2998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "おいし～\nこれは\n先輩も好きな味かな", 0, 1, 3, 1, 7, 3000);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "す、好きな人に\n近づく方法…", 0, 1, 3, 1, 8, 3001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 0, 1, 1, 1, 1, 3003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 0, 1, 1, 1, 2, 3004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 0, 1, 1, 1, 3, 3005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "わー！\nあれもこれも\n気になっちゃうな！", 0, 1, 1, 1, 4, 3006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 0, 1, 1, 1, 5, 3007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 0, 1, 1, 4, 101, 3008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 0, 1, 1, 8, 102, 3009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "歩き方がきれい？\nえへへ、そうかな…？", 0, 1, 2, 1, 1, 3010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 0, 1, 2, 1, 2, 3011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 0, 1, 2, 1, 3, 3012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 0, 1, 2, 1, 4, 3013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 0, 1, 2, 4, 101, 3014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "お茶の時間に\nしたいな～…\n一緒にどう？", 0, 1, 2, 8, 102, 3015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 0, 1, 2, 1, 1, 3016);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 2, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 0, 1, 2, 1, 2, 3017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 3, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 0, 1, 2, 1, 3, 3018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 0, 1, 3, 1, 2, 3020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 0, 1, 3, 1, 3, 3021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "いい香り～！\nなんだか気持ちが\n落ち着くね", 0, 1, 3, 1, 4, 3022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 0, 1, 3, 1, 5, 3023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "こ、これ美味し～！\n止められないよー！", 0, 1, 3, 1, 7, 3025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109201, 0, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 0, 1, 3, 1, 8, 3026);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 0, 1, 1, 1, 1, 3028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 0, 1, 1, 1, 2, 3029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 0, 1, 1, 1, 3, 3030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 0, 1, 1, 1, 4, 3031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "なにか面白そうなもの\nないですかね～？", 0, 1, 1, 1, 5, 3032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "ここのみんなとも\nもうお友だちですぅ！", 0, 1, 1, 4, 101, 3033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "おなかが減ったです…", 0, 1, 1, 8, 102, 3034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 0, 1, 2, 1, 1, 3035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 0, 1, 2, 1, 2, 3036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "あなたもお勉強\n苦手ですか？", 0, 1, 2, 1, 3, 3037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 0, 1, 2, 1, 4, 3038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "お友だちいっぱいで、\nルゥはうれしいです♪", 0, 1, 2, 4, 101, 3039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 0, 1, 2, 8, 102, 3040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 0, 1, 2, 1, 1, 3041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 2, "午後もいっぱい\n遊ぶですよ～！", 0, 1, 2, 1, 2, 3042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 3, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 0, 1, 2, 1, 3, 3043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 0, 1, 3, 1, 2, 3045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 0, 1, 3, 1, 3, 3046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 0, 1, 3, 1, 4, 3047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 0, 1, 3, 1, 5, 3048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 0, 1, 3, 1, 7, 3050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109301, 0, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 0, 1, 3, 1, 8, 3051);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "ピアノ…\nどこかにないかな？", 0, 1, 1, 1, 1, 3053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "ふぅ…\n落ち着くな…", 0, 1, 1, 1, 2, 3054);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "マナリアはいつも\n賑やかなんだ…", 0, 1, 1, 1, 3, 3055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "みんな…\n今頃何してるかな？", 0, 1, 1, 1, 4, 3056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 0, 1, 1, 1, 5, 3057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "人と話すのは\n得意じゃないな…", 0, 1, 1, 4, 101, 3058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 0, 1, 1, 8, 102, 3059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "やっぱり、私の翼や\n尻尾…気になる？", 0, 1, 2, 1, 1, 3060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 0, 1, 2, 1, 2, 3061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 0, 1, 2, 1, 3, 3062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 0, 1, 2, 1, 4, 3063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 0, 1, 2, 4, 101, 3064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 0, 1, 2, 8, 102, 3065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 0, 1, 2, 1, 1, 3066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 2, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 0, 1, 2, 1, 2, 3067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 3, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 0, 1, 2, 1, 3, 3068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "羽を伸ばしたく\nなっちゃうね…ふふ", 0, 1, 3, 1, 2, 3070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 0, 1, 3, 1, 3, 3071);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "いい香り…\nこれ、\nアンも好きそう…", 0, 1, 3, 1, 4, 3072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 0, 1, 3, 1, 5, 3073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 0, 1, 3, 1, 7, 3075);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109401, 0, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 0, 1, 3, 1, 8, 3076);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 0, 1, 1, 1, 1, 3078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 0, 1, 1, 1, 2, 3079);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 0, 1, 1, 1, 3, 3080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 0, 1, 1, 1, 4, 3081);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 0, 1, 1, 1, 5, 3082);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 0, 1, 1, 4, 101, 3083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 0, 1, 1, 8, 102, 3084);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 0, 1, 2, 1, 1, 3085);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 0, 1, 2, 1, 2, 3086);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "こんな場所で\nなんというプレイを！\n…妄想でした", 0, 1, 2, 1, 3, 3087);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 0, 1, 2, 1, 4, 3088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 0, 1, 2, 4, 101, 3089);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 0, 1, 2, 8, 102, 3090);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 0, 1, 2, 1, 1, 3091);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 2, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 0, 1, 2, 1, 2, 3092);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 3, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 0, 1, 2, 1, 3, 3093);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 0, 1, 3, 1, 2, 3095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 0, 1, 3, 1, 3, 3096);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 0, 1, 3, 1, 4, 3097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 0, 1, 3, 1, 5, 3098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 0, 1, 3, 1, 7, 3100);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109501, 0, "クウカの探している\nジャンルはどこに\nありますかね？", 0, 1, 3, 1, 8, 3101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 0, 1, 1, 1, 1, 3103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 0, 1, 1, 1, 2, 3104);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 0, 1, 1, 1, 3, 3105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 0, 1, 1, 1, 4, 3106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 0, 1, 1, 1, 5, 3107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 0, 1, 1, 4, 101, 3108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 0, 1, 1, 8, 102, 3109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 0, 1, 2, 1, 1, 3110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 0, 1, 2, 1, 2, 3111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 0, 1, 2, 1, 3, 3112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 0, 1, 2, 1, 4, 3113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 0, 1, 2, 4, 101, 3114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 0, 1, 2, 8, 102, 3115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 0, 1, 2, 1, 1, 3116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 2, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 0, 1, 2, 1, 2, 3117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 3, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 0, 1, 2, 1, 3, 3118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "天下分け目の\n戦いに備えて…\nマッタリデース", 0, 1, 3, 1, 2, 3120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "口寄せの術で\n召喚したいくらい\nかわいいデース", 0, 1, 3, 1, 3, 3121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 0, 1, 3, 1, 4, 3122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 0, 1, 3, 1, 5, 3123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 0, 1, 3, 1, 7, 3125);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109601, 0, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 0, 1, 3, 1, 8, 3126);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 0, 1, 1, 1, 1, 3128);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "異世界の演劇…\n許されるならば、\n観てみたいです", 0, 1, 1, 1, 2, 3129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 0, 1, 1, 1, 3, 3130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 0, 1, 1, 1, 4, 3131);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 0, 1, 1, 1, 5, 3132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 0, 1, 1, 4, 101, 3133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 0, 1, 1, 8, 102, 3134);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "お出かけでしたら、\nレムも同行\nさせてくださいね", 0, 1, 2, 1, 1, 3135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "レムにできること\nでしたら、なんでも\nお申し付けください", 0, 1, 2, 1, 2, 3136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 0, 1, 2, 1, 3, 3137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 0, 1, 2, 1, 4, 3138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 0, 1, 2, 4, 101, 3139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 0, 1, 2, 8, 102, 3140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 0, 1, 2, 1, 1, 3141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 2, "昼食の時間ですね\n何か食べたいものは\nありますか？", 0, 1, 2, 1, 2, 3142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 3, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 0, 1, 2, 1, 3, 3143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 0, 1, 3, 1, 2, 3145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 0, 1, 3, 1, 3, 3146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "よい香りですね\n魔女教の悪臭とは\n大違いです", 0, 1, 3, 1, 4, 3147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 0, 1, 3, 1, 5, 3148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 0, 1, 3, 1, 7, 3150);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 0, 1, 3, 1, 8, 3151);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 0, 1, 1, 1, 1, 3153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 0, 1, 1, 1, 2, 3154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 0, 1, 1, 1, 3, 3155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 0, 1, 1, 1, 4, 3156);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 0, 1, 1, 1, 5, 3157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 0, 1, 1, 4, 101, 3158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 0, 1, 1, 8, 102, 3159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 0, 1, 2, 1, 1, 3160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 0, 1, 2, 1, 2, 3161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 0, 1, 2, 1, 3, 3162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 0, 1, 2, 1, 4, 3163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 0, 1, 2, 4, 101, 3164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 0, 1, 2, 8, 102, 3165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 0, 1, 2, 1, 1, 3166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 2, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 0, 1, 2, 1, 2, 3167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 3, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 0, 1, 2, 1, 3, 3168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "…ここから動かない\nという、誓いを\n立てたくなるわね", 0, 1, 3, 1, 2, 3170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 0, 1, 3, 1, 3, 3171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 0, 1, 3, 1, 4, 3172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 0, 1, 3, 1, 5, 3173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 0, 1, 3, 1, 7, 3175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 0, 1, 3, 1, 8, 3176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 0, 1, 1, 1, 1, 3178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 0, 1, 1, 1, 2, 3179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 0, 1, 1, 1, 3, 3180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 0, 1, 1, 1, 4, 3181);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "今日のご飯…\nピーマルは\n入ってないわよね…？", 0, 1, 1, 1, 5, 3182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "また、みんなで\n一緒に買い物に\n行きたいな", 0, 1, 1, 4, 101, 3183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 0, 1, 1, 8, 102, 3184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "今からパックの\n毛繕いをするけど…\n一緒にする？", 0, 1, 2, 1, 1, 3185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 0, 1, 2, 1, 2, 3186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 0, 1, 2, 1, 3, 3187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 0, 1, 2, 1, 4, 3188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 0, 1, 2, 4, 101, 3189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 0, 1, 2, 8, 102, 3190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 0, 1, 2, 1, 1, 3191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 2, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 0, 1, 2, 1, 2, 3192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 3, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 0, 1, 2, 1, 3, 3193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 0, 1, 3, 1, 2, 3195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 0, 1, 3, 1, 3, 3196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 0, 1, 3, 1, 4, 3197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 0, 1, 3, 1, 5, 3198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 0, 1, 3, 1, 7, 3200);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 0, 1, 3, 1, 8, 3201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 0, 1, 1, 1, 1, 3203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 0, 1, 1, 1, 2, 3204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 0, 1, 1, 1, 3, 3205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 0, 1, 1, 1, 4, 3206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 0, 1, 1, 1, 5, 3207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 0, 1, 1, 4, 101, 3208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 0, 1, 1, 8, 102, 3209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 0, 1, 2, 1, 1, 3210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 0, 1, 2, 1, 2, 3211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 0, 1, 2, 1, 3, 3212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 0, 1, 2, 1, 4, 3213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 0, 1, 2, 4, 101, 3214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 0, 1, 2, 8, 102, 3215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 0, 1, 2, 1, 1, 3216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 2, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 0, 1, 2, 1, 2, 3217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 3, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 0, 1, 2, 1, 3, 3218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 0, 1, 3, 1, 2, 3220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 0, 1, 3, 1, 3, 3221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 0, 1, 3, 1, 4, 3222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "あったか～い♪\n夏の太陽みたい！", 0, 1, 3, 1, 5, 3223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 0, 1, 3, 1, 7, 3225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "夏のために\n最新ファッションを\n勉強ちう！", 0, 1, 3, 1, 8, 3226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 0, 1, 3, 1, 10, 3228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110001, 0, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 0, 1, 3, 1, 11, 3229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 0, 1, 1, 1, 1, 3230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 0, 1, 1, 1, 2, 3231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 0, 1, 1, 1, 3, 3232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 0, 1, 1, 1, 4, 3233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 0, 1, 1, 1, 5, 3234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "海が楽しいって\n思えたのも\nあの子のおかげね", 0, 1, 1, 4, 101, 3235);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 0, 1, 1, 8, 102, 3236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 0, 1, 2, 1, 1, 3237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 0, 1, 2, 1, 2, 3238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 0, 1, 2, 1, 3, 3239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 0, 1, 2, 1, 4, 3240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 0, 1, 2, 4, 101, 3241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 0, 1, 2, 8, 102, 3242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 0, 1, 2, 1, 1, 3243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 2, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 0, 1, 2, 1, 2, 3244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 3, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 0, 1, 2, 1, 3, 3245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 0, 1, 3, 1, 2, 3247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 0, 1, 3, 1, 3, 3248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "この匂い…\nなんだか夏の海を\n思い出すわ～", 0, 1, 3, 1, 4, 3249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 0, 1, 3, 1, 5, 3250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 0, 1, 3, 1, 7, 3252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "夏休み中でも\nしっかりお勉強\nしないと！", 0, 1, 3, 1, 8, 3253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 0, 1, 3, 1, 10, 3255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110101, 0, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 0, 1, 3, 1, 11, 3256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 0, 1, 1, 1, 1, 3257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 0, 1, 1, 1, 2, 3258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "子どもたちのために\n浮き輪を用意して\nおかないと…", 0, 1, 1, 1, 3, 3259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 0, 1, 1, 1, 4, 3260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 0, 1, 1, 1, 5, 3261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 0, 1, 1, 4, 101, 3262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 0, 1, 1, 8, 102, 3263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 0, 1, 2, 1, 1, 3264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 0, 1, 2, 1, 2, 3265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 0, 1, 2, 1, 3, 3266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 0, 1, 2, 1, 4, 3267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 0, 1, 2, 4, 101, 3268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 0, 1, 2, 8, 102, 3269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 0, 1, 2, 1, 1, 3270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 2, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 0, 1, 2, 1, 2, 3271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 3, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 0, 1, 2, 1, 3, 3272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 0, 1, 3, 1, 2, 3274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 0, 1, 3, 1, 3, 3275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "夏らしい\nいい香りね…\nなんの香りかしら？", 0, 1, 3, 1, 4, 3276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 0, 1, 3, 1, 5, 3277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 0, 1, 3, 1, 7, 3279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 0, 1, 3, 1, 8, 3280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 0, 1, 3, 1, 10, 3282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110301, 0, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 0, 1, 3, 1, 11, 3283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 0, 1, 1, 1, 1, 3284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "いてて…\nちょっと日焼け\nしすぎたかな", 0, 1, 1, 1, 2, 3285);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 0, 1, 1, 1, 3, 3286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "夏ならではの\n料理…か\n何がいいかな…", 0, 1, 1, 1, 4, 3287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "やっぱりここは\n涼しくて快適だな", 0, 1, 1, 1, 5, 3288);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 0, 1, 1, 4, 101, 3289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "水着で部屋の中に\nいるのって\n変な感じだな…", 0, 1, 1, 8, 102, 3290);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 0, 1, 2, 1, 1, 3291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 0, 1, 2, 1, 2, 3292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "夏バテしてないか？\nここで休んでけよ", 0, 1, 2, 1, 3, 3293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "…そう見られると\n照れるっていうか…", 0, 1, 2, 1, 4, 3294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 0, 1, 2, 4, 101, 3295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "おまえと\n夏の思い出ができて\nよかったよ", 0, 1, 2, 8, 102, 3296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 0, 1, 2, 1, 1, 3297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 2, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 0, 1, 2, 1, 2, 3298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 3, "今日もお疲れ\n明日のパトロールも\nよろしくな", 0, 1, 2, 1, 3, 3299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "泳ぎ疲れたからな\n少し休憩だ…", 0, 1, 3, 1, 2, 3301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "か、かわいい…\nいや、なんでもねぇ！", 0, 1, 3, 1, 3, 3302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 0, 1, 3, 1, 4, 3303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 0, 1, 3, 1, 5, 3304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "海で食べると\nまた一味違うよな", 0, 1, 3, 1, 7, 3306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "夏のサバイバル特集…\nちょっと読んでみるか", 0, 1, 3, 1, 8, 3307);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 0, 1, 3, 1, 10, 3309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110401, 0, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 0, 1, 3, 1, 11, 3310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 0, 1, 1, 1, 1, 3311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 0, 1, 1, 1, 2, 3312);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 0, 1, 1, 1, 3, 3313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 0, 1, 1, 1, 4, 3314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "クロールも\n犬かきも\n大得意さ～！", 0, 1, 1, 1, 5, 3315);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "なんだか歌いたい\n気分さ～\nキミもどう～？", 0, 1, 1, 4, 101, 3316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 0, 1, 1, 8, 102, 3317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 0, 1, 2, 1, 1, 3318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 0, 1, 2, 1, 2, 3319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 0, 1, 2, 1, 3, 3320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 0, 1, 2, 1, 4, 3321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 0, 1, 2, 4, 101, 3322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 0, 1, 2, 8, 102, 3323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 0, 1, 2, 1, 1, 3324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 2, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 0, 1, 2, 1, 2, 3325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 3, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 0, 1, 2, 1, 3, 3326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 0, 1, 3, 1, 2, 3328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 0, 1, 3, 1, 3, 3329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 0, 1, 3, 1, 4, 3330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 0, 1, 3, 1, 5, 3331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 0, 1, 3, 1, 7, 3333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 0, 1, 3, 1, 8, 3334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 0, 1, 3, 1, 10, 3336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110501, 0, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 0, 1, 3, 1, 11, 3337);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "あの人が\nわたしの騎士だったら\nいいのにな…", 0, 1, 1, 12, 103, 3338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105801, 0, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 0, 1, 2, 12, 103, 3339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 0, 1, 1, 12, 103, 3340);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105901, 0, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 0, 1, 2, 12, 103, 3341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106001, 0, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 0, 1, 1, 12, 103, 3342);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106001, 0, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 0, 1, 2, 12, 103, 3343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 0, 1, 1, 1, 1, 3344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 0, 1, 1, 1, 2, 3345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 0, 1, 1, 1, 3, 3346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 0, 1, 1, 1, 4, 3347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 0, 1, 1, 1, 5, 3348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 0, 1, 1, 4, 101, 3349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 0, 1, 1, 8, 102, 3350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 0, 1, 2, 1, 1, 3351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 0, 1, 2, 1, 2, 3352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "日が落ちたら\n線香花火\nやりたいわぁ♪", 0, 1, 2, 1, 3, 3353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "木陰でのんびり\nお昼寝…\n最高やね～", 0, 1, 2, 1, 4, 3354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 0, 1, 2, 4, 101, 3355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 0, 1, 2, 8, 102, 3356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 0, 1, 2, 1, 1, 3357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 2, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 0, 1, 2, 1, 2, 3358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 3, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 0, 1, 2, 1, 3, 3359);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 0, 1, 3, 1, 2, 3361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 0, 1, 3, 1, 3, 3362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 0, 1, 3, 1, 4, 3363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "これで\n冷えた身体も\nポカポカやねぇ", 0, 1, 3, 1, 5, 3364);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 0, 1, 3, 1, 7, 3366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 0, 1, 3, 1, 8, 3367);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 0, 1, 3, 1, 10, 3369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110601, 0, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 0, 1, 3, 1, 11, 3370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "真似したくなる\n人がここには\n大勢いますね", 0, 1, 1, 1, 1, 3371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "偽物を一人\n紛れこませました\n…冗談です", 0, 1, 1, 1, 2, 3372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "動物に変身して\nその辺りを\n散歩してきます", 0, 1, 1, 1, 3, 3373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "失った記憶は\nどの程度回復するの\nでしょうね", 0, 1, 1, 1, 4, 3374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 0, 1, 1, 1, 5, 3375);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "次は彼に\nどんなことを\n教えましょうか…", 0, 1, 1, 4, 101, 3376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "知識はともかく\n私の力はまだ\n必要なようですね", 0, 1, 1, 8, 102, 3377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "どうしました？\n…何でもない？\n…そうですか", 0, 1, 2, 1, 1, 3378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "退屈しています\n芸でもして\n楽しませなさい", 0, 1, 2, 1, 2, 3379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "研究のためです\n少し話し相手に\nなりなさい", 0, 1, 2, 1, 3, 3380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 0, 1, 2, 1, 4, 3381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "貴方にはまだ色々と\n教えるべきことが\nありますね", 0, 1, 2, 4, 101, 3382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "随分多くの女性と\n知り合いなのですね\n驚きました", 0, 1, 2, 8, 102, 3383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 1, "…\nおはようございます\n…まだ朝ですよ", 0, 1, 2, 1, 1, 3384);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 2, "食事にします\n…どうしました？\n早く持ってきなさい", 0, 1, 2, 1, 2, 3385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 3, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 0, 1, 2, 1, 3, 3386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "マサキより\n座り心地は\nいいですね", 0, 1, 3, 1, 2, 3388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "意外に複雑な\n構造ですね", 0, 1, 3, 1, 3, 3389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "真似るなら\n香りまで\n再現しなくては", 0, 1, 3, 1, 4, 3390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "発火現象の\n模倣…なかなか\n難しいものです", 0, 1, 3, 1, 5, 3391);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 0, 1, 3, 1, 7, 3393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107001, 0, "暇つぶしには\nちょうどよさそうです\n…", 0, 1, 3, 1, 8, 3394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "学院かぁ…\n友達の作り方の\n授業はないかな？", 0, 1, 1, 1, 1, 3396);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 0, 1, 1, 1, 2, 3397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 0, 1, 1, 1, 3, 3398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 0, 1, 1, 1, 4, 3399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 0, 1, 1, 1, 5, 3400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 0, 1, 1, 4, 101, 3401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 0, 1, 1, 8, 102, 3402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 0, 1, 2, 1, 1, 3403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 0, 1, 2, 1, 2, 3404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 0, 1, 2, 1, 3, 3405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 0, 1, 2, 1, 4, 3406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 0, 1, 2, 4, 101, 3407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 0, 1, 2, 8, 102, 3408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 0, 1, 2, 1, 1, 3409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 2, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 0, 1, 2, 1, 2, 3410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 3, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 0, 1, 2, 1, 3, 3411);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 0, 1, 3, 1, 2, 3413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 0, 1, 3, 1, 3, 3414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 0, 1, 3, 1, 4, 3415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 0, 1, 3, 1, 5, 3416);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 0, 1, 3, 1, 7, 3418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110701, 0, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 0, 1, 3, 1, 8, 3419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "あんた、何してんの。\nいいけど、なんでも", 0, 1, 1, 1, 1, 3421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 0, 1, 1, 1, 2, 3422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 0, 1, 1, 1, 3, 3423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 0, 1, 1, 1, 4, 3424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 0, 1, 1, 1, 5, 3425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 0, 1, 1, 4, 101, 3426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 0, 1, 1, 8, 102, 3427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 0, 1, 2, 1, 1, 3428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 0, 1, 2, 1, 2, 3429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 0, 1, 2, 1, 3, 3430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 0, 1, 2, 1, 4, 3431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0, 1, 2, 4, 101, 3432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 0, 1, 2, 8, 102, 3433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 0, 1, 2, 1, 1, 3434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 2, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 0, 1, 2, 1, 2, 3435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 3, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 0, 1, 2, 1, 3, 3436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 0, 1, 3, 1, 2, 3438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "なんだこれ\nかわいいかよ", 0, 1, 3, 1, 3, 3439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "ふーん。\nこの匂い…\nなんかいいじゃん", 0, 1, 3, 1, 4, 3440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 0, 1, 3, 1, 5, 3441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 0, 1, 3, 1, 7, 3443);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110801, 0, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 0, 1, 3, 1, 8, 3444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "イタズラってなにを\nしたらいいのかな？", 0, 1, 1, 1, 1, 3446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "カップケーキ\n上手に作れました\n食べてくれるかな…", 0, 1, 1, 1, 2, 3447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 0, 1, 1, 1, 3, 3448);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "オバケ…いないよね？\n怖くなんてないけど！", 0, 1, 1, 1, 4, 3449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 0, 1, 1, 1, 5, 3450);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 0, 1, 1, 4, 101, 3451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 0, 1, 1, 8, 102, 3452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 0, 1, 2, 1, 1, 3453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "触りましたね？\nイタズラしましたね？\nへんた～い！", 0, 1, 2, 1, 2, 3454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 0, 1, 2, 1, 3, 3455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 0, 1, 2, 1, 4, 3456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 0, 1, 2, 4, 101, 3457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 0, 1, 2, 8, 102, 3458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 1, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 0, 1, 2, 1, 1, 3459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 2, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 0, 1, 2, 1, 2, 3460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 3, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 0, 1, 2, 1, 3, 3461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 0, 1, 3, 1, 2, 3463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 0, 1, 3, 1, 3, 3464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "甘い匂いがいっぱい…\nお菓子の香りかな？", 0, 1, 3, 1, 4, 3465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 0, 1, 3, 1, 5, 3466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "ニンジンもこれくらい\nおいしければ\nいいのに…", 0, 1, 3, 1, 7, 3468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 0, 1, 3, 1, 8, 3469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111101, 0, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 0, 1, 3, 1, 12, 3471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "たくさんいたずら\nできるから\nハロウィン大好き！", 0, 1, 1, 1, 1, 3473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 0, 1, 1, 1, 2, 3474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 0, 1, 1, 1, 3, 3475);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 0, 1, 1, 1, 4, 3476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "どうすればみんなを\nびっくりさせられる\nかな～？", 0, 1, 1, 1, 5, 3477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 0, 1, 1, 4, 101, 3478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 0, 1, 1, 8, 102, 3479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 0, 1, 2, 1, 1, 3480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 0, 1, 2, 1, 2, 3481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 0, 1, 2, 1, 3, 3482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 0, 1, 2, 1, 4, 3483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 0, 1, 2, 4, 101, 3484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 0, 1, 2, 8, 102, 3485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 1, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 0, 1, 2, 1, 1, 3486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 2, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 0, 1, 2, 1, 2, 3487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 3, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 0, 1, 2, 1, 3, 3488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 0, 1, 3, 1, 2, 3490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 0, 1, 3, 1, 3, 3491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 0, 1, 3, 1, 4, 3492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 0, 1, 3, 1, 5, 3493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 0, 1, 3, 1, 7, 3495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "本のなかに\nいたずらのヒント\nないかな～？", 0, 1, 3, 1, 8, 3496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111201, 0, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 0, 1, 3, 1, 12, 3498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 0, 1, 1, 12, 103, 3500);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101101, 0, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 0, 1, 2, 12, 103, 3501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 0, 1, 1, 1, 1, 3502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 0, 1, 1, 1, 2, 3503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 0, 1, 1, 1, 3, 3504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 0, 1, 1, 1, 4, 3505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 0, 1, 1, 1, 5, 3506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 0, 1, 1, 4, 101, 3507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 0, 1, 1, 8, 102, 3508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 0, 1, 2, 1, 1, 3509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 0, 1, 2, 1, 2, 3510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 0, 1, 2, 1, 3, 3511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 0, 1, 2, 1, 4, 3512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 0, 1, 2, 4, 101, 3513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 0, 1, 2, 8, 102, 3514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 1, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 0, 1, 2, 1, 1, 3515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 2, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 0, 1, 2, 1, 2, 3516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 3, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 0, 1, 2, 1, 3, 3517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 0, 1, 3, 1, 2, 3519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 0, 1, 3, 1, 3, 3520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 0, 1, 3, 1, 4, 3521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 0, 1, 3, 1, 5, 3522);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "おいしい～♪\nおにいちゃんも\n食べようよー！", 0, 1, 3, 1, 7, 3524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 0, 1, 3, 1, 8, 3525);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111301, 0, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 0, 1, 3, 1, 12, 3527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ここ、好きだよ。\nみんな優しいから…", 0, 1, 1, 1, 1, 3529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "なんかさみしいな", 0, 1, 1, 1, 2, 3530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "知らない人怖いな", 0, 1, 1, 1, 3, 3531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "お部屋おっきいねー", 0, 1, 1, 1, 4, 3532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ルナね…\nお昼寝したいかも", 0, 1, 1, 1, 5, 3533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "おにいさん！\n今日は何して遊ぶの？", 0, 1, 1, 4, 101, 3534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "あのね、\nお料理教えてほしい！", 0, 1, 1, 8, 102, 3535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "あっ、おにいさんだ！", 0, 1, 2, 1, 1, 3536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ルナは元気だよ！\nいいこだもん！", 0, 1, 2, 1, 2, 3537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "今日は何しようかな", 0, 1, 2, 1, 3, 3538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ルナと遊んでよ！", 0, 1, 2, 1, 4, 3539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ねーねー、\nルナとお出かけして！", 0, 1, 2, 4, 101, 3540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "おにいさんと、\n遊びたいなー！", 0, 1, 2, 8, 102, 3541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 1, "ふふっ！\nなんかまぶしいね！", 0, 1, 2, 1, 1, 3542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 2, "ルナ、お腹空いたな", 0, 1, 2, 1, 2, 3543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 3, "どこに行こうかな？", 0, 1, 2, 1, 3, 3544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ルナ、これ好きだな", 0, 1, 3, 1, 2, 3546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "かわいい！　好き！", 0, 1, 3, 1, 3, 3547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "なんかいい匂い！", 0, 1, 3, 1, 4, 3548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "あったかいねー", 0, 1, 3, 1, 5, 3549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ふふっ\nおいしいね", 0, 1, 3, 1, 7, 3551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111401, 0, "ルナ、\n眠くなっちゃう…", 0, 1, 3, 1, 8, 3552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 0, 1, 1, 12, 103, 3554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105201, 0, "あなたとなら\nどんなことだって\nできる気がするわ♪", 0, 1, 2, 12, 103, 3555);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 0, 1, 1, 1, 1, 3556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 0, 1, 1, 1, 2, 3557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 0, 1, 1, 1, 3, 3558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 0, 1, 1, 1, 4, 3559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 0, 1, 1, 1, 5, 3560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 0, 1, 1, 4, 101, 3561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 0, 1, 1, 8, 102, 3562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "よっす！\nなんかオイシイ話でも\nあったりしない？", 0, 1, 2, 1, 1, 3563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 0, 1, 2, 1, 2, 3564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 0, 1, 2, 1, 3, 3565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 0, 1, 2, 1, 4, 3566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 0, 1, 2, 4, 101, 3567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 0, 1, 2, 8, 102, 3568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 0, 1, 2, 1, 1, 3569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 2, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 0, 1, 2, 1, 2, 3570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 3, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 0, 1, 2, 1, 3, 3571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 0, 1, 3, 1, 2, 3573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 0, 1, 3, 1, 3, 3574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 0, 1, 3, 1, 4, 3575);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 0, 1, 3, 1, 5, 3576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "いつでも全力で\nケンカするために\n力を蓄えないとな", 0, 1, 3, 1, 7, 3578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 0, 1, 3, 1, 8, 3579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 0, 1, 1, 1, 1, 3581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 0, 1, 1, 1, 2, 3582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 0, 1, 1, 1, 3, 3583);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 0, 1, 1, 1, 4, 3584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 0, 1, 1, 1, 5, 3585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 0, 1, 1, 4, 101, 3586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 0, 1, 1, 8, 102, 3587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 0, 1, 2, 1, 1, 3588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 0, 1, 2, 1, 2, 3589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 0, 1, 2, 1, 3, 3590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 0, 1, 2, 1, 4, 3591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 0, 1, 2, 4, 101, 3592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 0, 1, 2, 8, 102, 3593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 0, 1, 2, 1, 1, 3594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 2, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 0, 1, 2, 1, 2, 3595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 3, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 0, 1, 2, 1, 3, 3596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 0, 1, 3, 1, 2, 3598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 0, 1, 3, 1, 3, 3599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 0, 1, 3, 1, 4, 3600);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "体が温まってくると\nひと暴れしたくなるな", 0, 1, 3, 1, 5, 3601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 0, 1, 3, 1, 7, 3603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 0, 1, 3, 1, 8, 3604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 0, 1, 3, 1, 14, 3606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111501, 0, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 0, 1, 3, 1, 15, 3607);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 0, 1, 1, 1, 1, 3608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 0, 1, 1, 1, 2, 3609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 0, 1, 1, 1, 3, 3610);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 0, 1, 1, 1, 4, 3611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 0, 1, 1, 1, 5, 3612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 0, 1, 1, 4, 101, 3613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 0, 1, 1, 8, 102, 3614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 0, 1, 2, 1, 1, 3615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 0, 1, 2, 1, 2, 3616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 0, 1, 2, 1, 3, 3617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 0, 1, 2, 1, 4, 3618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 0, 1, 2, 4, 101, 3619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 0, 1, 2, 8, 102, 3620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 0, 1, 2, 1, 1, 3621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 2, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 0, 1, 2, 1, 2, 3622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 3, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 0, 1, 2, 1, 3, 3623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 0, 1, 3, 1, 2, 3625);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 0, 1, 3, 1, 3, 3626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 0, 1, 3, 1, 4, 3627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 0, 1, 3, 1, 5, 3628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 0, 1, 3, 1, 7, 3630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 0, 1, 3, 1, 8, 3631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "面白ーい！\nこの演出、ライブでも\nできないかな？", 0, 1, 3, 1, 14, 3633);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111601, 0, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 0, 1, 3, 1, 15, 3634);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101801, 0, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 0, 1, 1, 12, 103, 3635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101801, 0, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 0, 1, 2, 12, 103, 3636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 0, 1, 1, 1, 1, 3637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 0, 1, 1, 1, 2, 3638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 0, 1, 1, 1, 3, 3639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 0, 1, 1, 1, 4, 3640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 0, 1, 1, 1, 5, 3641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 0, 1, 1, 4, 101, 3642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 0, 1, 1, 8, 102, 3643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 0, 1, 2, 1, 1, 3644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 0, 1, 2, 1, 2, 3645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 0, 1, 2, 1, 3, 3646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 0, 1, 2, 1, 4, 3647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 0, 1, 2, 4, 101, 3648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 0, 1, 2, 8, 102, 3649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 0, 1, 2, 1, 1, 3650);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 2, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 0, 1, 2, 1, 2, 3651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 3, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 0, 1, 2, 1, 3, 3652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 0, 1, 3, 1, 2, 3654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 0, 1, 3, 1, 3, 3655);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 0, 1, 3, 1, 4, 3656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 0, 1, 3, 1, 5, 3657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 0, 1, 3, 1, 7, 3659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 0, 1, 3, 1, 8, 3660);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 0, 1, 3, 1, 14, 3662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111701, 0, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 0, 1, 3, 1, 15, 3663);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 0, 1, 1, 1, 1, 3664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 0, 1, 1, 1, 2, 3665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "初日の出って\n特別な感じがして\nいいわよね…", 0, 1, 1, 1, 3, 3666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 0, 1, 1, 1, 4, 3667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 0, 1, 1, 1, 5, 3668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 0, 1, 1, 4, 101, 3669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "今年もあいつと\n居られること…\n結構うれしいわ", 0, 1, 1, 8, 102, 3670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 0, 1, 2, 1, 1, 3671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 0, 1, 2, 1, 2, 3672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 0, 1, 2, 1, 3, 3673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "門松飾るから\nちょっと手伝って\nくれる？", 0, 1, 2, 1, 4, 3674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 0, 1, 2, 4, 101, 3675);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 0, 1, 2, 8, 102, 3676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 0, 1, 2, 1, 1, 3677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 2, "おせちに飽きたら\n次は何を食べよう\nかしら…", 0, 1, 2, 1, 2, 3678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 3, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 0, 1, 2, 1, 3, 3679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 0, 1, 3, 1, 2, 3681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 0, 1, 3, 1, 3, 3682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "いい匂いね\n落ち着いて\n眠れそう…", 0, 1, 3, 1, 4, 3683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 0, 1, 3, 1, 5, 3684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 0, 1, 3, 1, 7, 3686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112001, 0, "魔物を使わない\nおせちを\n調べておかなきゃ…", 0, 1, 3, 1, 8, 3687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 0, 1, 1, 1, 1, 3689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 0, 1, 1, 1, 2, 3690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 0, 1, 1, 1, 3, 3691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 0, 1, 1, 1, 4, 3692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 0, 1, 1, 1, 5, 3693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 0, 1, 1, 4, 101, 3694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 0, 1, 1, 8, 102, 3695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 0, 1, 2, 1, 1, 3696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 0, 1, 2, 1, 2, 3697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 0, 1, 2, 1, 3, 3698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 0, 1, 2, 1, 4, 3699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 0, 1, 2, 4, 101, 3700);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 0, 1, 2, 8, 102, 3701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 0, 1, 2, 1, 1, 3702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 2, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 0, 1, 2, 1, 2, 3703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 3, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 0, 1, 2, 1, 3, 3704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 0, 1, 3, 1, 2, 3706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 0, 1, 3, 1, 3, 3707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 0, 1, 3, 1, 4, 3708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 0, 1, 3, 1, 5, 3709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 0, 1, 3, 1, 7, 3711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112101, 0, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 0, 1, 3, 1, 8, 3712);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 0, 1, 1, 12, 103, 3714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103401, 0, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 0, 1, 2, 12, 103, 3715);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 0, 1, 1, 1, 1, 3716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "年を越したあとに\n食べるおそばも\nおいしいです", 0, 1, 1, 1, 2, 3717);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "甘酒を飲むと\n心まで温まる\n気がいたします", 0, 1, 1, 1, 3, 3718);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 0, 1, 1, 1, 4, 3719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 0, 1, 1, 1, 5, 3720);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "主さまの温もりは\n心まで温めてくれる\nようです", 0, 1, 1, 4, 101, 3721);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 0, 1, 1, 8, 102, 3722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "主さま、この晴れ着は\n似合っている\nでしょうか？", 0, 1, 2, 1, 1, 3723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "お餅はゆっくりと\n召し上がって\nくださいまし", 0, 1, 2, 1, 2, 3724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 0, 1, 2, 1, 3, 3725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 0, 1, 2, 1, 4, 3726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 0, 1, 2, 4, 101, 3727);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 0, 1, 2, 8, 102, 3728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 0, 1, 2, 1, 1, 3729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 2, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 0, 1, 2, 1, 2, 3730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 3, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 0, 1, 2, 1, 3, 3731);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "ふかふか…\nこのまま\n寝てしまいそうです", 0, 1, 3, 1, 2, 3733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 0, 1, 3, 1, 3, 3734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 0, 1, 3, 1, 4, 3735);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "風邪をひきませんよう\n暖かくいたしましょう", 0, 1, 3, 1, 5, 3736);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "食べ過ぎは\n正月太りの\nもとだというのに…", 0, 1, 3, 1, 7, 3738);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111901, 0, "主さまのために\n参拝の作法を\n予習しておきましょう", 0, 1, 3, 1, 8, 3739);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 0, 1, 1, 1, 1, 3741);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 0, 1, 1, 1, 2, 3742);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 0, 1, 1, 1, 3, 3743);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 0, 1, 1, 1, 4, 3744);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 0, 1, 1, 1, 5, 3745);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 0, 1, 1, 4, 101, 3746);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 0, 1, 1, 8, 102, 3747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 0, 1, 2, 1, 1, 3748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 0, 1, 2, 1, 2, 3749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 0, 1, 2, 1, 3, 3750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 0, 1, 2, 1, 4, 3751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 0, 1, 2, 4, 101, 3752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 0, 1, 2, 8, 102, 3753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 0, 1, 2, 1, 1, 3754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 2, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 0, 1, 2, 1, 2, 3755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 3, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 0, 1, 2, 1, 3, 3756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 0, 1, 3, 1, 2, 3758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 0, 1, 3, 1, 3, 3759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 0, 1, 3, 1, 4, 3760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 0, 1, 3, 1, 5, 3761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 0, 1, 3, 1, 7, 3763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112201, 0, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 0, 1, 3, 1, 8, 3764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 0, 1, 1, 1, 1, 3766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 0, 1, 1, 1, 2, 3767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "ふふ…\n今日はなんだか\n調子がいいかも", 0, 1, 1, 1, 3, 3768);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 0, 1, 1, 1, 4, 3769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 0, 1, 1, 1, 5, 3770);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "あの人がいると\nなんだかホッとする\n不思議…", 0, 1, 1, 4, 101, 3771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 0, 1, 1, 8, 102, 3772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 0, 1, 2, 1, 1, 3773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "よかったら魔法の\n練習に付き合って\nくれませんか？", 0, 1, 2, 1, 2, 3774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "あの…\nこの格好どうですか？\n似合ってると…いいな", 0, 1, 2, 1, 3, 3775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 0, 1, 2, 1, 4, 3776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "私、あなたのそばに\nいられたら、それで…", 0, 1, 2, 4, 101, 3777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 0, 1, 2, 8, 102, 3778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 0, 1, 2, 1, 1, 3779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 2, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 0, 1, 2, 1, 2, 3780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 3, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 0, 1, 2, 1, 3, 3781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 0, 1, 3, 1, 2, 3783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 0, 1, 3, 1, 3, 3784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 0, 1, 3, 1, 4, 3785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 0, 1, 3, 1, 5, 3786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 0, 1, 3, 1, 7, 3788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112301, 0, "ふふ…\n魔法少女になっても\n読書はやめられないな", 0, 1, 3, 1, 8, 3789);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 0, 1, 1, 12, 103, 3791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101001, 0, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 0, 1, 2, 12, 103, 3792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 0, 1, 1, 1, 1, 3793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 0, 1, 1, 1, 2, 3794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 0, 1, 1, 1, 3, 3795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 0, 1, 1, 1, 4, 3796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 0, 1, 1, 1, 5, 3797);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 0, 1, 1, 4, 101, 3798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 0, 1, 1, 8, 102, 3799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 0, 1, 2, 1, 1, 3800);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 0, 1, 2, 1, 2, 3801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 0, 1, 2, 1, 3, 3802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 0, 1, 2, 1, 4, 3803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 0, 1, 2, 4, 101, 3804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 0, 1, 2, 8, 102, 3805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 0, 1, 2, 1, 1, 3806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 2, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 0, 1, 2, 1, 2, 3807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 3, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 0, 1, 2, 1, 3, 3808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 0, 1, 3, 1, 2, 3810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 0, 1, 3, 1, 3, 3811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 0, 1, 3, 1, 4, 3812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 0, 1, 3, 1, 5, 3813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 0, 1, 3, 1, 7, 3815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180401, 0, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 0, 1, 3, 1, 8, 3816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 0, 1, 1, 1, 1, 3818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 0, 1, 1, 1, 2, 3819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 0, 1, 1, 1, 3, 3820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 0, 1, 1, 1, 4, 3821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 0, 1, 1, 1, 5, 3822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 0, 1, 1, 4, 101, 3823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 0, 1, 1, 8, 102, 3824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 0, 1, 2, 1, 1, 3825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 0, 1, 2, 1, 2, 3826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 0, 1, 2, 1, 3, 3827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 0, 1, 2, 1, 4, 3828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 0, 1, 2, 4, 101, 3829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 0, 1, 2, 8, 102, 3830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 0, 1, 2, 1, 1, 3831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 2, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 0, 1, 2, 1, 2, 3832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 3, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 0, 1, 2, 1, 3, 3833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 0, 1, 3, 1, 2, 3835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 0, 1, 3, 1, 3, 3836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "リラックスできる\nいい香りですね～♪", 0, 1, 3, 1, 4, 3837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 0, 1, 3, 1, 5, 3838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 0, 1, 3, 1, 7, 3840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112401, 0, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 0, 1, 3, 1, 8, 3841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 0, 1, 1, 1, 1, 3843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 0, 1, 1, 1, 2, 3844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 0, 1, 1, 1, 3, 3845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 0, 1, 1, 1, 4, 3846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 0, 1, 1, 1, 5, 3847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 0, 1, 1, 4, 101, 3848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 0, 1, 1, 8, 102, 3849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "なになにー？\nミオちゃんに\n何か用かな～？", 0, 1, 2, 1, 1, 3850);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 0, 1, 2, 1, 2, 3851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 0, 1, 2, 1, 3, 3852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 0, 1, 2, 1, 4, 3853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 0, 1, 2, 4, 101, 3854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 0, 1, 2, 8, 102, 3855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 0, 1, 2, 1, 1, 3856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 2, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 0, 1, 2, 1, 2, 3857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 3, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 0, 1, 2, 1, 3, 3858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 0, 1, 3, 1, 2, 3860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 0, 1, 3, 1, 3, 3861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 0, 1, 3, 1, 4, 3862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 0, 1, 3, 1, 5, 3863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 0, 1, 3, 1, 7, 3865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112601, 0, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 0, 1, 3, 1, 8, 3866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100101, 0, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 0, 1, 1, 12, 103, 3868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100101, 0, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 0, 1, 2, 12, 103, 3869);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100201, 0, "騎士クンの前で\n好きって言えるように\n練習しないと…", 0, 1, 1, 12, 103, 3870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100201, 0, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 0, 1, 2, 12, 103, 3871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 0, 1, 1, 12, 103, 3872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100301, 0, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 0, 1, 2, 12, 103, 3873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 0, 1, 1, 1, 1, 3874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 0, 1, 1, 1, 2, 3875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 0, 1, 1, 1, 3, 3876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 0, 1, 1, 1, 4, 3877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 0, 1, 1, 1, 5, 3878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 0, 1, 1, 4, 101, 3879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 0, 1, 1, 8, 102, 3880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 0, 1, 2, 1, 1, 3881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 0, 1, 2, 1, 2, 3882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 0, 1, 2, 1, 3, 3883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 0, 1, 2, 1, 4, 3884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 0, 1, 2, 4, 101, 3885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 0, 1, 2, 8, 102, 3886);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 0, 1, 2, 1, 1, 3887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 2, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 0, 1, 2, 1, 2, 3888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 3, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 0, 1, 2, 1, 3, 3889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 0, 1, 3, 1, 2, 3891);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 0, 1, 3, 1, 3, 3892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 0, 1, 3, 1, 4, 3893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 0, 1, 3, 1, 5, 3894);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 0, 1, 3, 1, 7, 3896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112501, 0, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 0, 1, 3, 1, 8, 3897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 0, 1, 1, 1, 1, 3899);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 0, 1, 1, 1, 2, 3900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 0, 1, 1, 1, 3, 3901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 0, 1, 1, 1, 4, 3902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 0, 1, 1, 1, 5, 3903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 0, 1, 1, 4, 101, 3904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 0, 1, 1, 8, 102, 3905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 0, 1, 2, 1, 1, 3906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 0, 1, 2, 1, 2, 3907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 0, 1, 2, 1, 3, 3908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 0, 1, 2, 1, 4, 3909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 0, 1, 2, 4, 101, 3910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 0, 1, 2, 8, 102, 3911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 0, 1, 2, 1, 1, 3912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 2, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 0, 1, 2, 1, 2, 3913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 3, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 0, 1, 2, 1, 3, 3914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 0, 1, 3, 1, 2, 3916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 0, 1, 3, 1, 3, 3917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 0, 1, 3, 1, 4, 3918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "炎はいいぞ\nそこには人類発展の\nドラマがある", 0, 1, 3, 1, 5, 3919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "フオオー！\n次から次へと固形食物\n原理気になるー！", 0, 1, 3, 1, 7, 3921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111001, 0, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 0, 1, 3, 1, 8, 3922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 0, 1, 1, 12, 103, 3924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101201, 0, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 0, 1, 2, 12, 103, 3925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192101, 0, "ちぇるーん☆", 0, 1, 2, 1, 1, 3933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 0, 1, 1, 1, 1, 3951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "どこかに楽しいもの\n落ちてないかにゃー", 0, 1, 1, 1, 2, 3952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 0, 1, 1, 1, 3, 3953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 0, 1, 1, 1, 4, 3954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 0, 1, 1, 1, 5, 3955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 0, 1, 1, 4, 101, 3956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 0, 1, 1, 8, 102, 3957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "え、なんですか？\n告白だったら\n間に合ってますけど", 0, 1, 2, 1, 1, 3958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 0, 1, 2, 1, 2, 3959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 0, 1, 2, 1, 3, 3960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 0, 1, 2, 1, 4, 3961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 0, 1, 2, 4, 101, 3962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 0, 1, 2, 8, 102, 3963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 0, 1, 2, 1, 1, 3964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 2, "今日のランチは\nどこで\n映えよっかにゃー", 0, 1, 2, 1, 2, 3965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 3, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 0, 1, 2, 1, 3, 3966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "ふかふかチエルン\n略して、ふかルン♪", 0, 1, 3, 1, 2, 3968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 0, 1, 3, 1, 3, 3969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 0, 1, 3, 1, 4, 3970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "マシュマロは\n焼かない方のが\nかわいくないです？", 0, 1, 3, 1, 5, 3971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "チエル食べても\n太らない体質\nなんでぇ～！", 0, 1, 3, 1, 7, 3973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (110901, 0, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 0, 1, 3, 1, 8, 3974);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 0, 1, 1, 1, 1, 3976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 0, 1, 1, 1, 2, 3977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "あたしの代わりに誰か\n働いてくれないかね", 0, 1, 1, 1, 3, 3978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "労働の後に食べる\nあんパン最高～", 0, 1, 1, 1, 4, 3979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 0, 1, 1, 1, 5, 3980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 0, 1, 1, 4, 101, 3981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "次の休みはあいつと\n一日中だらだら\nしたいな～", 0, 1, 1, 8, 102, 3982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 0, 1, 2, 1, 1, 3983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "お、差し入れ？\n気が利くねぇ\nあんがと", 0, 1, 2, 1, 2, 3984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "新しい依頼？\n今日はもう無理～\n閉店で～す", 0, 1, 2, 1, 3, 3985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 0, 1, 2, 1, 4, 3986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 0, 1, 2, 4, 101, 3987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 0, 1, 2, 8, 102, 3988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 0, 1, 2, 1, 1, 3989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 2, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 0, 1, 2, 1, 2, 3990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 3, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 0, 1, 2, 1, 3, 3991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 0, 1, 3, 1, 2, 3993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 0, 1, 3, 1, 3, 3994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "あんパンの香りの\nアロマとか\nあればいいのにな～", 0, 1, 3, 1, 4, 3995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 0, 1, 3, 1, 5, 3996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 0, 1, 3, 1, 7, 3998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112701, 0, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 0, 1, 3, 1, 8, 3999);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 0, 1, 1, 12, 103, 4001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104601, 0, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 0, 1, 2, 12, 103, 4002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "フロンティア\nスピリッツを\nお見せするべ！", 0, 1, 1, 1, 1, 4003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "この鎧を着ると\n気が引き締まる\n感じがするべ", 0, 1, 1, 1, 2, 4004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 0, 1, 1, 1, 3, 4005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 0, 1, 1, 1, 4, 4006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 0, 1, 1, 1, 5, 4007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "みんなオラより\n若ぇのに働いてて\n立派だべな", 0, 1, 1, 4, 101, 4008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 0, 1, 1, 8, 102, 4009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 0, 1, 2, 1, 1, 4010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 0, 1, 2, 1, 2, 4011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 0, 1, 2, 1, 3, 4012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 0, 1, 2, 1, 4, 4013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 0, 1, 2, 4, 101, 4014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 0, 1, 2, 8, 102, 4015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 0, 1, 2, 1, 1, 4016);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 2, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 0, 1, 2, 1, 2, 4017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 3, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 0, 1, 2, 1, 3, 4018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "牧草みたいに\nふっかふかだべ～", 0, 1, 3, 1, 2, 4020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "この子、目の感じが\nエリザベスに似てるべ", 0, 1, 3, 1, 3, 4021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "いい匂いだべ～\n疲れた体に\nしみるべな～", 0, 1, 3, 1, 4, 4022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "あったけぇな～\n牧場にも\n置いてみっかな～", 0, 1, 3, 1, 5, 4023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "うまいべ！\n生産者のこだわりを\n感じるべさ", 0, 1, 3, 1, 7, 4025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112801, 0, "酪農も戦いも\nまずは知識だべ！", 0, 1, 3, 1, 8, 4026);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 0, 1, 1, 1, 1, 4028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 0, 1, 1, 1, 2, 4029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 0, 1, 1, 1, 3, 4030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "なんだか先輩の\n気配がするような…\n気のせいかな？", 0, 1, 1, 1, 4, 4031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 0, 1, 1, 1, 5, 4032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 0, 1, 1, 4, 101, 4033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 0, 1, 1, 8, 102, 4034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 0, 1, 2, 1, 1, 4035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 0, 1, 2, 1, 2, 4036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 0, 1, 2, 1, 3, 4037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 0, 1, 2, 1, 4, 4038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 0, 1, 2, 4, 101, 4039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 0, 1, 2, 8, 102, 4040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 0, 1, 2, 1, 1, 4041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 2, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 0, 1, 2, 1, 2, 4042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 3, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 0, 1, 2, 1, 3, 4043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 0, 1, 3, 1, 2, 4045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 0, 1, 3, 1, 3, 4046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 0, 1, 3, 1, 4, 4047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 0, 1, 3, 1, 5, 4048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 0, 1, 3, 1, 7, 4050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113001, 0, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 0, 1, 3, 1, 8, 4051);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 0, 1, 1, 1, 1, 4053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 0, 1, 1, 1, 2, 4054);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "月々の食費が\n増えてしまいました\n申し訳ございません…", 0, 1, 1, 1, 3, 4055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 0, 1, 1, 1, 4, 4056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 0, 1, 1, 1, 5, 4057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 0, 1, 1, 4, 101, 4058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 0, 1, 1, 8, 102, 4059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "おでかけですか？\nではわたくしも\nお供いたします", 0, 1, 2, 1, 1, 4060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 0, 1, 2, 1, 2, 4061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 0, 1, 2, 1, 3, 4062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 0, 1, 2, 1, 4, 4063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 0, 1, 2, 4, 101, 4064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 0, 1, 2, 8, 102, 4065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 0, 1, 2, 1, 1, 4066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 2, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 0, 1, 2, 1, 2, 4067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 3, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 0, 1, 2, 1, 3, 4068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 0, 1, 3, 1, 2, 4070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 0, 1, 3, 1, 3, 4071);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 0, 1, 3, 1, 4, 4072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "主さまの心と\n繋がったときのような\n温もりを感じます", 0, 1, 3, 1, 5, 4073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 0, 1, 3, 1, 7, 4075);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180501, 0, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 0, 1, 3, 1, 8, 4076);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 0, 1, 1, 12, 103, 4078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104801, 0, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 0, 1, 2, 12, 103, 4079);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 0, 1, 1, 1, 1, 4080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 0, 1, 1, 1, 2, 4081);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 0, 1, 1, 1, 3, 4082);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 0, 1, 1, 1, 4, 4083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 0, 1, 1, 1, 5, 4084);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 0, 1, 1, 4, 101, 4085);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 0, 1, 1, 8, 102, 4086);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 0, 1, 2, 1, 1, 4087);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 0, 1, 2, 1, 2, 4088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 0, 1, 2, 1, 3, 4089);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 0, 1, 2, 1, 4, 4090);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 0, 1, 2, 4, 101, 4091);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 0, 1, 2, 8, 102, 4092);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 0, 1, 2, 1, 1, 4093);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 2, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 0, 1, 2, 1, 2, 4094);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 3, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 0, 1, 2, 1, 3, 4095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 0, 1, 3, 1, 2, 4097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 0, 1, 3, 1, 3, 4098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 0, 1, 3, 1, 4, 4099);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 0, 1, 3, 1, 5, 4100);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 0, 1, 3, 1, 7, 4102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (112901, 0, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 0, 1, 3, 1, 8, 4103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "退屈です…\nなにか楽しいことは\nないですか？", 0, 1, 1, 1, 1, 4105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 0, 1, 1, 1, 2, 4106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 0, 1, 1, 1, 3, 4107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 0, 1, 1, 1, 4, 4108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 0, 1, 1, 1, 5, 4109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 0, 1, 1, 4, 101, 4110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 0, 1, 1, 8, 102, 4111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 0, 1, 2, 1, 1, 4112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 0, 1, 2, 1, 2, 4113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 0, 1, 2, 1, 3, 4114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "お兄さんは\nヒマそうですね…\nうらやましいです", 0, 1, 2, 1, 4, 4115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 0, 1, 2, 4, 101, 4116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 0, 1, 2, 8, 102, 4117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 0, 1, 2, 1, 1, 4118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 2, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 0, 1, 2, 1, 2, 4119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 3, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 0, 1, 2, 1, 3, 4120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "はぁ～…\nもうここから一歩も\n動きたくないです", 0, 1, 3, 1, 2, 4122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 0, 1, 3, 1, 3, 4123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "なんかおしゃれな\n匂いがするですね…", 0, 1, 3, 1, 4, 4124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 0, 1, 3, 1, 5, 4125);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 0, 1, 3, 1, 7, 4127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "ふーむ…\nもっと楽しい本は\nないですかね？", 0, 1, 3, 1, 8, 4128);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 0, 1, 1, 1, 1, 4130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 0, 1, 1, 1, 2, 4131);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "アターック！\nいや、もっとこう…\nアタァーック！", 0, 1, 1, 1, 3, 4132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 0, 1, 1, 1, 4, 4133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 0, 1, 1, 1, 5, 4134);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 0, 1, 1, 4, 101, 4135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 0, 1, 1, 8, 102, 4136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 0, 1, 2, 1, 1, 4137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 0, 1, 2, 1, 2, 4138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 0, 1, 2, 1, 3, 4139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 0, 1, 2, 1, 4, 4140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 0, 1, 2, 4, 101, 4141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 0, 1, 2, 8, 102, 4142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 0, 1, 2, 1, 1, 4143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 2, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 0, 1, 2, 1, 2, 4144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 3, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 0, 1, 2, 1, 3, 4145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 0, 1, 3, 1, 2, 4147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 0, 1, 3, 1, 3, 4148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "心地よい香りで\n我が精神も安らぐ…", 0, 1, 3, 1, 4, 4149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 0, 1, 3, 1, 5, 4150);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 0, 1, 3, 1, 7, 4152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 0, 1, 3, 1, 8, 4153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 0, 1, 3, 1, 10, 4155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113201, 0, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 0, 1, 3, 1, 11, 4156);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 0, 1, 1, 1, 1, 4157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 0, 1, 1, 1, 2, 4158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 0, 1, 1, 1, 3, 4159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 0, 1, 1, 1, 4, 4160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 0, 1, 1, 1, 5, 4161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "騎士クン…\n今どこにいるの…？\n…会いたいな", 0, 1, 1, 4, 101, 4162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 0, 1, 1, 8, 102, 4163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 0, 1, 2, 1, 1, 4164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 0, 1, 2, 1, 2, 4165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 0, 1, 2, 1, 3, 4166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 0, 1, 2, 1, 4, 4167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 0, 1, 2, 4, 101, 4168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 0, 1, 2, 8, 102, 4169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 0, 1, 2, 1, 1, 4170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 2, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 0, 1, 2, 1, 2, 4171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 3, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 0, 1, 2, 1, 3, 4172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 0, 1, 3, 1, 2, 4174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 0, 1, 3, 1, 3, 4175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 0, 1, 3, 1, 4, 4176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "温かい…\n騎士クンがくれる\n力みたい…", 0, 1, 3, 1, 5, 4177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 0, 1, 3, 1, 7, 4179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180201, 0, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 0, 1, 3, 1, 8, 4180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 0, 1, 1, 12, 103, 4182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104901, 0, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 0, 1, 2, 12, 103, 4183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 0, 1, 1, 1, 1, 4184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 0, 1, 1, 1, 2, 4185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 0, 1, 1, 1, 3, 4186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 0, 1, 1, 1, 4, 4187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 0, 1, 1, 1, 5, 4188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 0, 1, 1, 4, 101, 4189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 0, 1, 1, 8, 102, 4190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 0, 1, 2, 1, 1, 4191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 0, 1, 2, 1, 2, 4192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 0, 1, 2, 1, 3, 4193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 0, 1, 2, 1, 4, 4194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 0, 1, 2, 4, 101, 4195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 0, 1, 2, 8, 102, 4196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 0, 1, 2, 1, 1, 4197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 2, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 0, 1, 2, 1, 2, 4198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 3, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 0, 1, 2, 1, 3, 4199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 0, 1, 3, 1, 2, 4201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "あんたたちも\n海を楽しみたいって\n思っているのかね", 0, 1, 3, 1, 3, 4202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 0, 1, 3, 1, 4, 4203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "こういう格好だと\n少し冷えちまうんだよ", 0, 1, 3, 1, 5, 4204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 0, 1, 3, 1, 7, 4206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 0, 1, 3, 1, 8, 4207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "へえ…\nなかなかいい\n乗り心地じゃないか", 0, 1, 3, 1, 10, 4209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113101, 0, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 0, 1, 3, 1, 11, 4210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 0, 1, 1, 1, 1, 4211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 0, 1, 1, 1, 2, 4212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "今のナナカちゃんは\nたぶん水属性！", 0, 1, 1, 1, 3, 4213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 0, 1, 1, 1, 4, 4214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 0, 1, 1, 1, 5, 4215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 0, 1, 1, 4, 101, 4216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 0, 1, 1, 8, 102, 4217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 0, 1, 2, 1, 1, 4218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 0, 1, 2, 1, 2, 4219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 0, 1, 2, 1, 3, 4220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 0, 1, 2, 1, 4, 4221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 0, 1, 2, 4, 101, 4222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 0, 1, 2, 8, 102, 4223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 0, 1, 2, 1, 1, 4224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 2, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 0, 1, 2, 1, 2, 4225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 3, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 0, 1, 2, 1, 3, 4226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 0, 1, 3, 1, 2, 4228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 0, 1, 3, 1, 3, 4229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 0, 1, 3, 1, 4, 4230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "夜になると急に冷えて\n肌寒いんだよね～…", 0, 1, 3, 1, 5, 4231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 0, 1, 3, 1, 7, 4233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 0, 1, 3, 1, 8, 4234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 0, 1, 3, 1, 10, 4236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113301, 0, "ナナカちゃんの\n誕生～\nなんちゃって", 0, 1, 3, 1, 11, 4237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "ファイトファイト☆\n一生懸命\n応援するね！", 0, 1, 1, 1, 1, 4238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 0, 1, 1, 1, 2, 4239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 0, 1, 1, 1, 3, 4240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 0, 1, 1, 1, 4, 4241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 0, 1, 1, 1, 5, 4242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 0, 1, 1, 4, 101, 4243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "キミへの応援\nちゃんと\n届いてるかな…", 0, 1, 1, 8, 102, 4244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 0, 1, 2, 1, 1, 4245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "どうしたの？\n元気がないなら\n私が応援するよ☆", 0, 1, 2, 1, 2, 4246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 0, 1, 2, 1, 3, 4247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 0, 1, 2, 1, 4, 4248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 0, 1, 2, 4, 101, 4249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "キミを応援\nすることができて…\n私、すごく幸せ…", 0, 1, 2, 8, 102, 4250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 0, 1, 2, 1, 1, 4251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 2, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 0, 1, 2, 1, 2, 4252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 3, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 0, 1, 2, 1, 3, 4253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 0, 1, 3, 1, 2, 4255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 0, 1, 3, 1, 3, 4256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 0, 1, 3, 1, 4, 4257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 0, 1, 3, 1, 5, 4258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 0, 1, 3, 1, 7, 4260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 0, 1, 3, 1, 8, 4261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 0, 1, 3, 1, 10, 4263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113401, 0, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 0, 1, 3, 1, 11, 4264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 0, 1, 1, 1, 1, 4265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 0, 1, 1, 1, 2, 4266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 0, 1, 1, 1, 3, 4267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 0, 1, 1, 1, 4, 4268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 0, 1, 1, 1, 5, 4269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 0, 1, 1, 4, 101, 4270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 0, 1, 1, 8, 102, 4271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 0, 1, 2, 1, 1, 4272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 0, 1, 2, 1, 2, 4273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 0, 1, 2, 1, 3, 4274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 0, 1, 2, 1, 4, 4275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 0, 1, 2, 4, 101, 4276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 0, 1, 2, 8, 102, 4277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 0, 1, 2, 1, 1, 4278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 2, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 0, 1, 2, 1, 2, 4279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 3, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 0, 1, 2, 1, 3, 4280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 0, 1, 3, 1, 2, 4282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 0, 1, 3, 1, 3, 4283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 0, 1, 3, 1, 4, 4284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 0, 1, 3, 1, 5, 4285);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 0, 1, 3, 1, 7, 4287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 0, 1, 3, 1, 8, 4288);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 0, 1, 3, 1, 10, 4290);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113501, 0, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 0, 1, 3, 1, 11, 4291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 0, 1, 1, 12, 103, 4292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102301, 0, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 0, 1, 2, 12, 103, 4293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 0, 1, 1, 1, 1, 4294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "この格好で\n過ごすのは\n少し勇気がいるな…", 0, 1, 1, 1, 2, 4295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 0, 1, 1, 1, 3, 4296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 0, 1, 1, 1, 4, 4297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 0, 1, 1, 1, 5, 4298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 0, 1, 1, 4, 101, 4299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "少年との\n海のバカンス…\nまた行きたい\nものだな", 0, 1, 1, 8, 102, 4300);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 0, 1, 2, 1, 1, 4301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 0, 1, 2, 1, 2, 4302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 0, 1, 2, 1, 3, 4303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 0, 1, 2, 1, 4, 4304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 0, 1, 2, 4, 101, 4305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 0, 1, 2, 8, 102, 4306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 0, 1, 2, 1, 1, 4307);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 2, "昼食のお誘いかい？\nいいよ、付き合おう", 0, 1, 2, 1, 2, 4308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 3, "もう夜か…\n一日が経つのは\nはやいものだね", 0, 1, 2, 1, 3, 4309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 0, 1, 3, 1, 2, 4311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "うん…ふわふわの\n感触が\nよくわかるな…", 0, 1, 3, 1, 3, 4312);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 0, 1, 3, 1, 4, 4313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 0, 1, 3, 1, 5, 4314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "兜を気にせず\n食べられるのは\n楽でいいな…", 0, 1, 3, 1, 7, 4316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "ふむ…鎧がないと\nページがめくりやすい", 0, 1, 3, 1, 8, 4317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 0, 1, 3, 1, 10, 4319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113601, 0, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 0, 1, 3, 1, 11, 4320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 0, 1, 1, 1, 1, 4321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 0, 1, 1, 1, 2, 4322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "色んな人を観察できて\n眼福眼福…♪", 0, 1, 1, 1, 3, 4323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 0, 1, 1, 1, 4, 4324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 0, 1, 1, 1, 5, 4325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 0, 1, 1, 4, 101, 4326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 0, 1, 1, 8, 102, 4327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 0, 1, 2, 1, 1, 4328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 0, 1, 2, 1, 2, 4329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 0, 1, 2, 1, 3, 4330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 0, 1, 2, 1, 4, 4331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 0, 1, 2, 4, 101, 4332);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 0, 1, 2, 8, 102, 4333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 0, 1, 2, 1, 1, 4334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 2, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 0, 1, 2, 1, 2, 4335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 3, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 0, 1, 2, 1, 3, 4336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 0, 1, 3, 1, 2, 4338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "…これ\nネネカが変身\nしてないよね？", 0, 1, 3, 1, 3, 4339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "いい香りだ…\n解析完了\n今度生成しようっと♪", 0, 1, 3, 1, 4, 4340);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "安心するな～…\nアジトに置けないから\n羨ましいよ", 0, 1, 3, 1, 5, 4341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 0, 1, 3, 1, 7, 4343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106801, 0, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 0, 1, 3, 1, 8, 4344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 0, 1, 1, 1, 1, 4346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 0, 1, 1, 1, 2, 4347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 0, 1, 1, 1, 3, 4348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 0, 1, 1, 1, 4, 4349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 0, 1, 1, 1, 5, 4350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 0, 1, 1, 4, 101, 4351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 0, 1, 1, 8, 102, 4352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 0, 1, 2, 1, 1, 4353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 0, 1, 2, 1, 2, 4354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 0, 1, 2, 1, 3, 4355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 0, 1, 2, 1, 4, 4356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 0, 1, 2, 4, 101, 4357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 0, 1, 2, 8, 102, 4358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 0, 1, 2, 1, 1, 4359);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 2, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 0, 1, 2, 1, 2, 4360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 3, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 0, 1, 2, 1, 3, 4361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 0, 1, 3, 1, 2, 4363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 0, 1, 3, 1, 3, 4364);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 0, 1, 3, 1, 4, 4365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 0, 1, 3, 1, 5, 4366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 0, 1, 3, 1, 7, 4368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113701, 0, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 0, 1, 3, 1, 8, 4369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "あの方の前では\n常に気高く\nありたいですわね", 0, 1, 1, 12, 103, 4371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103201, 0, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 0, 1, 2, 12, 103, 4372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 0, 1, 1, 12, 103, 4373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102801, 0, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 0, 1, 2, 12, 103, 4374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 0, 1, 1, 1, 1, 4375);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 0, 1, 1, 1, 2, 4376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 0, 1, 1, 1, 3, 4377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 0, 1, 1, 1, 4, 4378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "エンジェルになると\n人見知りがなおる！\n…わけないか", 0, 1, 1, 1, 5, 4379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 0, 1, 1, 4, 101, 4380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 0, 1, 1, 8, 102, 4381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 0, 1, 2, 1, 1, 4382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 0, 1, 2, 1, 2, 4383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 0, 1, 2, 1, 3, 4384);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 0, 1, 2, 1, 4, 4385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 0, 1, 2, 4, 101, 4386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 0, 1, 2, 8, 102, 4387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 0, 1, 2, 1, 1, 4388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 2, "ふぅ、いいことした\n後のランチは\nおいしいわね", 0, 1, 2, 1, 2, 4389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 3, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 0, 1, 2, 1, 3, 4390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 0, 1, 3, 1, 2, 4392);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 0, 1, 3, 1, 3, 4393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 0, 1, 3, 1, 4, 4394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 0, 1, 3, 1, 5, 4395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 0, 1, 3, 1, 7, 4397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113801, 0, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 0, 1, 3, 1, 8, 4398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 0, 1, 1, 1, 1, 4400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 0, 1, 1, 1, 2, 4401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 0, 1, 1, 1, 3, 4402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 0, 1, 1, 1, 4, 4403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "マントは動きに\nメリハリがついて\nいいですね", 0, 1, 1, 1, 5, 4404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 0, 1, 1, 4, 101, 4405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 0, 1, 1, 8, 102, 4406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 0, 1, 2, 1, 1, 4407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 0, 1, 2, 1, 2, 4408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 0, 1, 2, 1, 3, 4409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 0, 1, 2, 1, 4, 4410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 0, 1, 2, 4, 101, 4411);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 0, 1, 2, 8, 102, 4412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 0, 1, 2, 1, 1, 4413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 2, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 0, 1, 2, 1, 2, 4414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 3, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 0, 1, 2, 1, 3, 4415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "このフィット感\nどうにか服作りにも\n活かせれば…！", 0, 1, 3, 1, 2, 4417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 0, 1, 3, 1, 3, 4418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 0, 1, 3, 1, 4, 4419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 0, 1, 3, 1, 5, 4420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 0, 1, 3, 1, 7, 4422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 0, 1, 3, 1, 8, 4423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (113901, 0, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 0, 1, 3, 1, 12, 4425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 0, 1, 1, 1, 1, 4427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 0, 1, 1, 1, 2, 4428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 0, 1, 1, 1, 3, 4429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 0, 1, 1, 1, 4, 4430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 0, 1, 1, 1, 5, 4431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 0, 1, 1, 4, 101, 4432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 0, 1, 1, 8, 102, 4433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 0, 1, 2, 1, 1, 4434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 0, 1, 2, 1, 2, 4435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 0, 1, 2, 1, 3, 4436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 0, 1, 2, 1, 4, 4437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "あんたも一緒に\n新しい技の練習\nどうッスか？", 0, 1, 2, 4, 101, 4438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 0, 1, 2, 8, 102, 4439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 0, 1, 2, 1, 1, 4440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 2, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 0, 1, 2, 1, 2, 4441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 3, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 0, 1, 2, 1, 3, 4442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 0, 1, 3, 1, 2, 4444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 0, 1, 3, 1, 3, 4445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 0, 1, 3, 1, 4, 4446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 0, 1, 3, 1, 5, 4447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 0, 1, 3, 1, 7, 4449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 0, 1, 3, 1, 8, 4450);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114101, 0, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 0, 1, 3, 1, 12, 4452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 0, 1, 1, 12, 103, 4454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103001, 0, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 0, 1, 2, 12, 103, 4455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 0, 1, 1, 1, 1, 4456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 0, 1, 1, 1, 2, 4457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 0, 1, 1, 1, 3, 4458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 0, 1, 1, 1, 4, 4459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 0, 1, 1, 1, 5, 4460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 0, 1, 1, 4, 101, 4461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 0, 1, 1, 8, 102, 4462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 0, 1, 2, 1, 1, 4463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 0, 1, 2, 1, 2, 4464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "どうしたの？\n出かけるなら\n私も同行しようか", 0, 1, 2, 1, 3, 4465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 0, 1, 2, 1, 4, 4466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 0, 1, 2, 4, 101, 4467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 0, 1, 2, 8, 102, 4468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 0, 1, 2, 1, 1, 4469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 2, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 0, 1, 2, 1, 2, 4470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 3, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 0, 1, 2, 1, 3, 4471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "ハロウィンはまだ\nこれからだ\n一休みしようか", 0, 1, 3, 1, 2, 4473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 0, 1, 3, 1, 3, 4474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 0, 1, 3, 1, 4, 4475);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 0, 1, 3, 1, 5, 4476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 0, 1, 3, 1, 7, 4478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 0, 1, 3, 1, 8, 4479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114001, 0, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 0, 1, 3, 1, 12, 4481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 0, 1, 1, 1, 1, 4483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 0, 1, 1, 1, 2, 4484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 0, 1, 1, 1, 3, 4485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 0, 1, 1, 1, 4, 4486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 0, 1, 1, 1, 5, 4487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 0, 1, 1, 4, 101, 4488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 0, 1, 1, 8, 102, 4489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 0, 1, 2, 1, 1, 4490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 0, 1, 2, 1, 2, 4491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 0, 1, 2, 1, 3, 4492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 0, 1, 2, 1, 4, 4493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 0, 1, 2, 4, 101, 4494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 0, 1, 2, 8, 102, 4495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 0, 1, 2, 1, 1, 4496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 2, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 0, 1, 2, 1, 2, 4497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 3, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 0, 1, 2, 1, 3, 4498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 0, 1, 3, 1, 2, 4500);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 0, 1, 3, 1, 3, 4501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 0, 1, 3, 1, 4, 4502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "この温かさ…\n愛と希望に\n通じるものがある…", 0, 1, 3, 1, 5, 4503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 0, 1, 3, 1, 7, 4505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114201, 0, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 0, 1, 3, 1, 8, 4506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 0, 1, 1, 12, 103, 4508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103301, 0, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 0, 1, 2, 12, 103, 4509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぴっぷる！", 0, 1, 1, 1, 1, 4510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぴっぷるぴっぷる♪", 0, 1, 1, 1, 2, 4511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぴっ！", 0, 1, 1, 1, 3, 4512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぷっぷー", 0, 1, 1, 1, 4, 4513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぴぃ？", 0, 1, 1, 1, 5, 4514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぴっぷるんるん♪", 0, 1, 2, 1, 1, 4515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぷぅ！", 0, 1, 2, 1, 2, 4516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぴぴっ♪", 0, 1, 2, 1, 3, 4517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 0, "ぷぅー…", 0, 1, 2, 1, 4, 4518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 1, "ちゅ！", 0, 1, 2, 1, 1, 4519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 2, "ぷはー", 0, 1, 2, 1, 2, 4520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (192501, 3, "ぴぇー", 0, 1, 2, 1, 3, 4521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 0, 1, 1, 1, 1, 4531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 0, 1, 1, 1, 2, 4532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 0, 1, 1, 1, 3, 4533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 0, 1, 1, 1, 4, 4534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 0, 1, 1, 1, 5, 4535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 0, 1, 1, 4, 101, 4536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 0, 1, 1, 8, 102, 4537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "もし時間があるなら、\n私と街の見回りに\n行かない？", 0, 1, 2, 1, 1, 4538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 0, 1, 2, 1, 2, 4539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 0, 1, 2, 1, 3, 4540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 0, 1, 2, 1, 4, 4541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 0, 1, 2, 4, 101, 4542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 0, 1, 2, 8, 102, 4543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 0, 1, 2, 1, 1, 4544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 2, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 0, 1, 2, 1, 2, 4545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 3, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 0, 1, 2, 1, 3, 4546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 0, 1, 3, 1, 2, 4548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 0, 1, 3, 1, 3, 4549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 0, 1, 3, 1, 4, 4550);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 0, 1, 3, 1, 5, 4551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "こ、これは…\nおいしすぎて手が\n止まらないかも", 0, 1, 3, 1, 7, 4553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114301, 0, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 0, 1, 3, 1, 8, 4554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 0, 1, 1, 1, 1, 4556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 0, 1, 1, 1, 2, 4557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 0, 1, 1, 1, 3, 4558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 0, 1, 1, 1, 4, 4559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 0, 1, 1, 1, 5, 4560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 0, 1, 1, 4, 101, 4561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 0, 1, 1, 8, 102, 4562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 0, 1, 2, 1, 1, 4563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 0, 1, 2, 1, 2, 4564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 0, 1, 2, 1, 3, 4565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 0, 1, 2, 1, 4, 4566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 0, 1, 2, 4, 101, 4567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 0, 1, 2, 8, 102, 4568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 0, 1, 2, 1, 1, 4569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 2, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 0, 1, 2, 1, 2, 4570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 3, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 0, 1, 2, 1, 3, 4571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "適度な休息は\n成功の秘訣ですわ！", 0, 1, 3, 1, 2, 4573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 0, 1, 3, 1, 3, 4574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 0, 1, 3, 1, 4, 4575);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 0, 1, 3, 1, 5, 4576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 0, 1, 3, 1, 7, 4578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 0, 1, 3, 1, 8, 4579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 0, 1, 3, 1, 14, 4581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114401, 0, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 0, 1, 3, 1, 15, 4582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 0, 1, 1, 1, 1, 4583);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 0, 1, 1, 1, 2, 4584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 0, 1, 1, 1, 3, 4585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 0, 1, 1, 1, 4, 4586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 0, 1, 1, 1, 5, 4587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 0, 1, 1, 4, 101, 4588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 0, 1, 1, 8, 102, 4589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 0, 1, 2, 1, 1, 4590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 0, 1, 2, 1, 2, 4591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 0, 1, 2, 1, 3, 4592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "お姉さんの\n頼れるところ\n見せてあげるわね", 0, 1, 2, 1, 4, 4593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "何かあったら\n頼ってくれても\nいいんだからね？", 0, 1, 2, 4, 101, 4594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 0, 1, 2, 8, 102, 4595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 0, 1, 2, 1, 1, 4596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 2, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 0, 1, 2, 1, 2, 4597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 3, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 0, 1, 2, 1, 3, 4598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 0, 1, 3, 1, 2, 4600);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "私だって…\nかわいいものは\n好きでいいわよね？", 0, 1, 3, 1, 3, 4601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 0, 1, 3, 1, 4, 4602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 0, 1, 3, 1, 5, 4603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 0, 1, 3, 1, 7, 4605);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 0, 1, 3, 1, 8, 4606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 0, 1, 3, 1, 14, 4608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114601, 0, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 0, 1, 3, 1, 15, 4609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 0, 1, 1, 12, 103, 4610);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101601, 0, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 0, 1, 2, 12, 103, 4611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 0, 1, 1, 1, 1, 4612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 0, 1, 1, 1, 2, 4613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 0, 1, 1, 1, 3, 4614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 0, 1, 1, 1, 4, 4615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 0, 1, 1, 1, 5, 4616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 0, 1, 1, 4, 101, 4617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 0, 1, 1, 8, 102, 4618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 0, 1, 2, 1, 1, 4619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 0, 1, 2, 1, 2, 4620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 0, 1, 2, 1, 3, 4621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 0, 1, 2, 1, 4, 4622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 0, 1, 2, 4, 101, 4623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 0, 1, 2, 8, 102, 4624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 0, 1, 2, 1, 1, 4625);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 2, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 0, 1, 2, 1, 2, 4626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 3, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 0, 1, 2, 1, 3, 4627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 0, 1, 3, 1, 2, 4629);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 0, 1, 3, 1, 3, 4630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "華やかな香りね\nクリスマスに\nピッタリだわ", 0, 1, 3, 1, 4, 4631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 0, 1, 3, 1, 5, 4632);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 0, 1, 3, 1, 7, 4634);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 0, 1, 3, 1, 8, 4635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 0, 1, 3, 1, 14, 4637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114501, 0, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 0, 1, 3, 1, 15, 4638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 0, 1, 1, 1, 1, 4639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "今年こそ、もう少し\n身長が伸びます\nように…", 0, 1, 1, 1, 2, 4640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 0, 1, 1, 1, 3, 4641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 0, 1, 1, 1, 4, 4642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 0, 1, 1, 1, 5, 4643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 0, 1, 1, 4, 101, 4644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 0, 1, 1, 8, 102, 4645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 0, 1, 2, 1, 1, 4646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 0, 1, 2, 1, 2, 4647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 0, 1, 2, 1, 3, 4648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 0, 1, 2, 1, 4, 4649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 0, 1, 2, 4, 101, 4650);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 0, 1, 2, 8, 102, 4651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 0, 1, 2, 1, 1, 4652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 2, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 0, 1, 2, 1, 2, 4653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 3, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 0, 1, 2, 1, 3, 4654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 0, 1, 3, 1, 2, 4656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "なんだ？\nオマエも初詣に\n行きたいのか？", 0, 1, 3, 1, 3, 4657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 0, 1, 3, 1, 4, 4658);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "コイツがあれば\nうまい魚が\n焼けそうだな…", 0, 1, 3, 1, 5, 4659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 0, 1, 3, 1, 7, 4661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (114701, 0, "おみくじって\n書いてあることが\nむずかしいんだよな…", 0, 1, 3, 1, 8, 4662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 0, 1, 1, 1, 1, 4664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 0, 1, 1, 1, 2, 4665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 0, 1, 1, 1, 3, 4666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 0, 1, 1, 1, 4, 4667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 0, 1, 1, 1, 5, 4668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 0, 1, 1, 4, 101, 4669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 0, 1, 1, 8, 102, 4670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 0, 1, 2, 1, 1, 4671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 0, 1, 2, 1, 2, 4672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 0, 1, 2, 1, 3, 4673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 0, 1, 2, 1, 4, 4674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 0, 1, 2, 4, 101, 4675);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 0, 1, 2, 8, 102, 4676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 0, 1, 2, 1, 1, 4677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 2, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 0, 1, 2, 1, 2, 4678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 3, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 0, 1, 2, 1, 3, 4679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 0, 1, 3, 1, 2, 4681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 0, 1, 3, 1, 3, 4682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 0, 1, 3, 1, 4, 4683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 0, 1, 3, 1, 5, 4684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 0, 1, 3, 1, 7, 4686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180101, 0, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 0, 1, 3, 1, 8, 4687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 0, 1, 1, 1, 1, 4689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 0, 1, 1, 1, 2, 4690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 0, 1, 1, 1, 3, 4691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 0, 1, 1, 1, 4, 4692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 0, 1, 1, 1, 5, 4693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "ずっとこうして\n年の始まりを\n過ごしたいですね", 0, 1, 1, 4, 101, 4694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "いつか二人きりで\n初日の出を\n見たいです", 0, 1, 1, 8, 102, 4695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "お餅おかわりです！\nいくらでも\n食べられますよ！", 0, 1, 2, 1, 1, 4696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 0, 1, 2, 1, 2, 4697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 0, 1, 2, 1, 3, 4698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 0, 1, 2, 1, 4, 4699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 0, 1, 2, 4, 101, 4700);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 0, 1, 2, 8, 102, 4701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 0, 1, 2, 1, 1, 4702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 2, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 0, 1, 2, 1, 2, 4703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 3, "あたたかくして\n幸せな初夢を\n見ましょうね", 0, 1, 2, 1, 3, 4704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 0, 1, 3, 1, 2, 4706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 0, 1, 3, 1, 3, 4707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 0, 1, 3, 1, 4, 4708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 0, 1, 3, 1, 5, 4709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "おいしすぎて\n食べる手が\n止まりません～", 0, 1, 3, 1, 7, 4711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (111801, 0, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 0, 1, 3, 1, 8, 4712);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 0, 1, 1, 12, 103, 4714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100601, 0, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 0, 1, 2, 12, 103, 4715);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 0, 1, 1, 1, 1, 4716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 0, 1, 1, 1, 2, 4717);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 0, 1, 1, 1, 3, 4718);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 0, 1, 1, 1, 4, 4719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 0, 1, 1, 1, 5, 4720);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 0, 1, 1, 4, 101, 4721);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 0, 1, 1, 8, 102, 4722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 0, 1, 2, 1, 1, 4723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 0, 1, 2, 1, 2, 4724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 0, 1, 2, 1, 3, 4725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 0, 1, 2, 1, 4, 4726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 0, 1, 2, 4, 101, 4727);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 0, 1, 2, 8, 102, 4728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 0, 1, 2, 1, 1, 4729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 2, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 0, 1, 2, 1, 2, 4730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 3, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 0, 1, 2, 1, 3, 4731);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "少し休みます…\n時間が来たら\n起こすように", 0, 1, 3, 1, 2, 4733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 0, 1, 3, 1, 3, 4734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 0, 1, 3, 1, 4, 4735);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 0, 1, 3, 1, 5, 4736);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "いかなる強者でも\n兵站、補給は\n必要なものです", 0, 1, 3, 1, 7, 4738);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115001, 0, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 0, 1, 3, 1, 8, 4739);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 0, 1, 1, 1, 1, 4741);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 0, 1, 1, 1, 2, 4742);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "主さまも精霊の召喚を\n試してみますか？", 0, 1, 1, 1, 3, 4743);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 0, 1, 1, 1, 4, 4744);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 0, 1, 1, 1, 5, 4745);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "進化した精霊に\n負けぬよう\n修行に励みます", 0, 1, 1, 4, 101, 4746);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 0, 1, 1, 8, 102, 4747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 0, 1, 2, 1, 1, 4748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 0, 1, 2, 1, 2, 4749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "里で採れた果物を\n持って参りました\n召し上がってください", 0, 1, 2, 1, 3, 4750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 0, 1, 2, 1, 4, 4751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 0, 1, 2, 4, 101, 4752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 0, 1, 2, 8, 102, 4753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 0, 1, 2, 1, 1, 4754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 2, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 0, 1, 2, 1, 2, 4755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 3, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 0, 1, 2, 1, 3, 4756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "この癒し…\n主さまにも味わって\nいただきたい…", 0, 1, 3, 1, 2, 4758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 0, 1, 3, 1, 3, 4759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 0, 1, 3, 1, 4, 4760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "瞑想するのに\nちょうどよい暖かさで\nございます", 0, 1, 3, 1, 5, 4761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 0, 1, 3, 1, 7, 4763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115501, 0, "風の精霊について\nもっと詳しく知って\nおかなければ…", 0, 1, 3, 1, 8, 4764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 0, 1, 1, 1, 1, 4766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 0, 1, 1, 1, 2, 4767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 0, 1, 1, 1, 3, 4768);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 0, 1, 1, 1, 4, 4769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 0, 1, 1, 1, 5, 4770);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 0, 1, 1, 4, 101, 4771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 0, 1, 1, 8, 102, 4772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 0, 1, 2, 1, 1, 4773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 0, 1, 2, 1, 2, 4774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 0, 1, 2, 1, 3, 4775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 0, 1, 2, 1, 4, 4776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 0, 1, 2, 4, 101, 4777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 0, 1, 2, 8, 102, 4778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 0, 1, 2, 1, 1, 4779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 2, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 0, 1, 2, 1, 2, 4780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 3, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 0, 1, 2, 1, 3, 4781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 0, 1, 3, 1, 2, 4783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 0, 1, 3, 1, 3, 4784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 0, 1, 3, 1, 4, 4785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 0, 1, 3, 1, 5, 4786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "自然の恵みに\n感謝して…\nいただきます♪", 0, 1, 3, 1, 7, 4788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115601, 0, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 0, 1, 3, 1, 8, 4789);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 0, 1, 1, 12, 103, 4791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102201, 0, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 0, 1, 2, 12, 103, 4792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 0, 1, 1, 1, 1, 4793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 0, 1, 1, 1, 2, 4794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 0, 1, 1, 1, 3, 4795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 0, 1, 1, 1, 4, 4796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 0, 1, 1, 1, 5, 4797);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 0, 1, 1, 4, 101, 4798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 0, 1, 1, 8, 102, 4799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 0, 1, 2, 1, 1, 4800);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 0, 1, 2, 1, 2, 4801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 0, 1, 2, 1, 3, 4802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 0, 1, 2, 1, 4, 4803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 0, 1, 2, 4, 101, 4804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 0, 1, 2, 8, 102, 4805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 0, 1, 2, 1, 1, 4806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 2, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 0, 1, 2, 1, 2, 4807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 3, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 0, 1, 2, 1, 3, 4808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 0, 1, 3, 1, 2, 4810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 0, 1, 3, 1, 3, 4811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 0, 1, 3, 1, 4, 4812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 0, 1, 3, 1, 5, 4813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 0, 1, 3, 1, 7, 4815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ん…なるほどね。\n勉強になるわ…", 0, 1, 3, 1, 8, 4816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 0, 1, 1, 1, 1, 4818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "依頼があれば\n海でも山でも\n赴くさ！", 0, 1, 1, 1, 2, 4819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 0, 1, 1, 1, 3, 4820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 0, 1, 1, 1, 4, 4821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 0, 1, 1, 1, 5, 4822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 0, 1, 1, 4, 101, 4823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 0, 1, 1, 8, 102, 4824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 0, 1, 2, 1, 1, 4825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 0, 1, 2, 1, 2, 4826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 0, 1, 2, 1, 3, 4827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 0, 1, 2, 1, 4, 4828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 0, 1, 2, 4, 101, 4829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 0, 1, 2, 8, 102, 4830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 0, 1, 2, 1, 1, 4831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 2, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 0, 1, 2, 1, 2, 4832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 3, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 0, 1, 2, 1, 3, 4833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 0, 1, 3, 1, 2, 4835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 0, 1, 3, 1, 3, 4836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 0, 1, 3, 1, 4, 4837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "ポカポカするね…\n助手くん\n君もおいでよ", 0, 1, 3, 1, 5, 4838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 0, 1, 3, 1, 7, 4840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 0, 1, 3, 1, 8, 4841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 0, 1, 3, 1, 10, 4843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115701, 0, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 0, 1, 3, 1, 11, 4844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 0, 1, 1, 1, 1, 4845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 0, 1, 1, 1, 2, 4846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 0, 1, 1, 1, 3, 4847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 0, 1, 1, 1, 4, 4848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 0, 1, 1, 1, 5, 4849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 0, 1, 1, 4, 101, 4850);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 0, 1, 1, 8, 102, 4851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 0, 1, 2, 1, 1, 4852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 0, 1, 2, 1, 2, 4853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 0, 1, 2, 1, 3, 4854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 0, 1, 2, 1, 4, 4855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 0, 1, 2, 4, 101, 4856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 0, 1, 2, 8, 102, 4857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 0, 1, 2, 1, 1, 4858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 2, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 0, 1, 2, 1, 2, 4859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 3, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 0, 1, 2, 1, 3, 4860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 0, 1, 3, 1, 2, 4862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 0, 1, 3, 1, 3, 4863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 0, 1, 3, 1, 4, 4864);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 0, 1, 3, 1, 5, 4865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 0, 1, 3, 1, 7, 4867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180601, 0, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 0, 1, 3, 1, 8, 4868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 0, 1, 1, 1, 1, 4870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 0, 1, 1, 1, 2, 4871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 0, 1, 1, 1, 3, 4872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 0, 1, 1, 1, 4, 4873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 0, 1, 1, 1, 5, 4874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 0, 1, 1, 4, 101, 4875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 0, 1, 1, 8, 102, 4876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "どうかしら？\n私、きちんとドレス\n着られてる？", 0, 1, 2, 1, 1, 4877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 0, 1, 2, 1, 2, 4878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 0, 1, 2, 1, 3, 4879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 0, 1, 2, 1, 4, 4880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 0, 1, 2, 4, 101, 4881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 0, 1, 2, 8, 102, 4882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 0, 1, 2, 1, 1, 4883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 2, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 0, 1, 2, 1, 2, 4884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 3, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 0, 1, 2, 1, 3, 4885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 0, 1, 3, 1, 2, 4887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 0, 1, 3, 1, 3, 4888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "すてきな香り…\nお花かしら？\n森で探してみようかな", 0, 1, 3, 1, 4, 4889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 0, 1, 3, 1, 5, 4890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "レッスンのために\n栄養補給して\nおかないと！", 0, 1, 3, 1, 7, 4892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115801, 0, "教養を身に着ける\nことも大切よね！\nなになに…", 0, 1, 3, 1, 8, 4893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 0, 1, 1, 1, 1, 4895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 0, 1, 1, 1, 2, 4896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 0, 1, 1, 1, 3, 4897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 0, 1, 1, 1, 4, 4898);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 0, 1, 1, 1, 5, 4899);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 0, 1, 1, 4, 101, 4900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 0, 1, 1, 8, 102, 4901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 0, 1, 2, 1, 1, 4902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 0, 1, 2, 1, 2, 4903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 0, 1, 2, 1, 3, 4904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 0, 1, 2, 1, 4, 4905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 0, 1, 2, 4, 101, 4906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 0, 1, 2, 8, 102, 4907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 0, 1, 2, 1, 1, 4908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 2, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 0, 1, 2, 1, 2, 4909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 3, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 0, 1, 2, 1, 3, 4910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 0, 1, 3, 1, 2, 4912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 0, 1, 3, 1, 3, 4913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 0, 1, 3, 1, 4, 4914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 0, 1, 3, 1, 5, 4915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 0, 1, 3, 1, 7, 4917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (115901, 0, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 0, 1, 3, 1, 8, 4918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102001, 0, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 0, 1, 1, 12, 103, 4920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102001, 0, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 0, 1, 2, 12, 103, 4921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 0, 1, 1, 12, 103, 4922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100401, 0, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 0, 1, 2, 12, 103, 4923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 0, 1, 1, 12, 103, 4924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103601, 0, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 0, 1, 2, 12, 103, 4925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "くるくる〜♪\nうちのドレス\nかわええなぁ", 0, 1, 1, 1, 1, 4926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 0, 1, 1, 1, 2, 4927);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 0, 1, 1, 1, 3, 4928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 0, 1, 1, 1, 4, 4929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 0, 1, 1, 1, 5, 4930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 0, 1, 1, 4, 101, 4931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 0, 1, 1, 8, 102, 4932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 0, 1, 2, 1, 1, 4933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 0, 1, 2, 1, 2, 4934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 0, 1, 2, 1, 3, 4935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 0, 1, 2, 1, 4, 4936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 0, 1, 2, 4, 101, 4937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 0, 1, 2, 8, 102, 4938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 0, 1, 2, 1, 1, 4939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 2, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 0, 1, 2, 1, 2, 4940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 3, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 0, 1, 2, 1, 3, 4941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 0, 1, 3, 1, 2, 4943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 0, 1, 3, 1, 3, 4944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 0, 1, 3, 1, 4, 4945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 0, 1, 3, 1, 5, 4946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 0, 1, 3, 1, 7, 4948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116001, 0, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 0, 1, 3, 1, 8, 4949);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 0, 1, 1, 1, 1, 4951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 0, 1, 1, 1, 2, 4952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 0, 1, 1, 1, 3, 4953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 0, 1, 1, 1, 4, 4954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 0, 1, 1, 1, 5, 4955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 0, 1, 1, 4, 101, 4956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 0, 1, 1, 8, 102, 4957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 0, 1, 2, 1, 1, 4958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 0, 1, 2, 1, 2, 4959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 0, 1, 2, 1, 3, 4960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 0, 1, 2, 1, 4, 4961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 0, 1, 2, 4, 101, 4962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 0, 1, 2, 8, 102, 4963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 0, 1, 2, 1, 1, 4964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 2, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 0, 1, 2, 1, 2, 4965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 3, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 0, 1, 2, 1, 3, 4966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 0, 1, 3, 1, 2, 4968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 0, 1, 3, 1, 3, 4969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 0, 1, 3, 1, 4, 4970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 0, 1, 3, 1, 5, 4971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 0, 1, 3, 1, 7, 4973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116301, 0, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 0, 1, 3, 1, 8, 4974);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 0, 1, 1, 12, 103, 4976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102901, 0, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 0, 1, 2, 12, 103, 4977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 0, 1, 1, 1, 1, 4978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 0, 1, 1, 1, 2, 4979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 0, 1, 1, 1, 3, 4980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 0, 1, 1, 1, 4, 4981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 0, 1, 1, 1, 5, 4982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 0, 1, 1, 4, 101, 4983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 0, 1, 1, 8, 102, 4984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 0, 1, 2, 1, 1, 4985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 0, 1, 2, 1, 2, 4986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 0, 1, 2, 1, 3, 4987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0, 1, 2, 1, 4, 4988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 0, 1, 2, 4, 101, 4989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 0, 1, 2, 8, 102, 4990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 0, 1, 2, 1, 1, 4991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 2, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 0, 1, 2, 1, 2, 4992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 3, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 0, 1, 2, 1, 3, 4993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 0, 1, 3, 1, 2, 4995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 0, 1, 3, 1, 3, 4996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 0, 1, 3, 1, 4, 4997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 0, 1, 3, 1, 5, 4998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 0, 1, 3, 1, 7, 5000);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116201, 0, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 0, 1, 3, 1, 8, 5001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 0, 1, 1, 1, 1, 5003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 0, 1, 1, 1, 2, 5004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 0, 1, 1, 1, 3, 5005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 0, 1, 1, 1, 4, 5006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 0, 1, 1, 1, 5, 5007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 0, 1, 1, 4, 101, 5008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 0, 1, 1, 8, 102, 5009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 0, 1, 2, 1, 1, 5010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 0, 1, 2, 1, 2, 5011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 0, 1, 2, 1, 3, 5012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 0, 1, 2, 1, 4, 5013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 0, 1, 2, 4, 101, 5014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 0, 1, 2, 8, 102, 5015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 0, 1, 2, 1, 1, 5016);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 2, "お腹が空きました…\nご飯食べにいきますよ", 0, 1, 2, 1, 2, 5017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 3, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 0, 1, 2, 1, 3, 5018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 0, 1, 3, 1, 2, 5020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 0, 1, 3, 1, 3, 5021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 0, 1, 3, 1, 4, 5022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 0, 1, 3, 1, 5, 5023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 0, 1, 3, 1, 7, 5025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116501, 0, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 0, 1, 3, 1, 8, 5026);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 0, 1, 1, 12, 103, 5028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102501, 0, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 0, 1, 2, 12, 103, 5029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 0, 1, 1, 1, 1, 5030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 0, 1, 1, 1, 2, 5031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 0, 1, 1, 1, 3, 5032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 0, 1, 1, 1, 4, 5033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 0, 1, 1, 1, 5, 5034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 0, 1, 1, 4, 101, 5035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 0, 1, 1, 8, 102, 5036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 0, 1, 2, 1, 1, 5037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 0, 1, 2, 1, 2, 5038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 0, 1, 2, 1, 3, 5039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 0, 1, 2, 1, 4, 5040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 0, 1, 2, 4, 101, 5041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 0, 1, 2, 8, 102, 5042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 0, 1, 2, 1, 1, 5043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 2, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 0, 1, 2, 1, 2, 5044);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 3, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 0, 1, 2, 1, 3, 5045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 0, 1, 3, 1, 2, 5047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 0, 1, 3, 1, 3, 5048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 0, 1, 3, 1, 4, 5049);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 0, 1, 3, 1, 5, 5050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 0, 1, 3, 1, 7, 5052);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116601, 0, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 0, 1, 3, 1, 8, 5053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 0, 1, 1, 1, 1, 5055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 0, 1, 1, 1, 2, 5056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 0, 1, 1, 1, 3, 5057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 0, 1, 1, 1, 4, 5058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 0, 1, 1, 1, 5, 5059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 0, 1, 1, 4, 101, 5060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 0, 1, 1, 8, 102, 5061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 0, 1, 2, 1, 1, 5062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 0, 1, 2, 1, 2, 5063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 0, 1, 2, 1, 3, 5064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 0, 1, 2, 1, 4, 5065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 0, 1, 2, 4, 101, 5066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 0, 1, 2, 8, 102, 5067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 0, 1, 2, 1, 1, 5068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 2, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 0, 1, 2, 1, 2, 5069);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 3, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 0, 1, 2, 1, 3, 5070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 0, 1, 3, 1, 2, 5072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 0, 1, 3, 1, 3, 5073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 0, 1, 3, 1, 4, 5074);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "ぽかぽか…\n幸せなひとときですね", 0, 1, 3, 1, 5, 5075);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 0, 1, 3, 1, 7, 5077);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116701, 0, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 0, 1, 3, 1, 8, 5078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 0, 1, 1, 1, 1, 5080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 0, 1, 1, 1, 2, 5081);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 0, 1, 1, 1, 3, 5082);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 0, 1, 1, 1, 4, 5083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 0, 1, 1, 1, 5, 5084);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 0, 1, 1, 4, 101, 5085);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 0, 1, 1, 8, 102, 5086);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 0, 1, 2, 1, 1, 5087);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 0, 1, 2, 1, 2, 5088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 0, 1, 2, 1, 3, 5089);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 0, 1, 2, 1, 4, 5090);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 0, 1, 2, 4, 101, 5091);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 0, 1, 2, 8, 102, 5092);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 0, 1, 2, 1, 1, 5093);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 2, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 0, 1, 2, 1, 2, 5094);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 3, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 0, 1, 2, 1, 3, 5095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 0, 1, 3, 1, 2, 5097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 0, 1, 3, 1, 3, 5098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 0, 1, 3, 1, 4, 5099);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 0, 1, 3, 1, 5, 5100);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 0, 1, 3, 1, 7, 5102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116801, 0, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 0, 1, 3, 1, 8, 5103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102101, 0, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 0, 1, 1, 12, 103, 5105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102101, 0, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 0, 1, 2, 12, 103, 5106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 0, 1, 1, 1, 1, 5107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "他のバイトとの\nスケジュールを\n調整しないと…", 0, 1, 1, 1, 2, 5108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 0, 1, 1, 1, 3, 5109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 0, 1, 1, 1, 4, 5110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 0, 1, 1, 1, 5, 5111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 0, 1, 1, 4, 101, 5112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 0, 1, 1, 8, 102, 5113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 0, 1, 2, 1, 1, 5114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 0, 1, 2, 1, 2, 5115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 0, 1, 2, 1, 3, 5116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 0, 1, 2, 1, 4, 5117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 0, 1, 2, 4, 101, 5118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 0, 1, 2, 8, 102, 5119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 0, 1, 2, 1, 1, 5120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 2, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 0, 1, 2, 1, 2, 5121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 3, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 0, 1, 2, 1, 3, 5122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 0, 1, 3, 1, 2, 5124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 0, 1, 3, 1, 3, 5125);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 0, 1, 3, 1, 4, 5126);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 0, 1, 3, 1, 5, 5127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 0, 1, 3, 1, 7, 5129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116901, 0, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 0, 1, 3, 1, 8, 5130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "変身している間に\n色々と試しておこう\nまずは…", 0, 1, 1, 1, 1, 5132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 0, 1, 1, 1, 2, 5133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 0, 1, 1, 1, 3, 5134);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 0, 1, 1, 1, 4, 5135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 0, 1, 1, 1, 5, 5136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 0, 1, 1, 4, 101, 5137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 0, 1, 1, 8, 102, 5138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 0, 1, 2, 1, 1, 5139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 0, 1, 2, 1, 2, 5140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 0, 1, 2, 1, 3, 5141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 0, 1, 2, 1, 4, 5142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 0, 1, 2, 4, 101, 5143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 0, 1, 2, 8, 102, 5144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 0, 1, 2, 1, 1, 5145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 2, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 0, 1, 2, 1, 2, 5146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 3, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 0, 1, 2, 1, 3, 5147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 0, 1, 3, 1, 2, 5149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 0, 1, 3, 1, 3, 5150);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 0, 1, 3, 1, 4, 5151);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "ありがたいな\n少し足元が\n冷えていたところだ", 0, 1, 3, 1, 5, 5152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "これは…！\n持ち運べるように\n改良できないかな？", 0, 1, 3, 1, 7, 5154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (180301, 0, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 0, 1, 3, 1, 8, 5155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 0, 1, 1, 1, 1, 5157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 0, 1, 1, 1, 2, 5158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 0, 1, 1, 1, 3, 5159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 0, 1, 1, 1, 4, 5160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 0, 1, 1, 1, 5, 5161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "あのおかたの全てが\n早く私のものに\nなりますように…", 0, 1, 1, 4, 101, 5162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 0, 1, 1, 8, 102, 5163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 0, 1, 2, 1, 1, 5164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 0, 1, 2, 1, 2, 5165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 0, 1, 2, 1, 3, 5166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 0, 1, 2, 1, 4, 5167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 0, 1, 2, 4, 101, 5168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 0, 1, 2, 8, 102, 5169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 0, 1, 2, 1, 1, 5170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 2, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 0, 1, 2, 1, 2, 5171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 3, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 0, 1, 2, 1, 3, 5172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 0, 1, 3, 1, 2, 5174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 0, 1, 3, 1, 3, 5175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 0, 1, 3, 1, 4, 5176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "あのおかたの\nぬくもりを\n思い出しますわ…", 0, 1, 3, 1, 5, 5177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 0, 1, 3, 1, 7, 5179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 0, 1, 3, 1, 8, 5180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "二人乗りができると\nいいですわね…", 0, 1, 3, 1, 10, 5182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117001, 0, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 0, 1, 3, 1, 11, 5183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101701, 0, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 0, 1, 1, 12, 103, 5184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101701, 0, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 0, 1, 2, 12, 103, 5185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "この水着…\n一番に弟くんに\n見せたいな♪", 0, 1, 1, 1, 1, 5186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 0, 1, 1, 1, 2, 5187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 0, 1, 1, 1, 3, 5188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 0, 1, 1, 1, 4, 5189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 0, 1, 1, 1, 5, 5190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 0, 1, 1, 4, 101, 5191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 0, 1, 1, 8, 102, 5192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 0, 1, 2, 1, 1, 5193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 0, 1, 2, 1, 2, 5194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "海で迷子に\nならないように\n手を繋ごうね♪", 0, 1, 2, 1, 3, 5195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 0, 1, 2, 1, 4, 5196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 0, 1, 2, 4, 101, 5197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 0, 1, 2, 8, 102, 5198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 0, 1, 2, 1, 1, 5199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 2, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 0, 1, 2, 1, 2, 5200);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 3, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 0, 1, 2, 1, 3, 5201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 0, 1, 3, 1, 2, 5203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 0, 1, 3, 1, 3, 5204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 0, 1, 3, 1, 4, 5205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 0, 1, 3, 1, 5, 5206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 0, 1, 3, 1, 7, 5208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 0, 1, 3, 1, 8, 5209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 0, 1, 3, 1, 10, 5211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117101, 0, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 0, 1, 3, 1, 11, 5212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 0, 1, 1, 1, 1, 5213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 0, 1, 1, 1, 2, 5214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "とっておきの\nサマーソングを\nみんなに届けるよ！", 0, 1, 1, 1, 3, 5215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 0, 1, 1, 1, 4, 5216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 0, 1, 1, 1, 5, 5217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 0, 1, 1, 4, 101, 5218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 0, 1, 1, 8, 102, 5219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 0, 1, 2, 1, 1, 5220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 0, 1, 2, 1, 2, 5221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 0, 1, 2, 1, 3, 5222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 0, 1, 2, 1, 4, 5223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 0, 1, 2, 4, 101, 5224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 0, 1, 2, 8, 102, 5225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 0, 1, 2, 1, 1, 5226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 2, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 0, 1, 2, 1, 2, 5227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 3, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 0, 1, 2, 1, 3, 5228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 0, 1, 3, 1, 2, 5230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 0, 1, 3, 1, 3, 5231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 0, 1, 3, 1, 4, 5232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 0, 1, 3, 1, 5, 5233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 0, 1, 3, 1, 7, 5235);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 0, 1, 3, 1, 8, 5236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 0, 1, 3, 1, 10, 5238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117201, 0, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 0, 1, 3, 1, 11, 5239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 0, 1, 1, 1, 1, 5240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 0, 1, 1, 1, 2, 5241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 0, 1, 1, 1, 3, 5242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 0, 1, 1, 1, 4, 5243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 0, 1, 1, 1, 5, 5244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 0, 1, 1, 4, 101, 5245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 0, 1, 1, 8, 102, 5246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "騎士さんいいところに\nこの衣装\nどう思います？", 0, 1, 2, 1, 1, 5247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 0, 1, 2, 1, 2, 5248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 0, 1, 2, 1, 3, 5249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "仕立て屋も\nアイドルも\n諦めません！", 0, 1, 2, 1, 4, 5250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 0, 1, 2, 4, 101, 5251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 0, 1, 2, 8, 102, 5252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 0, 1, 2, 1, 1, 5253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 2, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 0, 1, 2, 1, 2, 5254);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 3, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 0, 1, 2, 1, 3, 5255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 0, 1, 3, 1, 2, 5257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 0, 1, 3, 1, 3, 5258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "いい香りですね～\n波の音が聞こえて\nきそうです…", 0, 1, 3, 1, 4, 5259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "泳いだ後は\n少し寒いので\nちょうどいいですね", 0, 1, 3, 1, 5, 5260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "夏バテしないように\n栄養はしっかり\n取らないと！", 0, 1, 3, 1, 7, 5262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 0, 1, 3, 1, 8, 5263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "デザインはもちろん\n触り心地も\n最高ですね♪", 0, 1, 3, 1, 10, 5265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117401, 0, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 0, 1, 3, 1, 11, 5266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 0, 1, 1, 12, 103, 5267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104001, 0, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 0, 1, 2, 12, 103, 5268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "この衣装で\nライブするのは\n恥ずかしいな…", 0, 1, 1, 1, 1, 5269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 0, 1, 1, 1, 2, 5270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 0, 1, 1, 1, 3, 5271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 0, 1, 1, 1, 4, 5272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 0, 1, 1, 1, 5, 5273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 0, 1, 1, 4, 101, 5274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "水着、似合ってるって\n言ってもらえるかな…", 0, 1, 1, 8, 102, 5275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "サインして、ですか？\nいいですけど…\n少し照れますね…", 0, 1, 2, 1, 1, 5276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 0, 1, 2, 1, 2, 5277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "なんでしょう？\nもしかして…\nボイトレに興味が？", 0, 1, 2, 1, 3, 5278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 0, 1, 2, 1, 4, 5279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 0, 1, 2, 4, 101, 5280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 0, 1, 2, 8, 102, 5281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 0, 1, 2, 1, 1, 5282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 2, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 0, 1, 2, 1, 2, 5283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 3, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 0, 1, 2, 1, 3, 5284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 0, 1, 3, 1, 2, 5286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 0, 1, 3, 1, 3, 5287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 0, 1, 3, 1, 4, 5288);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 0, 1, 3, 1, 5, 5289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "アイドルとして、\n食べすぎには\n気をつけないと…", 0, 1, 3, 1, 7, 5291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 0, 1, 3, 1, 8, 5292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "この子に乗りながら\nライブをする…\nというのはどうですか", 0, 1, 3, 1, 10, 5294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117301, 0, "まるで人魚になった\n気分…なんて、ふふ", 0, 1, 3, 1, 11, 5295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 0, 1, 1, 1, 1, 5296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 0, 1, 1, 1, 2, 5297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 0, 1, 1, 1, 3, 5298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 0, 1, 1, 1, 4, 5299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 0, 1, 1, 1, 5, 5300);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 0, 1, 1, 4, 101, 5301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 0, 1, 1, 8, 102, 5302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 0, 1, 2, 1, 1, 5303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 0, 1, 2, 1, 2, 5304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 0, 1, 2, 1, 3, 5305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 0, 1, 2, 1, 4, 5306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 0, 1, 2, 4, 101, 5307);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 0, 1, 2, 8, 102, 5308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 0, 1, 2, 1, 1, 5309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 2, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 0, 1, 2, 1, 2, 5310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 3, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 0, 1, 2, 1, 3, 5311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 0, 1, 3, 1, 2, 5313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 0, 1, 3, 1, 3, 5314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 0, 1, 3, 1, 4, 5315);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 0, 1, 3, 1, 5, 5316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 0, 1, 3, 1, 7, 5318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118301, 0, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 0, 1, 3, 1, 8, 5319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 0, 1, 1, 1, 1, 5322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 0, 1, 1, 1, 2, 5323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 0, 1, 1, 1, 3, 5324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 0, 1, 1, 1, 4, 5325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 0, 1, 1, 1, 5, 5326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 0, 1, 1, 4, 101, 5327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 0, 1, 1, 8, 102, 5328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 0, 1, 2, 1, 1, 5329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 0, 1, 2, 1, 2, 5330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 0, 1, 2, 1, 3, 5331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 0, 1, 2, 1, 4, 5332);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 0, 1, 2, 4, 101, 5333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 0, 1, 2, 8, 102, 5334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 0, 1, 2, 1, 1, 5335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 2, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 0, 1, 2, 1, 2, 5336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 3, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 0, 1, 2, 1, 3, 5337);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 0, 1, 3, 1, 2, 5339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 0, 1, 3, 1, 3, 5340);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 0, 1, 3, 1, 4, 5341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 0, 1, 3, 1, 5, 5342);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 0, 1, 3, 1, 7, 5344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118401, 0, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 0, 1, 3, 1, 8, 5345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 0, 1, 1, 1, 1, 5348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 0, 1, 1, 1, 2, 5349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 0, 1, 1, 1, 3, 5350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "新薬開発のために\n薬草を採取して\nおこうかしら", 0, 1, 1, 1, 4, 5351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "いつ急患が来ても\n対応できるように\nしておきましょうか", 0, 1, 1, 1, 5, 5352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 0, 1, 1, 4, 101, 5353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 0, 1, 1, 8, 102, 5354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 0, 1, 2, 1, 1, 5355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 0, 1, 2, 1, 2, 5356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 0, 1, 2, 1, 3, 5357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 0, 1, 2, 1, 4, 5358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 0, 1, 2, 4, 101, 5359);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 0, 1, 2, 8, 102, 5360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 0, 1, 2, 1, 1, 5361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 2, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 0, 1, 2, 1, 2, 5362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 3, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 0, 1, 2, 1, 3, 5363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 0, 1, 3, 1, 2, 5365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 0, 1, 3, 1, 3, 5366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 0, 1, 3, 1, 4, 5367);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 0, 1, 3, 1, 5, 5368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 0, 1, 3, 1, 7, 5370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117501, 0, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 0, 1, 3, 1, 8, 5371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 0, 1, 1, 1, 1, 5400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "美しさとは\n日々の努力が\n作るものなんだよ", 0, 1, 1, 1, 2, 5401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "大変だ…\nボクは美しく\nなりすぎたかも", 0, 1, 1, 1, 3, 5402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 0, 1, 1, 1, 4, 5403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "どんな服装をしても\nボクの美しさは\n変わらないね", 0, 1, 1, 1, 5, 5404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 0, 1, 1, 4, 101, 5405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 0, 1, 1, 8, 102, 5406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 0, 1, 2, 1, 1, 5407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 0, 1, 2, 1, 2, 5408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 0, 1, 2, 1, 3, 5409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 0, 1, 2, 1, 4, 5410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 0, 1, 2, 4, 101, 5411);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 0, 1, 2, 8, 102, 5412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 0, 1, 2, 1, 1, 5413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 2, "美は食で作られる\n美容にいいものを\n食べようよ！", 0, 1, 2, 1, 2, 5414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 3, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 0, 1, 2, 1, 3, 5415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "ふう～…\nリラックスする\nボクも美しい", 0, 1, 3, 1, 2, 5417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 0, 1, 3, 1, 3, 5418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "美しいボクは\n常にいい香りを\nしていなくちゃね", 0, 1, 3, 1, 4, 5419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 0, 1, 3, 1, 5, 5420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "バランスのいい食事が\n美しいボクを\n作るのさ", 0, 1, 3, 1, 7, 5422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117601, 0, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 0, 1, 3, 1, 8, 5423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 0, 1, 1, 12, 103, 5425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105701, 0, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 0, 1, 2, 12, 103, 5426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 0, 1, 1, 1, 1, 5427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 0, 1, 1, 1, 2, 5428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 0, 1, 1, 1, 3, 5429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 0, 1, 1, 1, 4, 5430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 0, 1, 1, 1, 5, 5431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 0, 1, 1, 4, 101, 5432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 0, 1, 1, 8, 102, 5433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 0, 1, 2, 1, 1, 5434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 0, 1, 2, 1, 2, 5435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 0, 1, 2, 1, 3, 5436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 0, 1, 2, 1, 4, 5437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 0, 1, 2, 4, 101, 5438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 0, 1, 2, 8, 102, 5439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 0, 1, 2, 1, 1, 5440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 2, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 0, 1, 2, 1, 2, 5441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 3, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 0, 1, 2, 1, 3, 5442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 0, 1, 3, 1, 2, 5444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 0, 1, 3, 1, 3, 5445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 0, 1, 3, 1, 4, 5446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 0, 1, 3, 1, 5, 5447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 0, 1, 3, 1, 7, 5449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118001, 0, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 0, 1, 3, 1, 8, 5450);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 0, 1, 1, 1, 1, 5452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 0, 1, 1, 1, 2, 5453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 0, 1, 1, 1, 3, 5454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 0, 1, 1, 1, 4, 5455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 0, 1, 1, 1, 5, 5456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 0, 1, 1, 4, 101, 5457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 0, 1, 1, 8, 102, 5458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 0, 1, 2, 1, 1, 5459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "この制服、\nハロウィンらしくて\n大好きさ～♪", 0, 1, 2, 1, 2, 5460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 0, 1, 2, 1, 3, 5461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 0, 1, 2, 1, 4, 5462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 0, 1, 2, 4, 101, 5463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 0, 1, 2, 8, 102, 5464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 0, 1, 2, 1, 1, 5465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 2, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 0, 1, 2, 1, 2, 5466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 3, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 0, 1, 2, 1, 3, 5467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 0, 1, 3, 1, 2, 5469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 0, 1, 3, 1, 3, 5470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 0, 1, 3, 1, 4, 5471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 0, 1, 3, 1, 5, 5472);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 0, 1, 3, 1, 7, 5474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 0, 1, 3, 1, 8, 5475);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117701, 0, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 0, 1, 3, 1, 12, 5477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 0, 1, 1, 1, 1, 5479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 0, 1, 1, 1, 2, 5480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 0, 1, 1, 1, 3, 5481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 0, 1, 1, 1, 4, 5482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 0, 1, 1, 1, 5, 5483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "幽霊になる前に\n天下統一を\n果たしたいデスね", 0, 1, 1, 4, 101, 5484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 0, 1, 1, 8, 102, 5485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ショーグン！\n悪霊退治に\n出かけるデース！", 0, 1, 2, 1, 1, 5486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "妖魔を家来に\nしてみるのは\nどうデス？", 0, 1, 2, 1, 2, 5487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "マントを使って\nウツセミの術の精度を\n高めるデス！", 0, 1, 2, 1, 3, 5488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 0, 1, 2, 1, 4, 5489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "あの世でも\nショーグンに\nお仕えしマスよー！", 0, 1, 2, 4, 101, 5490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 0, 1, 2, 8, 102, 5491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 0, 1, 2, 1, 1, 5492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 2, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 0, 1, 2, 1, 2, 5493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 3, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 0, 1, 2, 1, 3, 5494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 0, 1, 3, 1, 2, 5496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 0, 1, 3, 1, 3, 5497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 0, 1, 3, 1, 4, 5498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "パレードの熱気に\n負けないくらい\n熱いデス！", 0, 1, 3, 1, 5, 5499);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 0, 1, 3, 1, 7, 5501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 0, 1, 3, 1, 8, 5502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117801, 0, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 0, 1, 3, 1, 12, 5504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 0, 1, 1, 12, 103, 5531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101301, 0, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 0, 1, 2, 12, 103, 5532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 0, 1, 1, 1, 1, 5533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 0, 1, 1, 1, 2, 5534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 0, 1, 1, 1, 3, 5535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 0, 1, 1, 1, 4, 5536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 0, 1, 1, 1, 5, 5537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 0, 1, 1, 4, 101, 5538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 0, 1, 1, 8, 102, 5539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 0, 1, 2, 1, 1, 5540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 0, 1, 2, 1, 2, 5541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 0, 1, 2, 1, 3, 5542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 0, 1, 2, 1, 4, 5543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 0, 1, 2, 4, 101, 5544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 0, 1, 2, 8, 102, 5545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 0, 1, 2, 1, 1, 5546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 2, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 0, 1, 2, 1, 2, 5547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 3, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 0, 1, 2, 1, 3, 5548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 0, 1, 3, 1, 2, 5550);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 0, 1, 3, 1, 3, 5551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 0, 1, 3, 1, 4, 5552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "やばばば…\nあったかくて\n眠くなっちゃう～", 0, 1, 3, 1, 5, 5553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "これ食べて\nもうひとがんばり\nしよーっと！", 0, 1, 3, 1, 7, 5555);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 0, 1, 3, 1, 8, 5556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (117901, 0, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 0, 1, 3, 1, 12, 5558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 0, 1, 1, 1, 1, 5560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 0, 1, 1, 1, 2, 5561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 0, 1, 1, 1, 3, 5562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 0, 1, 1, 1, 4, 5563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 0, 1, 1, 1, 5, 5564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 0, 1, 1, 4, 101, 5565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 0, 1, 1, 8, 102, 5566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "何かわからないことが\nあるときは私に\n聞いてくださいね", 0, 1, 2, 1, 1, 5567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 0, 1, 2, 1, 2, 5568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 0, 1, 2, 1, 3, 5569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 0, 1, 2, 1, 4, 5570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 0, 1, 2, 4, 101, 5571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 0, 1, 2, 8, 102, 5572);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 1, "今日も一日\n管理人として\nがんばります♪", 0, 1, 2, 1, 1, 5573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 2, "お腹がすきました…\nそろそろお昼に\nしませんか？", 0, 1, 2, 1, 2, 5574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 3, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 0, 1, 2, 1, 3, 5575);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "良質なお仕事は\n良質な休憩から\nです♪", 0, 1, 3, 1, 2, 5577);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 0, 1, 3, 1, 3, 5578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 0, 1, 3, 1, 4, 5579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 0, 1, 3, 1, 5, 5580);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 0, 1, 3, 1, 7, 5582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118501, 0, "新条例はこのように\n活用されている、と…\nふむふむ…", 0, 1, 3, 1, 8, 5583);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 0, 1, 1, 1, 1, 5585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 0, 1, 1, 1, 2, 5586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 0, 1, 1, 1, 3, 5587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 0, 1, 1, 1, 4, 5588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 0, 1, 1, 1, 5, 5589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 0, 1, 1, 4, 101, 5590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 0, 1, 1, 8, 102, 5591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 0, 1, 2, 1, 1, 5592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 0, 1, 2, 1, 2, 5593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 0, 1, 2, 1, 3, 5594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 0, 1, 2, 1, 4, 5595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 0, 1, 2, 4, 101, 5596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 0, 1, 2, 8, 102, 5597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 0, 1, 2, 1, 1, 5598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 2, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 0, 1, 2, 1, 2, 5599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 3, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 0, 1, 2, 1, 3, 5600);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 0, 1, 3, 1, 2, 5602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 0, 1, 3, 1, 3, 5603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 0, 1, 3, 1, 4, 5604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 0, 1, 3, 1, 5, 5605);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "この味付け…\nあっちでも再現\nできるかな？", 0, 1, 3, 1, 7, 5607);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106701, 0, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 0, 1, 3, 1, 8, 5608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 0, 1, 1, 1, 1, 5610);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 0, 1, 1, 1, 2, 5611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 0, 1, 1, 1, 3, 5612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 0, 1, 1, 1, 4, 5613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 0, 1, 1, 1, 5, 5614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 0, 1, 1, 4, 101, 5615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 0, 1, 1, 8, 102, 5616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 0, 1, 2, 1, 1, 5617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 0, 1, 2, 1, 2, 5618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 0, 1, 2, 1, 3, 5619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 0, 1, 2, 1, 4, 5620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 0, 1, 2, 4, 101, 5621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "君との秘密が\nまた一つ\n増えちゃったね♪", 0, 1, 2, 8, 102, 5622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 0, 1, 2, 1, 1, 5623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 2, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 0, 1, 2, 1, 2, 5624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 3, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 0, 1, 2, 1, 3, 5625);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 0, 1, 3, 1, 2, 5627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 0, 1, 3, 1, 3, 5628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 0, 1, 3, 1, 4, 5629);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 0, 1, 3, 1, 5, 5630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "生徒の見本に\nなれるように\n好き嫌いはしないの", 0, 1, 3, 1, 7, 5632);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119001, 0, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 0, 1, 3, 1, 8, 5633);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 0, 1, 1, 12, 103, 5635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106301, 0, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 0, 1, 2, 12, 103, 5636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 0, 1, 1, 12, 103, 5637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105301, 0, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 0, 1, 2, 12, 103, 5638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "ああ～\nドＭミストが\nあふれてしまいます～", 0, 1, 1, 1, 1, 5639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 0, 1, 1, 1, 2, 5640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 0, 1, 1, 1, 3, 5641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 0, 1, 1, 1, 4, 5642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 0, 1, 1, 1, 5, 5643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 0, 1, 1, 4, 101, 5644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 0, 1, 1, 8, 102, 5645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 0, 1, 2, 1, 1, 5646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 0, 1, 2, 1, 2, 5647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 0, 1, 2, 1, 3, 5648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 0, 1, 2, 1, 4, 5649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 0, 1, 2, 4, 101, 5650);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 0, 1, 2, 8, 102, 5651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 0, 1, 2, 1, 1, 5652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 2, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 0, 1, 2, 1, 2, 5653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 3, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 0, 1, 2, 1, 3, 5654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 0, 1, 3, 1, 2, 5656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 0, 1, 3, 1, 3, 5657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 0, 1, 3, 1, 4, 5658);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 0, 1, 3, 1, 5, 5659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 0, 1, 3, 1, 7, 5661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119101, 0, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 0, 1, 3, 1, 8, 5662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 0, 1, 1, 1, 1, 5664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 0, 1, 1, 1, 2, 5665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 0, 1, 1, 1, 3, 5666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 0, 1, 1, 1, 4, 5667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 0, 1, 1, 1, 5, 5668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 0, 1, 1, 4, 101, 5669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 0, 1, 1, 8, 102, 5670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 0, 1, 2, 1, 1, 5671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 0, 1, 2, 1, 2, 5672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 0, 1, 2, 1, 3, 5673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 0, 1, 2, 1, 4, 5674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 0, 1, 2, 4, 101, 5675);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 0, 1, 2, 8, 102, 5676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 0, 1, 2, 1, 1, 5677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 2, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 0, 1, 2, 1, 2, 5678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 3, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 0, 1, 2, 1, 3, 5679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 0, 1, 3, 1, 2, 5681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 0, 1, 3, 1, 3, 5682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 0, 1, 3, 1, 4, 5683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 0, 1, 3, 1, 5, 5684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "これはオマエがくれる\nプリンとは\n別腹なの～", 0, 1, 3, 1, 7, 5686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 0, 1, 3, 1, 8, 5687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 0, 1, 3, 1, 14, 5689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119901, 0, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 0, 1, 3, 1, 15, 5690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 0, 1, 1, 1, 1, 5691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 0, 1, 1, 1, 2, 5692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 0, 1, 1, 1, 3, 5693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 0, 1, 1, 1, 4, 5694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 0, 1, 1, 1, 5, 5695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 0, 1, 1, 4, 101, 5696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 0, 1, 1, 8, 102, 5697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 0, 1, 2, 1, 1, 5698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 0, 1, 2, 1, 2, 5699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 0, 1, 2, 1, 3, 5700);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 0, 1, 2, 1, 4, 5701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 0, 1, 2, 4, 101, 5702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 0, 1, 2, 8, 102, 5703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 0, 1, 2, 1, 1, 5704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 2, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 0, 1, 2, 1, 2, 5705);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 3, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 0, 1, 2, 1, 3, 5706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 0, 1, 3, 1, 2, 5708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 0, 1, 3, 1, 3, 5709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 0, 1, 3, 1, 4, 5710);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 0, 1, 3, 1, 5, 5711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 0, 1, 3, 1, 7, 5713);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 0, 1, 3, 1, 8, 5714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 0, 1, 3, 1, 14, 5716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119201, 0, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 0, 1, 3, 1, 15, 5717);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 0, 1, 1, 12, 103, 5718);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105001, 0, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 0, 1, 2, 12, 103, 5719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 0, 1, 1, 1, 1, 5720);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 0, 1, 1, 1, 2, 5721);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 0, 1, 1, 1, 3, 5722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 0, 1, 1, 1, 4, 5723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 0, 1, 1, 1, 5, 5724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 0, 1, 1, 4, 101, 5725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 0, 1, 1, 8, 102, 5726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 0, 1, 2, 1, 1, 5727);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 0, 1, 2, 1, 2, 5728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 0, 1, 2, 1, 3, 5729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 0, 1, 2, 1, 4, 5730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 0, 1, 2, 4, 101, 5731);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 0, 1, 2, 8, 102, 5732);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 0, 1, 2, 1, 1, 5733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 2, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 0, 1, 2, 1, 2, 5734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 3, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 0, 1, 2, 1, 3, 5735);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 0, 1, 3, 1, 2, 5737);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 0, 1, 3, 1, 3, 5738);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 0, 1, 3, 1, 4, 5739);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 0, 1, 3, 1, 5, 5740);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 0, 1, 3, 1, 7, 5742);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 0, 1, 3, 1, 8, 5743);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 0, 1, 3, 1, 14, 5745);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (119301, 0, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 0, 1, 3, 1, 15, 5746);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 0, 1, 1, 1, 1, 5747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 0, 1, 1, 1, 2, 5748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 0, 1, 1, 1, 3, 5749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 0, 1, 1, 1, 4, 5750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 0, 1, 1, 1, 5, 5751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 0, 1, 1, 4, 101, 5752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 0, 1, 1, 8, 102, 5753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 0, 1, 2, 1, 1, 5754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "きゃあっ！？\nへ、変なところを\n触らないでください！", 0, 1, 2, 1, 2, 5755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 0, 1, 2, 1, 3, 5756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 0, 1, 2, 1, 4, 5757);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 0, 1, 2, 4, 101, 5758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 0, 1, 2, 8, 102, 5759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 0, 1, 2, 1, 1, 5760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 2, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 0, 1, 2, 1, 2, 5761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 3, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 0, 1, 2, 1, 3, 5762);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 0, 1, 3, 1, 2, 5764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 0, 1, 3, 1, 3, 5765);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 0, 1, 3, 1, 4, 5766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 0, 1, 3, 1, 5, 5767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 0, 1, 3, 1, 7, 5769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120601, 0, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 0, 1, 3, 1, 8, 5770);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 0, 1, 1, 1, 1, 5773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 0, 1, 1, 1, 2, 5774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 0, 1, 1, 1, 3, 5775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 0, 1, 1, 1, 4, 5776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 0, 1, 1, 1, 5, 5777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 0, 1, 1, 4, 101, 5778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 0, 1, 1, 8, 102, 5779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 0, 1, 2, 1, 1, 5780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 0, 1, 2, 1, 2, 5781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 0, 1, 2, 1, 3, 5782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 0, 1, 2, 1, 4, 5783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 0, 1, 2, 4, 101, 5784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 0, 1, 2, 8, 102, 5785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 0, 1, 2, 1, 1, 5786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 2, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 0, 1, 2, 1, 2, 5787);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 3, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 0, 1, 2, 1, 3, 5788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 0, 1, 3, 1, 2, 5790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 0, 1, 3, 1, 3, 5791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 0, 1, 3, 1, 4, 5792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 0, 1, 3, 1, 5, 5793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 0, 1, 3, 1, 7, 5795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120401, 0, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 0, 1, 3, 1, 8, 5796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 0, 1, 1, 1, 1, 5799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 0, 1, 1, 1, 2, 5800);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 0, 1, 1, 1, 3, 5801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 0, 1, 1, 1, 4, 5802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 0, 1, 1, 1, 5, 5803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 0, 1, 1, 4, 101, 5804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 0, 1, 1, 8, 102, 5805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 0, 1, 2, 1, 1, 5806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 0, 1, 2, 1, 2, 5807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 0, 1, 2, 1, 3, 5808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 0, 1, 2, 1, 4, 5809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 0, 1, 2, 4, 101, 5810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 0, 1, 2, 8, 102, 5811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 0, 1, 2, 1, 1, 5812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 2, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 0, 1, 2, 1, 2, 5813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 3, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 0, 1, 2, 1, 3, 5814);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 0, 1, 3, 1, 2, 5816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 0, 1, 3, 1, 3, 5817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "においをうまく使った\nイタズラって…\nなにかないかな？", 0, 1, 3, 1, 4, 5818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 0, 1, 3, 1, 5, 5819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 0, 1, 3, 1, 7, 5821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120501, 0, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 0, 1, 3, 1, 8, 5822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 0, 1, 1, 1, 1, 5825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 0, 1, 1, 1, 2, 5826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 0, 1, 1, 1, 3, 5827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 0, 1, 1, 1, 4, 5828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 0, 1, 1, 1, 5, 5829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 0, 1, 1, 4, 101, 5830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 0, 1, 1, 8, 102, 5831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 0, 1, 2, 1, 1, 5832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 0, 1, 2, 1, 2, 5833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 0, 1, 2, 1, 3, 5834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 0, 1, 2, 1, 4, 5835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 0, 1, 2, 4, 101, 5836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 0, 1, 2, 8, 102, 5837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 0, 1, 2, 1, 1, 5838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 2, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 0, 1, 2, 1, 2, 5839);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 3, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 0, 1, 2, 1, 3, 5840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 0, 1, 3, 1, 2, 5842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 0, 1, 3, 1, 3, 5843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "これは冬の花の香り…\n風情があって\nいいじゃないか", 0, 1, 3, 1, 4, 5844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 0, 1, 3, 1, 5, 5845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 0, 1, 3, 1, 7, 5847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120801, 0, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 0, 1, 3, 1, 8, 5848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 0, 1, 1, 1, 1, 5850);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 0, 1, 1, 1, 2, 5851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "この格好も\n慣れてくれば\n動きやすいわ", 0, 1, 1, 1, 3, 5852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 0, 1, 1, 1, 4, 5853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "おせち料理\nネビアの分も\n用意しなくちゃね", 0, 1, 1, 1, 5, 5854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 0, 1, 1, 4, 101, 5855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 0, 1, 1, 8, 102, 5856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 0, 1, 2, 1, 1, 5857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 0, 1, 2, 1, 2, 5858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 0, 1, 2, 1, 3, 5859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 0, 1, 2, 1, 4, 5860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 0, 1, 2, 4, 101, 5861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 0, 1, 2, 8, 102, 5862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 0, 1, 2, 1, 1, 5863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 2, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 0, 1, 2, 1, 2, 5864);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 3, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 0, 1, 2, 1, 3, 5865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 0, 1, 3, 1, 2, 5867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "ふふっ\nかわいいものって\n癒されるわね…", 0, 1, 3, 1, 3, 5868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "…ん\nこれが梅の香り？\n…いい匂いね", 0, 1, 3, 1, 4, 5869);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 0, 1, 3, 1, 5, 5870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 0, 1, 3, 1, 7, 5872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120701, 0, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 0, 1, 3, 1, 8, 5873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 0, 1, 1, 12, 103, 5875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104201, 0, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 0, 1, 2, 12, 103, 5876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 0, 1, 1, 1, 1, 5877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 0, 1, 1, 1, 2, 5878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 0, 1, 1, 1, 3, 5879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 0, 1, 1, 1, 4, 5880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 0, 1, 1, 1, 5, 5881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 0, 1, 1, 4, 101, 5882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 0, 1, 1, 8, 102, 5883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 0, 1, 2, 1, 1, 5884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 0, 1, 2, 1, 2, 5885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 0, 1, 2, 1, 3, 5886);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 0, 1, 2, 1, 4, 5887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 0, 1, 2, 4, 101, 5888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 0, 1, 2, 8, 102, 5889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 0, 1, 2, 1, 1, 5890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 2, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 0, 1, 2, 1, 2, 5891);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 3, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 0, 1, 2, 1, 3, 5892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 0, 1, 3, 1, 2, 5894);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 0, 1, 3, 1, 3, 5895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 0, 1, 3, 1, 4, 5896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 0, 1, 3, 1, 5, 5897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 0, 1, 3, 1, 7, 5899);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120901, 0, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 0, 1, 3, 1, 8, 5900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 0, 1, 1, 1, 1, 5902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 0, 1, 1, 1, 2, 5903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 0, 1, 1, 1, 3, 5904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 0, 1, 1, 1, 4, 5905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 0, 1, 1, 1, 5, 5906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 0, 1, 1, 4, 101, 5907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 0, 1, 1, 8, 102, 5908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 0, 1, 2, 1, 1, 5909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 0, 1, 2, 1, 2, 5910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 0, 1, 2, 1, 3, 5911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "あなたの想いが\n全力全開を超える\n力をくれました！", 0, 1, 2, 1, 4, 5912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 0, 1, 2, 4, 101, 5913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 0, 1, 2, 8, 102, 5914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 0, 1, 2, 1, 1, 5915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 2, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 0, 1, 2, 1, 2, 5916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 3, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 0, 1, 2, 1, 3, 5917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 0, 1, 3, 1, 2, 5919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 0, 1, 3, 1, 3, 5920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 0, 1, 3, 1, 4, 5921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 0, 1, 3, 1, 5, 5922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 0, 1, 3, 1, 7, 5924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121001, 0, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 0, 1, 3, 1, 8, 5925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 0, 1, 1, 1, 1, 5927);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 0, 1, 1, 1, 2, 5928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 0, 1, 1, 1, 3, 5929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 0, 1, 1, 1, 4, 5930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 0, 1, 1, 1, 5, 5931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "少年が\n目を覚ましてくれて\n本当によかった…", 0, 1, 1, 4, 101, 5932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 0, 1, 1, 8, 102, 5933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 0, 1, 2, 1, 1, 5934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 0, 1, 2, 1, 2, 5935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 0, 1, 2, 1, 3, 5936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 0, 1, 2, 1, 4, 5937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 0, 1, 2, 4, 101, 5938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 0, 1, 2, 8, 102, 5939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 0, 1, 2, 1, 1, 5940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 2, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 0, 1, 2, 1, 2, 5941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 3, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 0, 1, 2, 1, 3, 5942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 0, 1, 3, 1, 2, 5944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 0, 1, 3, 1, 3, 5945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 0, 1, 3, 1, 4, 5946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 0, 1, 3, 1, 5, 5947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "食の大切さは\nいつの食事からも\n感じられるね♪", 0, 1, 3, 1, 7, 5949);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121201, 0, "ふむ…面白い！\n今度全巻\n借りにこようっと", 0, 1, 3, 1, 8, 5950);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 0, 1, 1, 1, 1, 5952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 0, 1, 1, 1, 2, 5953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 0, 1, 1, 1, 3, 5954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 0, 1, 1, 1, 4, 5955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 0, 1, 1, 1, 5, 5956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 0, 1, 1, 4, 101, 5957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 0, 1, 1, 8, 102, 5958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 0, 1, 2, 1, 1, 5959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 0, 1, 2, 1, 2, 5960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 0, 1, 2, 1, 3, 5961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 0, 1, 2, 1, 4, 5962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 0, 1, 2, 4, 101, 5963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 0, 1, 2, 8, 102, 5964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 0, 1, 2, 1, 1, 5965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 2, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 0, 1, 2, 1, 2, 5966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 3, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 0, 1, 2, 1, 3, 5967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 0, 1, 3, 1, 2, 5969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 0, 1, 3, 1, 3, 5970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 0, 1, 3, 1, 4, 5971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 0, 1, 3, 1, 5, 5972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 0, 1, 3, 1, 7, 5974);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (116401, 0, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 0, 1, 3, 1, 8, 5975);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 0, 1, 1, 1, 1, 5977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 0, 1, 1, 1, 2, 5978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "あいつのこと、\n全部受け入れるって\n決めたから！", 0, 1, 1, 1, 3, 5979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 0, 1, 1, 1, 4, 5980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 0, 1, 1, 1, 5, 5981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 0, 1, 1, 4, 101, 5982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 0, 1, 1, 8, 102, 5983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 0, 1, 2, 1, 1, 5984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 0, 1, 2, 1, 2, 5985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 0, 1, 2, 1, 3, 5986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 0, 1, 2, 1, 4, 5987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 0, 1, 2, 4, 101, 5988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 0, 1, 2, 8, 102, 5989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 0, 1, 2, 1, 1, 5990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 2, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 0, 1, 2, 1, 2, 5991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 3, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 0, 1, 2, 1, 3, 5992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 0, 1, 3, 1, 2, 5994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 0, 1, 3, 1, 3, 5995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 0, 1, 3, 1, 4, 5996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 0, 1, 3, 1, 5, 5997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 0, 1, 3, 1, 7, 5999);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121101, 0, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 0, 1, 3, 1, 8, 6000);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "お友だち…\nできるかしら…？\nこんなわたしでも…", 0, 1, 1, 1, 1, 6002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 0, 1, 1, 1, 2, 6003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 0, 1, 1, 1, 3, 6004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "～♪", 0, 1, 1, 1, 4, 6005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 0, 1, 1, 1, 5, 6006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 0, 1, 1, 4, 101, 6007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 0, 1, 1, 8, 102, 6008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "ええと…\nお腹、空いてる？\nおねむじゃない？", 0, 1, 2, 1, 1, 6009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 0, 1, 2, 1, 2, 6010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "お喋り…？\nわたしでいいの…？\nうれしい…", 0, 1, 2, 1, 3, 6011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "あなたの好きな歌…\n教えて…？\n練習、するから…", 0, 1, 2, 1, 4, 6012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 0, 1, 2, 4, 101, 6013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "安心、して…\nわたしの歌で、\nあなたを守るから…", 0, 1, 2, 8, 102, 6014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 0, 1, 2, 1, 1, 6015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 2, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 0, 1, 2, 1, 2, 6016);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 3, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 0, 1, 2, 1, 3, 6017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "こんな特等席…\n恐縮、です…", 0, 1, 3, 1, 2, 6019);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "い、今のうちに、\nこの子でお世話の\n練習を…", 0, 1, 3, 1, 3, 6020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "いい香り…\n…つらい気持ちも\n癒してくれる", 0, 1, 3, 1, 4, 6021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "あったかい…\nいつまでも、\nこうしていられそう…", 0, 1, 3, 1, 5, 6022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 0, 1, 3, 1, 7, 6024);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118101, 0, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 0, 1, 3, 1, 8, 6025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 0, 1, 1, 1, 1, 6027);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 0, 1, 1, 1, 2, 6028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 0, 1, 1, 1, 3, 6029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 0, 1, 1, 1, 4, 6030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 0, 1, 1, 1, 5, 6031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 0, 1, 1, 4, 101, 6032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 0, 1, 1, 8, 102, 6033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 0, 1, 2, 1, 1, 6034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 0, 1, 2, 1, 2, 6035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 0, 1, 2, 1, 3, 6036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 0, 1, 2, 1, 4, 6037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 0, 1, 2, 4, 101, 6038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 0, 1, 2, 8, 102, 6039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 0, 1, 2, 1, 1, 6040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 2, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 0, 1, 2, 1, 2, 6041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 3, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 0, 1, 2, 1, 3, 6042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 0, 1, 3, 1, 2, 6044);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "今日も演技の\n練習に付き合ってね♪", 0, 1, 3, 1, 3, 6045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 0, 1, 3, 1, 4, 6046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 0, 1, 3, 1, 5, 6047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 0, 1, 3, 1, 7, 6049);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121301, 0, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 0, 1, 3, 1, 8, 6050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 0, 1, 1, 12, 103, 6052);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107501, 0, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 0, 1, 2, 12, 103, 6053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 0, 1, 1, 12, 103, 6054);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107601, 0, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 0, 1, 2, 12, 103, 6055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 0, 1, 1, 12, 103, 6056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107801, 0, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 0, 1, 2, 12, 103, 6057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 0, 1, 1, 1, 1, 6058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 0, 1, 1, 1, 2, 6059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 0, 1, 1, 1, 3, 6060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 0, 1, 1, 1, 4, 6061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 0, 1, 1, 1, 5, 6062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 0, 1, 1, 4, 101, 6063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 0, 1, 1, 8, 102, 6064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 0, 1, 2, 1, 1, 6065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 0, 1, 2, 1, 2, 6066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 0, 1, 2, 1, 3, 6067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 0, 1, 2, 1, 4, 6068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 0, 1, 2, 4, 101, 6069);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 0, 1, 2, 8, 102, 6070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 0, 1, 2, 1, 1, 6071);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 2, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 0, 1, 2, 1, 2, 6072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 3, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 0, 1, 2, 1, 3, 6073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 0, 1, 3, 1, 2, 6075);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 0, 1, 3, 1, 3, 6076);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 0, 1, 3, 1, 4, 6077);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 0, 1, 3, 1, 5, 6078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 0, 1, 3, 1, 7, 6080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121401, 0, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 0, 1, 3, 1, 8, 6081);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 0, 1, 1, 1, 1, 6083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 0, 1, 1, 1, 2, 6084);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 0, 1, 1, 1, 3, 6085);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 0, 1, 1, 1, 4, 6086);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 0, 1, 1, 1, 5, 6087);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 0, 1, 1, 4, 101, 6088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 0, 1, 1, 8, 102, 6089);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 0, 1, 2, 1, 1, 6090);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 0, 1, 2, 1, 2, 6091);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 0, 1, 2, 1, 3, 6092);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 0, 1, 2, 1, 4, 6093);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 0, 1, 2, 4, 101, 6094);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 0, 1, 2, 8, 102, 6095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 0, 1, 2, 1, 1, 6096);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 2, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 0, 1, 2, 1, 2, 6097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 3, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 0, 1, 2, 1, 3, 6098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 0, 1, 3, 1, 2, 6100);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 0, 1, 3, 1, 3, 6101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 0, 1, 3, 1, 4, 6102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 0, 1, 3, 1, 5, 6103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 0, 1, 3, 1, 7, 6105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121501, 0, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 0, 1, 3, 1, 8, 6106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 0, 1, 1, 12, 103, 6108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104501, 0, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 0, 1, 2, 12, 103, 6109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 0, 1, 1, 1, 1, 6110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 0, 1, 1, 1, 2, 6111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 0, 1, 1, 1, 3, 6112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 0, 1, 1, 1, 4, 6113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 0, 1, 1, 1, 5, 6114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 0, 1, 1, 4, 101, 6115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 0, 1, 1, 8, 102, 6116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 0, 1, 2, 1, 1, 6117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 0, 1, 2, 1, 2, 6118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 0, 1, 2, 1, 3, 6119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "しーっ！\n今、尾行中なんです\n話はあとで…", 0, 1, 2, 1, 4, 6120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 0, 1, 2, 4, 101, 6121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 0, 1, 2, 8, 102, 6122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 0, 1, 2, 1, 1, 6123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 2, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 0, 1, 2, 1, 2, 6124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 3, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 0, 1, 2, 1, 3, 6125);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 0, 1, 3, 1, 2, 6127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "くう…！\nこのかわいさは\n盗めないです…♪", 0, 1, 3, 1, 3, 6128);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 0, 1, 3, 1, 4, 6129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 0, 1, 3, 1, 5, 6130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 0, 1, 3, 1, 7, 6132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121601, 0, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 0, 1, 3, 1, 8, 6133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "あら、サレンさんは\nどこに行かれた\nのかしら？", 0, 1, 1, 1, 1, 6135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 0, 1, 1, 1, 2, 6136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 0, 1, 1, 1, 3, 6137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 0, 1, 1, 1, 4, 6138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 0, 1, 1, 1, 5, 6139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 0, 1, 1, 4, 101, 6140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 0, 1, 1, 8, 102, 6141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "ふふ、ごきげんよう\n私、絶好調でしてよ", 0, 1, 2, 1, 1, 6142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 0, 1, 2, 1, 2, 6143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 0, 1, 2, 1, 3, 6144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 0, 1, 2, 1, 4, 6145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 0, 1, 2, 4, 101, 6146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 0, 1, 2, 8, 102, 6147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 1, "さあ！\n今日も一日\n全力で参りますわ！", 0, 1, 2, 1, 1, 6148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 2, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 0, 1, 2, 1, 2, 6149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 3, "もう少しだけ…\n明日の準備をして\nおかないと…", 0, 1, 2, 1, 3, 6150);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 0, 1, 3, 1, 2, 6152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 0, 1, 3, 1, 3, 6153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 0, 1, 3, 1, 4, 6154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 0, 1, 3, 1, 5, 6155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 0, 1, 3, 1, 7, 6157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121701, 0, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 0, 1, 3, 1, 8, 6158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 0, 1, 1, 1, 1, 6161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 0, 1, 1, 1, 2, 6162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 0, 1, 1, 1, 3, 6163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 0, 1, 1, 1, 4, 6164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 0, 1, 1, 1, 5, 6165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 0, 1, 1, 4, 101, 6166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 0, 1, 1, 8, 102, 6167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 0, 1, 2, 1, 1, 6168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 0, 1, 2, 1, 2, 6169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 0, 1, 2, 1, 3, 6170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 0, 1, 2, 1, 4, 6171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 0, 1, 2, 4, 101, 6172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 0, 1, 2, 8, 102, 6173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 0, 1, 2, 1, 1, 6174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 2, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 0, 1, 2, 1, 2, 6175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 3, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 0, 1, 2, 1, 3, 6176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 0, 1, 3, 1, 2, 6178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 0, 1, 3, 1, 3, 6179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 0, 1, 3, 1, 4, 6180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 0, 1, 3, 1, 5, 6181);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 0, 1, 3, 1, 7, 6183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121801, 0, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 0, 1, 3, 1, 8, 6184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 0, 1, 1, 1, 1, 6187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 0, 1, 1, 1, 2, 6188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 0, 1, 1, 1, 3, 6189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 0, 1, 1, 1, 4, 6190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 0, 1, 1, 1, 5, 6191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 0, 1, 1, 4, 101, 6192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 0, 1, 1, 8, 102, 6193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 0, 1, 2, 1, 1, 6194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 0, 1, 2, 1, 2, 6195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 0, 1, 2, 1, 3, 6196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 0, 1, 2, 1, 4, 6197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "船の上では食材が\n限られるので料理の\nいい練習になります", 0, 1, 2, 4, 101, 6198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 0, 1, 2, 8, 102, 6199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 0, 1, 2, 1, 1, 6200);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 2, "船の上では\n規則正しい生活が\n重要ですから", 0, 1, 2, 1, 2, 6201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 3, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 0, 1, 2, 1, 3, 6202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "海に揺られるような\n心地よさですね\n気が休まります…", 0, 1, 3, 1, 2, 6204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 0, 1, 3, 1, 3, 6205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 0, 1, 3, 1, 4, 6206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "温かい…\n潮風で冷えた体に\n沁みますね", 0, 1, 3, 1, 5, 6207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 0, 1, 3, 1, 7, 6209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122001, 0, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 0, 1, 3, 1, 8, 6210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 0, 1, 1, 12, 103, 6212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102601, 0, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 0, 1, 2, 12, 103, 6213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 0, 1, 1, 1, 1, 6214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 0, 1, 1, 1, 2, 6215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 0, 1, 1, 1, 3, 6216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 0, 1, 1, 1, 4, 6217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 0, 1, 1, 1, 5, 6218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 0, 1, 1, 4, 101, 6219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 0, 1, 1, 8, 102, 6220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 0, 1, 2, 1, 1, 6221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 0, 1, 2, 1, 2, 6222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 0, 1, 2, 1, 3, 6223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 0, 1, 2, 1, 4, 6224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 0, 1, 2, 4, 101, 6225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 0, 1, 2, 8, 102, 6226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 0, 1, 2, 1, 1, 6227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 2, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 0, 1, 2, 1, 2, 6228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 3, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 0, 1, 2, 1, 3, 6229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 0, 1, 3, 1, 2, 6231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 0, 1, 3, 1, 3, 6232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 0, 1, 3, 1, 4, 6233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 0, 1, 3, 1, 5, 6234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 0, 1, 3, 1, 7, 6236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (121901, 0, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 0, 1, 3, 1, 8, 6237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 0, 1, 1, 1, 1, 6239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 0, 1, 1, 1, 2, 6240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 0, 1, 1, 1, 3, 6241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 0, 1, 1, 1, 4, 6242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 0, 1, 1, 1, 5, 6243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 0, 1, 1, 4, 101, 6244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 0, 1, 1, 8, 102, 6245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 0, 1, 2, 1, 1, 6246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 0, 1, 2, 1, 2, 6247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 0, 1, 2, 1, 3, 6248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 0, 1, 2, 1, 4, 6249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 0, 1, 2, 4, 101, 6250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 0, 1, 2, 8, 102, 6251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 0, 1, 2, 1, 1, 6252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 2, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 0, 1, 2, 1, 2, 6253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 3, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 0, 1, 2, 1, 3, 6254);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 0, 1, 3, 1, 2, 6256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 0, 1, 3, 1, 3, 6257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 0, 1, 3, 1, 4, 6258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 0, 1, 3, 1, 5, 6259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 0, 1, 3, 1, 7, 6261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122101, 0, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 0, 1, 3, 1, 8, 6262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 0, 1, 1, 1, 1, 6264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 0, 1, 1, 1, 2, 6265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 0, 1, 1, 1, 3, 6266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 0, 1, 1, 1, 4, 6267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 0, 1, 1, 1, 5, 6268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 0, 1, 1, 4, 101, 6269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 0, 1, 1, 8, 102, 6270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 0, 1, 2, 1, 1, 6271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 0, 1, 2, 1, 2, 6272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 0, 1, 2, 1, 3, 6273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 0, 1, 2, 1, 4, 6274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 0, 1, 2, 4, 101, 6275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 0, 1, 2, 8, 102, 6276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 0, 1, 2, 1, 1, 6277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 2, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 0, 1, 2, 1, 2, 6278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 3, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 0, 1, 2, 1, 3, 6279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 0, 1, 3, 1, 2, 6281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 0, 1, 3, 1, 3, 6282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 0, 1, 3, 1, 4, 6283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 0, 1, 3, 1, 5, 6284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 0, 1, 3, 1, 7, 6286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122201, 0, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 0, 1, 3, 1, 8, 6287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 0, 1, 1, 12, 103, 6289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100701, 0, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 0, 1, 2, 12, 103, 6290);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 0, 1, 1, 1, 1, 6291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 0, 1, 1, 1, 2, 6292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 0, 1, 1, 1, 3, 6293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 0, 1, 1, 1, 4, 6294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 0, 1, 1, 1, 5, 6295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 0, 1, 1, 4, 101, 6296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 0, 1, 1, 8, 102, 6297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 0, 1, 2, 1, 1, 6298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "よしよし！\n今日のけんぞくも\nいい子だね♪", 0, 1, 2, 1, 2, 6299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 0, 1, 2, 1, 3, 6300);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 0, 1, 2, 1, 4, 6301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 0, 1, 2, 4, 101, 6302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 0, 1, 2, 8, 102, 6303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 0, 1, 2, 1, 1, 6304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 2, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 0, 1, 2, 1, 2, 6305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 3, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 0, 1, 2, 1, 3, 6306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "わー！\nふかふかだ！\nきもちぃー♪", 0, 1, 3, 1, 2, 6308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 0, 1, 3, 1, 3, 6309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 0, 1, 3, 1, 4, 6310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 0, 1, 3, 1, 5, 6311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "はむはむ…\nんー！\nおいしー♪", 0, 1, 3, 1, 7, 6313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 0, 1, 3, 1, 8, 6314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "夏といえば\nかき氷だよね\n近くにお店は…", 0, 1, 1, 1, 1, 6316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 0, 1, 1, 1, 2, 6317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 0, 1, 1, 1, 3, 6318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 0, 1, 1, 1, 4, 6319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 0, 1, 1, 1, 5, 6320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "終わらない夏は\nないけれど…\nつい願ってしまうな", 0, 1, 1, 4, 101, 6321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 0, 1, 1, 8, 102, 6322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 0, 1, 2, 1, 1, 6323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 0, 1, 2, 1, 2, 6324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 0, 1, 2, 1, 3, 6325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 0, 1, 2, 1, 4, 6326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 0, 1, 2, 4, 101, 6327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 0, 1, 2, 8, 102, 6328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 0, 1, 2, 1, 1, 6329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 2, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 0, 1, 2, 1, 2, 6330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 3, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 0, 1, 2, 1, 3, 6331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 0, 1, 3, 1, 2, 6333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "肌触りのよさが\nこの格好だと\nよく分かるよ", 0, 1, 3, 1, 3, 6334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 0, 1, 3, 1, 4, 6335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 0, 1, 3, 1, 5, 6336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "夏を楽しみきるには\nエネルギーが\n必要だからね", 0, 1, 3, 1, 7, 6338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 0, 1, 3, 1, 8, 6339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 0, 1, 3, 1, 10, 6341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122501, 0, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 0, 1, 3, 1, 11, 6342);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 0, 1, 1, 1, 1, 6343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 0, 1, 1, 1, 2, 6344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 0, 1, 1, 1, 3, 6345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 0, 1, 1, 1, 4, 6346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 0, 1, 1, 1, 5, 6347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 0, 1, 1, 4, 101, 6348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 0, 1, 1, 8, 102, 6349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 0, 1, 2, 1, 1, 6350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 0, 1, 2, 1, 2, 6351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 0, 1, 2, 1, 3, 6352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 0, 1, 2, 1, 4, 6353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 0, 1, 2, 4, 101, 6354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 0, 1, 2, 8, 102, 6355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 0, 1, 2, 1, 1, 6356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 2, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 0, 1, 2, 1, 2, 6357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 3, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 0, 1, 2, 1, 3, 6358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 0, 1, 3, 1, 2, 6360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 0, 1, 3, 1, 3, 6361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 0, 1, 3, 1, 4, 6362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 0, 1, 3, 1, 5, 6363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 0, 1, 3, 1, 7, 6365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 0, 1, 3, 1, 8, 6366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 0, 1, 3, 1, 10, 6368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122401, 0, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 0, 1, 3, 1, 11, 6369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 0, 1, 1, 12, 103, 6370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101501, 0, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 0, 1, 2, 12, 103, 6371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 0, 1, 1, 1, 1, 6372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 0, 1, 1, 1, 2, 6373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 0, 1, 1, 1, 3, 6374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 0, 1, 1, 1, 4, 6375);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 0, 1, 1, 1, 5, 6376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 0, 1, 1, 4, 101, 6377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 0, 1, 1, 8, 102, 6378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 0, 1, 2, 1, 1, 6379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 0, 1, 2, 1, 2, 6380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 0, 1, 2, 1, 3, 6381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 0, 1, 2, 1, 4, 6382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 0, 1, 2, 4, 101, 6383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 0, 1, 2, 8, 102, 6384);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 0, 1, 2, 1, 1, 6385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 2, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 0, 1, 2, 1, 2, 6386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 3, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 0, 1, 2, 1, 3, 6387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 0, 1, 3, 1, 2, 6389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "かわいい～♪\n日光浴させて\nあげたいな♪", 0, 1, 3, 1, 3, 6390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 0, 1, 3, 1, 4, 6391);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 0, 1, 3, 1, 5, 6392);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 0, 1, 3, 1, 7, 6394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 0, 1, 3, 1, 8, 6395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 0, 1, 3, 1, 10, 6397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122601, 0, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 0, 1, 3, 1, 11, 6398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 0, 1, 1, 1, 1, 6399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 0, 1, 1, 1, 2, 6400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 0, 1, 1, 1, 3, 6401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 0, 1, 1, 1, 4, 6402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 0, 1, 1, 1, 5, 6403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 0, 1, 1, 4, 101, 6404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 0, 1, 1, 8, 102, 6405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 0, 1, 2, 1, 1, 6406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 0, 1, 2, 1, 2, 6407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 0, 1, 2, 1, 3, 6408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 0, 1, 2, 1, 4, 6409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 0, 1, 2, 4, 101, 6410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 0, 1, 2, 8, 102, 6411);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 0, 1, 2, 1, 1, 6412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 2, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 0, 1, 2, 1, 2, 6413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 3, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 0, 1, 2, 1, 3, 6414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 0, 1, 3, 1, 2, 6416);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 0, 1, 3, 1, 3, 6417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 0, 1, 3, 1, 4, 6418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 0, 1, 3, 1, 5, 6419);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 0, 1, 3, 1, 7, 6421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 0, 1, 3, 1, 8, 6422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 0, 1, 3, 1, 10, 6424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122901, 0, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 0, 1, 3, 1, 11, 6425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 0, 1, 1, 1, 1, 6426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 0, 1, 1, 1, 2, 6427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 0, 1, 1, 1, 3, 6428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 0, 1, 1, 1, 4, 6429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 0, 1, 1, 1, 5, 6430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 0, 1, 1, 4, 101, 6431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 0, 1, 1, 8, 102, 6432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "にいちゃん！\n早く海に行く準備\nしなよ～！", 0, 1, 2, 1, 1, 6433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 0, 1, 2, 1, 2, 6434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 0, 1, 2, 1, 3, 6435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 0, 1, 2, 1, 4, 6436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 0, 1, 2, 4, 101, 6437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 0, 1, 2, 8, 102, 6438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 0, 1, 2, 1, 1, 6439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 2, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 0, 1, 2, 1, 2, 6440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 3, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 0, 1, 2, 1, 3, 6441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 0, 1, 3, 1, 2, 6443);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 0, 1, 3, 1, 3, 6444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 0, 1, 3, 1, 4, 6445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 0, 1, 3, 1, 5, 6446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 0, 1, 3, 1, 7, 6448);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 0, 1, 3, 1, 8, 6449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 0, 1, 3, 1, 10, 6451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122801, 0, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 0, 1, 3, 1, 11, 6452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 0, 1, 1, 12, 103, 6453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (102701, 0, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 0, 1, 2, 12, 103, 6454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 0, 1, 1, 1, 1, 6455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 0, 1, 1, 1, 2, 6456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 0, 1, 1, 1, 3, 6457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 0, 1, 1, 1, 4, 6458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 0, 1, 1, 1, 5, 6459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 0, 1, 1, 4, 101, 6460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 0, 1, 1, 8, 102, 6461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 0, 1, 2, 1, 1, 6462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 0, 1, 2, 1, 2, 6463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 0, 1, 2, 1, 3, 6464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 0, 1, 2, 1, 4, 6465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 0, 1, 2, 4, 101, 6466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 0, 1, 2, 8, 102, 6467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 0, 1, 2, 1, 1, 6468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 2, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 0, 1, 2, 1, 2, 6469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 3, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 0, 1, 2, 1, 3, 6470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 0, 1, 3, 1, 2, 6472);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 0, 1, 3, 1, 3, 6473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 0, 1, 3, 1, 4, 6474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "濡れた体を\n乾かしましょう\nタオル、タオル…", 0, 1, 3, 1, 5, 6475);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 0, 1, 3, 1, 7, 6477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 0, 1, 3, 1, 8, 6478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 0, 1, 3, 1, 10, 6480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122701, 0, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 0, 1, 3, 1, 11, 6481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 0, 1, 1, 1, 1, 6482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 0, 1, 1, 1, 2, 6483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 0, 1, 1, 1, 3, 6484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 0, 1, 1, 1, 4, 6485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 0, 1, 1, 1, 5, 6486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 0, 1, 1, 4, 101, 6487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 0, 1, 1, 8, 102, 6488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "おいしい店とか\n知らないの？\n食べに行きましょ", 0, 1, 2, 1, 1, 6489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 0, 1, 2, 1, 2, 6490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 0, 1, 2, 1, 3, 6491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 0, 1, 2, 1, 4, 6492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 0, 1, 2, 4, 101, 6493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "どこまでだって\nあんたと一緒に\nいてやるんだから", 0, 1, 2, 8, 102, 6494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 0, 1, 2, 1, 1, 6495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 2, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 0, 1, 2, 1, 2, 6496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 3, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 0, 1, 2, 1, 3, 6497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 0, 1, 3, 1, 2, 6499);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 0, 1, 3, 1, 3, 6500);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "いい香りね\n塔にもこういうの\n置いておこうかしら", 0, 1, 3, 1, 4, 6501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 0, 1, 3, 1, 5, 6502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 0, 1, 3, 1, 7, 6504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123001, 0, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 0, 1, 3, 1, 8, 6505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 0, 1, 1, 1, 1, 6507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 0, 1, 1, 1, 2, 6508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 0, 1, 1, 1, 3, 6509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 0, 1, 1, 1, 4, 6510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 0, 1, 1, 1, 5, 6511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 0, 1, 1, 4, 101, 6512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 0, 1, 1, 8, 102, 6513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 0, 1, 2, 1, 1, 6514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 0, 1, 2, 1, 2, 6515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 0, 1, 2, 1, 3, 6516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 0, 1, 2, 1, 4, 6517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 0, 1, 2, 4, 101, 6518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 0, 1, 2, 8, 102, 6519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 0, 1, 2, 1, 1, 6520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 2, "支度が終わったら\n今日の冒険に\n出かけまひょか", 0, 1, 2, 1, 2, 6521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 3, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 0, 1, 2, 1, 3, 6522);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 0, 1, 3, 1, 2, 6524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 0, 1, 3, 1, 3, 6525);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 0, 1, 3, 1, 4, 6526);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 0, 1, 3, 1, 5, 6527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "この料理…\n冒険するときに\n作ってみまひょか", 0, 1, 3, 1, 7, 6529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123101, 0, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 0, 1, 3, 1, 8, 6530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 0, 1, 1, 12, 103, 6532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103101, 0, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 0, 1, 2, 12, 103, 6533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 0, 1, 1, 12, 103, 6534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104401, 0, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 0, 1, 2, 12, 103, 6535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 0, 1, 1, 1, 1, 6536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 0, 1, 1, 1, 2, 6537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 0, 1, 1, 1, 3, 6538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 0, 1, 1, 1, 4, 6539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 0, 1, 1, 1, 5, 6540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 0, 1, 1, 4, 101, 6541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 0, 1, 1, 8, 102, 6542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 0, 1, 2, 1, 1, 6543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 0, 1, 2, 1, 2, 6544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 0, 1, 2, 1, 3, 6545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 0, 1, 2, 1, 4, 6546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 0, 1, 2, 4, 101, 6547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 0, 1, 2, 8, 102, 6548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 0, 1, 2, 1, 1, 6549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 2, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 0, 1, 2, 1, 2, 6550);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 3, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 0, 1, 2, 1, 3, 6551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 0, 1, 3, 1, 2, 6553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 0, 1, 3, 1, 3, 6554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 0, 1, 3, 1, 4, 6555);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 0, 1, 3, 1, 5, 6556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 0, 1, 3, 1, 7, 6558);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123201, 0, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 0, 1, 3, 1, 8, 6559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 0, 1, 1, 1, 1, 6561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 0, 1, 1, 1, 2, 6562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 0, 1, 1, 1, 3, 6563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 0, 1, 1, 1, 4, 6564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 0, 1, 1, 1, 5, 6565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 0, 1, 1, 4, 101, 6566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 0, 1, 1, 8, 102, 6567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 0, 1, 2, 1, 1, 6568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "んー？\n今はだるいからパース\nあとでねー", 0, 1, 2, 1, 2, 6569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 0, 1, 2, 1, 3, 6570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 0, 1, 2, 1, 4, 6571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 0, 1, 2, 4, 101, 6572);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 0, 1, 2, 8, 102, 6573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 0, 1, 2, 1, 1, 6574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 2, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 0, 1, 2, 1, 2, 6575);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 3, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 0, 1, 2, 1, 3, 6576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "あーラクー\nいっしょーここで\nキューケーできるわー", 0, 1, 3, 1, 2, 6578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 0, 1, 3, 1, 3, 6579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 0, 1, 3, 1, 4, 6580);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "あったか～♪\n溶けてスライム\n戻りそ～", 0, 1, 3, 1, 5, 6581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 0, 1, 3, 1, 7, 6583);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123301, 0, "わ～なんかムズー\n読めなー\nウケるー", 0, 1, 3, 1, 8, 6584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 0, 1, 1, 1, 1, 6586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 0, 1, 1, 1, 2, 6587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 0, 1, 1, 1, 3, 6588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 0, 1, 1, 1, 4, 6589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 0, 1, 1, 1, 5, 6590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 0, 1, 1, 4, 101, 6591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 0, 1, 1, 8, 102, 6592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 0, 1, 2, 1, 1, 6593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 0, 1, 2, 1, 2, 6594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 0, 1, 2, 1, 3, 6595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 0, 1, 2, 1, 4, 6596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 0, 1, 2, 4, 101, 6597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 0, 1, 2, 8, 102, 6598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 0, 1, 2, 1, 1, 6599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 2, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 0, 1, 2, 1, 2, 6600);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 3, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 0, 1, 2, 1, 3, 6601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 0, 1, 3, 1, 2, 6603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 0, 1, 3, 1, 3, 6604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 0, 1, 3, 1, 4, 6605);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 0, 1, 3, 1, 5, 6606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 0, 1, 3, 1, 7, 6608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 0, 1, 3, 1, 8, 6609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123501, 0, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 0, 1, 3, 1, 12, 6611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 0, 1, 1, 1, 1, 6613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 0, 1, 1, 1, 2, 6614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 0, 1, 1, 1, 3, 6615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 0, 1, 1, 1, 4, 6616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 0, 1, 1, 1, 5, 6617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 0, 1, 1, 4, 101, 6618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 0, 1, 1, 8, 102, 6619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 0, 1, 2, 1, 1, 6620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 0, 1, 2, 1, 2, 6621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 0, 1, 2, 1, 3, 6622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 0, 1, 2, 1, 4, 6623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 0, 1, 2, 4, 101, 6624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 0, 1, 2, 8, 102, 6625);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 0, 1, 2, 1, 1, 6626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 2, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 0, 1, 2, 1, 2, 6627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 3, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 0, 1, 2, 1, 3, 6628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 0, 1, 3, 1, 2, 6630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "…かわいい\nこの子もパレードに\n連れて行きたいな", 0, 1, 3, 1, 3, 6631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 0, 1, 3, 1, 4, 6632);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 0, 1, 3, 1, 5, 6633);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 0, 1, 3, 1, 7, 6635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 0, 1, 3, 1, 8, 6636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123601, 0, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 0, 1, 3, 1, 12, 6638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 0, 1, 1, 12, 103, 6640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103701, 0, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 0, 1, 2, 12, 103, 6641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "やっぱり\nコスプレしてると\nテンションあがる～♪", 0, 1, 1, 1, 1, 6642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 0, 1, 1, 1, 2, 6643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 0, 1, 1, 1, 3, 6644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 0, 1, 1, 1, 4, 6645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 0, 1, 1, 1, 5, 6646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 0, 1, 1, 4, 101, 6647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 0, 1, 1, 8, 102, 6648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 0, 1, 2, 1, 1, 6649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 0, 1, 2, 1, 2, 6650);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 0, 1, 2, 1, 3, 6651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 0, 1, 2, 1, 4, 6652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 0, 1, 2, 4, 101, 6653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 0, 1, 2, 8, 102, 6654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 0, 1, 2, 1, 1, 6655);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 2, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 0, 1, 2, 1, 2, 6656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 3, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 0, 1, 2, 1, 3, 6657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 0, 1, 3, 1, 2, 6659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "いや～\nかわいいものをみると\n癒されますな～", 0, 1, 3, 1, 3, 6660);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 0, 1, 3, 1, 4, 6661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 0, 1, 3, 1, 5, 6662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 0, 1, 3, 1, 7, 6664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 0, 1, 3, 1, 8, 6665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123701, 0, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 0, 1, 3, 1, 12, 6667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 0, 1, 1, 1, 1, 6669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 0, 1, 1, 1, 2, 6670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 0, 1, 1, 1, 3, 6671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 0, 1, 1, 1, 4, 6672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 0, 1, 1, 1, 5, 6673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 0, 1, 1, 4, 101, 6674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 0, 1, 1, 8, 102, 6675);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 0, 1, 2, 1, 1, 6676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 0, 1, 2, 1, 2, 6677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 0, 1, 2, 1, 3, 6678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 0, 1, 2, 1, 4, 6679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 0, 1, 2, 4, 101, 6680);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 0, 1, 2, 8, 102, 6681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 0, 1, 2, 1, 1, 6682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 2, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 0, 1, 2, 1, 2, 6683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 3, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 0, 1, 2, 1, 3, 6684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 0, 1, 3, 1, 2, 6686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 0, 1, 3, 1, 3, 6687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 0, 1, 3, 1, 4, 6688);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 0, 1, 3, 1, 5, 6689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 0, 1, 3, 1, 7, 6691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123801, 0, "ウサギ族は地獄耳？\nワタシとの共通点だな", 0, 1, 3, 1, 8, 6692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 0, 1, 1, 12, 103, 6694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106401, 0, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 0, 1, 2, 12, 103, 6695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 0, 1, 1, 1, 1, 6696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 0, 1, 1, 1, 2, 6697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 0, 1, 1, 1, 3, 6698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 0, 1, 1, 1, 4, 6699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 0, 1, 1, 1, 5, 6700);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 0, 1, 1, 4, 101, 6701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 0, 1, 1, 8, 102, 6702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 0, 1, 2, 1, 1, 6703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 0, 1, 2, 1, 2, 6704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 0, 1, 2, 1, 3, 6705);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 0, 1, 2, 1, 4, 6706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 0, 1, 2, 4, 101, 6707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 0, 1, 2, 8, 102, 6708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 0, 1, 2, 1, 1, 6709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 2, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 0, 1, 2, 1, 2, 6710);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 3, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 0, 1, 2, 1, 3, 6711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 0, 1, 3, 1, 2, 6713);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 0, 1, 3, 1, 3, 6714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 0, 1, 3, 1, 4, 6715);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 0, 1, 3, 1, 5, 6716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 0, 1, 3, 1, 7, 6718);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (123901, 0, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 0, 1, 3, 1, 8, 6719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 0, 1, 1, 1, 1, 6721);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 0, 1, 1, 1, 2, 6722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 0, 1, 1, 1, 3, 6723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 0, 1, 1, 1, 4, 6724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 0, 1, 1, 1, 5, 6725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 0, 1, 1, 4, 101, 6726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 0, 1, 1, 8, 102, 6727);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 0, 1, 2, 1, 1, 6728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 0, 1, 2, 1, 2, 6729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 0, 1, 2, 1, 3, 6730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "視力は権能に\n影響しないわよ\n当たり前じゃない", 0, 1, 2, 1, 4, 6731);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 0, 1, 2, 4, 101, 6732);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 0, 1, 2, 8, 102, 6733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 0, 1, 2, 1, 1, 6734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 2, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 0, 1, 2, 1, 2, 6735);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 3, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 0, 1, 2, 1, 3, 6736);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 0, 1, 3, 1, 2, 6738);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 0, 1, 3, 1, 3, 6739);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 0, 1, 3, 1, 4, 6740);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 0, 1, 3, 1, 5, 6741);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "栄養価はともかく\n味のバランスは\n悪くないわね", 0, 1, 3, 1, 7, 6743);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106901, 0, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 0, 1, 3, 1, 8, 6744);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 0, 1, 1, 1, 1, 6746);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 0, 1, 1, 1, 2, 6747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 0, 1, 1, 1, 3, 6748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 0, 1, 1, 1, 4, 6749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 0, 1, 1, 1, 5, 6750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 0, 1, 1, 4, 101, 6751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 0, 1, 1, 8, 102, 6752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 0, 1, 2, 1, 1, 6753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 0, 1, 2, 1, 2, 6754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 0, 1, 2, 1, 3, 6755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 0, 1, 2, 1, 4, 6756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 0, 1, 2, 4, 101, 6757);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 0, 1, 2, 8, 102, 6758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 0, 1, 2, 1, 1, 6759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 2, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 0, 1, 2, 1, 2, 6760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 3, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 0, 1, 2, 1, 3, 6761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 0, 1, 3, 1, 2, 6763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 0, 1, 3, 1, 3, 6764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 0, 1, 3, 1, 4, 6765);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 0, 1, 3, 1, 5, 6766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 0, 1, 3, 1, 7, 6768);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 0, 1, 3, 1, 8, 6769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 0, 1, 3, 1, 14, 6771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124001, 0, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 0, 1, 3, 1, 15, 6772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 0, 1, 1, 1, 1, 6773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 0, 1, 1, 1, 2, 6774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 0, 1, 1, 1, 3, 6775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 0, 1, 1, 1, 4, 6776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 0, 1, 1, 1, 5, 6777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 0, 1, 1, 4, 101, 6778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 0, 1, 1, 8, 102, 6779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 0, 1, 2, 1, 1, 6780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 0, 1, 2, 1, 2, 6781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 0, 1, 2, 1, 3, 6782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 0, 1, 2, 1, 4, 6783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 0, 1, 2, 4, 101, 6784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 0, 1, 2, 8, 102, 6785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 0, 1, 2, 1, 1, 6786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 2, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 0, 1, 2, 1, 2, 6787);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 3, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 0, 1, 2, 1, 3, 6788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 0, 1, 3, 1, 2, 6790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 0, 1, 3, 1, 3, 6791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 0, 1, 3, 1, 4, 6792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 0, 1, 3, 1, 5, 6793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "お腹いっぱい\n食べられるのも\n幸せなことよね", 0, 1, 3, 1, 7, 6795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 0, 1, 3, 1, 8, 6796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 0, 1, 3, 1, 14, 6798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124101, 0, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 0, 1, 3, 1, 15, 6799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 0, 1, 1, 12, 103, 6800);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105101, 0, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 0, 1, 2, 12, 103, 6801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 0, 1, 1, 1, 1, 6802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 0, 1, 1, 1, 2, 6803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 0, 1, 1, 1, 3, 6804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 0, 1, 1, 1, 4, 6805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 0, 1, 1, 1, 5, 6806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 0, 1, 1, 4, 101, 6807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 0, 1, 1, 8, 102, 6808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 0, 1, 2, 1, 1, 6809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 0, 1, 2, 1, 2, 6810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 0, 1, 2, 1, 3, 6811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 0, 1, 2, 1, 4, 6812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 0, 1, 2, 4, 101, 6813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 0, 1, 2, 8, 102, 6814);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 0, 1, 2, 1, 1, 6815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 2, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 0, 1, 2, 1, 2, 6816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 3, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 0, 1, 2, 1, 3, 6817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 0, 1, 3, 1, 2, 6819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 0, 1, 3, 1, 3, 6820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 0, 1, 3, 1, 4, 6821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 0, 1, 3, 1, 5, 6822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 0, 1, 3, 1, 7, 6824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 0, 1, 3, 1, 8, 6825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "わっ！\n…………いや\n驚いてなどいない", 0, 1, 3, 1, 14, 6827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124201, 0, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 0, 1, 3, 1, 15, 6828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 0, 1, 1, 1, 1, 6829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 0, 1, 1, 1, 2, 6830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 0, 1, 1, 1, 3, 6831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 0, 1, 1, 1, 4, 6832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "グレアも誘って\nランドソルをもっと\n探索したい！", 0, 1, 1, 1, 5, 6833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "私たちも\nあの人を見習って\nバイトとかしよう！", 0, 1, 1, 4, 101, 6834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "いつかあの人を\n私たちの学院に\n招待したいな！", 0, 1, 1, 8, 102, 6835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 0, 1, 2, 1, 1, 6836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "これからなにするの？\n私たちもついて行って\nいいかな！？", 0, 1, 2, 1, 2, 6837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 0, 1, 2, 1, 3, 6838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 0, 1, 2, 1, 4, 6839);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 0, 1, 2, 4, 101, 6840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "あなたは\nグレアと私の友達！\nこれからもずっと！", 0, 1, 2, 8, 102, 6841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 0, 1, 2, 1, 1, 6842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 2, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 0, 1, 2, 1, 2, 6843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 3, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 0, 1, 2, 1, 3, 6844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 0, 1, 3, 1, 2, 6846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 0, 1, 3, 1, 3, 6847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 0, 1, 3, 1, 4, 6848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 0, 1, 3, 1, 5, 6849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 0, 1, 3, 1, 7, 6851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124301, 0, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 0, 1, 3, 1, 8, 6852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 0, 1, 1, 1, 1, 6855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 0, 1, 1, 1, 2, 6856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 0, 1, 1, 1, 3, 6857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 0, 1, 1, 1, 4, 6858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 0, 1, 1, 1, 5, 6859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 0, 1, 1, 4, 101, 6860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 0, 1, 1, 8, 102, 6861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 0, 1, 2, 1, 1, 6862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 0, 1, 2, 1, 2, 6863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 0, 1, 2, 1, 3, 6864);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 0, 1, 2, 1, 4, 6865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 0, 1, 2, 4, 101, 6866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 0, 1, 2, 8, 102, 6867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 0, 1, 2, 1, 1, 6868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 2, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 0, 1, 2, 1, 2, 6869);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 3, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 0, 1, 2, 1, 3, 6870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 0, 1, 3, 1, 2, 6872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "これ…\nアンに見せたら\n喜ぶだろうな…", 0, 1, 3, 1, 3, 6873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "いい香り…\nアンの感想も\n聞いてみたいな", 0, 1, 3, 1, 4, 6874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 0, 1, 3, 1, 5, 6875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 0, 1, 3, 1, 7, 6877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124401, 0, "アンがおススメ\nしてくれた本は\nこれかな…？", 0, 1, 3, 1, 8, 6878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 0, 1, 1, 1, 1, 6881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 0, 1, 1, 1, 2, 6882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 0, 1, 1, 1, 3, 6883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 0, 1, 1, 1, 4, 6884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 0, 1, 1, 1, 5, 6885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 0, 1, 1, 4, 101, 6886);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 0, 1, 1, 8, 102, 6887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 0, 1, 2, 1, 1, 6888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 0, 1, 2, 1, 2, 6889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 0, 1, 2, 1, 3, 6890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 0, 1, 2, 1, 4, 6891);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 0, 1, 2, 4, 101, 6892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 0, 1, 2, 8, 102, 6893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 0, 1, 2, 1, 1, 6894);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 2, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 0, 1, 2, 1, 2, 6895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 3, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 0, 1, 2, 1, 3, 6896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 0, 1, 3, 1, 2, 6898);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 0, 1, 3, 1, 3, 6899);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 0, 1, 3, 1, 4, 6900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 0, 1, 3, 1, 5, 6901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 0, 1, 3, 1, 7, 6903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124701, 0, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 0, 1, 3, 1, 8, 6904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 0, 1, 1, 1, 1, 6906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 0, 1, 1, 1, 2, 6907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 0, 1, 1, 1, 3, 6908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 0, 1, 1, 1, 4, 6909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 0, 1, 1, 1, 5, 6910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 0, 1, 1, 4, 101, 6911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 0, 1, 1, 8, 102, 6912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 0, 1, 2, 1, 1, 6913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 0, 1, 2, 1, 2, 6914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 0, 1, 2, 1, 3, 6915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 0, 1, 2, 1, 4, 6916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 0, 1, 2, 4, 101, 6917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 0, 1, 2, 8, 102, 6918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 0, 1, 2, 1, 1, 6919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 2, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 0, 1, 2, 1, 2, 6920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 3, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 0, 1, 2, 1, 3, 6921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 0, 1, 3, 1, 2, 6923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 0, 1, 3, 1, 3, 6924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 0, 1, 3, 1, 4, 6925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 0, 1, 3, 1, 5, 6926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 0, 1, 3, 1, 7, 6928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124601, 0, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 0, 1, 3, 1, 8, 6929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 0, 1, 1, 12, 103, 6931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104701, 0, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 0, 1, 2, 12, 103, 6932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "お正月だからって\nギャングのボスは\n油断できないよ～？", 0, 1, 1, 1, 1, 6933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 0, 1, 1, 1, 2, 6934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 0, 1, 1, 1, 3, 6935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 0, 1, 1, 1, 4, 6936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 0, 1, 1, 1, 5, 6937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 0, 1, 1, 4, 101, 6938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 0, 1, 1, 8, 102, 6939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 0, 1, 2, 1, 1, 6940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 0, 1, 2, 1, 2, 6941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 0, 1, 2, 1, 3, 6942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 0, 1, 2, 1, 4, 6943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 0, 1, 2, 4, 101, 6944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 0, 1, 2, 8, 102, 6945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 0, 1, 2, 1, 1, 6946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 2, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 0, 1, 2, 1, 2, 6947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 3, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 0, 1, 2, 1, 3, 6948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 0, 1, 3, 1, 2, 6950);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 0, 1, 3, 1, 3, 6951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "いい香りだけど\n着物に移らないように\n気をつけないとね", 0, 1, 3, 1, 4, 6952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 0, 1, 3, 1, 5, 6953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 0, 1, 3, 1, 7, 6955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124501, 0, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 0, 1, 3, 1, 8, 6956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 0, 1, 1, 1, 1, 6958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 0, 1, 1, 1, 2, 6959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 0, 1, 1, 1, 3, 6960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 0, 1, 1, 1, 4, 6961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 0, 1, 1, 1, 5, 6962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 0, 1, 1, 4, 101, 6963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 0, 1, 1, 8, 102, 6964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 0, 1, 2, 1, 1, 6965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 0, 1, 2, 1, 2, 6966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 0, 1, 2, 1, 3, 6967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 0, 1, 2, 1, 4, 6968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 0, 1, 2, 4, 101, 6969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 0, 1, 2, 8, 102, 6970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 0, 1, 2, 1, 1, 6971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 2, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 0, 1, 2, 1, 2, 6972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 3, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 0, 1, 2, 1, 3, 6973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 0, 1, 3, 1, 2, 6975);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 0, 1, 3, 1, 3, 6976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 0, 1, 3, 1, 4, 6977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 0, 1, 3, 1, 5, 6978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 0, 1, 3, 1, 7, 6980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124801, 0, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 0, 1, 3, 1, 8, 6981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 0, 1, 1, 1, 1, 6983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 0, 1, 1, 1, 2, 6984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 0, 1, 1, 1, 3, 6985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 0, 1, 1, 1, 4, 6986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 0, 1, 1, 1, 5, 6987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 0, 1, 1, 4, 101, 6988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 0, 1, 1, 8, 102, 6989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 0, 1, 2, 1, 1, 6990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 0, 1, 2, 1, 2, 6991);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 0, 1, 2, 1, 3, 6992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 0, 1, 2, 1, 4, 6993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 0, 1, 2, 4, 101, 6994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 0, 1, 2, 8, 102, 6995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 0, 1, 2, 1, 1, 6996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 2, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 0, 1, 2, 1, 2, 6997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 3, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 0, 1, 2, 1, 3, 6998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 0, 1, 3, 1, 2, 7000);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 0, 1, 3, 1, 3, 7001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "嗅いだことない\n香りだな\nなかなか気に入った！", 0, 1, 3, 1, 4, 7002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 0, 1, 3, 1, 5, 7003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 0, 1, 3, 1, 7, 7005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (124901, 0, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 0, 1, 3, 1, 8, 7006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 0, 1, 1, 12, 103, 7008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105501, 0, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 0, 1, 2, 12, 103, 7009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 0, 1, 1, 1, 1, 7010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 0, 1, 1, 1, 2, 7011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 0, 1, 1, 1, 3, 7012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 0, 1, 1, 1, 4, 7013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 0, 1, 1, 1, 5, 7014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 0, 1, 1, 4, 101, 7015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 0, 1, 1, 8, 102, 7016);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 0, 1, 2, 1, 1, 7017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 0, 1, 2, 1, 2, 7018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 0, 1, 2, 1, 3, 7019);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 0, 1, 2, 1, 4, 7020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 0, 1, 2, 4, 101, 7021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 0, 1, 2, 8, 102, 7022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 0, 1, 2, 1, 1, 7023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 2, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 0, 1, 2, 1, 2, 7024);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 3, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 0, 1, 2, 1, 3, 7025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 0, 1, 3, 1, 2, 7027);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 0, 1, 3, 1, 3, 7028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 0, 1, 3, 1, 4, 7029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 0, 1, 3, 1, 5, 7030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 0, 1, 3, 1, 7, 7032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125001, 0, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 0, 1, 3, 1, 8, 7033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 0, 1, 1, 1, 1, 7035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 0, 1, 1, 1, 2, 7036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 0, 1, 1, 1, 3, 7037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 0, 1, 1, 1, 4, 7038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "オトモダチでも\n作ってみますかー\nくすっ★", 0, 1, 1, 1, 5, 7039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "退屈ですねー\n騎士さん、構って\nくれないかなー", 0, 1, 1, 4, 101, 7040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 0, 1, 1, 8, 102, 7041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 0, 1, 2, 1, 1, 7042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "お出かけします？\nそれともおうちで\nまったりしますー？", 0, 1, 2, 1, 2, 7043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 0, 1, 2, 1, 3, 7044);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 0, 1, 2, 1, 4, 7045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 0, 1, 2, 4, 101, 7046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 0, 1, 2, 8, 102, 7047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 0, 1, 2, 1, 1, 7048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 2, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 0, 1, 2, 1, 2, 7049);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 3, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 0, 1, 2, 1, 3, 7050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 0, 1, 3, 1, 2, 7052);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 0, 1, 3, 1, 3, 7053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "くんくん★\nうーん…\nたぶん素敵な香り♪", 0, 1, 3, 1, 4, 7054);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "あったかーい★\n心も体も\nぽっかぽかですねー★", 0, 1, 3, 1, 5, 7055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 0, 1, 3, 1, 7, 7057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118201, 0, "へー★\nほほー★\nふんふーん★", 0, 1, 3, 1, 8, 7058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 0, 1, 1, 1, 1, 7060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 0, 1, 1, 1, 2, 7061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 0, 1, 1, 1, 3, 7062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 0, 1, 1, 1, 4, 7063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 0, 1, 1, 1, 5, 7064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 0, 1, 1, 4, 101, 7065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 0, 1, 1, 8, 102, 7066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 0, 1, 2, 1, 1, 7067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 0, 1, 2, 1, 2, 7068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 0, 1, 2, 1, 3, 7069);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 0, 1, 2, 1, 4, 7070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 0, 1, 2, 4, 101, 7071);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 0, 1, 2, 8, 102, 7072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 0, 1, 2, 1, 1, 7073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 2, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 0, 1, 2, 1, 2, 7074);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 3, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 0, 1, 2, 1, 3, 7075);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 0, 1, 3, 1, 2, 7077);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 0, 1, 3, 1, 3, 7078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 0, 1, 3, 1, 4, 7079);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 0, 1, 3, 1, 5, 7080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 0, 1, 3, 1, 7, 7082);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125101, 0, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 0, 1, 3, 1, 8, 7083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 0, 1, 1, 12, 103, 7085);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (107101, 0, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 0, 1, 2, 12, 103, 7086);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 0, 1, 1, 1, 1, 7087);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 0, 1, 1, 1, 2, 7088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 0, 1, 1, 1, 3, 7089);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 0, 1, 1, 1, 4, 7090);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 0, 1, 1, 1, 5, 7091);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 0, 1, 1, 4, 101, 7092);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 0, 1, 1, 8, 102, 7093);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 0, 1, 2, 1, 1, 7094);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 0, 1, 2, 1, 2, 7095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 0, 1, 2, 1, 3, 7096);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 0, 1, 2, 1, 4, 7097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 0, 1, 2, 4, 101, 7098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 0, 1, 2, 8, 102, 7099);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 0, 1, 2, 1, 1, 7100);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 2, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 0, 1, 2, 1, 2, 7101);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 3, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 0, 1, 2, 1, 3, 7102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 0, 1, 3, 1, 2, 7104);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 0, 1, 3, 1, 3, 7105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 0, 1, 3, 1, 4, 7106);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 0, 1, 3, 1, 5, 7107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 0, 1, 3, 1, 7, 7109);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125201, 0, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 0, 1, 3, 1, 8, 7110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 0, 1, 1, 1, 1, 7112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 0, 1, 1, 1, 2, 7113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 0, 1, 1, 1, 3, 7114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 0, 1, 1, 1, 4, 7115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 0, 1, 1, 1, 5, 7116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 0, 1, 1, 4, 101, 7117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 0, 1, 1, 8, 102, 7118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 0, 1, 2, 1, 1, 7119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 0, 1, 2, 1, 2, 7120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 0, 1, 2, 1, 3, 7121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 0, 1, 2, 1, 4, 7122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 0, 1, 2, 4, 101, 7123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 0, 1, 2, 8, 102, 7124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 0, 1, 2, 1, 1, 7125);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 2, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 0, 1, 2, 1, 2, 7126);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 3, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 0, 1, 2, 1, 3, 7127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 0, 1, 3, 1, 2, 7129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 0, 1, 3, 1, 3, 7130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 0, 1, 3, 1, 4, 7131);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 0, 1, 3, 1, 5, 7132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 0, 1, 3, 1, 7, 7134);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (120001, 0, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 0, 1, 3, 1, 8, 7135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 0, 1, 1, 1, 1, 7137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 0, 1, 1, 1, 2, 7138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 0, 1, 1, 1, 3, 7139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 0, 1, 1, 1, 4, 7140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 0, 1, 1, 1, 5, 7141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 0, 1, 1, 4, 101, 7142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 0, 1, 1, 8, 102, 7143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 0, 1, 2, 1, 1, 7144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 0, 1, 2, 1, 2, 7145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 0, 1, 2, 1, 3, 7146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 0, 1, 2, 1, 4, 7147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 0, 1, 2, 4, 101, 7148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 0, 1, 2, 8, 102, 7149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 0, 1, 2, 1, 1, 7150);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 2, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 0, 1, 2, 1, 2, 7151);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 3, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 0, 1, 2, 1, 3, 7152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 0, 1, 3, 1, 2, 7154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 0, 1, 3, 1, 3, 7155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 0, 1, 3, 1, 4, 7156);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 0, 1, 3, 1, 5, 7157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 0, 1, 3, 1, 7, 7159);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125301, 0, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 0, 1, 3, 1, 8, 7160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 0, 1, 1, 12, 103, 7162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100901, 0, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 0, 1, 2, 12, 103, 7163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 0, 1, 1, 1, 1, 7164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "ふふ…\nこれがレンジャーの\n制服ですか", 0, 1, 1, 1, 2, 7165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "そろそろ\nパトロールの\n時間だ…", 0, 1, 1, 1, 3, 7166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "魔物について\nもっと知りたいな\n本を探しに行こう", 0, 1, 1, 1, 4, 7167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "レンジャーになって\n体調を崩す日が\n減った気がします…", 0, 1, 1, 1, 5, 7168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 0, 1, 1, 4, 101, 7169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "こんな私にも\nできることはある…\nそう信じています", 0, 1, 1, 8, 102, 7170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 0, 1, 2, 1, 1, 7171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 0, 1, 2, 1, 2, 7172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 0, 1, 2, 1, 3, 7173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "ニャットちゃん\nあなたにも\n懐いてきましたね", 0, 1, 2, 1, 4, 7174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 0, 1, 2, 4, 101, 7175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 0, 1, 2, 8, 102, 7176);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 0, 1, 2, 1, 1, 7177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 2, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 0, 1, 2, 1, 2, 7178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 3, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 0, 1, 2, 1, 3, 7179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 0, 1, 3, 1, 2, 7181);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 0, 1, 3, 1, 3, 7182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "森の香りがします…\n今日はよく\n眠れそうです…", 0, 1, 3, 1, 4, 7183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "ニャットちゃんと\n同じぐらい\n暖かいです…", 0, 1, 3, 1, 5, 7184);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "レンジャーの仕事を\nしていると\nお腹が空きます", 0, 1, 3, 1, 7, 7186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125401, 0, "どんなに忙しくても\n読書だけは\nやめられませんね", 0, 1, 3, 1, 8, 7187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 0, 1, 1, 1, 1, 7189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 0, 1, 1, 1, 2, 7190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 0, 1, 1, 1, 3, 7191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 0, 1, 1, 1, 4, 7192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 0, 1, 1, 1, 5, 7193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 0, 1, 1, 4, 101, 7194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 0, 1, 1, 8, 102, 7195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 0, 1, 2, 1, 1, 7196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 0, 1, 2, 1, 2, 7197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 0, 1, 2, 1, 3, 7198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 0, 1, 2, 1, 4, 7199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 0, 1, 2, 4, 101, 7200);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 0, 1, 2, 8, 102, 7201);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 0, 1, 2, 1, 1, 7202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 2, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 0, 1, 2, 1, 2, 7203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 3, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 0, 1, 2, 1, 3, 7204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 0, 1, 3, 1, 2, 7206);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "と、飛びかかっては\nこない…ですよね…？", 0, 1, 3, 1, 3, 7207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 0, 1, 3, 1, 4, 7208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 0, 1, 3, 1, 5, 7209);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "こんなに食べていたら\nはしたない…\nでしょうか？", 0, 1, 3, 1, 7, 7211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125801, 0, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 0, 1, 3, 1, 8, 7212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 0, 1, 1, 1, 1, 7214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 0, 1, 1, 1, 2, 7215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 0, 1, 1, 1, 3, 7216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 0, 1, 1, 1, 4, 7217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 0, 1, 1, 1, 5, 7218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 0, 1, 1, 4, 101, 7219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 0, 1, 1, 8, 102, 7220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 0, 1, 2, 1, 1, 7221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 0, 1, 2, 1, 2, 7222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 0, 1, 2, 1, 3, 7223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 0, 1, 2, 1, 4, 7224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 0, 1, 2, 4, 101, 7225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 0, 1, 2, 8, 102, 7226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 0, 1, 2, 1, 1, 7227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 2, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 0, 1, 2, 1, 2, 7228);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 3, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 0, 1, 2, 1, 3, 7229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 0, 1, 3, 1, 2, 7231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 0, 1, 3, 1, 3, 7232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "いい匂い…\nそれになんだか\n集中力が増したかも", 0, 1, 3, 1, 4, 7233);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 0, 1, 3, 1, 5, 7234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 0, 1, 3, 1, 7, 7236);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125501, 0, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 0, 1, 3, 1, 8, 7237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 0, 1, 1, 1, 1, 7239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 0, 1, 1, 1, 2, 7240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 0, 1, 1, 1, 3, 7241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "せっかくですから\nより良い素材を\n自分で集めたいですね", 0, 1, 1, 1, 4, 7242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "このポーションが\nみなさんの役に立つと\nうれしいですね", 0, 1, 1, 1, 5, 7243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 0, 1, 1, 4, 101, 7244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 0, 1, 1, 8, 102, 7245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 0, 1, 2, 1, 1, 7246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 0, 1, 2, 1, 2, 7247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "依頼で気になる\nところがあれば\n何でもご確認ください", 0, 1, 2, 1, 3, 7248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 0, 1, 2, 1, 4, 7249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 0, 1, 2, 4, 101, 7250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 0, 1, 2, 8, 102, 7251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 0, 1, 2, 1, 1, 7252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 2, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 0, 1, 2, 1, 2, 7253);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 3, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 0, 1, 2, 1, 3, 7254);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 0, 1, 3, 1, 2, 7256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 0, 1, 3, 1, 3, 7257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 0, 1, 3, 1, 4, 7258);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "この火力…\n錬金術の調合に\n使えそうですね", 0, 1, 3, 1, 5, 7259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 0, 1, 3, 1, 7, 7261);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125701, 0, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 0, 1, 3, 1, 8, 7262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 0, 1, 1, 12, 103, 7264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106101, 0, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 0, 1, 2, 12, 103, 7265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "みんなー！\nギルドハウスで\nぼくと握手！", 0, 1, 1, 1, 1, 7266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 0, 1, 1, 1, 2, 7267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 0, 1, 1, 1, 3, 7268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 0, 1, 1, 1, 4, 7269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 0, 1, 1, 1, 5, 7270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 0, 1, 1, 4, 101, 7271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 0, 1, 1, 8, 102, 7272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 0, 1, 2, 1, 1, 7273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 0, 1, 2, 1, 2, 7274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 0, 1, 2, 1, 3, 7275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 0, 1, 2, 1, 4, 7276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 0, 1, 2, 4, 101, 7277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 0, 1, 2, 8, 102, 7278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 0, 1, 2, 1, 1, 7279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 2, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 0, 1, 2, 1, 2, 7280);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 3, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 0, 1, 2, 1, 3, 7281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 0, 1, 3, 1, 2, 7283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 0, 1, 3, 1, 3, 7284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 0, 1, 3, 1, 4, 7285);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 0, 1, 3, 1, 5, 7286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 0, 1, 3, 1, 7, 7288);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (125601, 0, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 0, 1, 3, 1, 8, 7289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 0, 1, 1, 1, 1, 7291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 0, 1, 1, 1, 2, 7292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 0, 1, 1, 1, 3, 7293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 0, 1, 1, 1, 4, 7294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 0, 1, 1, 1, 5, 7295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 0, 1, 1, 4, 101, 7296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 0, 1, 1, 8, 102, 7297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 0, 1, 2, 1, 1, 7298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 0, 1, 2, 1, 2, 7299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 0, 1, 2, 1, 3, 7300);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 0, 1, 2, 1, 4, 7301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 0, 1, 2, 4, 101, 7302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 0, 1, 2, 8, 102, 7303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 0, 1, 2, 1, 1, 7304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 2, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 0, 1, 2, 1, 2, 7305);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 3, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 0, 1, 2, 1, 3, 7306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 0, 1, 3, 1, 2, 7308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 0, 1, 3, 1, 3, 7309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 0, 1, 3, 1, 4, 7310);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 0, 1, 3, 1, 5, 7311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 0, 1, 3, 1, 7, 7313);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126201, 0, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 0, 1, 3, 1, 8, 7314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 0, 1, 1, 12, 103, 7316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100801, 0, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 0, 1, 2, 12, 103, 7317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 0, 1, 1, 1, 1, 7318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 0, 1, 1, 1, 2, 7319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 0, 1, 1, 1, 3, 7320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 0, 1, 1, 1, 4, 7321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 0, 1, 1, 1, 5, 7322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 0, 1, 1, 4, 101, 7323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 0, 1, 1, 8, 102, 7324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 0, 1, 2, 1, 1, 7325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 0, 1, 2, 1, 2, 7326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 0, 1, 2, 1, 3, 7327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 0, 1, 2, 1, 4, 7328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 0, 1, 2, 4, 101, 7329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 0, 1, 2, 8, 102, 7330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 0, 1, 2, 1, 1, 7331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 2, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 0, 1, 2, 1, 2, 7332);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 3, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 0, 1, 2, 1, 3, 7333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 0, 1, 3, 1, 2, 7335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 0, 1, 3, 1, 3, 7336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 0, 1, 3, 1, 4, 7337);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 0, 1, 3, 1, 5, 7338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 0, 1, 3, 1, 7, 7340);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126301, 0, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 0, 1, 3, 1, 8, 7341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 0, 1, 1, 1, 1, 7343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 0, 1, 1, 1, 2, 7344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 0, 1, 1, 1, 3, 7345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 0, 1, 1, 1, 4, 7346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 0, 1, 1, 1, 5, 7347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 0, 1, 1, 4, 101, 7348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 0, 1, 1, 8, 102, 7349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 0, 1, 2, 1, 1, 7350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 0, 1, 2, 1, 2, 7351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 0, 1, 2, 1, 3, 7352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 0, 1, 2, 1, 4, 7353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 0, 1, 2, 4, 101, 7354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 0, 1, 2, 8, 102, 7355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 0, 1, 2, 1, 1, 7356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 2, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 0, 1, 2, 1, 2, 7357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 3, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 0, 1, 2, 1, 3, 7358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 0, 1, 3, 1, 2, 7360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 0, 1, 3, 1, 3, 7361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 0, 1, 3, 1, 4, 7362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 0, 1, 3, 1, 5, 7363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 0, 1, 3, 1, 7, 7365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126401, 0, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 0, 1, 3, 1, 8, 7366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 0, 1, 1, 1, 1, 7368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 0, 1, 1, 1, 2, 7369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 0, 1, 1, 1, 3, 7370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 0, 1, 1, 1, 4, 7371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 0, 1, 1, 1, 5, 7372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 0, 1, 1, 4, 101, 7373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 0, 1, 1, 8, 102, 7374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 0, 1, 2, 1, 1, 7375);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 0, 1, 2, 1, 2, 7376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 0, 1, 2, 1, 3, 7377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 0, 1, 2, 1, 4, 7378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 0, 1, 2, 4, 101, 7379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 0, 1, 2, 8, 102, 7380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 0, 1, 2, 1, 1, 7381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 2, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 0, 1, 2, 1, 2, 7382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 3, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 0, 1, 2, 1, 3, 7383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 0, 1, 3, 1, 2, 7385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 0, 1, 3, 1, 3, 7386);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 0, 1, 3, 1, 4, 7387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 0, 1, 3, 1, 5, 7388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 0, 1, 3, 1, 7, 7390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "夏のトレンドを\n押さえておくのも\n大事ですわね", 0, 1, 3, 1, 8, 7391);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 0, 1, 3, 1, 10, 7393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126701, 0, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 0, 1, 3, 1, 11, 7394);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 0, 1, 1, 1, 1, 7395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 0, 1, 1, 1, 2, 7396);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 0, 1, 1, 1, 3, 7397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 0, 1, 1, 1, 4, 7398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 0, 1, 1, 1, 5, 7399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 0, 1, 1, 4, 101, 7400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 0, 1, 1, 8, 102, 7401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 0, 1, 2, 1, 1, 7402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 0, 1, 2, 1, 2, 7403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 0, 1, 2, 1, 3, 7404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 0, 1, 2, 1, 4, 7405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 0, 1, 2, 4, 101, 7406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 0, 1, 2, 8, 102, 7407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 0, 1, 2, 1, 1, 7408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 2, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 0, 1, 2, 1, 2, 7409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 3, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 0, 1, 2, 1, 3, 7410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 0, 1, 3, 1, 2, 7412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "なかなか気に入った…\nというのは\n誰にも秘密です", 0, 1, 3, 1, 3, 7413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 0, 1, 3, 1, 4, 7414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 0, 1, 3, 1, 5, 7415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "私がどのような味が\n好きなのかは…\n当然秘密です", 0, 1, 3, 1, 7, 7417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 0, 1, 3, 1, 8, 7418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 0, 1, 3, 1, 10, 7420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126601, 0, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 0, 1, 3, 1, 11, 7421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 0, 1, 1, 12, 103, 7422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105401, 0, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 0, 1, 2, 12, 103, 7423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 0, 1, 1, 1, 1, 7424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 0, 1, 1, 1, 2, 7425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 0, 1, 1, 1, 3, 7426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 0, 1, 1, 1, 4, 7427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 0, 1, 1, 1, 5, 7428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 0, 1, 1, 4, 101, 7429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 0, 1, 1, 8, 102, 7430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 0, 1, 2, 1, 1, 7431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 0, 1, 2, 1, 2, 7432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 0, 1, 2, 1, 3, 7433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 0, 1, 2, 1, 4, 7434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 0, 1, 2, 4, 101, 7435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 0, 1, 2, 8, 102, 7436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 0, 1, 2, 1, 1, 7437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 2, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 0, 1, 2, 1, 2, 7438);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 3, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 0, 1, 2, 1, 3, 7439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 0, 1, 3, 1, 2, 7441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 0, 1, 3, 1, 3, 7442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 0, 1, 3, 1, 4, 7443);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 0, 1, 3, 1, 5, 7444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 0, 1, 3, 1, 7, 7446);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "試験に使った参考書、\nたまには読み返して\nおかないとね", 0, 1, 3, 1, 8, 7447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "ふふ♪\nいい手触りね～", 0, 1, 3, 1, 10, 7449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126801, 0, "…はふう～…\nゴロゴロするのって\n最高ね～", 0, 1, 3, 1, 11, 7450);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 0, 1, 1, 1, 1, 7451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 0, 1, 1, 1, 2, 7452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 0, 1, 1, 1, 3, 7453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 0, 1, 1, 1, 4, 7454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 0, 1, 1, 1, 5, 7455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 0, 1, 1, 4, 101, 7456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 0, 1, 1, 8, 102, 7457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 0, 1, 2, 1, 1, 7458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 0, 1, 2, 1, 2, 7459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 0, 1, 2, 1, 3, 7460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 0, 1, 2, 1, 4, 7461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 0, 1, 2, 4, 101, 7462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 0, 1, 2, 8, 102, 7463);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 0, 1, 2, 1, 1, 7464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 2, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 0, 1, 2, 1, 2, 7465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 3, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 0, 1, 2, 1, 3, 7466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "素肌にふわふわが…\n気持ちいい…♪", 0, 1, 3, 1, 2, 7468);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "うふふ…\n一緒に海…行く？", 0, 1, 3, 1, 3, 7469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 0, 1, 3, 1, 4, 7470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 0, 1, 3, 1, 5, 7471);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 0, 1, 3, 1, 7, 7473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 0, 1, 3, 1, 8, 7474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "これで合ってる…？\n遊び方わからなくて…", 0, 1, 3, 1, 10, 7476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126901, 0, "ふわふわ…♪\n海の底にいるみたい…", 0, 1, 3, 1, 11, 7477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 0, 1, 1, 1, 1, 7478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 0, 1, 1, 1, 2, 7479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 0, 1, 1, 1, 3, 7480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 0, 1, 1, 1, 4, 7481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 0, 1, 1, 1, 5, 7482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 0, 1, 1, 4, 101, 7483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 0, 1, 1, 8, 102, 7484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 0, 1, 2, 1, 1, 7485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 0, 1, 2, 1, 2, 7486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 0, 1, 2, 1, 3, 7487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 0, 1, 2, 1, 4, 7488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 0, 1, 2, 4, 101, 7489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 0, 1, 2, 8, 102, 7490);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 0, 1, 2, 1, 1, 7491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 2, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 0, 1, 2, 1, 2, 7492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 3, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 0, 1, 2, 1, 3, 7493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 0, 1, 3, 1, 2, 7495);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 0, 1, 3, 1, 3, 7496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 0, 1, 3, 1, 4, 7497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 0, 1, 3, 1, 5, 7498);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "クウカの口の中に\nたくさん…♪\n止まりません…！", 0, 1, 3, 1, 7, 7500);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 0, 1, 3, 1, 8, 7501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 0, 1, 3, 1, 10, 7503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127001, 0, "これは…\n妄想に役立ちそうな\n感触です…！", 0, 1, 3, 1, 11, 7504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 0, 1, 1, 12, 103, 7505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (100501, 0, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 0, 1, 2, 12, 103, 7506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 0, 1, 1, 1, 1, 7507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 0, 1, 1, 1, 2, 7508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 0, 1, 1, 1, 3, 7509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 0, 1, 1, 1, 4, 7510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 0, 1, 1, 1, 5, 7511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 0, 1, 1, 4, 101, 7512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 0, 1, 1, 8, 102, 7513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 0, 1, 2, 1, 1, 7514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 0, 1, 2, 1, 2, 7515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 0, 1, 2, 1, 3, 7516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 0, 1, 2, 1, 4, 7517);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 0, 1, 2, 4, 101, 7518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 0, 1, 2, 8, 102, 7519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 0, 1, 2, 1, 1, 7520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 2, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 0, 1, 2, 1, 2, 7521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 3, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 0, 1, 2, 1, 3, 7522);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 0, 1, 3, 1, 2, 7524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 0, 1, 3, 1, 3, 7525);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 0, 1, 3, 1, 4, 7526);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 0, 1, 3, 1, 5, 7527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 0, 1, 3, 1, 7, 7529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 0, 1, 3, 1, 8, 7530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 0, 1, 3, 1, 10, 7532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127101, 0, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 0, 1, 3, 1, 11, 7533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 0, 1, 1, 1, 1, 7534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 0, 1, 1, 1, 2, 7535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 0, 1, 1, 1, 3, 7536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 0, 1, 1, 1, 4, 7537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 0, 1, 1, 1, 5, 7538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 0, 1, 1, 4, 101, 7539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 0, 1, 1, 8, 102, 7540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 0, 1, 2, 1, 1, 7541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 0, 1, 2, 1, 2, 7542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 0, 1, 2, 1, 3, 7543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 0, 1, 2, 1, 4, 7544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 0, 1, 2, 4, 101, 7545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 0, 1, 2, 8, 102, 7546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 0, 1, 2, 1, 1, 7547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 2, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 0, 1, 2, 1, 2, 7548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 3, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 0, 1, 2, 1, 3, 7549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 0, 1, 3, 1, 2, 7551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 0, 1, 3, 1, 3, 7552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 0, 1, 3, 1, 4, 7553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 0, 1, 3, 1, 5, 7554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 0, 1, 3, 1, 7, 7556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126501, 0, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 0, 1, 3, 1, 8, 7557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 0, 1, 1, 1, 1, 7559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 0, 1, 1, 1, 2, 7560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 0, 1, 1, 1, 3, 7561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 0, 1, 1, 1, 4, 7562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 0, 1, 1, 1, 5, 7563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 0, 1, 1, 4, 101, 7564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 0, 1, 1, 8, 102, 7565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 0, 1, 2, 1, 1, 7566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 0, 1, 2, 1, 2, 7567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 0, 1, 2, 1, 3, 7568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 0, 1, 2, 1, 4, 7569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 0, 1, 2, 4, 101, 7570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 0, 1, 2, 8, 102, 7571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 0, 1, 2, 1, 1, 7572);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 2, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 0, 1, 2, 1, 2, 7573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 3, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 0, 1, 2, 1, 3, 7574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 0, 1, 3, 1, 2, 7576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 0, 1, 3, 1, 3, 7577);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 0, 1, 3, 1, 4, 7578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 0, 1, 3, 1, 5, 7579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 0, 1, 3, 1, 7, 7581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127201, 0, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 0, 1, 3, 1, 8, 7582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 0, 1, 1, 12, 103, 7584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (104301, 0, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 0, 1, 2, 12, 103, 7585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "魔法の練習！\nむむむ～…\nそれっ…！", 0, 1, 1, 1, 1, 7586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 0, 1, 1, 1, 2, 7587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 0, 1, 1, 1, 3, 7588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 0, 1, 1, 1, 4, 7589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 0, 1, 1, 1, 5, 7590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 0, 1, 1, 4, 101, 7591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 0, 1, 1, 8, 102, 7592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 0, 1, 2, 1, 1, 7593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 0, 1, 2, 1, 2, 7594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 0, 1, 2, 1, 3, 7595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 0, 1, 2, 1, 4, 7596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 0, 1, 2, 4, 101, 7597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 0, 1, 2, 8, 102, 7598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 0, 1, 2, 1, 1, 7599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 2, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 0, 1, 2, 1, 2, 7600);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 3, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 0, 1, 2, 1, 3, 7601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "充電ちう～\n…ガッコーのイスも\nこれにして～", 0, 1, 3, 1, 2, 7603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 0, 1, 3, 1, 3, 7604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "リフレッシュの\n魔法発見！\nいい香り～♪", 0, 1, 3, 1, 4, 7605);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 0, 1, 3, 1, 5, 7606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "超おいし～♪\n学食にあったら\n毎日リピする～", 0, 1, 3, 1, 7, 7608);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127301, 0, "ふむふむ…\nねむみパない…\n続きはまた今度～", 0, 1, 3, 1, 8, 7609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 0, 1, 1, 1, 1, 7611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 0, 1, 1, 1, 2, 7612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 0, 1, 1, 1, 3, 7613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 0, 1, 1, 1, 4, 7614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 0, 1, 1, 1, 5, 7615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 0, 1, 1, 4, 101, 7616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 0, 1, 1, 8, 102, 7617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 0, 1, 2, 1, 1, 7618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 0, 1, 2, 1, 2, 7619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 0, 1, 2, 1, 3, 7620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 0, 1, 2, 1, 4, 7621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 0, 1, 2, 4, 101, 7622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 0, 1, 2, 8, 102, 7623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 0, 1, 2, 1, 1, 7624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 2, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 0, 1, 2, 1, 2, 7625);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 3, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 0, 1, 2, 1, 3, 7626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 0, 1, 3, 1, 2, 7628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 0, 1, 3, 1, 3, 7629);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 0, 1, 3, 1, 4, 7630);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 0, 1, 3, 1, 5, 7631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 0, 1, 3, 1, 7, 7633);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127501, 0, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 0, 1, 3, 1, 8, 7634);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 0, 1, 1, 12, 103, 7636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (101401, 0, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 0, 1, 2, 12, 103, 7637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 0, 1, 1, 1, 1, 7638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "たまには違う服装\n違う得物を使うのも\n悪くないね", 0, 1, 1, 1, 2, 7639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 0, 1, 1, 1, 3, 7640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 0, 1, 1, 1, 4, 7641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 0, 1, 1, 1, 5, 7642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 0, 1, 1, 4, 101, 7643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 0, 1, 1, 8, 102, 7644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 0, 1, 2, 1, 1, 7645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 0, 1, 2, 1, 2, 7646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 0, 1, 2, 1, 3, 7647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 0, 1, 2, 1, 4, 7648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 0, 1, 2, 4, 101, 7649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 0, 1, 2, 8, 102, 7650);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 0, 1, 2, 1, 1, 7651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 2, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 0, 1, 2, 1, 2, 7652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 3, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 0, 1, 2, 1, 3, 7653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 0, 1, 3, 1, 2, 7655);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 0, 1, 3, 1, 3, 7656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "この香り…\nサラサリア土産の\n香木かい？", 0, 1, 3, 1, 4, 7657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 0, 1, 3, 1, 5, 7658);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 0, 1, 3, 1, 7, 7660);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127601, 0, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 0, 1, 3, 1, 8, 7661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 0, 1, 1, 1, 1, 7663);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 0, 1, 1, 1, 2, 7664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 0, 1, 1, 1, 3, 7665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 0, 1, 1, 1, 4, 7666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 0, 1, 1, 1, 5, 7667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 0, 1, 1, 4, 101, 7668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 0, 1, 1, 8, 102, 7669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 0, 1, 2, 1, 1, 7670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 0, 1, 2, 1, 2, 7671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 0, 1, 2, 1, 3, 7672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 0, 1, 2, 1, 4, 7673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 0, 1, 2, 4, 101, 7674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 0, 1, 2, 8, 102, 7675);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 0, 1, 2, 1, 1, 7676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 2, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 0, 1, 2, 1, 2, 7677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 3, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 0, 1, 2, 1, 3, 7678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 0, 1, 3, 1, 2, 7680);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 0, 1, 3, 1, 3, 7681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 0, 1, 3, 1, 4, 7682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 0, 1, 3, 1, 5, 7683);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 0, 1, 3, 1, 7, 7685);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126001, 0, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 0, 1, 3, 1, 8, 7686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 0, 1, 1, 12, 103, 7688);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (103801, 0, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 0, 1, 2, 12, 103, 7689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "お兄の、けはい…\nん、みつけた\nぎゅ", 0, 1, 1, 1, 1, 7690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "きょろきょろ\nここも\nぱとろる、するよ", 0, 1, 1, 1, 2, 7691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "おうち\nにぎやか\nさみしく、ない", 0, 1, 1, 1, 3, 7692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 0, 1, 1, 1, 4, 7693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "はっぴーちゃん\nどこ？\nお兄、たべた？", 0, 1, 1, 1, 5, 7694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 0, 1, 1, 4, 101, 7695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 0, 1, 1, 8, 102, 7696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 0, 1, 2, 1, 1, 7697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "くろぷれしあ\nかっこいい？\nん…あざます", 0, 1, 2, 1, 2, 7698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 0, 1, 2, 1, 3, 7699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 0, 1, 2, 1, 4, 7700);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "かみ、りぼん\nおてつだい\nよろ\nです", 0, 1, 2, 4, 101, 7701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "ここ\nすむ\nね？\nまいにち、いっしょ", 0, 1, 2, 8, 102, 7702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 1, "ぐっもにん\nあさごはん\nあさごはん", 0, 1, 2, 1, 1, 7703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 2, "お兄\nおつかれ？\nれっつ、まいぺーす", 0, 1, 2, 1, 2, 7704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 3, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 0, 1, 2, 1, 3, 7705);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "ふかふか\nパパの、おなか\nみたい", 0, 1, 3, 1, 2, 7707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 0, 1, 3, 1, 3, 7708);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "いい、におい\nでも、お兄\nもっと、いい、におい", 0, 1, 3, 1, 4, 7709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "とりま\nお肉、やこ", 0, 1, 3, 1, 5, 7710);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "もぐもぐ…\nむげん肉まん、も\nきぼう", 0, 1, 3, 1, 7, 7712);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (126101, 0, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 0, 1, 3, 1, 8, 7713);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 0, 1, 1, 1, 1, 7715);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 0, 1, 1, 1, 2, 7716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 0, 1, 1, 1, 3, 7717);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 0, 1, 1, 1, 4, 7718);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 0, 1, 1, 1, 5, 7719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 0, 1, 1, 4, 101, 7720);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 0, 1, 1, 8, 102, 7721);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 0, 1, 2, 1, 1, 7722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 0, 1, 2, 1, 2, 7723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 0, 1, 2, 1, 3, 7724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 0, 1, 2, 1, 4, 7725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 0, 1, 2, 4, 101, 7726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 0, 1, 2, 8, 102, 7727);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 0, 1, 2, 1, 1, 7728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 2, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 0, 1, 2, 1, 2, 7729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 3, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 0, 1, 2, 1, 3, 7730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 0, 1, 3, 1, 2, 7732);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 0, 1, 3, 1, 3, 7733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 0, 1, 3, 1, 4, 7734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 0, 1, 3, 1, 5, 7735);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 0, 1, 3, 1, 7, 7737);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 0, 1, 3, 1, 8, 7738);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 0, 1, 3, 1, 14, 7740);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127901, 0, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 0, 1, 3, 1, 15, 7741);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 0, 1, 1, 12, 103, 7742);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106601, 0, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 0, 1, 2, 12, 103, 7743);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 0, 1, 1, 1, 1, 7744);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 0, 1, 1, 1, 2, 7745);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 0, 1, 1, 1, 3, 7746);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 0, 1, 1, 1, 4, 7747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 0, 1, 1, 1, 5, 7748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 0, 1, 1, 4, 101, 7749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 0, 1, 1, 8, 102, 7750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 0, 1, 2, 1, 1, 7751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 0, 1, 2, 1, 2, 7752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 0, 1, 2, 1, 3, 7753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 0, 1, 2, 1, 4, 7754);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 0, 1, 2, 4, 101, 7755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 0, 1, 2, 8, 102, 7756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 0, 1, 2, 1, 1, 7757);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 2, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 0, 1, 2, 1, 2, 7758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 3, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 0, 1, 2, 1, 3, 7759);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 0, 1, 3, 1, 2, 7761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 0, 1, 3, 1, 3, 7762);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 0, 1, 3, 1, 4, 7763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 0, 1, 3, 1, 5, 7764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 0, 1, 3, 1, 7, 7766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 0, 1, 3, 1, 8, 7767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 0, 1, 3, 1, 14, 7769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128001, 0, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 0, 1, 3, 1, 15, 7770);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 0, 1, 1, 1, 1, 7771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 0, 1, 1, 1, 2, 7772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 0, 1, 1, 1, 3, 7773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 0, 1, 1, 1, 4, 7774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 0, 1, 1, 1, 5, 7775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 0, 1, 1, 4, 101, 7776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 0, 1, 1, 8, 102, 7777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 0, 1, 2, 1, 1, 7778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 0, 1, 2, 1, 2, 7779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 0, 1, 2, 1, 3, 7780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 0, 1, 2, 1, 4, 7781);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 0, 1, 2, 4, 101, 7782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 0, 1, 2, 8, 102, 7783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 0, 1, 2, 1, 1, 7784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 2, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 0, 1, 2, 1, 2, 7785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 3, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 0, 1, 2, 1, 3, 7786);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 0, 1, 3, 1, 2, 7788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 0, 1, 3, 1, 3, 7789);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 0, 1, 3, 1, 4, 7790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 0, 1, 3, 1, 5, 7791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 0, 1, 3, 1, 7, 7793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128101, 0, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 0, 1, 3, 1, 8, 7794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 0, 1, 1, 1, 1, 7797);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 0, 1, 1, 1, 2, 7798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 0, 1, 1, 1, 3, 7799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 0, 1, 1, 1, 4, 7800);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 0, 1, 1, 1, 5, 7801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 0, 1, 1, 4, 101, 7802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 0, 1, 1, 8, 102, 7803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 0, 1, 2, 1, 1, 7804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 0, 1, 2, 1, 2, 7805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 0, 1, 2, 1, 3, 7806);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 0, 1, 2, 1, 4, 7807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 0, 1, 2, 4, 101, 7808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 0, 1, 2, 8, 102, 7809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 0, 1, 2, 1, 1, 7810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 2, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 0, 1, 2, 1, 2, 7811);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 3, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 0, 1, 2, 1, 3, 7812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 0, 1, 3, 1, 2, 7814);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 0, 1, 3, 1, 3, 7815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 0, 1, 3, 1, 4, 7816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 0, 1, 3, 1, 5, 7817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 0, 1, 3, 1, 7, 7819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128201, 0, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 0, 1, 3, 1, 8, 7820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 0, 1, 1, 1, 1, 7823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 0, 1, 1, 1, 2, 7824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 0, 1, 1, 1, 3, 7825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 0, 1, 1, 1, 4, 7826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 0, 1, 1, 1, 5, 7827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 0, 1, 1, 4, 101, 7828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 0, 1, 1, 8, 102, 7829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 0, 1, 2, 1, 1, 7830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 0, 1, 2, 1, 2, 7831);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 0, 1, 2, 1, 3, 7832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 0, 1, 2, 1, 4, 7833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 0, 1, 2, 4, 101, 7834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 0, 1, 2, 8, 102, 7835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 0, 1, 2, 1, 1, 7836);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 2, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 0, 1, 2, 1, 2, 7837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 3, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 0, 1, 2, 1, 3, 7838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 0, 1, 3, 1, 2, 7840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 0, 1, 3, 1, 3, 7841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 0, 1, 3, 1, 4, 7842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 0, 1, 3, 1, 5, 7843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 0, 1, 3, 1, 7, 7845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128301, 0, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 0, 1, 3, 1, 8, 7846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 0, 1, 1, 1, 1, 7848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 0, 1, 1, 1, 2, 7849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 0, 1, 1, 1, 3, 7850);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 0, 1, 1, 1, 4, 7851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 0, 1, 1, 1, 5, 7852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 0, 1, 1, 4, 101, 7853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "来年も彼の隣で\n初日の出が\n見られるといいな～", 0, 1, 1, 8, 102, 7854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 0, 1, 2, 1, 1, 7855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 0, 1, 2, 1, 2, 7856);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 0, 1, 2, 1, 3, 7857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 0, 1, 2, 1, 4, 7858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 0, 1, 2, 4, 101, 7859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 0, 1, 2, 8, 102, 7860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 0, 1, 2, 1, 1, 7861);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 2, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 0, 1, 2, 1, 2, 7862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 3, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 0, 1, 2, 1, 3, 7863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 0, 1, 3, 1, 2, 7865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 0, 1, 3, 1, 3, 7866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 0, 1, 3, 1, 4, 7867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 0, 1, 3, 1, 5, 7868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 0, 1, 3, 1, 7, 7870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128401, 0, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 0, 1, 3, 1, 8, 7871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 0, 1, 1, 12, 103, 7873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (106501, 0, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 0, 1, 2, 12, 103, 7874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 0, 1, 1, 1, 1, 7875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 0, 1, 1, 1, 2, 7876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 0, 1, 1, 1, 3, 7877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 0, 1, 1, 1, 4, 7878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 0, 1, 1, 1, 5, 7879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 0, 1, 1, 4, 101, 7880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 0, 1, 1, 8, 102, 7881);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 0, 1, 2, 1, 1, 7882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 0, 1, 2, 1, 2, 7883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 0, 1, 2, 1, 3, 7884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 0, 1, 2, 1, 4, 7885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 0, 1, 2, 4, 101, 7886);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 0, 1, 2, 8, 102, 7887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 0, 1, 2, 1, 1, 7888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 2, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 0, 1, 2, 1, 2, 7889);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 3, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 0, 1, 2, 1, 3, 7890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 0, 1, 3, 1, 2, 7892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 0, 1, 3, 1, 3, 7893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 0, 1, 3, 1, 4, 7894);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 0, 1, 3, 1, 5, 7895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 0, 1, 3, 1, 7, 7897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128501, 0, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 0, 1, 3, 1, 8, 7898);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "この新しい力…\nさらに使いこなせる\nようになるには…", 0, 1, 1, 1, 1, 7900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 0, 1, 1, 1, 2, 7901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 0, 1, 1, 1, 3, 7902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "風の力の制御…\n段々とコツが\n掴めてきたな", 0, 1, 1, 1, 4, 7903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 0, 1, 1, 1, 5, 7904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 0, 1, 1, 4, 101, 7905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 0, 1, 1, 8, 102, 7906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 0, 1, 2, 1, 1, 7907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 0, 1, 2, 1, 2, 7908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 0, 1, 2, 1, 3, 7909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 0, 1, 2, 1, 4, 7910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 0, 1, 2, 4, 101, 7911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 0, 1, 2, 8, 102, 7912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 0, 1, 2, 1, 1, 7913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 2, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 0, 1, 2, 1, 2, 7914);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 3, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 0, 1, 2, 1, 3, 7915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "休める時は休む…\n戦い続けるには\n必要なことだからね", 0, 1, 3, 1, 2, 7917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 0, 1, 3, 1, 3, 7918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 0, 1, 3, 1, 4, 7919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 0, 1, 3, 1, 5, 7920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "この味付け…\n今度試してみようか\n味見は任せたからね", 0, 1, 3, 1, 7, 7922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128801, 0, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 0, 1, 3, 1, 8, 7923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 0, 1, 1, 1, 1, 7925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 0, 1, 1, 1, 2, 7926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 0, 1, 1, 1, 3, 7927);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 0, 1, 1, 1, 4, 7928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 0, 1, 1, 1, 5, 7929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 0, 1, 1, 4, 101, 7930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 0, 1, 1, 8, 102, 7931);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 0, 1, 2, 1, 1, 7932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 0, 1, 2, 1, 2, 7933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 0, 1, 2, 1, 3, 7934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 0, 1, 2, 1, 4, 7935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 0, 1, 2, 4, 101, 7936);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 0, 1, 2, 8, 102, 7937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 0, 1, 2, 1, 1, 7938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 2, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 0, 1, 2, 1, 2, 7939);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 3, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 0, 1, 2, 1, 3, 7940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 0, 1, 3, 1, 2, 7942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 0, 1, 3, 1, 3, 7943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 0, 1, 3, 1, 4, 7944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 0, 1, 3, 1, 5, 7945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 0, 1, 3, 1, 7, 7947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128901, 0, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 0, 1, 3, 1, 8, 7948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 0, 1, 1, 1, 1, 7950);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 0, 1, 1, 1, 2, 7951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 0, 1, 1, 1, 3, 7952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 0, 1, 1, 1, 4, 7953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 0, 1, 1, 1, 5, 7954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 0, 1, 1, 4, 101, 7955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 0, 1, 1, 8, 102, 7956);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 0, 1, 2, 1, 1, 7957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 0, 1, 2, 1, 2, 7958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 0, 1, 2, 1, 3, 7959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 0, 1, 2, 1, 4, 7960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 0, 1, 2, 4, 101, 7961);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 0, 1, 2, 8, 102, 7962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 0, 1, 2, 1, 1, 7963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 2, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 0, 1, 2, 1, 2, 7964);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 3, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 0, 1, 2, 1, 3, 7965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 0, 1, 3, 1, 2, 7967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 0, 1, 3, 1, 3, 7968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 0, 1, 3, 1, 4, 7969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 0, 1, 3, 1, 5, 7970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 0, 1, 3, 1, 7, 7972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (128701, 0, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 0, 1, 3, 1, 8, 7973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 0, 1, 1, 1, 1, 7975);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 0, 1, 1, 1, 2, 7976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 0, 1, 1, 1, 3, 7977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 0, 1, 1, 1, 4, 7978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 0, 1, 1, 1, 5, 7979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 0, 1, 1, 4, 101, 7980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 0, 1, 1, 8, 102, 7981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 0, 1, 2, 1, 1, 7982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 0, 1, 2, 1, 2, 7983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 0, 1, 2, 1, 3, 7984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 0, 1, 2, 1, 4, 7985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 0, 1, 2, 4, 101, 7986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 0, 1, 2, 8, 102, 7987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 0, 1, 2, 1, 1, 7988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 2, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 0, 1, 2, 1, 2, 7989);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 3, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 0, 1, 2, 1, 3, 7990);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 0, 1, 3, 1, 2, 7992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 0, 1, 3, 1, 3, 7993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 0, 1, 3, 1, 4, 7994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 0, 1, 3, 1, 5, 7995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 0, 1, 3, 1, 7, 7997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129001, 0, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 0, 1, 3, 1, 8, 7998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 0, 1, 1, 1, 1, 8000);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 0, 1, 1, 1, 2, 8001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 0, 1, 1, 1, 3, 8002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 0, 1, 1, 1, 4, 8003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 0, 1, 1, 1, 5, 8004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 0, 1, 1, 4, 101, 8005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 0, 1, 1, 8, 102, 8006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 0, 1, 2, 1, 1, 8007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 0, 1, 2, 1, 2, 8008);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 0, 1, 2, 1, 3, 8009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 0, 1, 2, 1, 4, 8010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 0, 1, 2, 4, 101, 8011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 0, 1, 2, 8, 102, 8012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 0, 1, 2, 1, 1, 8013);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 2, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 0, 1, 2, 1, 2, 8014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 3, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 0, 1, 2, 1, 3, 8015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 0, 1, 3, 1, 2, 8017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 0, 1, 3, 1, 3, 8018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 0, 1, 3, 1, 4, 8019);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 0, 1, 3, 1, 5, 8020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 0, 1, 3, 1, 7, 8022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 0, 1, 3, 1, 8, 8023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 0, 1, 3, 1, 10, 8025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129301, 0, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 0, 1, 3, 1, 11, 8026);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 0, 1, 1, 12, 103, 8027);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108901, 0, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 0, 1, 2, 12, 103, 8028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 0, 1, 1, 12, 103, 8029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108801, 0, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 0, 1, 2, 12, 103, 8030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 0, 1, 1, 12, 103, 8031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (108701, 0, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 0, 1, 2, 12, 103, 8032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 0, 1, 1, 1, 1, 8033);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 0, 1, 1, 1, 2, 8034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 0, 1, 1, 1, 3, 8035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 0, 1, 1, 1, 4, 8036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 0, 1, 1, 1, 5, 8037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 0, 1, 1, 4, 101, 8038);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 0, 1, 1, 8, 102, 8039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 0, 1, 2, 1, 1, 8040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 0, 1, 2, 1, 2, 8041);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 0, 1, 2, 1, 3, 8042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 0, 1, 2, 1, 4, 8043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 0, 1, 2, 4, 101, 8044);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 0, 1, 2, 8, 102, 8045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 1, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 0, 1, 2, 1, 1, 8046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 2, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 0, 1, 2, 1, 2, 8047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 3, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 0, 1, 2, 1, 3, 8048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 0, 1, 3, 1, 2, 8050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 0, 1, 3, 1, 3, 8051);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 0, 1, 3, 1, 4, 8052);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 0, 1, 3, 1, 5, 8053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 0, 1, 3, 1, 7, 8055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 0, 1, 3, 1, 8, 8056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 0, 1, 3, 1, 10, 8058);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129401, 0, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 0, 1, 3, 1, 11, 8059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "さてと…\n明日の訓練メニュー\n考えねえとな", 0, 1, 1, 1, 1, 8060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 0, 1, 1, 1, 2, 8061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 0, 1, 1, 1, 3, 8062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 0, 1, 1, 1, 4, 8063);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 0, 1, 1, 1, 5, 8064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 0, 1, 1, 4, 101, 8065);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 0, 1, 1, 8, 102, 8066);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 0, 1, 2, 1, 1, 8067);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 0, 1, 2, 1, 2, 8068);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 0, 1, 2, 1, 3, 8069);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 0, 1, 2, 1, 4, 8070);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 0, 1, 2, 4, 101, 8071);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 0, 1, 2, 8, 102, 8072);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 1, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 0, 1, 2, 1, 1, 8073);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 2, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 0, 1, 2, 1, 2, 8074);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 3, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 0, 1, 2, 1, 3, 8075);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 0, 1, 3, 1, 2, 8077);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 0, 1, 3, 1, 3, 8078);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 0, 1, 3, 1, 4, 8079);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 0, 1, 3, 1, 5, 8080);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 0, 1, 3, 1, 7, 8082);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129801, 0, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 0, 1, 3, 1, 8, 8083);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 0, 1, 1, 1, 1, 8085);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 0, 1, 1, 1, 2, 8086);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 0, 1, 1, 1, 3, 8087);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 0, 1, 1, 1, 4, 8088);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 0, 1, 1, 1, 5, 8089);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 0, 1, 1, 4, 101, 8090);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 0, 1, 1, 8, 102, 8091);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 0, 1, 2, 1, 1, 8092);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 0, 1, 2, 1, 2, 8093);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 0, 1, 2, 1, 3, 8094);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 0, 1, 2, 1, 4, 8095);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 0, 1, 2, 4, 101, 8096);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 0, 1, 2, 8, 102, 8097);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 1, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 0, 1, 2, 1, 1, 8098);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 2, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 0, 1, 2, 1, 2, 8099);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 3, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 0, 1, 2, 1, 3, 8100);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 0, 1, 3, 1, 2, 8102);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 0, 1, 3, 1, 3, 8103);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 0, 1, 3, 1, 4, 8104);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 0, 1, 3, 1, 5, 8105);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 0, 1, 3, 1, 7, 8107);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129901, 0, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 0, 1, 3, 1, 8, 8108);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "……（そわそわ）", 0, 1, 1, 1, 1, 8110);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "ここに寄らずの花陣…\n作ったらダメですよね", 0, 1, 1, 1, 2, 8111);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 0, 1, 1, 1, 3, 8112);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "身を隠せる場所が\nあると…\n助かるんですけど", 0, 1, 1, 1, 4, 8113);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 0, 1, 1, 1, 5, 8114);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 0, 1, 1, 4, 101, 8115);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "この家なら安全─\nかもしれません\n魔物が出ないので", 0, 1, 1, 8, 102, 8116);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 0, 1, 2, 1, 1, 8117);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "長話─苦手です\n疲れるので…\nふぅ…", 0, 1, 2, 1, 2, 8118);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 0, 1, 2, 1, 3, 8119);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "世話をするのは\n得意です\n…花の、ですよ？", 0, 1, 2, 1, 4, 8120);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 0, 1, 2, 4, 101, 8121);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 0, 1, 2, 8, 102, 8122);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 1, "花に、お水─？\n…いりません", 0, 1, 2, 1, 1, 8123);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 2, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 0, 1, 2, 1, 2, 8124);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 3, "夜番。私がします\nあまり眠らない\n性質なので…", 0, 1, 2, 1, 3, 8125);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "ここなら根を張れる\nかもしれません\n…張りませんけど", 0, 1, 3, 1, 2, 8127);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 0, 1, 3, 1, 3, 8128);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "花みたいに─\nいい匂い、です", 0, 1, 3, 1, 4, 8129);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 0, 1, 3, 1, 5, 8130);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "食事も立派な戦支度\n媛が言ってました", 0, 1, 3, 1, 7, 8132);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129601, 0, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 0, 1, 3, 1, 8, 8133);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 0, 1, 1, 1, 1, 8135);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "あ、どこからか\nお花の匂いが…\nいい香りですね", 0, 1, 1, 1, 2, 8136);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 0, 1, 1, 1, 3, 8137);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 0, 1, 1, 1, 4, 8138);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 0, 1, 1, 1, 5, 8139);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 0, 1, 1, 4, 101, 8140);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 0, 1, 1, 8, 102, 8141);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 0, 1, 2, 1, 1, 8142);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 0, 1, 2, 1, 2, 8143);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 0, 1, 2, 1, 3, 8144);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 0, 1, 2, 1, 4, 8145);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 0, 1, 2, 4, 101, 8146);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 0, 1, 2, 8, 102, 8147);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 1, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 0, 1, 2, 1, 1, 8148);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 2, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 0, 1, 2, 1, 2, 8149);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 3, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 0, 1, 2, 1, 3, 8150);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 0, 1, 3, 1, 2, 8152);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 0, 1, 3, 1, 3, 8153);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 0, 1, 3, 1, 4, 8154);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 0, 1, 3, 1, 5, 8155);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 0, 1, 3, 1, 7, 8157);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130701, 0, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 0, 1, 3, 1, 8, 8158);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 0, 1, 1, 1, 1, 8160);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 0, 1, 1, 1, 2, 8161);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 0, 1, 1, 1, 3, 8162);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 0, 1, 1, 1, 4, 8163);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 0, 1, 1, 1, 5, 8164);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 0, 1, 1, 4, 101, 8165);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 0, 1, 1, 8, 102, 8166);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 0, 1, 2, 1, 1, 8167);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 0, 1, 2, 1, 2, 8168);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 0, 1, 2, 1, 3, 8169);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 0, 1, 2, 1, 4, 8170);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 0, 1, 2, 4, 101, 8171);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 0, 1, 2, 8, 102, 8172);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 1, "はう、そうでした\nお花にお水を\nあげる時間です！", 0, 1, 2, 1, 1, 8173);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 2, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 0, 1, 2, 1, 2, 8174);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 3, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 0, 1, 2, 1, 3, 8175);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 0, 1, 3, 1, 2, 8177);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 0, 1, 3, 1, 3, 8178);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 0, 1, 3, 1, 4, 8179);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 0, 1, 3, 1, 5, 8180);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 0, 1, 3, 1, 7, 8182);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130801, 0, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 0, 1, 3, 1, 8, 8183);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 0, 1, 1, 1, 1, 8185);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 0, 1, 1, 1, 2, 8186);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 0, 1, 1, 1, 3, 8187);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 0, 1, 1, 1, 4, 8188);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 0, 1, 1, 1, 5, 8189);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 0, 1, 1, 4, 101, 8190);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 0, 1, 1, 8, 102, 8191);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 0, 1, 2, 1, 1, 8192);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 0, 1, 2, 1, 2, 8193);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 0, 1, 2, 1, 3, 8194);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 0, 1, 2, 1, 4, 8195);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 0, 1, 2, 4, 101, 8196);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 0, 1, 2, 8, 102, 8197);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 1, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 0, 1, 2, 1, 1, 8198);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 2, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 0, 1, 2, 1, 2, 8199);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 3, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 0, 1, 2, 1, 3, 8200);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 0, 1, 3, 1, 2, 8202);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 0, 1, 3, 1, 3, 8203);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 0, 1, 3, 1, 4, 8204);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 0, 1, 3, 1, 5, 8205);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 0, 1, 3, 1, 7, 8207);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131001, 0, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 0, 1, 3, 1, 8, 8208);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 0, 1, 1, 12, 103, 8210);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (105601, 0, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 0, 1, 2, 12, 103, 8211);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 0, 1, 1, 1, 1, 8212);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 0, 1, 1, 1, 2, 8213);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 0, 1, 1, 1, 3, 8214);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 0, 1, 1, 1, 4, 8215);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 0, 1, 1, 1, 5, 8216);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 0, 1, 1, 4, 101, 8217);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 0, 1, 1, 8, 102, 8218);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 0, 1, 2, 1, 1, 8219);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 0, 1, 2, 1, 2, 8220);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 0, 1, 2, 1, 3, 8221);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 0, 1, 2, 1, 4, 8222);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 0, 1, 2, 4, 101, 8223);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 0, 1, 2, 8, 102, 8224);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 1, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 0, 1, 2, 1, 1, 8225);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 2, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 0, 1, 2, 1, 2, 8226);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 3, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 0, 1, 2, 1, 3, 8227);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 0, 1, 3, 1, 2, 8229);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 0, 1, 3, 1, 3, 8230);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 0, 1, 3, 1, 4, 8231);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 0, 1, 3, 1, 5, 8232);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 0, 1, 3, 1, 7, 8234);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130901, 0, "パークの\nガイドブックとか\nあったらいいですね♪", 0, 1, 3, 1, 8, 8235);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 0, 1, 1, 1, 1, 8237);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 0, 1, 1, 1, 2, 8238);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 0, 1, 1, 1, 3, 8239);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 0, 1, 1, 1, 4, 8240);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 0, 1, 1, 1, 5, 8241);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 0, 1, 1, 4, 101, 8242);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 0, 1, 1, 8, 102, 8243);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 0, 1, 2, 1, 1, 8244);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 0, 1, 2, 1, 2, 8245);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 0, 1, 2, 1, 3, 8246);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 0, 1, 2, 1, 4, 8247);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 0, 1, 2, 4, 101, 8248);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 0, 1, 2, 8, 102, 8249);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 1, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 0, 1, 2, 1, 1, 8250);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 2, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 0, 1, 2, 1, 2, 8251);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 3, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 0, 1, 2, 1, 3, 8252);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 0, 1, 3, 1, 2, 8254);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 0, 1, 3, 1, 3, 8255);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 0, 1, 3, 1, 4, 8256);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 0, 1, 3, 1, 5, 8257);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 0, 1, 3, 1, 7, 8259);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 0, 1, 3, 1, 8, 8260);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 0, 1, 3, 1, 10, 8262);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131101, 0, "あーやべ\n一瞬で寝そー\nヤバだわ～", 0, 1, 3, 1, 11, 8263);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 0, 1, 1, 1, 1, 8264);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 0, 1, 1, 1, 2, 8265);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 0, 1, 1, 1, 3, 8266);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 0, 1, 1, 1, 4, 8267);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 0, 1, 1, 1, 5, 8268);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 0, 1, 1, 4, 101, 8269);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 0, 1, 1, 8, 102, 8270);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 0, 1, 2, 1, 1, 8271);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 0, 1, 2, 1, 2, 8272);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 0, 1, 2, 1, 3, 8273);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 0, 1, 2, 1, 4, 8274);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 0, 1, 2, 4, 101, 8275);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 0, 1, 2, 8, 102, 8276);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 1, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 0, 1, 2, 1, 1, 8277);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 2, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 0, 1, 2, 1, 2, 8278);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 3, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 0, 1, 2, 1, 3, 8279);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 0, 1, 3, 1, 2, 8281);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 0, 1, 3, 1, 3, 8282);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 0, 1, 3, 1, 4, 8283);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 0, 1, 3, 1, 5, 8284);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 0, 1, 3, 1, 7, 8286);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129701, 0, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 0, 1, 3, 1, 8, 8287);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 0, 1, 1, 1, 1, 8289);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "海への行き来は\nテレポーテーションに\nお任せです★", 0, 1, 1, 1, 2, 8290);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 0, 1, 1, 1, 3, 8291);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 0, 1, 1, 1, 4, 8292);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 0, 1, 1, 1, 5, 8293);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 0, 1, 1, 4, 101, 8294);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 0, 1, 1, 8, 102, 8295);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 0, 1, 2, 1, 1, 8296);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 0, 1, 2, 1, 2, 8297);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 0, 1, 2, 1, 3, 8298);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 0, 1, 2, 1, 4, 8299);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 0, 1, 2, 4, 101, 8300);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 0, 1, 2, 8, 102, 8301);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 1, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 0, 1, 2, 1, 1, 8302);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 2, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 0, 1, 2, 1, 2, 8303);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 3, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 0, 1, 2, 1, 3, 8304);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 0, 1, 3, 1, 2, 8306);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 0, 1, 3, 1, 3, 8307);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 0, 1, 3, 1, 4, 8308);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 0, 1, 3, 1, 5, 8309);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 0, 1, 3, 1, 7, 8311);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 0, 1, 3, 1, 8, 8312);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 0, 1, 3, 1, 10, 8314);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131201, 0, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 0, 1, 3, 1, 11, 8315);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 0, 1, 1, 1, 1, 8316);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 0, 1, 1, 1, 2, 8317);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 0, 1, 1, 1, 3, 8318);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 0, 1, 1, 1, 4, 8319);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 0, 1, 1, 1, 5, 8320);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 0, 1, 1, 4, 101, 8321);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 0, 1, 1, 8, 102, 8322);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 0, 1, 2, 1, 1, 8323);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 0, 1, 2, 1, 2, 8324);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 0, 1, 2, 1, 3, 8325);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 0, 1, 2, 1, 4, 8326);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 0, 1, 2, 4, 101, 8327);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 0, 1, 2, 8, 102, 8328);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 1, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 0, 1, 2, 1, 1, 8329);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 2, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 0, 1, 2, 1, 2, 8330);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 3, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 0, 1, 2, 1, 3, 8331);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 0, 1, 3, 1, 2, 8333);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 0, 1, 3, 1, 3, 8334);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 0, 1, 3, 1, 4, 8335);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 0, 1, 3, 1, 5, 8336);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 0, 1, 3, 1, 7, 8338);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 0, 1, 3, 1, 8, 8339);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 0, 1, 3, 1, 10, 8341);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131601, 0, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 0, 1, 3, 1, 11, 8342);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 0, 1, 1, 1, 1, 8343);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 0, 1, 1, 1, 2, 8344);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 0, 1, 1, 1, 3, 8345);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 0, 1, 1, 1, 4, 8346);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 0, 1, 1, 1, 5, 8347);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 0, 1, 1, 4, 101, 8348);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 0, 1, 1, 8, 102, 8349);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 0, 1, 2, 1, 1, 8350);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 0, 1, 2, 1, 2, 8351);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 0, 1, 2, 1, 3, 8352);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 0, 1, 2, 1, 4, 8353);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 0, 1, 2, 4, 101, 8354);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 0, 1, 2, 8, 102, 8355);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 1, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 0, 1, 2, 1, 1, 8356);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 2, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 0, 1, 2, 1, 2, 8357);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 3, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 0, 1, 2, 1, 3, 8358);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 0, 1, 3, 1, 2, 8360);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 0, 1, 3, 1, 3, 8361);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 0, 1, 3, 1, 4, 8362);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 0, 1, 3, 1, 5, 8363);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 0, 1, 3, 1, 7, 8365);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "文字を読むのと\nデータを読むのは\nまったく違うわ", 0, 1, 3, 1, 8, 8366);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 0, 1, 3, 1, 10, 8368);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131701, 0, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 0, 1, 3, 1, 11, 8369);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 0, 1, 1, 1, 1, 8370);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 0, 1, 1, 1, 2, 8371);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 0, 1, 1, 1, 3, 8372);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 0, 1, 1, 1, 4, 8373);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 0, 1, 1, 1, 5, 8374);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 0, 1, 1, 4, 101, 8375);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 0, 1, 1, 8, 102, 8376);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 0, 1, 2, 1, 1, 8377);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 0, 1, 2, 1, 2, 8378);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 0, 1, 2, 1, 3, 8379);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 0, 1, 2, 1, 4, 8380);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 0, 1, 2, 4, 101, 8381);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 0, 1, 2, 8, 102, 8382);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 1, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 0, 1, 2, 1, 1, 8383);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 2, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 0, 1, 2, 1, 2, 8384);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 3, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 0, 1, 2, 1, 3, 8385);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 0, 1, 3, 1, 2, 8387);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 0, 1, 3, 1, 3, 8388);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 0, 1, 3, 1, 4, 8389);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "なんていうか…\n心があたたかいわ\n…変な感じ！", 0, 1, 3, 1, 5, 8390);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 0, 1, 3, 1, 7, 8392);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (118601, 0, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 0, 1, 3, 1, 8, 8393);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 0, 1, 1, 1, 1, 8395);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 0, 1, 1, 1, 2, 8396);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 0, 1, 1, 1, 3, 8397);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 0, 1, 1, 1, 4, 8398);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 0, 1, 1, 1, 5, 8399);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 0, 1, 1, 4, 101, 8400);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 0, 1, 1, 8, 102, 8401);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 0, 1, 2, 1, 1, 8402);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 0, 1, 2, 1, 2, 8403);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "熱中症には\n気を付けないと\nだめだよ～？", 0, 1, 2, 1, 3, 8404);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 0, 1, 2, 1, 4, 8405);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 0, 1, 2, 4, 101, 8406);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 0, 1, 2, 8, 102, 8407);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 1, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 0, 1, 2, 1, 1, 8408);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 2, "ふふ♪\n波音が心地良い\n時間だね～☆", 0, 1, 2, 1, 2, 8409);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 3, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 0, 1, 2, 1, 3, 8410);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 0, 1, 3, 1, 2, 8412);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 0, 1, 3, 1, 3, 8413);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 0, 1, 3, 1, 4, 8414);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 0, 1, 3, 1, 5, 8415);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 0, 1, 3, 1, 7, 8417);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 0, 1, 3, 1, 8, 8418);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 0, 1, 3, 1, 10, 8420);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131801, 0, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 0, 1, 3, 1, 11, 8421);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 0, 1, 1, 1, 1, 8422);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 0, 1, 1, 1, 2, 8423);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 0, 1, 1, 1, 3, 8424);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 0, 1, 1, 1, 4, 8425);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 0, 1, 1, 1, 5, 8426);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 0, 1, 1, 4, 101, 8427);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 0, 1, 1, 8, 102, 8428);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 0, 1, 2, 1, 1, 8429);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 0, 1, 2, 1, 2, 8430);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 0, 1, 2, 1, 3, 8431);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 0, 1, 2, 1, 4, 8432);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 0, 1, 2, 4, 101, 8433);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 0, 1, 2, 8, 102, 8434);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 1, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 0, 1, 2, 1, 1, 8435);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 2, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 0, 1, 2, 1, 2, 8436);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 3, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 0, 1, 2, 1, 3, 8437);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 0, 1, 3, 1, 2, 8439);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 0, 1, 3, 1, 3, 8440);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 0, 1, 3, 1, 4, 8441);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 0, 1, 3, 1, 5, 8442);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 0, 1, 3, 1, 7, 8444);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (181201, 0, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 0, 1, 3, 1, 8, 8445);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 0, 1, 1, 1, 1, 8447);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 0, 1, 1, 1, 2, 8448);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 0, 1, 1, 1, 3, 8449);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 0, 1, 1, 1, 4, 8450);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 0, 1, 1, 1, 5, 8451);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 0, 1, 1, 4, 101, 8452);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 0, 1, 1, 8, 102, 8453);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 0, 1, 2, 1, 1, 8454);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 0, 1, 2, 1, 2, 8455);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 0, 1, 2, 1, 3, 8456);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 0, 1, 2, 1, 4, 8457);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 0, 1, 2, 4, 101, 8458);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 0, 1, 2, 8, 102, 8459);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 1, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 0, 1, 2, 1, 1, 8460);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 2, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 0, 1, 2, 1, 2, 8461);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 3, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 0, 1, 2, 1, 3, 8462);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 0, 1, 3, 1, 2, 8464);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "細かいところまで\nすごーく丁寧に\n作られてる…", 0, 1, 3, 1, 3, 8465);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 0, 1, 3, 1, 4, 8466);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 0, 1, 3, 1, 5, 8467);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 0, 1, 3, 1, 7, 8469);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 0, 1, 3, 1, 8, 8470);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 0, 1, 3, 1, 10, 8472);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (131901, 0, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 0, 1, 3, 1, 11, 8473);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 0, 1, 1, 1, 1, 8474);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 0, 1, 1, 1, 2, 8475);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 0, 1, 1, 1, 3, 8476);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 0, 1, 1, 1, 4, 8477);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 0, 1, 1, 1, 5, 8478);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 0, 1, 1, 4, 101, 8479);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 0, 1, 1, 8, 102, 8480);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 0, 1, 2, 1, 1, 8481);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 0, 1, 2, 1, 2, 8482);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 0, 1, 2, 1, 3, 8483);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 0, 1, 2, 1, 4, 8484);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 0, 1, 2, 4, 101, 8485);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 0, 1, 2, 8, 102, 8486);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 1, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 0, 1, 2, 1, 1, 8487);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 2, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 0, 1, 2, 1, 2, 8488);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 3, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 0, 1, 2, 1, 3, 8489);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 0, 1, 3, 1, 2, 8491);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 0, 1, 3, 1, 3, 8492);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 0, 1, 3, 1, 4, 8493);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 0, 1, 3, 1, 5, 8494);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 0, 1, 3, 1, 7, 8496);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "海に関する詩文なども\nあるのでしょうか…\n気になります", 0, 1, 3, 1, 8, 8497);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 0, 1, 3, 1, 10, 8499);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132101, 0, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 0, 1, 3, 1, 11, 8500);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "これがこの世界の\n居住環境か…\n面白い構造だね", 0, 1, 1, 1, 1, 8501);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 0, 1, 1, 1, 2, 8502);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "目新しいものが\nたくさんあるね\n胸が躍るよ", 0, 1, 1, 1, 3, 8503);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 0, 1, 1, 1, 4, 8504);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 0, 1, 1, 1, 5, 8505);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 0, 1, 1, 4, 101, 8506);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 0, 1, 1, 8, 102, 8507);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 0, 1, 2, 1, 1, 8508);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 0, 1, 2, 1, 2, 8509);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "この世界においては\nボクは異物だからね\n大人しくしているさ", 0, 1, 2, 1, 3, 8510);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 0, 1, 2, 1, 4, 8511);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 0, 1, 2, 4, 101, 8512);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 0, 1, 2, 8, 102, 8513);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 1, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 0, 1, 2, 1, 1, 8514);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 2, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 0, 1, 2, 1, 2, 8515);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 3, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 0, 1, 2, 1, 3, 8516);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 0, 1, 3, 1, 2, 8518);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 0, 1, 3, 1, 3, 8519);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 0, 1, 3, 1, 4, 8520);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 0, 1, 3, 1, 5, 8521);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 0, 1, 3, 1, 7, 8523);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "素晴らしいね\nここには叡智が\n詰まっている", 0, 1, 3, 1, 8, 8524);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 0, 1, 3, 1, 10, 8526);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132001, 0, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 0, 1, 3, 1, 11, 8527);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 0, 1, 1, 12, 103, 8528);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109701, 0, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 0, 1, 2, 12, 103, 8529);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 0, 1, 1, 12, 103, 8530);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109801, 0, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 0, 1, 2, 12, 103, 8531);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 0, 1, 1, 12, 103, 8532);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (109901, 0, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 0, 1, 2, 12, 103, 8533);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 0, 1, 1, 1, 1, 8534);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 0, 1, 1, 1, 2, 8535);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 0, 1, 1, 1, 3, 8536);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 0, 1, 1, 1, 4, 8537);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 0, 1, 1, 1, 5, 8538);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 0, 1, 1, 4, 101, 8539);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 0, 1, 1, 8, 102, 8540);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 0, 1, 2, 1, 1, 8541);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 0, 1, 2, 1, 2, 8542);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 0, 1, 2, 1, 3, 8543);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 0, 1, 2, 1, 4, 8544);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 0, 1, 2, 4, 101, 8545);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 0, 1, 2, 8, 102, 8546);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 1, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 0, 1, 2, 1, 1, 8547);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 2, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 0, 1, 2, 1, 2, 8548);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 3, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 0, 1, 2, 1, 3, 8549);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 0, 1, 3, 1, 2, 8551);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 0, 1, 3, 1, 3, 8552);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 0, 1, 3, 1, 4, 8553);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 0, 1, 3, 1, 5, 8554);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 0, 1, 3, 1, 7, 8556);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132201, 0, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 0, 1, 3, 1, 8, 8557);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 0, 1, 1, 1, 1, 8559);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 0, 1, 1, 1, 2, 8560);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 0, 1, 1, 1, 3, 8561);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 0, 1, 1, 1, 4, 8562);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 0, 1, 1, 1, 5, 8563);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 0, 1, 1, 4, 101, 8564);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 0, 1, 1, 8, 102, 8565);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 0, 1, 2, 1, 1, 8566);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 0, 1, 2, 1, 2, 8567);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 0, 1, 2, 1, 3, 8568);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 0, 1, 2, 1, 4, 8569);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 0, 1, 2, 4, 101, 8570);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 0, 1, 2, 8, 102, 8571);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 1, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 0, 1, 2, 1, 1, 8572);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 2, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 0, 1, 2, 1, 2, 8573);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 3, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 0, 1, 2, 1, 3, 8574);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 0, 1, 3, 1, 2, 8576);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 0, 1, 3, 1, 3, 8577);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 0, 1, 3, 1, 4, 8578);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 0, 1, 3, 1, 5, 8579);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 0, 1, 3, 1, 7, 8581);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127701, 0, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 0, 1, 3, 1, 8, 8582);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 0, 1, 1, 1, 1, 8584);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 0, 1, 1, 1, 2, 8585);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 0, 1, 1, 1, 3, 8586);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 0, 1, 1, 1, 4, 8587);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 0, 1, 1, 1, 5, 8588);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 0, 1, 1, 4, 101, 8589);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 0, 1, 1, 8, 102, 8590);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 0, 1, 2, 1, 1, 8591);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 0, 1, 2, 1, 2, 8592);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 0, 1, 2, 1, 3, 8593);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 0, 1, 2, 1, 4, 8594);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 0, 1, 2, 4, 101, 8595);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 0, 1, 2, 8, 102, 8596);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 1, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 0, 1, 2, 1, 1, 8597);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 2, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 0, 1, 2, 1, 2, 8598);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 3, "よく休んでください\n布団は干しといたんで\n…姉さんが", 0, 1, 2, 1, 3, 8599);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "ふー…\nこのままずーっと\nここに居たい…", 0, 1, 3, 1, 2, 8601);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "あんたも一緒に\nサボっちゃうか～\n…なんて", 0, 1, 3, 1, 3, 8602);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 0, 1, 3, 1, 4, 8603);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 0, 1, 3, 1, 5, 8604);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 0, 1, 3, 1, 7, 8606);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (127801, 0, "食に関する本が\nやけに多い…\nお国柄ですかね", 0, 1, 3, 1, 8, 8607);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 0, 1, 1, 1, 1, 8609);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 0, 1, 1, 1, 2, 8610);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 0, 1, 1, 1, 3, 8611);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 0, 1, 1, 1, 4, 8612);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 0, 1, 1, 1, 5, 8613);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 0, 1, 1, 4, 101, 8614);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 0, 1, 1, 8, 102, 8615);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 0, 1, 2, 1, 1, 8616);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 0, 1, 2, 1, 2, 8617);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 0, 1, 2, 1, 3, 8618);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 0, 1, 2, 1, 4, 8619);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 0, 1, 2, 4, 101, 8620);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 0, 1, 2, 8, 102, 8621);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 1, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 0, 1, 2, 1, 1, 8622);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 2, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 0, 1, 2, 1, 2, 8623);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 3, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 0, 1, 2, 1, 3, 8624);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 0, 1, 3, 1, 2, 8626);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 0, 1, 3, 1, 3, 8627);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 0, 1, 3, 1, 4, 8628);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 0, 1, 3, 1, 5, 8629);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 0, 1, 3, 1, 7, 8631);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130201, 0, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 0, 1, 3, 1, 8, 8632);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 0, 1, 1, 1, 1, 8634);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 0, 1, 1, 1, 2, 8635);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 0, 1, 1, 1, 3, 8636);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 0, 1, 1, 1, 4, 8637);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 0, 1, 1, 1, 5, 8638);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 0, 1, 1, 4, 101, 8639);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 0, 1, 1, 8, 102, 8640);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 0, 1, 2, 1, 1, 8641);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 0, 1, 2, 1, 2, 8642);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 0, 1, 2, 1, 3, 8643);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 0, 1, 2, 1, 4, 8644);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 0, 1, 2, 4, 101, 8645);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 0, 1, 2, 8, 102, 8646);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 1, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 0, 1, 2, 1, 1, 8647);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 2, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 0, 1, 2, 1, 2, 8648);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 3, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 0, 1, 2, 1, 3, 8649);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 0, 1, 3, 1, 2, 8651);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 0, 1, 3, 1, 3, 8652);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 0, 1, 3, 1, 4, 8653);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 0, 1, 3, 1, 5, 8654);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 0, 1, 3, 1, 7, 8656);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130001, 0, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 0, 1, 3, 1, 8, 8657);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 0, 1, 1, 1, 1, 8659);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 0, 1, 1, 1, 2, 8660);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 0, 1, 1, 1, 3, 8661);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 0, 1, 1, 1, 4, 8662);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 0, 1, 1, 1, 5, 8663);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 0, 1, 1, 4, 101, 8664);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 0, 1, 1, 8, 102, 8665);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 0, 1, 2, 1, 1, 8666);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 0, 1, 2, 1, 2, 8667);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 0, 1, 2, 1, 3, 8668);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 0, 1, 2, 1, 4, 8669);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 0, 1, 2, 4, 101, 8670);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 0, 1, 2, 8, 102, 8671);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 1, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 0, 1, 2, 1, 1, 8672);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 2, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 0, 1, 2, 1, 2, 8673);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 3, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 0, 1, 2, 1, 3, 8674);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "これ…割れ物防止の\nクッションに\n使えるかも…？", 0, 1, 3, 1, 2, 8676);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 0, 1, 3, 1, 3, 8677);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 0, 1, 3, 1, 4, 8678);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 0, 1, 3, 1, 5, 8679);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "これがあったら\nもうお料理を\n作らなくて済むかも？", 0, 1, 3, 1, 7, 8681);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (130101, 0, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 0, 1, 3, 1, 8, 8682);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 0, 1, 1, 1, 1, 8684);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 0, 1, 1, 1, 2, 8685);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 0, 1, 1, 1, 3, 8686);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 0, 1, 1, 1, 4, 8687);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 0, 1, 1, 1, 5, 8688);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 0, 1, 1, 4, 101, 8689);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 0, 1, 1, 8, 102, 8690);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 0, 1, 2, 1, 1, 8691);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 0, 1, 2, 1, 2, 8692);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 0, 1, 2, 1, 3, 8693);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 0, 1, 2, 1, 4, 8694);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 0, 1, 2, 4, 101, 8695);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 0, 1, 2, 8, 102, 8696);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 1, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 0, 1, 2, 1, 1, 8697);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 2, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 0, 1, 2, 1, 2, 8698);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 3, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 0, 1, 2, 1, 3, 8699);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 0, 1, 3, 1, 2, 8701);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 0, 1, 3, 1, 3, 8702);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 0, 1, 3, 1, 4, 8703);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 0, 1, 3, 1, 5, 8704);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 0, 1, 3, 1, 7, 8706);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132801, 0, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 0, 1, 3, 1, 8, 8707);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132803, 0, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 0, 1, 3, 1, 14, 8709);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132804, 0, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 0, 1, 3, 1, 15, 8710);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 0, 1, 1, 1, 1, 8711);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 0, 1, 1, 1, 2, 8712);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 0, 1, 1, 1, 3, 8713);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 0, 1, 1, 1, 4, 8714);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 0, 1, 1, 1, 5, 8715);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 0, 1, 1, 4, 101, 8716);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 0, 1, 1, 8, 102, 8717);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 0, 1, 2, 1, 1, 8718);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 0, 1, 2, 1, 2, 8719);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 0, 1, 2, 1, 3, 8720);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 0, 1, 2, 1, 4, 8721);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 0, 1, 2, 4, 101, 8722);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 0, 1, 2, 8, 102, 8723);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 1, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 0, 1, 2, 1, 1, 8724);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 2, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 0, 1, 2, 1, 2, 8725);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 3, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 0, 1, 2, 1, 3, 8726);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 0, 1, 3, 1, 2, 8728);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 0, 1, 3, 1, 3, 8729);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 0, 1, 3, 1, 4, 8730);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 0, 1, 3, 1, 5, 8731);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 0, 1, 3, 1, 7, 8733);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 0, 1, 3, 1, 8, 8734);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 0, 1, 3, 1, 14, 8736);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (132901, 0, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 0, 1, 3, 1, 15, 8737);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 0, 1, 1, 1, 1, 8738);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 0, 1, 1, 1, 2, 8739);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 0, 1, 1, 1, 3, 8740);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 0, 1, 1, 1, 4, 8741);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 0, 1, 1, 1, 5, 8742);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 0, 1, 1, 4, 101, 8743);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 0, 1, 1, 8, 102, 8744);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 0, 1, 2, 1, 1, 8745);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 0, 1, 2, 1, 2, 8746);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 0, 1, 2, 1, 3, 8747);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 0, 1, 2, 1, 4, 8748);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 0, 1, 2, 4, 101, 8749);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 0, 1, 2, 8, 102, 8750);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 1, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 0, 1, 2, 1, 1, 8751);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 2, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 0, 1, 2, 1, 2, 8752);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 3, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 0, 1, 2, 1, 3, 8753);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 0, 1, 3, 1, 2, 8755);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 0, 1, 3, 1, 3, 8756);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 0, 1, 3, 1, 4, 8757);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 0, 1, 3, 1, 5, 8758);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 0, 1, 3, 1, 7, 8760);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 0, 1, 3, 1, 8, 8761);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 0, 1, 3, 1, 10, 8763);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (129501, 0, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 0, 1, 3, 1, 11, 8764);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 0, 1, 1, 1, 1, 8765);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 0, 1, 1, 1, 2, 8766);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 0, 1, 1, 1, 3, 8767);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 0, 1, 1, 1, 4, 8768);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 0, 1, 1, 1, 5, 8769);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 0, 1, 1, 4, 101, 8770);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 0, 1, 1, 8, 102, 8771);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 0, 1, 2, 1, 1, 8772);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 0, 1, 2, 1, 2, 8773);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 0, 1, 2, 1, 3, 8774);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 0, 1, 2, 1, 4, 8775);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 0, 1, 2, 4, 101, 8776);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 0, 1, 2, 8, 102, 8777);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 1, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 0, 1, 2, 1, 1, 8778);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 2, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 0, 1, 2, 1, 2, 8779);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 3, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 0, 1, 2, 1, 3, 8780);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 0, 1, 3, 1, 2, 8782);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 0, 1, 3, 1, 3, 8783);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 0, 1, 3, 1, 4, 8784);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 0, 1, 3, 1, 5, 8785);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 0, 1, 3, 1, 7, 8787);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133001, 0, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 0, 1, 3, 1, 8, 8788);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 0, 1, 1, 1, 1, 8790);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 0, 1, 1, 1, 2, 8791);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 0, 1, 1, 1, 3, 8792);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 0, 1, 1, 1, 4, 8793);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 0, 1, 1, 1, 5, 8794);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 0, 1, 1, 4, 101, 8795);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 0, 1, 1, 8, 102, 8796);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 0, 1, 2, 1, 1, 8797);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 0, 1, 2, 1, 2, 8798);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 0, 1, 2, 1, 3, 8799);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 0, 1, 2, 1, 4, 8800);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 0, 1, 2, 4, 101, 8801);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 0, 1, 2, 8, 102, 8802);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 1, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 0, 1, 2, 1, 1, 8803);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 2, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 0, 1, 2, 1, 2, 8804);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 3, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 0, 1, 2, 1, 3, 8805);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "これに座って飲む\n甘酒は格別だな", 0, 1, 3, 1, 2, 8807);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 0, 1, 3, 1, 3, 8808);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 0, 1, 3, 1, 4, 8809);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 0, 1, 3, 1, 5, 8810);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 0, 1, 3, 1, 7, 8812);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133201, 0, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 0, 1, 3, 1, 8, 8813);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 0, 1, 1, 1, 1, 8815);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 0, 1, 1, 1, 2, 8816);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 0, 1, 1, 1, 3, 8817);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 0, 1, 1, 1, 4, 8818);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 0, 1, 1, 1, 5, 8819);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 0, 1, 1, 4, 101, 8820);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 0, 1, 1, 8, 102, 8821);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 0, 1, 2, 1, 1, 8822);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 0, 1, 2, 1, 2, 8823);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 0, 1, 2, 1, 3, 8824);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 0, 1, 2, 1, 4, 8825);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 0, 1, 2, 4, 101, 8826);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 0, 1, 2, 8, 102, 8827);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 1, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 0, 1, 2, 1, 1, 8828);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 2, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 0, 1, 2, 1, 2, 8829);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 3, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 0, 1, 2, 1, 3, 8830);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 0, 1, 3, 1, 2, 8832);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 0, 1, 3, 1, 3, 8833);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 0, 1, 3, 1, 4, 8834);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 0, 1, 3, 1, 5, 8835);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 0, 1, 3, 1, 7, 8837);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133501, 0, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 0, 1, 3, 1, 8, 8838);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 0, 1, 1, 1, 1, 8840);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "お姉ちゃん…\n早く会いたいなぁ…", 0, 1, 1, 1, 2, 8841);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 0, 1, 1, 1, 3, 8842);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 0, 1, 1, 1, 4, 8843);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 0, 1, 1, 1, 5, 8844);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 0, 1, 1, 4, 101, 8845);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 0, 1, 1, 8, 102, 8846);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 0, 1, 2, 1, 1, 8847);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 0, 1, 2, 1, 2, 8848);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 0, 1, 2, 1, 3, 8849);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 0, 1, 2, 1, 4, 8850);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 0, 1, 2, 4, 101, 8851);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 0, 1, 2, 8, 102, 8852);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 1, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 0, 1, 2, 1, 1, 8853);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 2, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 0, 1, 2, 1, 2, 8854);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 3, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 0, 1, 2, 1, 3, 8855);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 0, 1, 3, 1, 2, 8857);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 0, 1, 3, 1, 3, 8858);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 0, 1, 3, 1, 4, 8859);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 0, 1, 3, 1, 5, 8860);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 0, 1, 3, 1, 7, 8862);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133601, 0, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 0, 1, 3, 1, 8, 8863);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 0, 1, 1, 1, 1, 8865);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 0, 1, 1, 1, 2, 8866);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 0, 1, 1, 1, 3, 8867);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 0, 1, 1, 1, 4, 8868);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 0, 1, 1, 1, 5, 8869);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 0, 1, 1, 4, 101, 8870);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 0, 1, 1, 8, 102, 8871);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 0, 1, 2, 1, 1, 8872);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 0, 1, 2, 1, 2, 8873);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 0, 1, 2, 1, 3, 8874);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 0, 1, 2, 1, 4, 8875);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 0, 1, 2, 4, 101, 8876);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 0, 1, 2, 8, 102, 8877);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 1, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 0, 1, 2, 1, 1, 8878);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 2, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 0, 1, 2, 1, 2, 8879);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 3, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 0, 1, 2, 1, 3, 8880);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 0, 1, 3, 1, 2, 8882);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 0, 1, 3, 1, 3, 8883);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 0, 1, 3, 1, 4, 8884);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 0, 1, 3, 1, 5, 8885);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 0, 1, 3, 1, 7, 8887);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133701, 0, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 0, 1, 3, 1, 8, 8888);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 0, 1, 1, 1, 1, 8890);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 0, 1, 1, 1, 2, 8891);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 0, 1, 1, 1, 3, 8892);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 0, 1, 1, 1, 4, 8893);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 0, 1, 1, 1, 5, 8894);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 0, 1, 1, 4, 101, 8895);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 0, 1, 1, 8, 102, 8896);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 0, 1, 2, 1, 1, 8897);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 0, 1, 2, 1, 2, 8898);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 0, 1, 2, 1, 3, 8899);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 0, 1, 2, 1, 4, 8900);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 0, 1, 2, 4, 101, 8901);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 0, 1, 2, 8, 102, 8902);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 1, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 0, 1, 2, 1, 1, 8903);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 2, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 0, 1, 2, 1, 2, 8904);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 3, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 0, 1, 2, 1, 3, 8905);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 0, 1, 3, 1, 1, 8906);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 0, 1, 3, 1, 2, 8907);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 0, 1, 3, 1, 3, 8908);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 0, 1, 3, 1, 4, 8909);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 0, 1, 3, 1, 5, 8910);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 0, 1, 3, 1, 6, 8911);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 0, 1, 3, 1, 7, 8912);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133901, 0, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 0, 1, 3, 1, 8, 8913);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 0, 1, 1, 1, 1, 8915);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 0, 1, 1, 1, 2, 8916);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 0, 1, 1, 1, 3, 8917);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 0, 1, 1, 1, 4, 8918);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 0, 1, 1, 1, 5, 8919);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 0, 1, 1, 4, 101, 8920);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 0, 1, 1, 8, 102, 8921);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 0, 1, 2, 1, 1, 8922);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 0, 1, 2, 1, 2, 8923);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 0, 1, 2, 1, 3, 8924);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 0, 1, 2, 1, 4, 8925);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 0, 1, 2, 4, 101, 8926);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 0, 1, 2, 8, 102, 8927);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 1, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 0, 1, 2, 1, 1, 8928);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 2, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 0, 1, 2, 1, 2, 8929);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 3, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 0, 1, 2, 1, 3, 8930);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 0, 1, 3, 1, 2, 8932);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 0, 1, 3, 1, 3, 8933);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 0, 1, 3, 1, 4, 8934);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 0, 1, 3, 1, 5, 8935);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 0, 1, 3, 1, 7, 8937);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (133801, 0, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 0, 1, 3, 1, 8, 8938);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 0, 1, 1, 1, 1, 8940);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "唱喚術の練習…\nここでやったら\n迷惑よね…", 0, 1, 1, 1, 2, 8941);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 0, 1, 1, 1, 3, 8942);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 0, 1, 1, 1, 4, 8943);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 0, 1, 1, 1, 5, 8944);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 0, 1, 1, 4, 101, 8945);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 0, 1, 1, 8, 102, 8946);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 0, 1, 2, 1, 1, 8947);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 0, 1, 2, 1, 2, 8948);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 0, 1, 2, 1, 3, 8949);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 0, 1, 2, 1, 4, 8950);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 0, 1, 2, 4, 101, 8951);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 0, 1, 2, 8, 102, 8952);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 1, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 0, 1, 2, 1, 1, 8953);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 2, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 0, 1, 2, 1, 2, 8954);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 3, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 0, 1, 2, 1, 3, 8955);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 0, 1, 3, 1, 2, 8957);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "あなたとも契約…\n…できる、かしら？\nふふっ…", 0, 1, 3, 1, 3, 8958);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 0, 1, 3, 1, 4, 8959);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 0, 1, 3, 1, 5, 8960);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 0, 1, 3, 1, 7, 8962);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134301, 0, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 0, 1, 3, 1, 8, 8963);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 0, 1, 1, 12, 103, 8965);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (122301, 0, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 0, 1, 2, 12, 103, 8966);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 0, 1, 1, 1, 1, 8967);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 0, 1, 1, 1, 2, 8968);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 0, 1, 1, 1, 3, 8969);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 0, 1, 1, 1, 4, 8970);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 0, 1, 1, 1, 5, 8971);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 0, 1, 1, 4, 101, 8972);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 0, 1, 1, 8, 102, 8973);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 0, 1, 2, 1, 1, 8974);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 0, 1, 2, 1, 2, 8975);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 0, 1, 2, 1, 3, 8976);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 0, 1, 2, 1, 4, 8977);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 0, 1, 2, 4, 101, 8978);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 0, 1, 2, 8, 102, 8979);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 1, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 0, 1, 2, 1, 1, 8980);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 2, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 0, 1, 2, 1, 2, 8981);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 3, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 0, 1, 2, 1, 3, 8982);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 0, 1, 3, 1, 2, 8983);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 0, 1, 3, 1, 3, 8984);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 0, 1, 3, 1, 4, 8985);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 0, 1, 3, 1, 5, 8986);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 0, 1, 3, 1, 7, 8987);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134401, 0, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 0, 1, 3, 1, 8, 8988);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 0, 1, 1, 1, 1, 8992);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 0, 1, 1, 1, 2, 8993);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 0, 1, 1, 1, 3, 8994);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 0, 1, 1, 1, 4, 8995);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 0, 1, 1, 1, 5, 8996);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 0, 1, 1, 4, 101, 8997);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 0, 1, 1, 8, 102, 8998);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 0, 1, 2, 1, 1, 8999);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 0, 1, 2, 1, 2, 9000);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 0, 1, 2, 1, 3, 9001);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 0, 1, 2, 1, 4, 9002);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 0, 1, 2, 4, 101, 9003);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 0, 1, 2, 8, 102, 9004);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 1, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 0, 1, 2, 1, 1, 9005);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 2, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 0, 1, 2, 1, 2, 9006);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 3, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 0, 1, 2, 1, 3, 9007);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 0, 1, 3, 1, 2, 9009);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 0, 1, 3, 1, 3, 9010);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 0, 1, 3, 1, 4, 9011);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 0, 1, 3, 1, 5, 9012);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 0, 1, 3, 1, 7, 9014);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134601, 0, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 0, 1, 3, 1, 8, 9015);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 0, 1, 1, 1, 1, 9017);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 0, 1, 1, 1, 2, 9018);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 0, 1, 1, 1, 3, 9019);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 0, 1, 1, 1, 4, 9020);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 0, 1, 1, 1, 5, 9021);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 0, 1, 1, 4, 101, 9022);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 0, 1, 1, 8, 102, 9023);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 0, 1, 2, 1, 1, 9024);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 0, 1, 2, 1, 2, 9025);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 0, 1, 2, 1, 3, 9026);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 0, 1, 2, 1, 4, 9027);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 0, 1, 2, 4, 101, 9028);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 0, 1, 2, 8, 102, 9029);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 1, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 0, 1, 2, 1, 1, 9030);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 2, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 0, 1, 2, 1, 2, 9031);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 3, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 0, 1, 2, 1, 3, 9032);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 0, 1, 3, 1, 2, 9034);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 0, 1, 3, 1, 3, 9035);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 0, 1, 3, 1, 4, 9036);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 0, 1, 3, 1, 5, 9037);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 0, 1, 3, 1, 7, 9039);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134501, 0, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 0, 1, 3, 1, 8, 9040);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 0, 1, 1, 1, 1, 9042);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 0, 1, 1, 1, 2, 9043);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 0, 1, 1, 1, 3, 9044);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "うう…\nおなかが空きました…\nお米が食べたいです…", 0, 1, 1, 1, 4, 9045);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 0, 1, 1, 1, 5, 9046);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 0, 1, 1, 4, 101, 9047);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 0, 1, 1, 8, 102, 9048);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "頭がたかーい！\n…ですよっ！", 0, 1, 2, 1, 1, 9049);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 0, 1, 2, 1, 2, 9050);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 0, 1, 2, 1, 3, 9051);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 0, 1, 2, 1, 4, 9052);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 0, 1, 2, 4, 101, 9053);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 0, 1, 2, 8, 102, 9054);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 1, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 0, 1, 2, 1, 1, 9055);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 2, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 0, 1, 2, 1, 2, 9056);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 3, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 0, 1, 2, 1, 3, 9057);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 0, 1, 3, 1, 2, 9059);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "ふむ…\nそばに置いてあげても\nいいでしょう", 0, 1, 3, 1, 3, 9060);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "ほわぁ～\nいいかおり～", 0, 1, 3, 1, 4, 9061);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "あたたかい…\nなんだか元気が\nでてきますねっ", 0, 1, 3, 1, 5, 9062);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 0, 1, 3, 1, 7, 9064);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (134701, 0, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 0, 1, 3, 1, 8, 9065);
